    ["O"] = "",
    ["D"] = "",
  },
  [2750] = {
    ["T"] = "Ein schlechtes Ei",
    ["O"] = "",
    ["D"] = "",
  },
  [2751] = {
    ["T"] = "Barbarische Waffenschmiedekunst",
    ["O"] = "Bringt 2 gemusterte bronzene Armschienen, 2 bronzene Großschwerter und 2 scharfe Klauen zu Orokk Omosh in Orgrimmar.",
    ["D"] = "Zwanzig Generationen von Omoshs haben diesen Hammer bereits in Händen gehalten, $n. Hunderttausend Tonnen Metall haben wir geformt. Doch leider, leider endet alles mit Orokk, dem Fehlschlag der Omosh: ohne Verwandte und nicht in der Lage, sich fortzupflanzen.$B$BIch kann nicht zulassen, dass die geheimen Rezepturen der Omosh mit mir sterben! Jemand muss das Werk fortführen, ganz gleich, ob derjenige ein Omosh ist oder nicht!$B$BBeweist Orokk, dass Ihr fähig seid, $n. Bringt Orokk die folgenden Dinge: zwei gemusterte bronzene Armschienen, zwei bronzene Großschwerter und zwei scharfe Klauen. Bringt sie Orokk, dann machen wir weiter.",
  },
  [2752] = {
    ["T"] = "Über Eisenschulterstücke",
    ["O"] = "Bringt 4 bronzene Streitäxte und 4 bronzene Kriegshämmer zu Orokk Omosh in Orgrimmar.",
    ["D"] = "Orokk sieht den Eifer in Euren Augen, $n. Wie ein Schwamm saugt Ihr das Wissen der Omoshs auf.$B$BDrei Lektionen werde ich Euch lehren.$B$BKehrt zum Amboss zurück, schwitzt in der Hitze der Schmiede und stellt für Orokk vier bronzene Streitäxte und vier bronzene Kriegshämmer her. Erst dann könnt Ihr etwas über Eisenschulterstücke lernen.",
  },
  [2753] = {
    ["T"] = "In den Boden getrampelt",
    ["O"] = "Bringt 4 grüne Eisenhelme, 4 grüne Eisenarmschienen und 2 grüne Eisengamaschen zu Orokk Omosh in Orgrimmar.",
    ["D"] = "Nur zwei Lektionen noch hat Orokk für Euch, $n. Eure Muskeln schmerzen, ich weiß, doch macht weiter - für die Omoshs!$B$BBringt Orokk vier grüne Eisenhelme, vier grüne Eisenarmschienen und zwei grüne Eisengamaschen - zur Belohnung werdet Ihr lernen und Eure Feinde werden leiden!",
  },
  [2754] = {
    ["T"] = "Hörner der Raserei",
    ["O"] = "Bringt 2 robuste Eisenschlägel, 2 bronzene Stiefel mit Versilberung und 2 bronzene Stulpen mit Versilberung zu Orokk Omosh in Orgrimmar.",
    ["D"] = "Jetzt lehrt Orokk Euch die letzte Lektion, $n. Zurück zum heiligen Amboss und der heiligen Schmiede mit Euch! Bringt Orokk zwei robuste Eisenschlägel, zwei bronzene Stiefel mit Versilberung und zwei bronzene Stulpen mit Versilberung.$B$BSeht die Furcht in den Augen der Feinde, wenn Ihr Euch nähert!",
  },
  [2755] = {
    ["T"] = "Freude der Omoshs",
    ["O"] = "Seht zu und lernt den Freudentanz der Omoshs.",
    ["D"] = "Orokk weint nicht, $n. Orokk hat Ruß ins Auge bekommen.$B$B<Orokk wischt eine Träne weg.>$B$BNoch ein letztes Familiengeheimnis der Omosh wird Orokk Euch beibringen. Dank $n wird Orokk glücklich sterben.$B$BLernt den Freudentanz der Omoshs... und dann lernt Ihr Eisenfaust!",
  },
  [2756] = {
    ["T"] = "Die alten Traditionen",
    ["O"] = "Bringt 4 Stahlbrustplatten und 4 Stahlplattenhelme zu Aturk dem Amboss in Orgrimmar.",
    ["D"] = "Früher, zur Zeit der Legenden, zerquetschte Aturk die Schädel seiner Feinde und zermahlte die Knochen zu einem feinen Pulver, das er zu Fasern verarbeitete. Diese Tage sind lange vergangen, $R. Der Kriegshäuptling verlangt Frieden unter den Klans. Alte Feinde werden nur noch misstrauisch beäugt!$B$BEs ist unglaublich, ich weiß, doch es ist nun einmal Thralls Wille.$B$BAber genug gejammert! Ihr seid zum Lernen hier, und Ihr werdet etwas lernen! Bringt mir vier Stahlbrustplatten und vier Stahlhelme, dann bekommt Ihr den Geschmack des Krieges zu kosten!",
  },
  [2757] = {
    ["T"] = "Auf nach Beutebucht!",
    ["O"] = "Sprecht mit McGavan in Beutebucht.",
    ["D"] = "Hört mal, wenn Ihr hier herumhängt, lernt Ihr nix.$B$BIch kenn da so \'n Typen, der \'n Typen kennt. Der treibt sich bei der Schmiede in Beutebucht rum. Wenn Ihr ihm das Insigne hier zeigt, dann erzählt er Euch alles über Mithril.$B$BAlso, was sagt Ihr? Überlasst diesen Babykram den Kleinkindern, $n - für Euch ist es Zeit weiterzuziehen.$B$BFalls Ihr Interesse haben solltet, der Typ heißt McGavan. Zeigt ihm das hier, dann wird er sehen, dass es Euch ernst ist.",
  },
  [2758] = {
    ["T"] = "Die Herkunft der Schmiedekunst",
    ["O"] = "Bringt 6 goldene Schuppenarmschienen zu Hank dem Hammer in Sturmwind.",
    ["D"] = "Als ich etwa so alt war wie Ihr, nahm mich ein Zwerg namens Galvan unter seine Fittiche und bildete mich in der Kunst des Rüstungsschmiedens aus. Bevor er sich nach Beutebucht aufmachte, nahm er mir das Versprechen ab, dass ich all denjenigen ein Lehrer sein würde, die den Wunsch hätten und entschlossen genug wären, selbst eine Schmiedelaufbahn einschlagen zu wollen. Und hier bin ich nun - all die Jahre später.$B$BAlso, es ist jetzt Zeit für Euch, ein bisschen was zu lernen. Stellt mir sechs goldene Schuppenarmschienen her, dann zeige ich Euch, was echte Schmiedekunst ist, $n.",
  },
  [2759] = {
    ["T"] = "Auf der Suche nach Galvan",
    ["O"] = "Sprecht in Beutebucht mit McGavan.",
    ["D"] = "Obwohl Galvan Beutebucht schon vor vielen Jahren verlassen hat, leben noch immer Mitglieder des Ordens in der Stadt und hoffen, dass sie eines Tages jemanden finden werden, der der Lehre von Galvan würdig ist.$B$BNehmt dieses Insigne und zeigt es McGavan in Beutebucht.$B$BSolltet Ihr es tatsächlich bis dorthin schaffen, dann weiß er, dass Ihr würdig seid. Ich glaube an Euch, $n!",
  },
  [2760] = {
    ["T"] = "Der Mithrilorden",
    ["O"] = "Sprecht mit Galvan dem Alten im Schlingendorntal.",
    ["D"] = "Die meisten unserer Rekruten schaffen es nicht bis Beutebucht, $n. Der Glanz des Insigne reicht normalerweise aus, um selbst im zuverlässigsten Schmied die Beutegier ausbrechen zu lassen. Narren, alle zusammen, denn das Insigne ist nichts als Talmi im Vergleich zum Wert der Gegenstände, die Ihr zu sehen bekommen werdet!$B$BFindet Galvan den Alten und zeigt ihm diese Anstecknadel. Er lebt weit im Norden, zwischen Zul\'Gurub und dem Ogerhügel der Mosh\'Ogg.",
  },
  [2761] = {
    ["T"] = "Hier verhütten, da verhütten",
    ["O"] = "Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 40 Eisenbarren.",
    ["D"] = "Viele Schmiede glauben, der Bergbau und die Schmiedekunst seien zwei völlig getrennte, voneinander unabhängige Dinge, aber wie kann es Schmiedekunst geben ohne Bergbau? Genau, das ist völlig unmöglich! Und daher muss der vollendete Schmied auch ein tüchtiger Minenarbeiter sein.$B$BZeigt Galvan, dass Ihr fähig seid, $n. Bringt mir vierzig Barren Eisen und vierzig Barren Mithril. Dafür sagt Galvan Euch dann alles über verschnörkelte Mithrilhosen!",
  },
  [2762] = {
    ["T"] = "Der große silberne Betrüger",
    ["O"] = "Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 5 Echtsilberbarren.",
    ["D"] = "Es heißt, dass Echtsilber noch stärker und haltbarer sei als Mithril. Galvan widerspricht dieser Einschätzung der Mineralien zwar nicht, aber Echtsilber trägt seinen Namen zu Unrecht. Echtsilber ist nicht echt! Mithril ist ein wesentlich edleres Mineral! HA! Aber wie dem auch sei...für diese Lektion müsst Ihr den silbernen Betrüger finden, $n.$B$BBringt mir vierzig Barren Mithril und fünf Barren Echtsilber. Im Gegenzug werdet Ihr in das Geheimnis der verschnörkelten Mithrilhandschuhe eingeführt, und, was noch wichtiger ist, Ihr werdet lernen, dass Echtsilber lügt!",
  },
  [2763] = {
    ["T"] = "Die Kunst der Magieerfüllung",
    ["O"] = "Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 4 Citrine.",
    ["D"] = "Der Abbau von wertvollen Edelsteinen ist für unsere Zunft von äußerster Wichtigkeit, $n. Und nur wenige Edelsteine sind wertvoller als der Citrin. Die magischen Eigenschaften, mit denen wir unsere Waren ausstatten, werden deutlich verstärkt, wenn Citrine im Spiel sind.$B$BIn dieser Lektion verstärken wir vierzig Barren Mithril mit vier Citrinen. So bekommt Ihr ein besseres Verständnis für die magische Edelsteinkunde und lernt außerdem, wie man verschnörkelte Mithrilschultern herstellt.",
  },
  [2764] = {
    ["T"] = "Galvans bester Schüler",
    ["O"] = "Sprecht in Gadgetzan mit Trenton Lichthammer.",
    ["D"] = "Wenn Ihr bereit seid, Eure Ausbildung bei Galvan abzuschließen, dann solltet Ihr nach Gadgetzan reisen.$B$BDort findet Ihr meinen besten Schüler, Trenton Lichthammer. Er kann Euch viel über verschnörkeltes Mithril beibringen. Ansonsten bleibt hier und lernt weiter!$B$BAch ja, Gadgetzan liegt natürlich in Tanaris!",
  },
  [2765] = {
    ["T"] = "Schmiedkunstexperte!",
    ["O"] = "Fordert Eure Belohnung von Galvan dem Alten ein!",
    ["D"] = "Schon als Ihr das erste Mal zu mir kamt, wusste ich, dass Ihr Euch als einer meiner besten Schüler herausstellen würdet! Ohne zu zögern trenntet Ihr Euch von einem nicht recht mächtigen Gegenstand und gabt ihn McGavan. Ihr tatet dies, nur damit Ihr mich treffen konntet. Solch blindes Vertrauen und Hingabe an unser Handwerk soll nicht unbelohnt bleiben.$B$BIhr seid zu einem wahren Experten der Schmiedekunst geworden, $n. Nun erntet den Lohn dafür!",
  },
  [2766] = {
    ["T"] = "Findet OOX-22/FE!",
    ["O"] = "Bringt das Notrufsignal zu Glotz\' Leitroboter in einer Höhle in der Nähe des Wildschrammtals.",
    ["D"] = "Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zum Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.$B$B\"Mein Name ist Glotz Widrikus und mein Leitroboter ist abgestürzt! Ich werde Euch belohnen, wenn Ihr ihn aufspürt. Bringt dem Roboter bitte diesen Signalgeber!\"$B$B\"Ich habe jetzt die Koordinaten des Roboters für Euch berechnet. Er ist in der Nähe des Wildschrammtals abgestürzt!\"",
  },
  [2767] = {
    ["T"] = "Rettet OOX-22/FE",
    ["O"] = "Bringt OOX-22/FE in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["D"] = "Aus dem Roboter ertönt erneut die blecherne Stimme des Gnoms:$B$B\"Ich muss OOX-22/FE an einen offenen, sicheren Ort schaffen, wo er die langwierige Startprozedur beginnen kann. Er hat eine eingebaute Tarnvorrichtung, aber ich brauche etwas Zeit, um sie von hier aus wieder zum Funktionieren zu bringen. Eskortiert den Roboter von seinem momentanen Standort zu, ach, sagen wir mal, der Hauptstraße. Das sollte der perfekte Ort für mein Vorhaben sein, und bis Ihr dort seid, sollte ich auch alles vorbereitet haben und online gehen können.\"$B$B\"Eskortiert ihn dorthin und kommt dann zu mir nach Beutebucht! Glotz Widrikus - Ende!\"",
  },
  [2768] = {
    ["T"] = "Wünschel-mato-Rute",
    ["O"] = "Bringt die Wünschel-mato-Rute nach Gadgetzan zu Chefingenieur Bilgenritzel.",
    ["D"] = "Unteroffizier Bly hat mich bestohlen! Er sagte, er wolle sie sich nur ausleihen, aber er hat meine geliebte Wünschel-mato-Rute gestohlen! Wo soll ich denn bitte ohne diese Rute wissen, wo ich neue Wasserlöcher graben muss?$B$BFindet Bly und bringt mir meine Rute! Ich habe gehört, er hätte seine Bande von zwielichtigen Abenteurern auf eine Schatzsuche nach Zul\'Farrak im Westen geführt. Ich möchte wetten, dort findet Ihr ihn.$B$BHoffen wir, dass die Trolle ihn schon erledigt haben, denn falls Ihr Euch wegen der Rute mit ihm anlegen müsst, dann steht Euch ein schwerer Kampf bevor.",
  },
  [2769] = {
    ["T"] = "Die Brüder Kupferbolz",
    ["O"] = "Sprecht mit Wizzel Kupferbolz in der Schimmernden Ebene.",
    ["D"] = "Seid Ihr den Gebrüdern Kupferbolz schon mal begegnet? Sie sind drüben in der Schimmernden Ebene, in Kalimdor, um genau zu sein, und treten dort in Raketenwagenrennen gegen die Goblins an. Normalerweise gewinnen sie, aber das ist keine große Überraschung - der Wagen der Goblins fliegt so oft in die Luft, dass er es kaum jemals über die Ziellinie schafft!$B$BAber trotzdem suchen die Kupferbolz-Brüder ständig nach neuen Möglichkeiten, ihren Wagen noch schneller zu machen, und Wizzel hat mir gesagt, dass er für sein nächstes Experiment noch eine sehr seltene Komponente benötigt.$B$BWahrscheinlich kommt er an dieses Ersatzteil nicht allein ran. Sprecht mit ihm, dann erfahrt Ihr mehr darüber.",
  },
  [2770] = {
    ["T"] = "Gahz\'rilla",
    ["O"] = "Bringt Wizzel Kupferbolz in der Schimmernden Ebene Gahz\'rillas energiegeladene Schuppe.",
    ["D"] = "Tief im Inneren von Zul\'Farrak, der Stadt der Sandtrolle in Tanaris, liegt ein hochheiliger Teich. An diesem Teich beschwören die Trolle eine gewaltige Bestie, Gahz\'rilla! Der ist so wild, dass sogar seine Schuppen vor Energie knistern. Und genau diese Energie will ich in meinem Wagen einsetzen.$B$BBringt mir die energiegeladene Schuppe von Gahz\'rilla!$B$BWie man Gahz\'rilla allerdings beschwört, das verraten die Trolle niemandem. Wenn Ihr ihm begegnen wollt, müsst Ihr daher erst den Trollen das Geheimnis entreißen.",
  },
  [2771] = {
    ["T"] = "Ein schlauer Kopf auf den Schultern",
    ["O"] = "Bringt Trenton Lichthammer 2 Mithrilhelmkappen und 1 verschnörkelte Mithrilschulter.",
    ["D"] = "Es scheint, als habe der Meister dies von Anfang an geplant. Ich habe nicht gelernt, solche Gegenstände herzustellen, wie Ihr es könnt, und Ihr habt nicht gelernt, solche Gegenstände herzustellen, wie ich es kann.$B$BDer Meister ist klug, $n. Wir werden uns gegenseitig etwas beibringen!$B$BZeigt mir, wie man eine Mithrilhelmkappe und verschnörkelte Mithrilschultern herstellt, dann zeige ich Euch, wie man einen verschnörkelten Mithrilhelm macht.",
  },
  [2772] = {
    ["T"] = "Die Welt zu Euren Füßen",
    ["O"] = "Bringt Trenton Lichthammer 2 schwere Mithrilstiefel und 1 verschnörkelte Mithrilhose.",
    ["D"] = "Bei der Talmeisterschaft der Schmiedekunst letztes Jahr mussten alle, die zusahen, erleben, wie mir so ein feiger Hund von der Gießerei der Venture Company die Beine unter dem Körper wegzog. Aber den Grund, warum ich weitermachen und die Meisterschaft am Ende gewinnen konnte, den wusste niemand. Genau, verschnörkelte Mithrilstiefel!$B$BAber für die Meisterschaft dieses Jahr muss ich lernen, wie man verschnörkelte Mithrilhosen fertigt, denn ich muss gegen einen Schredder der Venture Co. antreten! Bringt mir bei, wie man die Hosen anfertigt, und lernt, was es mit den Stiefeln auf sich hat!",
  },
  [2773] = {
    ["T"] = "Mithril-Kid",
    ["O"] = "Bringt Trenton Lichthammer 2 schwere Mithrilbrustplatten und 1 verschnörkelten Mithrilhandschuh.",
    ["D"] = "Hier in der Gegend bin ich als Mithril-Kid bekannt. Ich beliefere Abenteurer aus aller Welt mit verschnörkeltem Mithril.$B$B<Trenton flüstert.>$B$BDie wissen aber nicht, dass ich bisher noch keine verschnörkelten Mithrilhandschuhe herstellen kann. Psst! Das bleibt unser kleines Geheimnis, ja?$B$BBringt mir bei, wie man verschnörkelte Mithrilhandschuhe fertigt, $n, dann zeige ich Euch im Gegenzug den Höhepunkt meiner Ausbildung unter Galvan: die verschnörkelte Mithrilbrustplatte.",
  },
  [2781] = {
    ["T"] = "GESUCHT: Kalif Skorpidstich",
    ["O"] = "Bringt Chefingenieur Bilgenritzel in Gadgetzan den Kopf von Kalif Skorpidstich.",
    ["D"] = "GESUCHT: Kalif Skorpidstich!$B$BDie Gadgetzan Water Company aus Gadgetzan in Tanaris ist bereit, eine hohe Belohnung für den Kopf von Kalif Skorpidstich, dem Anführer der Wüstenläufernomaden, zu zahlen. Seine Verbrechen belaufen sich auf:$B$B1. Ermordung von Angestellten der Gadgetzan Water Company!$B2. Stehlen von Rohstoffen der Gadgetzan Water Company!$B3. Besetzen von Eigentum der Gadgetzan Water Company!$B$BBringt Beweise für Kalif Skorpidstichs Untergang zu Chefingenieur Bilgenritzel und Ihr werdet umgehend belohnt!",
  },
  [2782] = {
    ["T"] = "Rin\'jis Geheimnis",
    ["O"] = "Oran Schlangenfuß, eine bekannte Gelehrte auf dem Gebiet der Bleichborkentrolle, könnte die Tafel vielleicht übersetzen. Normalerweise hält sie sich im Magierviertel von Unterstadt auf.",
    ["D"] = "Die Tafel ist mit Schriftzeichen der Bleichborkentrolle bedeckt. Rin\'jis Geheimnis ist wirklich ein Mysterium... $B$BGut möglich, dass Ihr hier etwas ziemlich Wertvolles in Händen haltet. Andererseits könnte ebenso gut das Gegenteil der Fall sein. Ein Troll der Bleichborken in Not würde wahrscheinlich alles tun, um einen Ausweg aus seinen Problemen zu finden...",
  },
  [2783] = {
    ["T"] = "Kleinliches Gezänk",
    ["O"] = "Sprecht mit dem gefallenen Helden der Horde im Grenzgebiet zwischen den Verwüsteten Landen und dem Sumpf.",
    ["D"] = "Wir bekämpfen den falschen Feind, $n.$B$BDie wahre Macht in diesem Land wird zahlen- und kräftemäßig immer stärker, während wir uns mit den Orcs über verbrannte Erde zanken.$B$BSollen wir wirklich in diesem Land einen Krieg gegen die Horde führen, nur um eines Tages von den Armeen der Brennenden Legion hinweggefegt zu werden? Es ist sinnlos.$B$BBegrabt Eure Streitigkeiten, $n, und sprecht mit dem gefallenen Helden der Horde. Er kennt die Wahrheit über die Verwüsteten Lande.",
  },
  [2784] = {
    ["T"] = "In Ungnade fallen",
    ["O"] = "Lauscht der Geschichte des gefallenen Helden der Horde.",
    ["D"] = "Hört meine Geschichte, $R.",
  },
  [2801] = {
    ["T"] = "Eine Geschichte voller Leid",
    ["O"] = "Lauscht der Geschichte des gefallenen Helden der Horde.",
    ["D"] = "Bevor wir fortfahren können, müsst Ihr meine Geschichte hören.",
  },
  [2821] = {
    ["T"] = "Das Zeichen der Qualität",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 10 dicke Yetibälge.",
    ["D"] = "Meine Einstellung zur Lederverarbeitung verlangt, dass die Arbeit von bestmöglicher Qualität zu sein hat - ohne Kompromisse. Jeder, der behauptet, Qualität sei unwichtig, wird auch versuchen, Euch ein Küstengrundstück in Dun Morogh zu verkaufen!$B$BAuf meiner jüngsten Suche nach hochwertigem Material fand ich dicke Yetibälge. Die Yetis sind in Feralas zuhause und viele findet man im Wildschrammtal. Ich hätte gern welche, und wenn Ihr mir zehn davon bringt, dürft Ihr Euch ein beliebiges Stück meiner Lederwaren aussuchen. Haltet Ihr das für angemessen? Dann sind wir im Geschäft.",
  },
  [2822] = {
    ["T"] = "Das Zeichen der Qualität",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 10 dicke Yetibälge.",
    ["D"] = "Ich bin stolz auf meine Lederarbeiten und ich glaube, das sieht man ihnen an; Qualität sollte niemals zweitrangig sein. Jeder, der behauptet, Qualität sei unwichtig, wird Euch wahrscheinlich auch glauben machen wollen, dass die Erde keine Geheimnisse enthält!$B$BAuf meiner jüngsten Suche nach hochwertigem Material habe ich dicke Yetibälge entdeckt. Die Yetis sind in Feralas beheimatet und man findet sie häufig westlich des Wildschrammtals. Ihr dürft Euch ein beliebiges Stück meiner Lederwaren aussuchen für einen Zehnerstapel Yetibälge. Wenn das für Euch akzeptabel ist, dann sind wir im Geschäft!",
  },
  [2841] = {
    ["T"] = "Maschinenkriege",
    ["O"] = "Besorgt die Maschinenblaupausen und Thermadrahts Safekombination aus Gnomeregan und bringt sie zu Nogg nach Orgrimmar.",
    ["D"] = "Vor zwei Jahren noch hat man sich jedes Mal darüber lustig gemacht, wenn die Rede auf Gnomentechnologie kam. Diese Zeiten haben sich definitiv geändert. Jetzt haben sie eine neue Maschine gebaut, die besser ist als all unsere Schreddermodelle zusammen. Ich brauche die Blaupausen dafür, $n!$B$BDummerweise sind die Pläne in einem Safe bei Robogenieur Thermadraht sicher verwahrt. Was die Sache noch erschwert: Nur Robogenieur Thermadraht kennt die Kombination zu diesem Safe.$B$BTut, was Ihr tun müsst, um diese Kombination zu erlangen, $n. Dann öffnet den Safe und bringt mir diese Blaupausen!",
  },
  [2842] = {
    ["T"] = "Chefingenieur Scooty",
    ["O"] = "Sprecht mit Scooty in Beutebucht.",
    ["D"] = "In dem Moment, als wir durch Kernobee von der neuen Gnomenmaschine hörten, haben Scooty und ich mit der Arbeit am Transpolyporter 6000 begonnen: dem schnellsten und verlässlichsten Weg nach Gnomeregan.$B$BEndlich ist er bereit, Passagiere aufzunehmen, $n. Wenn Ihr es einmal versuchen wollt, geht nach Beutebucht und redet mit Scooty am Windreiterhorst. Um nach Beutebucht zu gelangen, nehmt das Boot von Ratschet aus.$B$BNatürlich könnt Ihr auch versuchen, Euch durch die von Zwergen verseuchten Berge von Dun Morogh nach Gnomeregan durchzuschlagen!",
  },
  [2843] = {
    ["T"] = "Gnomer-weeeeg!",
    ["O"] = "Wartet, bis Scooty den Goblintransponder kalibriert hat.",
    ["D"] = "Solange Ihr den Transponder bei Euch habt, müsst Ihr lediglich auf diese Plattform hier treten und Ihr werdet nach Gnomeregan teleportiert.$B$BIhr werdet vielleicht ein paar ungewöhnliche Nebenwirkungen erleben, aber keine davon sollte dauerhaft sein... zumindest nicht mehr.$B$BIch hätte fast den wichtigsten Teil vergessen - die Rückkehr. Wenn Ihr wieder zurückkommen wollt, tretet einfach auf die Transpolyporter-Plattform in Gnomeregan!$B$BJetzt lasst uns diesen Transponder auf Euer Gewicht und Eure Größe einstellen.",
  },
  [2844] = {
    ["T"] = "Der Riesenwächter",
    ["O"] = "Findet das verlorene Nachtelfenmädchen.",
    ["D"] = "Ich bin so unfähig, $n. Ich sollt\' auf meine kleine Freundin, Shay, aufpassen, aber irgendwie ist sie mir verlor\'n gegangen. Ich kann nicht genau sagen, was passiert ist, ehrlich, aber als ich sie das letzte Mal gesehen habe, lief\'n wir durch die Ruinen direkt nördlich von hier.$B$BAlles war wunderbar, aber dann hab ich mich umgedreht, un\'... sie war weg.$B$BUnd jetz\' hab ich keine Ahnung, was ich tun soll. Habt Ihr \'ne Idee, $n? Ich mache mir furchtbare Sorgen.$B$BHey, wie wär\'s, wenn Ihr nach Shay suchen würdet? Eure Beine sind viel schneller als meine.",
  },
  [2845] = {
    ["T"] = "Streunende Shay",
    ["O"] = "Nehmt Shays Glocke aus der Truhe.$B$BBegleitet Shay Laubschleicher zu Felsbeißers Lager.",
    ["D"] = "Glaubt Ihr, Ihr könnt mir helfen, meinen Weg zurück zu meinem Freund, Felsbeißer, zu finden? Er sollte auf mich aufpassen... Aber ich neige dazu, gelegentlich davonzuschlendern. Es gibt immer so viele Dinge zu sehen und ich lasse mich dann ablenken.$B$BEs gibt allerdings etwas, was immer meine Aufmerksamkeit erregt.$B$BIn dieser Truhe findet Ihr eine Glocke, die mir sehr teuer ist. Ich liebe einfach ihren Ton. Ihr habt mein Wort: Wenn ich davonschlendere, müsst Ihr lediglich damit klingeln und ich komme angelaufen.",
  },
  [2846] = {
    ["T"] = "Tiara der Tiefen",
    ["O"] = "Bringt die Tiara der Tiefen zu Tabetha in den Düstermarschen.",
    ["D"] = "Vor langer Zeit besaß ich ein wunderschönes Schmuckstück, die Tiara der Tiefen. Diese war nicht nur schön, sondern enthielt auch große Macht für diejenigen, die damit umzugehen wussten.$B$BAls nun die Kunde von der Tiara die Hydromantin Velratha erreichte, musste sie sie haben. Sie schickte Handlanger in mein Haus und die stahlen sie, während ich weg war. Diese Diebe!$B$BIch will meine Tiara wiederhaben! Geht nach Zul\'Farrak, der Trollstadt in Tanaris, findet Velratha und ringt ihr die Tiara ab. Bringt sie mir zurück und Ihr steht in meiner Gunst.",
  },
  [2847] = {
    ["T"] = "Wildlederrüstung",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 10 Stücke dickes Leder.",
    ["D"] = "$n, Ihr werdet ziemlich gut als Lederer! Ich kenne da einige Muster, an denen Ihr vielleicht interessiert seid... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was jemand Eures Geschicks herstellen kann.$B$BIch werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details.",
  },
  [2848] = {
    ["T"] = "Wildlederschultern",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 6 dicke Rüstungssets und eine Wildranke.",
    ["D"] = "Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei erstklassigen Schutz für die Oberarme und die Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine mächtige, allerdings zufällige Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.$B$BFür dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein.",
  },
  [2849] = {
    ["T"] = "Wildlederweste",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke.",
    ["D"] = "Wildlederwesten sind die Grundlage aller Wildlederrüstungssets. Das Gerüst der Weste verbindet sich mit der Wildranke, um ein Produkt zu erzeugen, das sowohl maximalen Schutz für den Torso bietet als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.$B$BFür dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Das ist ein großer Auftrag, aber sicher keiner, bei dessen Erfüllung Ihr Schwierigkeiten haben werdet.",
  },
  [2850] = {
    ["T"] = "Wildlederhelm",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke.",
    ["D"] = "Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige Verbesserung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!$B$BFür dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Erfüllt diesen Auftrag und das Muster gehört Euch!",
  },
  [2851] = {
    ["T"] = "Wildlederstiefel",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken.",
    ["D"] = "Ihr habt schon eine ganze Menge gelernt, $n; dennoch gibt es noch mehr für Euch zu lernen. Als Nächstes sind Wildlederstiefel an der Reihe, der Grundstein eines jeden Sets.$B$BDiese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteuerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.$B$BFür dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers besorgen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr all das beisammen habt, gebt mir Bescheid.",
  },
  [2852] = {
    ["T"] = "Wildledergamaschen",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.",
    ["D"] = "Jetzt, nachdem Ihr die Grundlagen beherrscht, seid Ihr bereit, Euch komplexeren Dingen zuzuwenden, $n. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.$B$BFür dieses Muster müsst Ihr mir 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken besorgen.",
  },
  [2853] = {
    ["T"] = "Meister des Wildleders",
    ["O"] = "Gebt Telonis, dem Lederverarbeitungsmeister von Darnassus, Pratts Brief.",
    ["D"] = "Nun, $n, Ihr gebt dem Wort Sorgfalt eine neue Bedeutung. Es steht nur noch ein Wildlederrüstungsmuster aus, denn Ihr habt alle anderen gemeistert. Ich werde nicht von Euch verlangen, Euch unnötig für dieses Muster abzuplagen. Ihr habt es Euch bereits mehr als verdient... Aber ich kann es Euch nicht selbst geben. Mein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen.$B$BBringt diesen Brief zu Telonis. Er lehrt Lederverarbeitung in Darnassus. Zeigt ihm den Brief und lasst Euch von ihm als Gleichgestellten anerkennen, so wie ich das auch tue.",
  },
  [2854] = {
    ["T"] = "Wildlederrüstung",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 10 Stücke dickes Leder.",
    ["D"] = "$n, Euer Ruf als Lederer eilt Euch voraus. Ich besitze Wissen über die Lederverarbeitung, das zu erwerben weise von Euch wäre... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was ein Handwerker Eures Geschicks herstellen kann.$B$BIch werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details.",
  },
  [2855] = {
    ["T"] = "Wildlederschultern",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 6 dicke Rüstungssets und eine Wildranke.",
    ["D"] = "Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei besten Schutz für Oberarme und Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine zufällige, aber magieerfüllte Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.$B$BFür dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein.",
  },
  [2856] = {
    ["T"] = "Wildlederweste",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenbrustplatten, 2 Schildkrötenschuppenhandschuhe und eine Wildranke.",
    ["D"] = "Wildlederwesten sind die Grundlage jeglicher Wildlederrüstungssets. Die Struktur der Weste und das Wildrankengeflecht darin erzeugen ein Produkt, das sowohl maximalen Schutz für den Torso bietet, als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.$B$BFür dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Dies ist keine einfache Lektion, aber eine, für die Ihr sicherlich das nötige Geschick habt.",
  },
  [2857] = {
    ["T"] = "Wildlederhelm",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke.",
    ["D"] = "Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige magische Verstärkung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!$B$BFür dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Schließt diese Lektion erfolgreich ab und das Muster gehört Euch!",
  },
  [2858] = {
    ["T"] = "Wildlederstiefel",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken.",
    ["D"] = "Euer Wissen über die Herstellung von Wildlederrüstungen wächst, $n, aber trotzdem habt Ihr noch einiges zu lernen.$B$BWildlederstiefel sind die Grundlage eines jeden Sets. Diese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteurerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.$B$BFür dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers herstellen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr diese Dinge habt, werden wir weitermachen.",
  },
  [2859] = {
    ["T"] = "Wildledergamaschen",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.",
    ["D"] = "Ihr seid so weit, komplexere Lektionen zu meistern, $n. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.$B$BUm Euch dieses Muster zu geben, benötige ich 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.",
  },
  [2860] = {
    ["T"] = "Meister des Wildleders",
    ["O"] = "Gebt Una, der Lederverarbeitungsmeisterin von Donnerfels, Jangdors Schreiben.",
    ["D"] = "$n, Ihr habt alles erlernt, was ich Euch lehren kann. Es gibt allerdings noch ein letztes Wildlederrüstungsmuster. Ich werde nicht von Euch verlangen, Euch unnötig dafür abzuplagen. Ihr habt es Euch bereits mehr als verdient. Jedoch kann ich dieses Muster nicht einfach mit Euch teilen.$B$BMein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen. Bringt diese Notiz zu Una. Sie lehrt Lederverarbeitung in Donnerfels. Zeigt ihr den Brief und lasst Euch von ihr als Gleichgestellten anerkennen, so wie ich das auch tue.",
  },
  [2861] = {
    ["T"] = "Tabethas Aufgabe",
    ["O"] = "Sprecht mit Tabetha in den Düstermarschen.",
    ["D"] = "Gerüchte besagen, dass Tabetha, eine Magierkollegin, die tief in den Düstermarschen lebt, ein Hühnchen mit einem gewissen Sandtroll in Zul\'Farrak zu rupfen hat. Wenn Ihr mit Tabetha sprecht, kann sie Euch sicher mehr dazu sagen.$B$BUnd nehmt ein paar Freunde mit, $n. Falls Tabetha Euch nach Zul\'Farrak schickt, wollt Ihr sicher nicht allein hingehen.$B$BIhr findet Tabethas Hütte westlich von Theramore und direkt nördlich der Ruinen der Steinbrecher.",
  },
  [2862] = {
    ["T"] = "Krieg gegen die Waldpfoten",
    ["O"] = "Bringt Hadoken Flinkschreiter im Camp Mojache 10 Mähnen eines Waldpfotengnolls.",
    ["D"] = "Die Gnolle... sie sind mehr als nur ein Ärgernis. Sie sind eine konstante Bedrohung für die Existenz dieses gesamten Lagers! Es ist unwichtig, ob sie Eingeborene des Feralas sind oder nicht. Wenn wir uns nicht gegen sie wappnen, werden wir uns eines Tages aus dem gesamten Gebiet vertrieben sehen.$B$BDie Gnollangriffe auf das Lager wurden in letzter Zeit heftiger. Wir brauchen Eure Hilfe dabei, ihre Anzahl zu reduzieren. Bringt mir zehn ihrer Mähnen und ich werde Euch stattlich für Eure Mühe entlohnen.$B$BLos!",
  },
  [2863] = {
    ["T"] = "Schlag gegen die Alphas",
    ["O"] = "Tötet 5 Alphas der Waldpfoten und kehrt innerhalb einer Stunde zu Hadoken Flinkschreiter zurück.",
    ["D"] = "Der Plan besteht darin, verschiedene Anführer der Gnolle - Alphas, wie sie sich selbst nennen - in schneller Folge auszuschalten. Wenn wir einen solch schnellen und präzisen Schlag gegen sie erzielen können, würde es in ihren Rängen zu Chaos führen. Vielleicht würde es dann endlich auch in ihren dicken, von Flöhen zerbissenen Köpfen klar, dass wir sie vernichten können und werden!$B$BIch möchte, dass Ihr der Ausführende meines Willens seid. Eliminiert innerhalb einer Stunde fünf Alphas, wenn das klappen soll, $n, sonst bleiben wir in dieser Pattsituation!",
  },
  [2864] = {
    ["T"] = "Tran\'rek",
    ["O"] = "Sprecht mit Tran\'rek in Gadgetzan.",
    ["D"] = "Mein Vetter Tran\'rek ist in Schwierigkeiten. Er versprach mir vor einer Woche eine Fuhre Skarabäuspanzerschalen und hat bisher nicht geliefert. Er ist mein Vetter, also würde ich es wirklich hassen, wenn ich jemanden nach Gadgetzan schicken müsste, um ihm etwas zu brechen.$B$BKönnt Ihr für mich mit Tran\'rek sprechen? Lasst ihn wissen, dass er diese Lieferung zusammenbekommen muss, bevor ich irgendetwas Unüberlegtes mache.$B$BGadgetzan liegt im nördlichen Tanaris, in Kalimdor.",
  },
  [2865] = {
    ["T"] = "Skarabäuspanzerschalen",
    ["O"] = "Bringt Tran\'rek in Gadgetzan 5 unbeschädigte Skarabäuspanzerschalen.",
    ["D"] = "Die Skarabäen von Tanaris haben sehr harte Panzerschalen! Hart genug, um sie als Baumaterial für eine Menge Dinge zu verwenden. So viele Dinge!$B$BTatsächlich sind diese Panzer so nützlich... dass die Skarabäen beinahe bis zur Ausrottung gejagt wurden.$B$BIch weiß, wo es noch mehr Skarabäen gibt, und wenn Ihr mir versprecht, mir ihre Panzerschalen zu bringen, sage ich Euch, wo sie sind. Versprochen?$B$BO.k., die Skarabäen haben eine Kolonie in Zul\'Farrak. Ich schätze, die Trolle jagen sie nicht wegen ihrer Panzerschalen.$B$BAber Ihr könnt das tun! Geht nach Zul\'Farrak und besorgt mir unbeschädigte Skarabäuspanzerschalen!",
  },
  [2866] = {
    ["T"] = "Die Ruinen von Solarsal",
    ["O"] = "Erforscht die Ruinen von Solarsal und findet etwas über die Anwesenheit der Naga dort heraus.",
    ["D"] = "Südlich von uns befinden sich die Ruinen von Solarsal. Dies ist eine Gegend aus längst vergangener Zeit. Wir haben die Ruinen ursprünglich erforscht und nichts Besonderes darin gefunden. Daher sind wir friedlich wieder abgezogen.$B$BVor Kurzem haben die Hasskammnaga die Ruinen in alarmierender Anzahl besetzt. Zuerst hielten wir das für die Vorbereitungen auf einen Angriff. Obwohl diese Bedrohung immer noch gegeben ist, glauben wir, dass dort etwas anderes vorgeht.$B$BGeht dorthin, erforscht die Ruinen und achtet auf alles, was Euch ungewöhnlich vorkommt. Viel Glück, $n.",
  },
  [2867] = {
    ["T"] = "Kehrt zur Mondfederfeste zurück",
    ["O"] = "Kehrt zu Shandris Mondfeder in der Mondfederfeste zurück und berichtet von Euren Entdeckungen.",
    ["D"] = "Mit der Entdeckung des seltsamen Pavillons im Herzen der Ruinen von Solarsal ist es an der Zeit, zur Mondfederfeste zurückzukehren und General Shandris Mondfeder zu berichten, was hier entdeckt wurde.",
  },
  [2869] = {
    ["T"] = "Gegen die Hasskämme",
    ["O"] = "Bringt Latronicus Mondspeer in der Mondfederfeste 10 Nagaschuppen der Hasskämme.",
    ["D"] = "Es ist derzeit die beste Handlungsweise, davon auszugehen, dass die Naga, was auch immer sie vorhaben, langfristig versuchen werden, die Insel für sich zu beanspruchen. Wir müssen aktive Schritte unternehmen, um diese Bedrohung auszuschalten, bevor es zu spät ist.$B$BBringt mir 10 Schuppen der Hasskämme in gutem Zustand. Es dürfte offensichtlich sein, wie man an diese herankommt. Ihr werdet Euch an den Naga das erste Mal Eure Sporen verdienen und wir haben dann eine geschwächte Nagapräsenz auf der Sardor-Insel - beides sind Aussichten, die uns zum Vorteil gereichen.",
  },
  [2870] = {
    ["T"] = "Gegen Lord Shalzaru",
    ["O"] = "Vernichtet Lord Shalzaru und bringt dann Latronicus Mondspeer in der Mondfederfeste das geheimnisvolle Relikt, das er entdeckt hat.",
    ["D"] = "$n, unsere neuesten Spähberichte haben uns einen guten Einblick in die Kommandokette der Naga erlaubt. In ihren Truppen gibt es viele Adlige und die sind es, die den Ton angeben.$B$BUm genau zu sein, wir haben einen der grausameren Adligen als Lord Shalzaru identifiziert. Späher berichten, dass er höchstpersönlich bei einer kürzlich durchgeführten Nagaausgrabung in einer Höhle südlich der eigentlichen Ruinen, jenseits des Wassers, auf der Insel des Schreckens, ein seltsames Relikt gefunden hat.$B$BMacht dem ein Ende, indem Ihr ihm ein Ende macht, und bringt mir dieses Relikt, damit wir es untersuchen können.",
  },
  [2871] = {
    ["T"] = "Überbringung des Relikts",
    ["O"] = "Bringt das geheimnisvolle Relikt zu Vestia Mondspeer in der Mondfederfeste.",
    ["D"] = "Wenn Ihr so nett wärt, das Relikt zu meiner Frau Vestia zu bringen, wüsste ich das zu schätzen. Sie ist nicht nur eine gute Priesterin für unsere Gemeinde, sondern auch eine der versiertesten Gelehrten, die es gibt. Ich könnte wahrscheinlich nicht einmal dann herausfinden, wo an diesem Ding oben oder unten ist, wenn mein Leben davon abhinge. Meine Frau wiederum... Also wenn irgendwer herausfinden kann, was das ist, dann sie... Und wir müssen es wissen!$B$BSie befindet sich gleich um die Ecke, ohne Zweifel am Studieren. Danke, $n.",
  },
  [2872] = {
    ["T"] = "Stoleys Schuld",
    ["O"] = "Sprecht mit Stoley beim Dampfdruckpier.",
    ["D"] = "Mein alter Kumpel Stoley schuldet mir eine ganz bestimmte Flasche Rum. Er schmeißt die Bar am Dampfdruckpier in Tanaris, Kalimdor, und er versprach mir, dass der Rum dort stärker ist als alles, was man in Beutebucht finden kann. Er ist so davon überzeugt, dass er sagt, wenn ich diesen Rum probiere und ihm nicht zustimme, würde er das Geld, das er mir schuldet, verdoppeln!$B$BNun, ich glaube, er ist nicht mehr so zuversichtlich wie damals, als wir zuletzt miteinander geredet haben, und hat jetzt wohl Angst, mir diesen Rum zu geben. Ich lasse eine Wette aber nicht auf sich beruhen, also wie wäre es, wenn Ihr für mich mit ihm redet?",
  },
  [2873] = {
    ["T"] = "Stoleys Lieferung",
    ["O"] = "Bringt Stoleys Lieferung zu Stoley beim Dampfdruckpier.",
    ["D"] = "Ich schulde \"Seewolf\" MacKinley in Beutebucht etwas Rum, um eine Wette zu begleichen, aber mir ist der Rum ausgegangen! Kapitän Cuergo hat meine letzte Lieferung gestohlen und lagert sie in dieser ummauerten Piratenstadt im Süden.$B$BWenn Ihr meine Rumlieferung finden und zurückbringen könnt, dann kann ich MacKinley beweisen, dass Kalimdors Drinks die aus Beutebucht um Längen schlagen.",
  },
  [2874] = {
    ["T"] = "Lieferung für MacKinley",
    ["O"] = "Bringt Stoleys Flasche zu \"Seewolf\" MacKinley in Beutebucht.",
    ["D"] = "Danke nochmals für Eure Hilfe, $n. Hier habt Ihr eine Flasche Dampfdruckrum. Bringt sie zu MacKinley und er wird endlich seine verrückten Ansichten über Drinks aus Beutebucht über Bord werfen können.",
  },
  [2875] = {
    ["T"] = "GESUCHT: Andre Feuerbart",
    ["O"] = "Bringt Sicherheitschef Bilgenritzel beim Dampfdruckpier Andres Kopf.",
    ["D"] = "Der niederträchtige Pirat Andre Feuerbart wird aufgrund seiner gegen die Einwohner des Dampfdruckpiers und Gadgetzan gerichteten Verbrechen gesucht.$B$BDurch Erlass der Ratsregierung ist hiermit jeder, der dies liest, zu tödlicher Gewaltanwendung bei der Festnahme von Andre und seinen Spießgesellen autorisiert.$B$BDes Weiteren gilt durch zusätzlichen Erlass, dass, sollte Andre verstorben oder anderweitig während seiner Anhörung unpässlich sein, sein Kopf als Ersatz gilt und die Aushändigung dieses Kopfes an Sicherheitschef Bilgenritzel als vertragsbindend angesehen wird.",
  },
  [2876] = {
    ["T"] = "Schiffzeitpläne",
    ["O"] = "Berichtet den Behörden des Dampfdruckpiers von den Schiffzeitplänen.",
    ["D"] = "Auf dieser Schriftrolle sind die Zeitpläne für Lieferungen zwischen dem Dampfdruckpier und Beutebucht verzeichnet. Sowohl vergangene als auch zukünftige Lieferungen sind aufgeführt sowie Schiffsnamen. Große rote Kreuze befinden sich neben einigen der Schiffsreisen und Anmerkungen wie \"Große Beute\" oder \"Harter Kampf\" neben anderen.$B$BJemand beim Dampfdruckpier wird dieses Dokument sicher sehen wollen.",
  },
  [2877] = {
    ["T"] = "Den Lauerfels aufräumen",
    ["O"] = "Tötet 10 grüne Schlicke sowie 10 jadegrüne Schlamme und meldet Euch dann wieder bei Fraggar Donnermantel beim Nistgipfel.",
    ["D"] = "Wir Wildhämmer haben die Vergangenheit besser in Erinnerung als unsere Brüder in Eisenschmiede. Wenn Ihr Euch hier beliebt machen wollt, dann müsst Ihr Euch schon Eure Hände für uns schmutzig machen!$B$BDie Horde hat ihre Stellungen im Hinterland beim Lauerfels aufgegeben. Jetzt wimmelt es dort von üblen Schlicken und Schlammen. Wir haben Pläne für dieses Gebiet, aber zuerst muss uns eine Aufräummannschaft dort eine Bresche hineinschlagen.$B$BUnd hier kommt Ihr ins Spiel, Sonnenschein! Geht da runter und erledigt 10 grüne Schlicke und 10 jadegrüne Schlamme und meldet Euch dann wieder bei mir. Zieht los!",
  },
  [2878] = {
    ["T"] = "Verderbte Liedblume",
    ["O"] = "",
    ["D"] = "",
  },
  [2879] = {
    ["T"] = "Der Stock von Equinex",
    ["O"] = "Aktiviert Troyas Stock und findet den Monolithen von Equinex.",
    ["D"] = "Es sieht so aus, als könnte dieser Stock der Stock von Equinex sein. Der Stock von Equinex ist in Wirklichkeit ein Schlüssel. Mit ihm kann man den Monolithen von Equinex öffnen, der immer noch in den nördlichen Ruinen von Rabenwind steht.$B$BFindet die vier Feuer, die noch immer in den Ruinen brennen: Samha, Imbel, Byltan und Lahassa. Beschafft Euch deren Essenz. Und dann benutzt diese Essenz, während Ihr vor dem Monolithen von Equinex steht, um den Stock zu aktivieren. Wenn dies wirklich der Stock von Equinex ist, dann könnt Ihr den Monolithen öffnen und ein hochheiliges Artefakt daraus bergen.",
  },
  [2880] = {
    ["T"] = "Kopfgeld für Trollhalsketten",
    ["O"] = "Bringt Fraggar Donnermantel beim Nistgipfel 5 Trollstammeshalsketten.",
    ["D"] = "Seid Euch darüber im Klaren, $R - wir sind NICHT Teil der Allianz! Die Wildhämmer sind unabhängig. Um den Wert einer Person zu beweisen, verlassen sie sich lieber auf Taten und hören nicht auf das Geschwätz derer, die mit ihrem Gequassel versuchen, ihre Untätigkeit zu überspielen.$B$BWenn Ihr hier Freunde sucht, beweist uns erst Euren Wert! Die üblen Trolle, die die Hänge des Hinterlands überrennen, tragen manchmal Stammeshalsketten. Bringt mir 5 davon und wir werden das als einen Schritt in die richtige Richtung für Euch verbuchen.",
  },
  [2881] = {
    ["T"] = "Kopfgeld für Trollhalsketten",
    ["O"] = "",
    ["D"] = "",
  },
  [2882] = {
    ["T"] = "Cuergos Gold",
    ["O"] = "Findet Cuergos Gold!",
    ["D"] = "Diese Karte zeigt ein Stück Strand an einer Südküste. Unter der Karte stehen elegant geschriebene Worte:$B$B$BSüdlich des Piers$BDürft Ihr drauf hoffen$BFindet Flagge, Mast und Knochen$BGrabt dort, wenn Ihr tapfer sein wollt$BGrabt und erbt Cuergos Gold!$BEinen Schlüssel werdet Ihr brauchen, einen Schlüssel werdet Ihr sehen$BDie Mannen dort woll\'n als Bezahlung Euer Blut Euch nehm\'n.",
  },
  [2902] = {
    ["T"] = "Waldpfotennachforschung",
    ["O"] = "Findet die Schlachtpläne der Gnolle irgendwo in den Gnoll-Lagern südlich vom Camp Mojache.",
    ["D"] = "Gnolle sind zwar nicht die Hellsten, aber sie werden bestimmt die Strategie ihrer jüngsten Aktivitäten irgendwo dokumentiert haben. Wenn wir diese Pläne finden würden, hätten wir eine bessere Vorstellung davon, was sie so aufbringt.$B$BIch möchte, dass Ihr zu den Waldpfoten zurückgeht und nach allem sucht, was Ähnlichkeit mit ihren Schlachtplänen haben könnte. Irgendwas muss es geben, ihre Angriffe waren einfach zu gut koordiniert. Findet diese Schlachtpläne und kommt zu mir zurück, sobald Ihr sie habt.",
  },
  [2903] = {
    ["T"] = "Die Schlachtpläne",
    ["O"] = "Bringt die Schlachtpläne der Waldpfoten zu Hadoken Flinkschreiter ins Camp Mojache.",
    ["D"] = "Jetzt, nachdem die Schlachtpläne der Gnolle entdeckt wurden, bleibt nur noch, die Pläne zu Hadoken Flinkschreiter ins Camp Mojache zurückzubringen. Vielleicht kann er oder irgendjemand dort besser darauf schließen, was die Gnolle vorhaben.",
  },
  [2904] = {
    ["T"] = "Eine schöne Bescherung",
    ["O"] = "Begleitet Kernobee zur Uhrwerkgasse und meldet Euch dann wieder bei Scooty in Beutebucht.",
    ["D"] = "<Kernobee stöhnt.> Was... Was ist passiert?$B$BDas Letzte, an das ich mich erinnere, ist, wie ich von einer Gruppe Dunkeleisenzwerge gefangen, halb tot geprügelt und dann hier zu diesen schmutzigen Troggs geworfen wurde.$B$BHolt mich hier raus, $n!$B$BSo ist der Plan: Bringt mich zum Ausgang; Wenn wir es lebend hier herausschaffen, geht nach Beutebucht und lasst Scooty wissen, dass ich nach Ratschet gegangen bin, um Tüftelwiz zu warnen, dass die Dunkeleisenzwerge in die Herstellung dieser Supermaschine verwickelt sind.$B$BGehen wir!",
  },
  [2922] = {
    ["T"] = "Rettet Techbots Hirn!",
    ["O"] = "Bringt Techbots Speicherkern zu Tüftlermeister Oberfunks nach Eisenschmiede.",
    ["D"] = "Als unsere geliebte Stadt Gnomeregan fiel, muss ein verirrter Funke mein armes Geschöpf, Techbot, völlig durcheinander gebracht haben. Früher half er mir und meinen Kollegen mit seinen zahllosen Dingsdas und seiner endlosen Speicherkapazität, aber sein positronisches Gehirn wurde polarisiert. Es ist jetzt negatronisch... und Techbots Verhalten wurde umgekehrt. Er wütet jetzt in ganz Gnomeregan!$B$BDoch vielleicht kann man ihn noch retten. Wenn Ihr seinen Speicherkern auffinden und zu mir bringen könnt, kann ich die Ursache der Polarisierung möglicherweise finden und meinen armen Techbot reparieren.$B$B",
  },
  [2923] = {
    ["T"] = "Tüftlermeister Oberfunks",
    ["O"] = "Sprecht mit Tüftlermeister Oberfunks in Eisenschmiede.",
    ["D"] = "Tüftlermeister Oberfunks ist ob des Schicksals seiner mechanischen Schöpfung, Techbot, ziemlich bestürzt. Ich erinnere mich an Techbot als ein freundliches und hilfsbereites Werkzeug der Gnome, aber Oberfunks erzählte mir, er hätte eine Fehlfunktion.$B$BWirklich traurig. Aber mit etwas Glück kann er vielleicht repariert werden!$B$BSprecht mit Tüftlermeister Oberfunks in Tüftlerstadt, Eisenschmiede. Er würde sich über Eure Hilfe freuen.",
  },
  [2924] = {
    ["T"] = "Grundlegende Artifixe",
    ["O"] = "Bringt Klockmort Spannsplint in Eisenschmiede 12 grundlegende Artifixe.",
    ["D"] = "Ein beinahe universell einsetzbares mechanisches Bauteil - das grundlegende Artifix - war den Tüftlern und Schmieden von Gnomeregan von großem Nutzen. Doch während der panischen Flucht aus unserer Stadt hat niemand daran gedacht, einige von diesen Geräten mitzunehmen! Und ich brauche eine ganze Menge davon für eine Arbeit, die ich in Kürze in Angriff nehmen werde.$B$BBitte, geht nach Gnomeregan und bringt mir einige grundlegende Artifixe. Ihr werdet sie in den tieferen Bereichen unserer Stadt finden, in Künstliche Extrapolatoren genannten Behältern.$B$BDanke, $n, und bitte beeilt Euch!",
  },
  [2925] = {
    ["T"] = "Klockmorts Grundlagen",
    ["O"] = "Sprecht mit Klockmort Spannsplint in Eisenschmiede.",
    ["D"] = "Ein Gnomenfreund, Klockmort Spannsplint, hat mich aus Eisenschmiede kontaktiert. Er teilt mir mit, dass er Hilfe bei einer gefährlichen, aber unverzichtbaren Mission nach Gnomeregan braucht, der einst so großen Gnomenstadt, die jetzt verseucht und voller verrückter Gnome und ihrer Gerätschaften ist.$B$BIch muss hier bleiben, aber mir blutet das Herz, wenn ich meinem Freund nicht helfen kann. Wenn Ihr an meiner Stelle gehen könnt, dann stünde ich tief in Eurer Schuld.$B$BIhr könnt Klockmort in Tüftlerstadt in Eisenschmiede finden.",
  },
  [2926] = {
    ["T"] = "Gnogaine",
    ["O"] = "Sammelt mit der leeren bleiernen Sammelphiole radioaktive Ablagerungen bestrahlter Eindringlinge oder Plünderer. Sobald sie voll ist, bringt Ihr sie zu Ozzie Wechselvolt nach Kharanos zurück.",
    ["D"] = "Seit dem Unfall redet Gnoarn nur noch davon, Gnomeregan vom Troggbefall zu befreien und ein Heilmittel für die Erkrankung, die die Stadt verseucht, zu finden, $n.$B$BZum Glück sind Razzle und ich gerade dabei, ein solches Heilmittel zu entwickeln... oder vielleicht auch ein Rezept für wirklich starken Rum. Genau wissen wir das aber erst, wenn wir es ausprobiert haben! Logischerweise benötigen wir zum Test unserer Theorie ein paar Dinge, vor allem Ablagerungen von den bestrahlten Troggs in Gnomeregan. Füllt diese Phiole mit dem grünen Leuchten und bringt sie zurück!",
  },
  [2927] = {
    ["T"] = "Der Tag danach",
    ["O"] = "Sprecht mit Ozzie Wechselvolt in Kharanos in Dun Morogh.",
    ["D"] = "Ihr glaubt doch wohl nicht, dass ich schon immer so kahlköpfig war, oder? Nein, $gwerter Herr:holde Dame;! Das Strahlungsleck in Gnomeregan rief diesen tragischen Haarverlust hervor. Vor jenem Tag hatte ich den einzigen Gnomenafro in ganz Azeroth.$B$B<Gnoarn schüttelt drohend die Faust.>$B$BVerdammt seien diese verdammten dreckigen Troggs, verdammt seien sie alle!$B$BVielleicht ist es noch nicht zu spät, $n. Wir können sie vielleicht immer noch retten - die Stadt, meine ich! Sprecht mit Ozzie Wechselvolt in Kharanos. Er und sein Partner Razzle arbeiten an einer Lösung.",
  },
  [2928] = {
    ["T"] = "Gyrobohrmatische Exkavation",
    ["O"] = "Bringt 24 robomechanische Innereien zu Shoni nach Sturmwind.",
    ["D"] = "Es ist schrecklich, $n. Wir werden nicht genug Teile haben, um unsere Flotte aus gyrobohrmatischen Exkavationatoren nach Dun Morogh zu bringen.$B$BWir brauchen Ersatzteile, $n. Und welch besseren Ort als Gnomeregan könnte es geben, um Ersatzteile für unsere Fahrzeuge zu besorgen? Es heißt, dass Bots und Mechs dort herumlaufen wie Vieh auf der Weide.$B$BIhr müsst für mich nach Gnomeregan gehen und diese Roboter auseinander nehmen. Bringt mir ihre Innereien. Zwei Dutzend sollten ausreichen.",
  },
  [2929] = {
    ["T"] = "Der große Verrat",
    ["O"] = "Reist nach Gnomeregan und tötet Robogenieur Thermadraht. Kehrt zu Hochtüftler Mekkadrill zurück, wenn der Auftrag ausgeführt ist.",
    ["D"] = "Ich habe Thermadraht vertraut, $R. Ich hätte nie vermutet, dass er mich und das ganze Gnomenvolk verraten würde. Und wofür? Macht? Reichtum? Alles Dinge, die er mit der Zeit auch so erlangt hätte. Jetzt sind wir aus unserer Heimat verdrängt worden und dieser Wahnsinnige ist dafür verantwortlich. Der König des Nichts...$B$BWir werden Gnomeregan zurückerobern, $n. Wir werden nicht ruhen, bis die Stadt wieder unter unserer Kontrolle steht. Wenn Ihr Euch unserem Kampf anschließen wollt, habe ich eine einfache Bitte an Euch: Tötet den Verräter. Vernichtet Robogenieur Thermadraht.",
  },
  [2930] = {
    ["T"] = "Datenrettung",
    ["O"] = "Bringt Mechanikermeister Gussmuff in Eisenschmiede eine Prismalochkarte.",
    ["D"] = "Als wir aus Gnomeregan flohen, haben wir so viele Daten zurückgelassen! Lebenswichtige Informationen!$B$BIch brauche die Daten auf einer Prismalochkarte aus einem Matrix-Prägographen 3005-D, einem Hochsicherheitsterminal, tief in Gnomeregan. Ihr müsst auf dieses Terminal zugreifen, aber dafür braucht Ihr die Zugangsberechtigung...und um die zu erhalten, müsst Ihr erst Zugang zu den Terminals niedrigerer Sicherheitsstufen - Modelle A, B und C - haben. DAFÜR wiederum braucht Ihr Sicherheitslochkarten, angefangen mit einer weißen...Und wir haben alle Lochkarten in der Stadt vergessen.$B$BDas ist eine Katastrophe! Könnt Ihr helfen?",
  },
  [2931] = {
    ["T"] = "Gussmuffs Auftrag",
    ["O"] = "Sprecht mit Mechanikermeister Gussmuff in Eisenschmiede.",
    ["D"] = "Mechanikermeister Gussmuff in Eisenschmiede sucht Abenteurer! Er organisiert Teams, um Gnomeregan auf einer Datensicherungsmission höchster Sicherheitsstufe und Wichtigkeit zu betreten.$B$BGussmuff mag zwar nicht wie ich ein Mitglied der Erleuchteten Vereinigung für Arkanologie, Alchemie und Ingenieurwissenschaften sein, aber er ist dennoch ein kluger Gnom, und wenn er es für wichtig hält, dann besteht nach meinen Berechnungen eine vierundachtzigprozentige Wahrscheinlichkeit, dass es wirklich wichtig ist.$B$BDas ist eine gute Chance!",
  },
  [2932] = {
    ["T"] = "Grausige Botschaft",
    ["O"] = "Sammelt Schädel der Bleichborken und hängt sie an Nimboyas Pike. Platziert Nimboyas behängte Pike in einem der Bleichborkendörfer im Hinterland und kehrt dann zu Nimboya ins Schlingendorntal zurück.",
    ["D"] = "Die Trolle der Bleichborken waren einst Verbündete der Horde, aber sie haben Thrall hintergangen... und das muss bestraft werden!$B$BHier, nehmt meine Pike und geht weit gen Norden ins Hinterland. Ihr werdet es nordöstlich von Tarrens Mühle vorfinden, einem Dorf, das von unseren Verbündeten, den Verlassenen, kontrolliert wird.$B$BWenn Ihr ins Hinterland kommt, jagt Trolle der Bleichborken. Sammelt ihre Schädel und rammt meine Pike in einem ihrer kleineren Dörfer in den Boden: in Hiri\'watha oder Zun\'watha.$B$BZeigt ihnen, dass ihre heimtückischen Taten Konsequenzen nach sich ziehen!",
  },
  [2933] = {
    ["T"] = "Giftflaschen",
    ["O"] = "Bringt eine Giftflasche zu einem Apotheker nach Tarrens Mühle.",
    ["D"] = "Diese Flaschen enthalten ein übles grünes Gift.$B$BWenn man die Anzahl der Flaschen in diesem Dorf bedenkt, müssen die Bleichborken es für sehr wichtig halten. Vielleicht will einer der Apotheker im nahe gelegenen Tarrens Mühle eine Probe davon.",
  },
  [2934] = {
    ["T"] = "Unbeschädigter Giftbeutel",
    ["O"] = "Bringt Apotheker Lydon in Tarrens Mühle einen unbeschädigten Giftbeutel.",
    ["D"] = "Ich benötige eine frische Probe von dem Gift, das Ihr mir gebracht habt. Die Spinnen, von denen es gesammelt wurde, dürften im Hinterland leben. Jagt sie, bis Ihr einen unbeschädigten Giftbeutel findet, und bringt ihn mir. Ihr findet viele Spinnen nahe der Trollstadt Shadra\'Alor.$B$BUnd trödelt nicht rum, $n, denn der Giftbeutel muss frisch sein, wenn Ihr ihn abliefert.",
  },
  [2935] = {
    ["T"] = "Konsultiert Meister Gadrin",
    ["O"] = "Sprecht mit Meister Gadrin in Sen\'jin.",
    ["D"] = "Das Gift der Brutwachen war nicht so potent wie das Gift, das Ihr mir ursprünglich gebracht habt. Es muss von einer anderen Spinne gewesen sein.$B$BIch will eine frische Probe dieses Gifts. Um herauszufinden, woher es kam, müsst Ihr Euch mit einem Troll unterhalten, der die Bleichborken gut kennt – Meister Gadrin im Dorf Sen\'jin. Reist nach Durotar und sprecht mit Gadrin. Wenn wir Glück haben, weiß er, woher die Bleichborken ihr Gift bekommen.",
  },
  [2936] = {
    ["T"] = "Der Spinnengott",
    ["O"] = "Lest von der Schrifttafel des Theka, um den Namen des Spinnengottes der Bleichborken zu erfahren, und kehrt dann zu Meister Gadrin zurück.",
    ["D"] = "Die Bleichborken verehren einen Spinnengott und von ihm bekommen sie ihr wirkungsvolles Gift. Wenn Ihr also ihr Gift wollt, müsst Ihr ihren Gott beschwören!$B$BUm das zu tun, müsst Ihr seinen wahren Namen wissen und dieser Name steht auf einer Tafel, die in Tanaris, in der Trollstadt von Zul\'Farrak, in einer Gruft vergraben ist, die von Theka dem Märtyrer bewacht wird.$B$BBesiegt Theka und findet und lest diese Tafel, um den Namen des Spinnengottes herauszufinden. Wenn Ihr ihn kennt, kehrt zu mir zurück.",
  },
  [2937] = {
    ["T"] = "Die Beschwörung von Shadra",
    ["O"] = "Besiegt Shadra am Altar von Shadra\'Alor und bringt ihr Gift zu Apotheker Lydon nach Tarrens Mühle.",
    ["D"] = "Nehmt dieses Pergament und verlest es am Altar von Shadra\'Alor tief in der Welt der Bleichborken im Hinterland. Dies wird Shadra, den Spinnengott, beschwören.",
  },
  [2938] = {
    ["T"] = "Gift für Unterstadt",
    ["O"] = "Bringt Faranells Päckchen zu Apothekermeister Faranell nach Unterstadt.",
    ["D"] = "Das Gift, das Ihr vom Spinnengott der Bleichborken beschafft habt, ist ein großer Fund! Ich konnte mit nur einem Tropfen von dem Zeug fast zwei Dutzend Hasen töten! Na gut, einige dieser Hasen waren sehr jung und klein genug, um auf eine Handfläche zu passen, und daher war ihre Widerstandskraft gegen Toxine bestimmt niedrig, aber man kann nicht leugnen, dass dieses Gift mächtig ist!$B$BIch würde gern eine Probe zu meinem Vorgesetzten, Apotheker Faranell, in das Apothekarium von Unterstadt entsenden. Ich bin sicher, er würde es gern selbst testen.",
  },
  [2939] = {
    ["T"] = "Auf der Suche nach Wissen",
    ["O"] = "Sprecht mit Daryn Leuchtwind im Dorf Rut\'theran.",
    ["D"] = "Auch wenn die Ruinen von Feralas ziemlich gefährlich sein können, so erzählen sie doch viel darüber, was hier passiert ist...$B$BAls ich vor einigen Tagen das Geröll südlich von hier durchstöberte, fand ich etwas, das wie ein gewöhnlicher Stock aussah. Allerdings werde ich das Gefühl nicht los, dass es mehr damit auf sich hat. Angelas und ich haben über unseren Büchern hier gebrütet, aber wir konnten nichts darüber herausfinden.$B$BIch habe aber eine Kollegin in Darnassus, die uns vielleicht erzählen kann, was das ist, $n. Warum sprecht Ihr nicht mit ihr und seht, ob sie uns helfen kann?",
  },
  [2940] = {
    ["T"] = "Feralas: Ein historischer Abriss",
    ["O"] = "Fragt Daryn Leuchtwind, ob Ihr Euch ihr Buch ausleihen dürft.",
    ["D"] = "Es sieht so aus, als sei dieses Buch seit langer Zeit von niemandem mehr geöffnet worden. Sein Einband ist ziemlich abgenutzt und seine Seiten sind vergilbt, aber nach einer kurzen Untersuchung stellt Ihr fest, dass es genau das sein könnte, was Troyas sucht.$B$BIhr steckt es ein, aber dann fällt Euch ein, dass Ihr vielleicht vorher fragen solltet, bevor Ihr es ausleiht.",
  },
  [2941] = {
    ["T"] = "Der Borger",
    ["O"] = "Bringt den Brief zu Curgle Kurbelhüpf nach Tanaris.",
    ["D"] = "Ich habe in meinem Leben viele Dinge studiert und meine jüngste Leidenschaft sind die Schnappkiefer, die den Strand im Hinterland bevölkern. Es gibt einen bestimmten, den ich gern sehen würde, einen Riesenschnappkiefer namens Gammerita. Ich würde gern selbst hingehen, aber meine Forschungen halten mich hier. Ein Bild von Gammerita wäre wohl die nächstbessere Lösung.$B$BBringt diesen Brief zu Curgle Kurbelhüpf nach Tanaris. Sie hat etwas für mich erfunden, das Bilder einfangen kann. Ich glaube, sie nannte es \"Schnappschuss\"...",
  },
  [2942] = {
    ["T"] = "Der Morgenstein",
    ["O"] = "Bringt den funkelnden Stein und den Stock von Equinex zu Troyas Mondhauch in die Mondfederfeste.",
    ["D"] = "In dem Moment, in dem das Artefakt vom Monolithen getrennt wird, fühlt Ihr, wie die Energie im Stock von Equinex sich zu verflüchtigen beginnt.",
  },
  [2943] = {
    ["T"] = "Rückkehr zu Troyas",
    ["O"] = "Liefert das Buch bei Troyas Mondhauch in der Mondfederfeste ab.",
    ["D"] = "Hier, $n. Bitte, passt auf mein Buch auf.$B$BNun aber schnell. Troyas wartet bestimmt schon ganz ungeduldig auf Eure Rückkehr.",
  },
  [2944] = {
    ["T"] = "Der Superschnapper FX",
    ["O"] = "Benutzt den Superschnapper FX, um einen Schnappschuss von Gammerita zu machen, und kehrt dann zu Daryn Leuchtwind nach Rut\'theran zurück.",
    ["D"] = "Werft erst mal einen Blick auf meine neue Erfindung, $n. Visiert einfach an, wovon Ihr ein Bild haben möchtet, und drückt dann auf den Knopf.$B$BWas war das für eine Kreatur aus dem Hinterland, die Ihr erwähnt habt? Gammerita? Tja, viel Erfolg bei der Suche nach ihr. Ich bin sicher, Daryn wird mit dem Schnappschuss, den Ihr ihr zurückbringt, sehr zufrieden sein.$B$BWie auch immer, hier habt Ihr den Superschnapper. Viel Spaß!",
  },
  [2945] = {
    ["T"] = "Schmutzverkrusteter Ring",
    ["O"] = "Findet einen Weg, den schmutzverkrusteten Ring zu säubern.",
    ["D"] = "Ihr habt bei einer der Leichen der Dunkeleisenzwerge etwas entdeckt, das wie ein Ring aussieht. Es ist jedoch schwer zu sagen, was für ein Ring das ist. Das Ding ist derart mit Dreck und Verkrustungen überzogen, dass man es unmöglich an einen Finger stecken kann. Ihr glaubt, unter den Verkrustungen Edelmetall schimmern und vielleicht sogar einen Edelstein glitzern zu sehen.$B$BWenn Ihr diesen Ring verwenden wollt, müsst Ihr nach etwas suchen, das die Verkrustungen entfernt.",
  },
  [2946] = {
    ["T"] = "Mal sehen, was passiert...",
    ["O"] = "Bringt die Miniaturplatinscheiben zu den Toren von Uldum in Tanaris und sucht nach Hinweisen auf die Verbindung zwischen beidem.",
    ["D"] = "Wir möchten, dass Ihr die Mininaturscheiben, die Ihr gefunden habt, zu den versiegelten Toren von Uldum bringt. Wir... wir sind uns nicht ganz sicher, was dann passieren wird.$B$BEs klingt ein bisschen riskant, sicher, das geben wir auch zu. Es könnte allerdings auch sein, dass überhaupt nichts passiert. Vielleicht müsst Ihr mit den Scheiben irgendetwas anstellen. Vielleicht öffnen sich die Türen, wenn Ihr die Scheiben bei Euch habt. Versucht alles Mögliche und seid offen für alles, was passieren könnte.$B$BKehrt nach Eisenschmiede zurück, wenn Ihr herausgefunden habt, was die Scheiben bewirken. Es wird einiges zu untersuchen geben, wenn Ihr das erledigt habt!",
  },
  [2947] = {
    ["T"] = "Die Rückkehr des Rings",
    ["O"] = "Ihr könnt den Ring entweder behalten oder die Person finden, die für die Prägung und Gravuren auf der Innenseite des Rings verantwortlich ist.",
    ["D"] = "Ihr öffnet die Schachtel, die der Funkelmat 5200 ausgegeben hat, und entdeckt, was Ihr vermutet hattet - einen glänzenden Ring! Beim Anstecken des Rings fühlt Ihr magische Macht durch Euren Körper fließen.$B$BAuf der Innenseite des Rings befindet sich eine kleine, aber dennoch klar erkennbare Gravur des Siegels von Eisenschmiede. Daneben sind drei kleine Initialen eingraviert: \"TdK\". Dies könnten entweder die Initialen des ursprünglichen Besitzers oder die des Herstellers des Rings sein. Den Ring behalten...oder ihn dieser Person zurückgeben? Entscheidungen über Entscheidungen...",
  },
  [2948] = {
    ["T"] = "Gnomenverbesserungen",
    ["O"] = "Bringt Talvash del Kissel in Eisenschmiede den glänzenden Goldring, einen Silberbarren, einen Moosachat und 30 Silbermünzen.",
    ["D"] = "Hört mir zu, $n. Dieser Ring gehört Euch, das ist schon richtig... aber ich kann Euch einen besseren machen! Das Design ist, wie ich bereits sagte, wirklich einfach. Ich bin heutzutage wesentlich geschickter und für die läppische Summe von dreißig Silbermünzen werde ich ihn für Euch aufpeppen! Na ja, eigentlich etwas mehr als dreißig...$B$BIch bin derzeit in einer misslichen Lage. Mein ganzer Materialvorrat ist aufgebraucht. Wenn Ihr mir einen Barren Silber und einen Moosachat bringt, verziere ich Euch den Ring damit und peppe ihn richtig schön auf!!$B$BVertraut mir!",
  },
  [2949] = {
    ["T"] = "Die Rückkehr des Rings",
    ["O"] = "Ihr könnt den Ring entweder behalten oder die Person finden, die für die Prägung und Gravuren auf der Innenseite des Rings verantwortlich ist.",
    ["D"] = "Ihr öffnet die Schachtel, die der Funkelmat 5200 ausgegeben hat, und entdeckt einen glänzenden Ring! Beim Anstecken des Rings fühlt Ihr magische Macht durch Euren Körper fließen.$B$BAuf der Innenseite des Rings befindet sich eine kleine, aber dennoch klar erkennbare Gravur des Siegels von Orgrimmar. Daneben sind Buchstaben eingraviert, aber selbst nach der Säuberung sind nur die ersten drei zu erkennen: \'NOG...\'$B$BDies könnten entweder die Initialen des ursprünglichen Besitzers oder die des Herstellers des Rings sein. Den Ring behalten...oder \'NOG\' suchen - Entscheidungen, Entscheidungen...",
  },
  [2950] = {
    ["T"] = "Noggs Ringerneuerung",
    ["O"] = "Bringt Nogg in Orgrimmar den glänzenden Goldring, einen Silberbarren, einen Moosachat und 30 Silbermünzen.",
    ["D"] = "Nein, nein, nein - ich behaupte nicht plötzlich, der Ring sei meiner. Normalerweise mache ich so etwas nicht, aber Eure Information über die Dunkeleisenzwerge war sehr nützlich. Ich werde diesen Ring für Euch so verbessern, dass selbst der, der dieses einfache kleine Ding hergestellt hat, es nicht hätte besser machen können. Wahrscheinlich war es irgendein Gnom...$B$BWenn Ihr mir einen Barren Silber, einen Moosachat und dreißig Silbermünzen bringt, verziere ich den Ring damit derart, dass seine Magie noch verstärkt wird. Interessiert?",
  },
  [2951] = {
    ["T"] = "Der Funkelmat 5200!",
    ["O"] = "Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren.",
    ["D"] = "Dieser Apparat ist etwas ganz Besonderes unter den Gnomen, er bietet dem Wahnsinn außerhalb dieses Raums tapfer die Stirn. Eine kleine Plakette auf dem Gerät besagt: \"Der Funkelmat 5200\", gefolgt von einem kurzen Text:$B$B\"Schmutz und Verkrustungen ruinieren Euer Funkelzeug? Der Funkelmat 5200 definiert das Wort Glanz neu! Gebt den Gegenstand hinein, den Ihr gesäubert haben wollt, und werft drei Silbermünzen in den Münzschlitz. Lasst den Funkelmat 5200 einen Moment arbeiten - und voilà! Eure Wertgegenstände werden sauber und glänzend herauskommen!\"",
  },
  [2952] = {
    ["T"] = "Der Funkelmat 5200!",
    ["O"] = "",
    ["D"] = "",
  },
  [2953] = {
    ["T"] = "Funkelmatastisch",
    ["O"] = "",
    ["D"] = "",
  },
  [2954] = {
    ["T"] = "Der Steinbehüter",
    ["O"] = "Findet den Zweck des Steinbehüters von Norgannon heraus und interagiert dann mit dem Podest von Uldum in der Tanariswüste.",
    ["D"] = "Willkommen - Ich bin der Wächter des Eingangs. Ich werde Euch den Zugang zum Areal von Uldum nur gewähren, wenn Ihr die Platten von Uldum in Euren Scheibensatz integriert habt.$B$BDas Erscheinen des Steinbehüters von Norgannon muss die Reaktion sein, die man sich davon erhoffte, als man Euch mit den Platinscheiben in die Tanariswüste schickte. Wie Euch aufgetragen, werdet Ihr die Geschichte, die hinter dem Steinbehüter steckt, herausfinden müssen, bevor Ihr weitermachen könnt.",
  },
  [2962] = {
    ["T"] = "Das einzige Heilmittel ist mehr grünes Leuchten",
    ["O"] = "Reist nach Gnomeregan und bringt etwas von der hoch konzentrierten radioaktiven Ablagerung zurück. Seid gewarnt, die Ablagerung ist instabil und wird ziemlich schnell zerfallen.$B$BOzzie wird außerdem Eure schwere bleierne Phiole benötigen, nachdem die Aufgabe erledigt ist.",
    ["D"] = "Eure Probe war nicht nur von unzureichender Menge, sondern auch noch verunreinigt.$B$BWas wir brauchen, ist mehr von dem grünen Leuchten. Die Troggs sind offensichtlich nicht stark genug verstrahlt, um unseren Ansprüchen zu genügen. Ihr werdet nach Gnomeregan gehen und etwas der radioaktiven Ablagerung direkt von der Quelle besorgen müssen. Ich spreche von den bestrahlten Schleimen, Lauerern und Schrecken. Seid gewarnt, diese Art von Ablagerung ist schwer abzufüllen. Es könnte Euch mehrere Versuche mit der Phiole kosten, bis Ihr eine geeignete Probe bergen könnt.",
  },
  [2963] = {
    ["T"] = "Omen von Uldum",
    ["O"] = "Sprecht mit Historiker Karnik in der Halle der Forscher in Eisenschmiede.",
    ["D"] = "Seid mir erneut gegrüßt, $n! Seit Eurer Entdeckung in Uldaman ist die Forscherliga voller Aufregung. Die Liga hat daran gearbeitet, die Geheimnisse der Miniaturplatinscheiben zu enträtseln, die Ihr gefunden habt. Euer treffliches Erscheinen hier in der Halle erlaubt es mir, Euch mitzuteilen, dass wir ziemlich wahrscheinlich eine Spur haben!$B$BHistoriker Karnik hat einige wichtige Hinweise auf den wahren Ursprung des Tals der Behüter in der Wüste von Tanaris gefunden. Er möchte so schnell wie möglich mit Euch sprechen!",
  },
  [2964] = {
    ["T"] = "Eine zukünftige Aufgabe",
    ["O"] = "Sprecht mit Hochforscher Magellas in Eisenschmiede.",
    ["D"] = "Nun, $n... Wir scheinen derzeit in einer temporären Sackgasse zu stecken. Wir wissen, was wir brauchen - die Platten von Uldum -, aber wir haben keinen Schimmer, wo diese sein könnten. Wir müssen eine Menge Nachforschungen anstellen, um einen guten Einsatzplan zusammenzustellen. Ich habe ein paar Ideen, wo man mit der Suche anfangen könnte. Ich hege große Hoffnungen, dass wir Euch bald bitten werden, loszuziehen und sie zu bergen.$B$BDer Hochforscher muss hiervon in Kenntnis gesetzt werden, $n. Zweifellos wird er Euch ebenfalls persönlich dafür danken wollen, dass Ihr der Liga wieder einmal geholfen habt.",
  },
  [2965] = {
    ["T"] = "Omen von Uldum",
    ["O"] = "Sprecht mit Nara Wildmähne auf der Anhöhe der Ältesten in Donnerfels.",
    ["D"] = "Ah, $n, es ist vortrefflich, dass Ihr erneut zu mir gekommen seid. Eure Funde in der Grabungsstätte von Uldaman haben uns Gelehrten sicherlich einigen Gesprächsstoff geliefert. Ich unterhielt mich also gerade mit einer der Druiden, Nara Wildmähne, als mir dämmerte, dass ihre früheren Erfahrungen als Abenteurer von großer Wichtigkeit für unseren nächsten Schritt sein könnten.$B$BIch möchte, dass Ihr mit ihr über ihre Reisen ins Tal der Behüter in der Wüste von Tanaris sprecht. Sie erwartet Euch auf der Anhöhe der Ältesten.",
  },
  [2966] = {
    ["T"] = "Mal sehen, was passiert...",
    ["O"] = "Bringt die Miniaturplatinscheiben zu den Toren von Uldum in Tanaris und sucht nach Hinweisen auf die Verbindung zwischen beidem.",
    ["D"] = "Weiser Wahrspruch und ich würden Euch bitten, die Miniaturscheiben, die Ihr gefunden habt, zu den versiegelten Toren von Uldum zu bringen. Wir... wir sind uns nicht ganz sicher, was dann passieren wird.$B$BDas klingt ein wenig riskant, sicher, das geben wir auch zu. Es könnte allerdings auch gar nichts passieren. Vielleicht müsst Ihr die Scheiben mit etwas benutzen. Vielleicht öffnen sich die Türen, wenn Ihr die Scheiben bei Euch habt. Versucht alles Mögliche und seid bereit für alles, was passieren könnte.$B$BFindet alles heraus, was Ihr könnt, und kehrt dann zu mir zurück. Wir werden dann viel zu bereden haben.",
  },
  [2967] = {
    ["T"] = "Rückkehr nach Donnerfels",
    ["O"] = "Sprecht mit Nara Wildmähne in Donnerfels.",
    ["D"] = "Ihr greift erneut auf das Podest zu und die Platinscheiben erscheinen plötzlich wieder in Eurem Rucksack.$B$BNachdem Ihr das Wissen des Steinbehüters verinnerlicht habt und die Scheiben wieder in Eurem Besitz sind, ist es jetzt an der Zeit, nach Donnerfels zurückzukehren und Nara Wildmähne von Eurer Entdeckung in Kenntnis zu setzen.",
  },
  [2968] = {
    ["T"] = "Eine zukünftige Aufgabe",
    ["O"] = "Sprecht mit dem Weisen Wahrspruch in Donnerfels.",
    ["D"] = "Nun, $n... Wir scheinen unglücklicherweise in einer Sackgasse zu stecken. Wir wissen, was wir brauchen - die Platten von Uldum -, aber wir haben keinen Schimmer, wo diese sein könnten. Wir werden Nachforschungen darüber anstellen und dann einen soliden Einsatzplan entwickeln. Ich habe ein paar Ideen, wo man mit der Suche anfangen könnte; Ich hege große Hoffnungen, dass wir Euch bald bitten werden, Euch wieder unseren Bemühungen um die Entdeckung der Wahrheit anzuschließen.$B$BWeiser Wahrspruch muss hiervon in Kenntnis gesetzt werden, $n; zweifellos wird er Euch ebenfalls persönlich dafür danken wollen, dass Ihr noch einmal dem Ruf gefolgt seid.",
  },
  [2969] = {
    ["T"] = "Freiheit für alle Kreaturen",
    ["O"] = "Befreit mit dem Bambuskäfigschlüssel die Grimmlingflitzer aus dem Pferch im Grimmtotemlager. Kindal Mondweber sowie mindestens 6 Grimmlingflitzer müssen überleben.$B$BIhr habt 6 Minuten, bis Kindal beschließt, dass es Zeit zum Rückzug und Eure Aufgabe gescheitert ist.",
    ["D"] = "Der dreckige Grimmtotemklan fängt Feendrachen ein, $n. Jer\'kai und ich haben keine Ahnung, was sie mit ihnen vorhaben, aber es ist bestimmt nichts Gutes.$B$BWir sind nicht stark genug, um uns durch das Lager zu kämpfen und die gefangenen Drachen zu befreien, aber mit Eurer Hilfe...$B$BWenn Ihr willens seid und ein paar starke Freunde habt, dann befreit mit diesem Schlüssel die Kreaturen aus ihrem Käfig und stellt sicher, dass sie mit ihrem Leben davonkommen. Ich werde die ganze Zeit an Eurer Seite sein. Jer\'kai wird hier bleiben.",
  },
  [2970] = {
    ["T"] = "Gerechtigkeit üben",
    ["O"] = "Tötet 12 Naturalisten der Grimmtotem, 10 Räuber der Grimmtotem sowie 6 Schamanen der Grimmtotem und kehrt dann zu Jer\'kai Mondweber nach Feralas zurück.",
    ["D"] = "Wie sie Euch ja schon mitgeteilt hat, tragen Kindal und ich den Kampf jetzt zu den Grimmtotem. Gemeinsam sollten wir in der Lage sein, einige der Tauren ohne große Gefahr für uns selbst auszuschalten, aber wir würden etwas Hilfe sehr begrüßen, wenn Ihr dazu bereit wärt.$B$BIhr habt das Lager unterhalb von uns ja schon gesehen, aber es gibt mehr davon im Osten. Verwendet Eure eigenen Taktiken, um den Tauren so viel Schaden wie möglich zuzufügen. Wir können uns alle wieder hier treffen, nachdem sie Kindals Pfeile und Eure Waffen geschmeckt haben!",
  },
  [2972] = {
    ["T"] = "Gerechtigkeit üben",
    ["O"] = "Bringt Jer\'kais Siegelring zu Hohepriesterin Tyrande Wisperwind nach Darnassus.",
    ["D"] = "Hier draußen in der Wildnis haben Kindal und ich nur geringe Mittel, um Euch für Eure Hilfe zu entlohnen, aber wir wollen nicht, dass Ihr Euch nicht wertgeschätzt fühlt. Nehmt meinen Siegelring. Solltet Ihr Euch jemals in Teldrassil wiederfinden, geht zum Tempel des Mondes im Herzen von Darnassus. Dort werdet Ihr meine Hohepriesterin vorfinden, Tyrande Wisperwind. Der Ring wird sie wissen lassen, dass es uns gut geht, und Eure Geschichte untermauern.$B$BWir beide danken Euch, $n.",
  },
  [2973] = {
    ["T"] = "Schimmer für einen neuen Umhang",
    ["O"] = "Bringt 10 regenbogenfarbene Grimmlingflitzerflügel zu Krueg Schädelspalter nach Camp Mojache.",
    ["D"] = "Grüße, $C. Ich bin mit meinen Gefährten auf einer Mission für unsere Meister aus Orgrimmar hinunter nach Feralas gereist.$B$BMeine Lebensgefährtin Korrin liebt neue Kleider und daher habe ich vor, ihr einen neuen Umhang zu fertigen, der mit den hauchzarten Flügeln von Feendrachen geschmückt ist. Seid Ihr vielleicht in der Lage, mir zu helfen? Grimmlingflitzer mit prachtvollen Flügeln, wie ich sie will, gibt es westlich vom Camp Mojache.",
  },
  [2974] = {
    ["T"] = "Eine grausige Entdeckung",
    ["O"] = "Bringt 20 Grimmtotemhörner zu Krueg Schädelspalter in Camp Mojache.",
    ["D"] = "Was!? Ihr seid in dieser Gegend auf Tauren getroffen? Ihr macht Witze! Wie sahen sie aus?$B$BDer Grimmtotemklan! Ihr habt einige von denen gefunden? Belgrom, einer unserer Meister, interessiert sich für diese Sippe... Er hat sogar ein Kopfgeld auf sie ausgesetzt.$B$BIhr müsst wieder zurückgehen, $C. Geht zurück und bringt mir ihre Hörner... haufenweise, wenn Ihr könnt. Mit den Grimmtotem ist nicht zu spaßen und man sollte sie nicht unterschätzen. Geht zurück und erschlagt sie; lasst die Flüsse in Feralas rot werden von ihrem dunklen Blut.",
  },
  [2975] = {
    ["T"] = "Die Oger von Feralas",
    ["O"] = "Rok Orhan im Camp Mojache will, dass Ihr 10 Oger der Gordunni, 10 Magier der Gordunni und 5 Schläger der Gordunni tötet.",
    ["D"] = "Der Einfluss des Ogerstamms der Gordunni ist in diesem Gebiet kaum zu übersehen. Schaut Euch mal um, $n. Seine bloße Anwesenheit zeichnet das Land. Wir müssen die Gordunni zurückdrängen, bevor sie sich in Feralas weiter ausbreiten können.$B$BFolgt dem Fluss nach Norden und Ihr werdet ihren Außenposten finden. Verringert ihre Zahl, $n. Wir müssen ihnen klar machen, dass Ihresgleichen hier nicht willkommen ist.",
  },
  [2976] = {
    ["T"] = "Eine grausige Entdeckung",
    ["O"] = "Bringt die Kiste mit Grimmtotemhörnern zu Belgrom Felshammer nach Orgrimmar.",
    ["D"] = "Was?!$B$BHm, ich hab wohl wirklich ein Kopfgeld erwähnt, nicht wahr? Gut, das wird mich lehren, meinen Mund nicht zu weit aufzureißen, oder?$B$BIhr habt die Arbeit gemacht, also ist es wohl nur gerecht, wenn Ihr den größten Lohn einheimst. Nehmt die Hörner, ich lasse sie für Euch in eine Kiste packen. Findet Belgrom Felshammer im Tal der Ehre. Er wird wissen wollen, was Ihr gesehen habt, und Euch für Eure harte Arbeit belohnen.$B$BMacht es gut, $C, und danke noch einmal für alles, was Ihr getan habt.",
  },
  [2977] = {
    ["T"] = "Rückkehr nach Eisenschmiede",
    ["O"] = "Sprecht mit Historiker Karnik in Eisenschmiede.",
    ["D"] = "Ihr greift erneut auf das Podest zu und die Platinscheiben erscheinen plötzlich wieder in Eurem Rucksack.$B$BNachdem Ihr das Wissen des Steinbehüters verinnerlicht habt und die Scheiben wieder in Eurem Besitz sind, ist es jetzt an der Zeit, nach Eisenschmiede zurückzukehren und die Forscherliga von Eurer Entdeckung in Kenntnis zu setzen.",
  },
  [2978] = {
    ["T"] = "Die Gordunnischriftrolle",
    ["O"] = "Bringt die Gordunnischriftrolle zu Rok Orhan ins Camp Mojache.",
    ["D"] = "Ihr hebt die verschmutzte Schriftrolle auf und bemerkt, dass sie eine Art Versammlung oder Zeremonie darstellt. Rok Orhan wird sich für diese Schriftrolle, und was sie für die Gordunni bedeutet, interessieren.",
  },
  [2979] = {
    ["T"] = "Dunkle Zeremonie",
    ["O"] = "Rok Orhan im Camp Mojache will, dass Ihr eine Gordunnikugel findet.",
    ["D"] = "Dies scheinen Pläne für eine Art dunkle Zeremonie zu sein, $n. Soweit ich das erkennen kann, wird dieses Ritual von den Magierlords der Gordunni ausgeführt.$B$BFindet einen dieser Magierlords und sammelt eine Kugel von ihm.$B$BDie Kugel eines Magiers enthält große Macht und wird uns dabei helfen, mehr über diese Zeremonie und die Magie, die die Gordunni beherrschen, herauszufinden.",
  },
  [2980] = {
    ["T"] = "Die Oger von Feralas",
    ["O"] = "Rok Orhan im Camp Mojache will, dass Ihr 10 Schamanen der Gordunni, 10 Hexenmeister der Gordunni und 5 Raufer der Gordunni tötet.",
    ["D"] = "Jetzt, da Ihr bewiesen habt, dass Ihr ein wahrer Kämpfer seid, vertraue ich darauf, dass Ihr den Mut besitzt, mit der vorliegenden Aufgabe weiterzumachen.$B$BReist in die Obere Wildnis südwestlich von hier. Dort gibt es einige verlassene und zerstörte Gebäude auf dem Land, das einst den Nachtelfen gehörte. Sucht nach diesen Ruinen, und wenn Ihr verwelkte Bäume und entweihtes Gelände seht, dann werdet Ihr die Gordunni finden. Drängt sie zurück.$B$BDiese Oger sind viel stärker als diejenigen, denen Ihr bisher entgegengetreten seid, $n. Seht Euch vor.",
  },
  [2981] = {
    ["T"] = "Eine Bedrohung in Feralas",
    ["O"] = "Belgrom Felshammer will, dass Ihr mit Rok Orhan im Camp Mojache sprecht.",
    ["D"] = "Feralas ist der ideale Ort für jemanden mit Eurer Erfahrung, $C, der sich selbst beweisen will. Ein Stamm von Ogern, die sich Gordunni nennen, ist die vorherrschende Militärmacht dort, und ihre Zahl wächst. Sie müssen aufgehalten werden, bevor sie unsere Anwesenheit in Feralas bedrohen.$B$BEine mir bekannte Kriegerin mit Namen Rok Orhan lebt im Camp Mojache in Feralas; geht zu ihr. Sie wird Euch mehr über die Situation erzählen, wenn Ihr dort ankommt.",
  },
  [2982] = {
    ["T"] = "Die Obere Wildnis",
    ["O"] = "Angelas Mondhauch will, dass Ihr 8 Hexenmeister der Gordunni, 8 Schamanen der Gordunni und 8 Schläger der Gordunni tötet.",
    ["D"] = "Obwohl es gefährlich ist, haben Troyas und ich die Ruinen erforscht, die in den Wäldern von Feralas zu finden sind.$B$BIn jüngster Zeit ist Troyas jedoch oft allein in die Obere Wildnis gegangen und ich mache mir Sorgen um ihn. Die Oger der Gordunni haben dieses Gebiet besetzt und sie reagieren nicht sehr freundlich auf jemanden, der ihr Territorium betritt.$B$BIch hoffte, dass Ihr vielleicht zu den Ruinen im Südosten reisen und einige der Oger beseitigen würdet. Macht es ein wenig sicherer für meinen Troyas und ich werde Euch sehr dankbar sein.",
  },
  [2983] = {
    ["T"] = "Ruf des Feuers",
    ["O"] = "Sucht Kranal Fiss im Brachland.",
    ["D"] = "Es ist eine Weile her, seit Ihr zuletzt geprüft wurdet, $n. Ihr wart in Euren Studien bisher sehr geduldig, doch es ist an der Zeit, dass Ihr etwas über größere Mächte erlernt... Es ist an der Zeit, dass Ihr wahre Macht erfahrt. Das Element des Feuers ist Euer, wenn Ihr bereit seid, dafür zu bezahlen.$B$BSucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich des Wegekreuzes, aber gebt Acht... wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen.",
  },
  [2984] = {
    ["T"] = "Ruf des Feuers",
    ["O"] = "Sucht Kranal Fiss im Brachland.",
    ["D"] = "Es ist lange her, seit Ihr zuletzt geprüft wurdet, $n. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über Zerstörung zu lernen... und über Chaos. Ihr könnt das Element des Feuers verstehen lernen, wenn Ihr bereit seid, dafür zu bezahlen.$B$BSucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich des Wegekreuzes, aber gebt Acht, wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen.",
  },
  [2985] = {
    ["T"] = "Ruf des Wassers",
    ["O"] = "Sucht Islen Wasserdeuter im Brachland.",
    ["D"] = "Ihr seht aus, als wäret Ihr bereit für die nächste Prüfung, $n. Ihr wart bislang geduldig in Euren Studien, doch die Zeit ist gekommen, da Ihr mehr erlernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu erlernen.$B$BSucht nach Islen Wasserdeuter im Brachland. Sie befindet sich an der Ostküste, südlich von Ratschet und dem Südstrom. Ihr findet sie in ihrer Angelhütte. Viel Glück, $n.",
  },
  [2986] = {
    ["T"] = "Ruf des Wassers",
    ["O"] = "Sucht Islen Wasserdeuter im Brachland.",
    ["D"] = "Es ist lange her, seit Ihr zuletzt geprüft wurdet, $n. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu lernen.$B$BSucht nach Islen Wasserdeuter im Brachland. Sie befindet sich an der Ostküste, südlich von Ratschet und dem Südstrom. Ihr findet sie in ihrer Fischerhütte. Viel Glück, $n.",
  },
  [2987] = {
    ["T"] = "Gordunnikobalt",
    ["O"] = "Orwin Gizzmick im Camp Mojache will 12 Proben Gordunnikobalt.",
    ["D"] = "Ich bin gerade erst hier in Feralas angekommen und habe gehört, dass die Oger der Gordunni, die die Wildnis hier bewohnen, einige interessante Mineralien besitzen. Keiner weiß, was die so vorhaben, aber sie passen höllisch gut auf dieses Zeug auf! Sie lassen es nicht einfach herumliegen. Oh nein, sie vergraben es, damit Leute wie ich es nicht in die Finger bekommen.$B$BWenn Ihr wollt, gebe ich Euch meine Schaufel. Geht zum Außenposten der Gordunni nördlich von hier und grabt mir etwas Gordunnikobalt aus. Haltet nach blauem Glühen Ausschau und fangt dann dort an zu graben!",
  },
  [2988] = {
    ["T"] = "Käfige der Bleichborken",
    ["O"] = "Durchsucht die Käfige in den beiden Dörfern der Bleichborken und kehrt dann zu Greifenmeister Krallenaxt zurück.",
    ["D"] = "Der Sohn unseres mächtigsten Greifen ist verschwunden! Sturmschwinges Erbe, Scharfschnabel, ging vor ein paar Tagen bei der Jagd mit seinen Eltern verloren und wir glauben, dass die Trolle ihn geschnappt haben.$B$BIhr müsst uns helfen! Durchsucht die Käfige in den beiden Dörfern der Bleichborkentrolle, um herauszufinden, ob Scharfschnabel dort ist.",
  },
  [2989] = {
    ["T"] = "Der Altar von Zul",
    ["O"] = "Überprüft die Oberfläche des Altars von Zul und kehrt dann zu Greifenmeister Krallenaxt zurück.",
    ["D"] = "Scharfschnabel war nicht bei den Bleichborkentrollen. Vielleicht haben ihn die Blutfratzentrolle geholt. Geht zum Altar von Zul am Westrand des Territoriums der Blutfratzen und sucht nach dem Greifenjungen.",
  },
  [2990] = {
    ["T"] = "Thadius Grimmschatten",
    ["O"] = "Bringt Scharfschnabels Feder zu Thadius Grimmschatten in die Verwüsteten Lande.",
    ["D"] = "Bringt diese Feder zum Rutengänger Thadius Grimmschatten. Er verließ vor Jahren den Nistgipfel, um sich dunklerem Wissen zuzuwenden, aber in seinem Herzen ist er immer noch ein Wildhammer!$B$BUnd vielleicht können uns seine Talente dabei helfen, Scharfschnabel zu finden.$B$BDiese Feder stammt aus dem Nest, in dem das Greifenjunge schläft. Wenn Ihr sie Thadius gebt, kann er mit etwas Glück seine Rutengangmagie verwenden, um Scharfschnabel zu finden.$B$BViel Glück, $n. Ihr werdet Thadius in den Verwüsteten Landen finden, im Magierturm der Burg Nethergarde.",
  },
  [2991] = {
    ["T"] = "Nekrums Medaillon",
    ["O"] = "Bringt Thadius Grimmschatten in den Verwüsteten Landen Nekrums Medaillon.",
    ["D"] = "Ich werde herausfinden, wo Euer Greif ist, aber erst müsst Ihr etwas für mich tun. Es gibt da einen Troll in Zul\'Farrak, Nekrum der Ausweider, der ein Medaillon besitzt, das ich wirklich gern studieren würde. Bringt mir das Medaillon und ich werde meine Kräfte dafür verwenden, Scharfschnabel zu finden.$B$BZul\'Farrak ist jenseits des Ozeans, im Westen, in der Wüste von Tanaris.",
  },
  [2992] = {
    ["T"] = "Die Weissagung",
    ["O"] = "Wartet, bis Grimmschatten das Rutengangritual durchgeführt hat.",
    ["D"] = "Lasst mir etwas Platz, während ich ein Rutengangritual durchführe. Ich werde die Verbindung zwischen der Feder, die Ihr mir gebracht habt, und ihrem Besitzer herstellen und dann selbst dieser Verbindung zu Scharfschnabel folgen.$B$BZumindest... wenn alles nach Plan verläuft. Wenn nicht, nun... dann schließt Ihr besser die Augen.",
  },
  [2993] = {
    ["T"] = "Rückkehr ins Hinterland",
    ["O"] = "Sprecht mit Greifenmeister Krallenaxt.",
    ["D"] = "Scharfschnabel wird in einem Käfig hoch oben auf einer Klippe am Gipfel des Jintha\'Alor im Hinterland gefangen gehalten. Der Schlüssel zu diesem Käfig ist im Besitz der Hohepriesterin der Blutfratzentrolle, der üblen Priesterin Hexx.$B$BDa. Ich habe meinen Teil erfüllt. Ich wünsche Euch Glück, $n.",
  },
  [2994] = {
    ["T"] = "Scharfschnabels Rettung",
    ["O"] = "Kämpft Euch bis zum Gipfel von Jintha\'Alor durch, tötet die üble Priesterin Hexx, um an den Schlüssel zu Scharfschnabels Käfig zu kommen, und rettet Scharfschnabel.",
    ["D"] = "Wir müssen Scharfschnabel retten!$B$BKämpft Euch bis zum Gipfel von Jintha\'Alor im Herzen des Blutfratzenterritoriums vor, holt Euch den Schlüssel zu Scharfschnabels Käfig von der üblen Priesterin Hexx und dann sucht und befreit Scharfschnabel.",
  },
  [2995] = {
    ["T"] = "Nachrichtenwege",
    ["O"] = "Verbrennt die Hochtalaufzeichnungen, Hochtalberichte und Hochtalnotizen.",
    ["D"] = "Die Jagdhütte von Quel\'Danil ist ein Zentrum der Kommunikation für die Hochelfen des Hinterlands. Ihre Botschafter sind damit beschäftigt, Beziehungen zu anderen Völkern zu knüpfen, mit denen sie ihr Wissen über die Trolle der Bleichborken teilen, die das Gebiet im südlichen Hinterland bewohnen. Alle Aufzeichnungen betreffs dieses Nachrichtenaustauschs werden in der Hütte aufbewahrt.$B$BWir können es nicht brauchen, wenn die Hochtalelfen Allianzen mit anderen schließen, $n.$B$BVernichtet die Hochtaldokumente. Wir müssen ihre Nachrichtenwege niederbrennen... im wahrsten Sinne des Wortes.",
  },
  [2996] = {
    ["T"] = "Die Suche nach Strahad",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["D"] = "Ich habe Euch in der Vergangenheit viele Fertigkeiten beigebracht, aber es ist an der Zeit, dass Euch ein anderer lehrt. Er lebt in Ratschet und sein Name ist Strahad. Er ist ein Mensch, aber lasst Euch deswegen nicht dazu hinreißen, ihn für schwach zu halten. Er mag klein sein, aber er ist auch mächtig. Er kann Euch mächtigere Beschwörungen lehren - ich fühle, dass Ihr dazu bereit seid. Geht nun zu ihm. Er ist in Ratschet... Über der Hafenstadt gleich bei seinem Turm.",
  },
  [2997] = {
    ["T"] = "Foliant der Offenbarung",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["D"] = "Hallo, $n. Ich weiß, Ihr seid damit beschäftigt, den Leuten von Dun Morogh zu helfen, aber wenn Ihr etwas Zeit habt, könntet Ihr Euch auf den Weg nach Eisenschmiede hinauf machen, um mit einer unserer am meisten Respektierten zu reden: Tiza Kriegshammer. Zu ihr kommen die jüngeren Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von einem erwartet wird.$B$BIhr könnt sie oben in der Halle der Mysterien finden. Falls Ihr noch nie dort wart, das ist im Mystikerviertel. Viel Glück, $n.",
  },
  [2998] = {
    ["T"] = "Foliant der Offenbarung",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["D"] = "Seid gegrüßt, $GBruder:Schwester;. Duthorian Rall, einer unserer einflussreichsten Paladine, hat kürzlich von Euch gesprochen. Ich habe mitbekommen, wie er darüber sprach, dass es an der Zeit sei, dass Ihr ein wenig getestet würdet... Dabei fielen auch einige Komplimente darüber, wie gut Ihr bisher abgeschnitten habt.$B$BWenn ich Ihr wäre, würde ich nach Sturmwind gehen und mit ihm sprechen, sobald Ihr Zeit habt. Ich bin sicher, er ist in der Kathedrale des Lichts - dort verbringt er einen Großteil seiner Zeit.",
  },
  [2999] = {
    ["T"] = "Foliant der Offenbarung",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["D"] = "Hallo, $n. Ich weiß, Ihr seid damit beschäftigt, den Leuten von Dun Morogh zu helfen, aber wenn Ihr etwas Zeit habt, könntet Ihr nach oben gehen, um mit einer unserer am meisten Respektierten zu reden: Tiza Kriegshammer. Zu ihr kommen die jüngeren Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von einem erwartet wird.",
  },
  [3000] = {
    ["T"] = "Foliant der Offenbarung",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["D"] = "Hallo, $n. Ich weiß, Ihr seid damit beschäftigt, den Völkern Azeroths zu helfen, aber wenn Ihr etwas Zeit habt, solltet Ihr nach Eisenschmiede zurückkehren, um mit einem der am meisten respektierten Paladine Eurer Heimat zu reden: Tiza Kriegshammer. Zu ihr kommen die jüngeren Zwergenpaladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von uns allen erwartet wird.$B$BIhr findet sie im Obergeschoss der Halle der Mysterien, die sich im Mystikerviertel befindet.",
  },
  [3001] = {
    ["T"] = "Die Suche nach Strahad",
    ["O"] = "Findet Strahad Farsan in Ratschet.",
    ["D"] = "Ich habe Euch in der Vergangenheit viele Fertigkeiten beigebracht, aber es ist an der Zeit, dass Euch ein anderer lehrt. Er lebt in Ratschet und sein Name ist Strahad. Er ist ein Mensch, aber lasst Euch deswegen nicht dazu hinreißen, ihn für schwach zu halten oder als Euren Feind anzusehen - zumindest vorerst. Er ist mächtig und sollte respektiert werden. Er kann Euch große Beschwörungen lehren, Beschwörungen, für die Ihr, glaube ich, bereit seid. Geht nun zu ihm.$B$BEr ist in Ratschet... Über der Hafenstadt, gleich bei dem Turm dort.",
  },
  [3002] = {
    ["T"] = "Die Gordunnikugel",
    ["O"] = "Bringt die Gordunnikugel zu Uthel\'nay in die Dunkeldornenlodge in Orgrimmar.",
    ["D"] = "Obwohl ich meinen Feind genau kenne, bin ich ein Krieger, $n. Ich kann Euch nicht sagen, über welche Fähigkeiten diese Kugel verfügt.$B$BIch kenne einen Magier in Orgrimmar, der uns mehr über die Kugel sagen können wird. Bringt sie zu ihm.",
  },
  [3022] = {
    ["T"] = "Vorsichtig behandeln",
    ["O"] = "Bringt die sorgfältig verpackte Kiste zu Erelas Himmelsbern in das Dorf Rut\'theran.",
    ["D"] = "$n, ich sehe deutlich, dass Ihr Euch liebend gern meiner noblen Sache zuwenden möchtet. Ja, natürlich könnt Ihr etwas für mich tun!$B$BBringt diese Kiste mit Eiern zu Erelas Himmelsbern. Das ist ein Druide, der sich um diese Eier kümmert. Er bebrütet sie und bildet dann die jungen Hippogryphen aus. Ihr findet ihn in Teldrassil, in einem kleinen Dorf im Süden namens Rut\'theran.",
  },
  [3042] = {
    ["T"] = "Trollaushärter",
    ["O"] = "Bringt 20 Phiolen Trollaushärter zu Trenton Lichthammer in Gadgetzan.",
    ["D"] = "Die Trolle der Sandwüter von Zul\'Farrak stellen ein Aushärtungsmittel aus Sandstein her, und ich kann dies verwenden, um die von mir hergestellten Waren auszuhärten, ja, so ist es! Von Kennern wird es hoch gepriesen; wenn Ihr mir also einen guten Vorrat dieses Härters bringt, werde ich Euch reich belohnen.$B$BZul\'Farrak liegt nordwestlich von Gadgetzan. Viel Glück.",
  },
  [3062] = {
    ["T"] = "Dunkles Herz",
    ["O"] = "Talo Dornhuf im Camp Mojache will das dunkle Herz von Edana Hasskralle.",
    ["D"] = "Edana Hasskralle ist die Königin der Nordschwingen, die in den Ruinen im Westen zu Hause sind. Sie ist das vollkommen Böse; ich habe gehört, dass sie ein Herz aus Kristall hat. Sie ist bar jeglicher Emotionen außer dem Hass, den sie für alle anderen fühlt. Ihr müsst Euch Edana stellen und mir ihr dunkles Herz zurückbringen.$B$BIch habe von einem Horn gehört, das die Harpyien tragen - wenn es bei den Hasskrallensteinen, im nördlichen Teil der Ruinen, geblasen wird, wird die Königin beschworen.$B$BStellt Euch ihre Überraschung vor, wenn es nicht die ihren sind, die auf sie warten, sondern ihr Tod.",
  },
  [3063] = {
    ["T"] = "Rache an den Nordschwingen",
    ["O"] = "Talo Dornhuf möchte, dass Ihr 4 Harpyien der Nordschwingen, 4 Wildfedern der Nordschwingen, 4 Windruferinnen der Nordschwingen und 4 Töter der Nordschwingen erschlagt.",
    ["D"] = "Zu meiner Zeit war ich ein erfahrener Jäger. Es gab keine Kreatur, die ich fürchtete. Bis...$B$BIch war draußen im Wald, um nach einem heftigen Kampf meine Klinge zu schärfen, als ich in einen Hinterhalt geriet. Harpyien umzingelten mich, und ich schaffte es kaum, mich in Sicherheit zu bringen.$B$BSelbst heute noch kann ich das Echo ihrer Schreie vernehmen...$B$BDanach war ich nicht mehr derselbe. Aber der Hass kocht in meinem Inneren - ich will Rache, auch noch nach all diesen Jahren.$B$BDie Harpyien der Nordschwingen leben westlich von hier. $n, rächt mich.",
  },
  [3065] = {
