  [3661] = {
    ["T"] = "Elunes Lieblinge?",
    ["O"] = "Sammelt 15 Wildekinfedern aus dem Hinterland für Erelas Himmelsbern in Rut\'theran.",
    ["D"] = "Wildekin sind unberechenbare Kreaturen, $n. Sie sind zu einer außerordentlichen Zärtlichkeit fähig, aber diese Kreaturen können auch eine enorme Wildheit an den Tag legen, wenn etwas für sie sehr Wichtiges bedroht wird.$B$BDiese Kreaturen wurden angeblich von Elune geschaffen und ich bin daran interessiert herauszufinden, ob dies wirklich der Wahrheit entspricht.$B$BVor Kurzem habe ich etwas von einer Population von Wildekin gehört, die im Hinterland leben - heimtückische, primitive und wilde Eulenbestien. Man findet sie unter den wild lebenden Tieren dort. Würdet Ihr einige Wildekinfedern für mich sammeln?",
  },
  [3681] = {
    ["T"] = "Foliant der Offenbarung",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["D"] = "Verzeiht, dass wir Eure Reise unterbrechen, $R, aber ich dachte, Ihr solltet wissen, dass uns vor Kurzem eine Meldung aus Sturmwind erreicht hat, dass sich Paladine zum Training dort melden sollen.$B$BWenn ich es nicht besser wüsste, würde ich sagen, dass bald etwas Besonderes geschehen wird und es für Euch an der Zeit ist, die Folianten zu studieren.$B$BSucht Duthorian Rall in Sturmwind auf. Er trainiert für gewöhnlich Paladine in der Kathedrale des Lichts.",
  },
  [3701] = {
    ["T"] = "Die glimmenden Ruinen von Thaurissan",
    ["O"] = "Begebt Euch zu den Ruinen von Thaurissan in der Brennenden Steppe und sammelt Informationen über die Relikte von Thaurissan. Kehrt zur königlichen Historikerin Archesonus zurück, wenn Ihr die Informationen besorgt habt.",
    ["D"] = "Begebt Euch zu den glimmenden Ruinen von Thaurissan in der Brennenden Steppe und sammelt so viele Informationen wie möglich.$B$BEs heißt, dass geheimnisvolle Relikte in den Ruinen verstreut seien. Vielleicht solltet Ihr Eure Suche dort beginnen, $n. Nehmt Euch vor den thaurissanischen Wächtern in Acht: Es sind Auftragsmörder, die die Ruinen bewachen und die dort möglicherweise noch immer begrabenen Geheimnisse beschützen, wie auch immer sie aussehen mögen.",
  },
  [3702] = {
    ["T"] = "Die glimmenden Ruinen von Thaurissan",
    ["O"] = "Hört die Erzählung der königlichen Historikerin Archesonus über die Geschichte von Thaurissan.",
    ["D"] = "Würdet Ihr gern einen Überblick über die Geschichte der Dunkeleisenzwerge erhalten?",
  },
  [3721] = {
    ["T"] = "Ein eigener OOX",
    ["O"] = "",
    ["D"] = "",
  },
  [3741] = {
    ["T"] = "Hilarys Halskette",
    ["O"] = "Sucht Hilarys Halskette und bringt sie Hilary in Seenhain zurück.",
    ["D"] = "Meine Freundin Hilary hat ihre Halskette verloren und wir können sie einfach nicht finden. Den ganzen Tag lang fischen wir schon hier und haben auch schon rundum überall gesucht. Ich kann mir nur vorstellen, dass sie in den See gefallen ist. Unsere Eltern möchten nicht, dass wir im See schwimmen... meine Mama sagt, dass es unter Wasser ein paar garstige Dinge gibt, aber Ihr seht aus, als ob Ihr keine Angst vor garstigen Dingen hättet.$B$BKönnt Ihr die Kette bitte für uns suchen?",
  },
  [3761] = {
    ["T"] = "Erde von Un\'Goro",
    ["O"] = "Bringt 20 Erdproben von Un\'Goro zu Ghede in Donnerfels.",
    ["D"] = "Die Erde des Kraters von Un\'Goro ist angeblich reich an starken magischen Eigenschaften. Der Zirkel des Cenarius möchte diese Erde in großen Mengen untersuchen; zieht daher in die Wildnis von Un\'Goro und sammelt genug davon für unsere ständig laufenden Forschungen.$B$BDie angereicherte Erde ist manchmal zu Haufen aufgeworfen und einige der Wildtiere könnten vielleicht nützliche Proben bei sich haben. Bringt sie raus zu Ghede; sucht einfach nach den Erdhaufen und dem Tauren, der Befehle brüllt. Er ist kein schlechter Kerl, aber verärgert ihn lieber nicht!",
  },
  [3762] = {
    ["T"] = "Erzdruide Runentotem helfen",
    ["O"] = "Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.",
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.",
  },
  [3763] = {
    ["T"] = "Erzdruide Hirschhaupt helfen",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.",
  },
  [3764] = {
    ["T"] = "Erde von Un\'Goro",
    ["O"] = "Bringt 20 Erdproben von Un\'Goro zu Jenal in Darnassus.",
    ["D"] = "Die Erde des Kraters von Un\'Goro ist angeblich reich an starken magischen Eigenschaften. Ich möchte Wahrheit von Unwahrheit unterscheiden; begebt Euch daher in die Wildnis von Un\'Goro und sammelt ausreichend hochwertige Erdproben für unsere ständig laufenden Studien.$B$BIhr glaubt möglicherweise, dass die Aufgabe, etwas zu sammeln, unter Eurer Würde sei; doch mit solch einer Einstellung macht Ihr Euch weder bei mir noch beim Zirkel des Cenarius beliebt. Bringt die Proben zu Jenal, der zweifellos draußen hinter diesem Baum müßig herumlungert; sucht einfach nach den Erdhaufen. Er wird sich um die Logistik kümmern, nicht ich.",
  },
  [3765] = {
    ["T"] = "Verderbnis in der Fremde",
    ["O"] = "Reist zu Gershala Nachtraunen in Auberdine.",
    ["D"] = "Sturmwind - geschäftiger Dreh- und Angelpunkt der Aktivität.$b$bIch bin weit gereist, um Hilfe zu suchen. Man hört häufig, dass mutige Abenteurer durch die Straßen dieser Stadt ziehen.$b$bMein Bruder Gershala benötigt Hilfe in Dunkelküste. Er lebt in der Küstenstadt Auberdine.$b$bDie Reise ist lang und abenteuerlich. Daher suche ich nach Hilfe. Es sollte $gein erfahrener:eine erfahrene:c; $C sein, so wie Ihr.",
  },
  [3781] = {
    ["T"] = "Morgenkornforschung",
    ["O"] = "Bringt den Samenkorngutschein des Erzdruiden zu Mathrengyl Bärenfährte in der Enklave des Cenarius von Darnassus.",
    ["D"] = "Mit dem, was wir einen Immergrünbeutel nennen, haben wir Tharlendrissamenkörner in der Erde des Kraters von Un\'Goro kultiviert. Diese Samenkörner sind zu einer Ansammlung wirksamer Kräuter erblüht. Ein Ergebnis ist das Morgenkorn, ein rätselhaftes Kraut, von dem wir wenig wissen; ich habe vor, dieses Geheimnis zu enthüllen.$B$BBringt das nach unten zu Mathrengyl, und er wird Euch einige Samenkörner geben, mit denen Ihr beginnen könnt. Wenn Ihr diese verbraucht habt, müsst Ihr weitere von ihm kaufen.",
  },
  [3782] = {
    ["T"] = "Morgenkornforschung",
    ["O"] = "Bringt den Samenkorngutschein zu Bashana Runentotem in Donnerfels.",
    ["D"] = "Mit dem, was wir einen Immergrünbeutel nennen, haben wir Tharlendrissamenkörner in der Erde des Kraters von Un\'Goro kultiviert. Diese Samenkörner sind zu einer Ansammlung wirksamer Kräuter erblüht. Ein Ergebnis ist das Morgenkorn, ein rätselhaftes Kraut, von dem wir wenig wissen; ich hoffe, dieses Geheimnis für den Zirkel zu enthüllen.$B$BBringt diesen Gutschein zu meiner Tochter Bashana, und sie wird Euch einige Samenkörner geben, damit Ihr uns helfen könnt. Wenn Ihr diese verbraucht habt, müsst Ihr weitere von ihr kaufen.",
  },
  [3783] = {
    ["T"] = "Sind wir endlich da, Yeti?",
    ["O"] = "Sammelt 10 dicke Yetifelle für Umi Rumpelkicher in der Ewigen Warte.",
    ["D"] = "Meine Freunde hatten immerzu tolle Ideen. Ich hatte nie das Gefühl mit ihnen mithalten zu können... bis heute! Gerade habe ich die Pläne für etwas Großes angefertigt! Wenn Ihr versprecht, es nicht zu erzählen, weihe ich Euch in das Geheimnis ein... $B$BIch werde einen mechanischen Yeti herstellen! Meine Freunde werden ganz bestimmt durchdrehen, wenn sie ihn sehen, aber wenn sie darüber hinweg sind, bin ich mir sicher, dass sie beeindruckt sein werden!$B$BFür den Anfang benötige ich dickes Yetifell von Eisdistelyetis. Werdet Ihr helfen? Ihr könnt sie im Südosten finden.",
  },
  [3784] = {
    ["T"] = "Erzdruide Runentotem helfen",
    ["O"] = "Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.",
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.",
  },
  [3785] = {
    ["T"] = "Morgenkornforschung",
    ["O"] = "Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un\'Goro, um Morgenkorn zu kultivieren.$B$BBringt 10 Einheiten Morgenkorn zu Mathrengyl Bärenfährte in Darnassus.",
    ["D"] = "Hier ist Euer Immergrünbeutel, den Ihr für die Nutzung der Tharlendrissamenkörner benötigen werdet. Ihr benötigt außerdem noch Erdproben von Un\'Goro als Dünger für die Samenkörner. Verwendet einfach den Beutel, wenn Ihr alles habt; der Beutel wird den Rest erledigen.$B$BWenn Ihr genug Einheiten Morgenkorn habt, bringt sie mir hierher in die Enklave. Und belästigt bitte nicht den Erzdruiden; er ist ein sehr beschäftigter Mann und muss nicht jedes Mal gestört werden, wenn jemand mit einer Ladung Morgenkorn zurückkehrt!",
  },
  [3786] = {
    ["T"] = "Morgenkornforschung",
    ["O"] = "Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un\'Goro, um Morgenkorn zu kultivieren.$B$BBringt 10 Einheiten Morgenkorn zu Bashana Runentotem in Donnerfels.",
    ["D"] = "Hier ist Euer Immergrünbeutel, den Ihr für die Nutzung der Tharlendrissamenkörner benötigen werdet. Ihr benötigt außerdem noch Erdproben von Un\'Goro als Dünger für die Samenkörner. Verwendet einfach den Beutel, wenn Ihr alles habt; der Beutel wird den Rest erledigen.$B$BWenn Ihr genug Einheiten Morgenkorn habt, bringt sie zu mir hierher auf die Anhöhe der Ältesten. Belästigt bitte nicht den Erzdruiden; er wird sehr mit den Forschungsarbeiten beschäftigt sein sowie mit den tausend anderen Sorgen, die er hat!",
  },
  [3787] = {
    ["T"] = "Lebenshands Bitte",
    ["O"] = "Sprecht mit Quintis Lebenshand in der Mondfederfeste.",
    ["D"] = "Entschuldigung...$B$BEin Kollege von mir würde gern mit Euch über die Forschungsarbeiten sprechen, die Ihr zusammen mit dem Zirkel des Cenarius durchführt. Er ist ein Druide namens Quintis Lebenshand und lebt in der Mondfederfeste in Feralas. Er würde gern mit Euch über Morgenkorn reden... und er würde es vorziehen, wenn unser Gespräch dem Erzdruiden nicht zu Ohren kommt.$B$BFalls Ihr interessiert seid, dann sprecht mit ihm; zweifellos wird alles klargelegt, wenn Ihr es tut.",
  },
  [3788] = {
    ["T"] = "Lebenshands Bitte",
    ["O"] = "Sprecht mit Quintis Lebenshand in der Mondfederfeste.",
    ["D"] = "Entschuldigung...$B$BEin Kollege von mir würde gern mit Euch über die Forschungsarbeiten sprechen, die Ihr zusammen mit dem Zirkel des Cenarius durchführt. Er ist ein Druide namens Quintis Lebenshand und lebt hier in der Mondfederfeste. Er würde gern mit Euch über Morgenkorn reden... und er würde es vorziehen, wenn unser Gespräch dem Erzdruiden nicht zu Ohren kommt.$B$BFalls Ihr interessiert seid, dann sprecht mit ihm; zweifellos wird alles klargelegt, wenn Ihr es tut.",
  },
  [3789] = {
    ["T"] = "Erzdruide Hirschhaupt helfen",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.",
  },
  [3790] = {
    ["T"] = "Erzdruide Hirschhaupt helfen",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.",
  },
  [3791] = {
    ["T"] = "Das Geheimnis des Morgenkorns",
    ["O"] = "Bringt 10 Einheiten Morgenkorn zu Quintis Lebenshand in der Mondfederfeste.",
    ["D"] = "Lasst mich offen mit Euch sein, $n. Ich bin interessiert zu wissen - nein, ich muss es unbedingt wissen, warum der Erzdruide in Darnassus den Namen des Zirkels des Cenarius sowie dessen Mittel benutzt, um große Mengen Morgenkorn zu beschaffen. Ich bin ein Forscher - Flüche sind meine Spezialität. Ich habe gehört, dass Morgenkorn unter den richtigen Bedingungen Fähigkeiten verströmt wie andere Kräuter, die in primitiven Flüchen eingesetzt werden.$B$BIch möchte mehr darüber wissen und bitte Euch um Eure Hilfe. Bringt mir 10 Einheiten Morgenkorn und ich werde mich um Euch kümmern.",
  },
  [3792] = {
    ["T"] = "Morgenkorn für die Mondfederfeste",
    ["O"] = "",
    ["D"] = "",
