  [4120] = {
    ["T"] = "Die Kraft der Verderbnis",
    ["O"] = "Talo Dornhuf im Camp Mojache in Feralas möchte, dass Ihr 12 Wutklauengrizzlys und 12 Teufelspfotenverheerer im Teufelswald erledigt.",
    ["D"] = "Als Jäger strebe ich danach, die Stärken nachzuahmen, die ich bei meinen Gegnern sehe. Man kann den Kampf zu seinen Gunsten wenden, indem man einfach seinen Feind gut kennt, $n.$B$BAn einem Ort namens Teufelswald, nördlich des Eschentals, wurden die Kreaturen durch dämonische Energien verderbt. Die Bären und Wölfe wurden von ihnen verrückt, allerdings glaube ich, dass sie dadurch auch noch stärker geworden sind.$B$BGeht, $n. Reist zum Teufelswald und tretet den Bestien gegenüber. Erfahrt selbst, ob sie wirklich so stark sind, wie ich glaube.",
  },
  [4121] = {
    ["T"] = "Gefährliche Zwickmühle",
    ["O"] = "Eskortiert Euren Gefangenen, Grark Lorkrub, durch die Brennende Steppe und durch den Schwarzfels in die Sengende Schlucht.$B$BEuch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.$B$BIhr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen.",
    ["D"] = "Glaubt Ihr wirklich, dass Ihr es mit mir als Gefangenem zum vereinbarten Treffpunkt schafft? Habt Ihr wirklich gedacht, Ihr könntet in eine schwer bewachte Festung des Schwarzfelsklans eindringen und völlig unbemerkt ihren wertvollsten Besitz entführen?$B$BMan wird Euch vernichten!",
  },
  [4122] = {
    ["T"] = "Grark Lorkrub",
    ["O"] = "Begebt Euch in die Brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden.$B$BWenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die Sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.",
    ["D"] = "Ich habe ihn durch die ganze Sengende Schlucht, durch den Berg und in die Brennende Steppe hinein verfolgt. Der Verräter wurde auf frischer Tat dabei ertappt, wie er den Schwarzfelsorcs wichtige Informationen über die tagtäglichen Vorgänge bei uns lieferte. Ich spreche von Grark Lorkrub.$B$BEr sitzt jetzt in der Brennenden Steppe, sicher unter deren Schutz - glaubt er jedenfalls.$B$BNehmt diese Fesseln, $n. Findet Grark, legt ihm die Fesseln an und führt ihn durch den Schwarzfels. Wenn Ihr das tut, ist ein Tribut Euer.",
  },
  [4123] = {
    ["T"] = "Das Herz des Berges",
    ["O"] = "Bringt das \'Herz des Berges\' zu Maxwort Funkelglanz in der Brennenden Steppe.",
    ["D"] = "Schon seit Jahren suche ich einen ganz bestimmten Edelstein. Er wird das \'Herz des Berges\' genannt und ist so groß wie Eure Faust! Die Dunkeleisenzwerge haben den Stein in ihrem Gewölbe weggeschlossen und ich kann machen, was ich will, sie wollen ihn mir einfach nicht verkaufen!$B$BAlso muss ich Gewalt anwenden.$B$BKämpft Euch zum unteren Gewölbe der Schwarzfelstiefen durch, brecht in den geheimen Safe dort ein und holt Euch das Herz. Dazu müsst Ihr den Wachmann Stahlgriff besiegen, aber der taucht erst auf, wenn Ihr jeden Reliktkasten im Gewölbe geöffnet habt!$B$BViel Glück.",
  },
  [4124] = {
    ["T"] = "Der vermisste Kurier",
    ["O"] = "Sprecht mit Ginro Herdfeuer in der Mondfederfeste.",
    ["D"] = "Einer meiner Kollegen, Ginro Herdfeuer, braucht Hilfe und Informationen über den Aufenthaltsort eines Kuriers und der Pakete, die er bei sich hatte. Es ist etwa eine Woche her, dass der Kurier verschwunden ist; unsere eigene Suche hat nichts ergeben und wir wissen nicht, was wir noch tun sollen. Leider musste ich den Kurier für vermisst erklären.$B$BWenn Ihr freie Zeit und Kapazitäten habt, dann sprecht doch bitte mit Ginro. Er sollte irgendwo hier in der Festung sein, vielleicht in seinem Quartier.",
  },
  [4125] = {
    ["T"] = "Der vermisste Kurier",
    ["O"] = "Sucht das Boot des Kuriers irgendwo entlang der Küste von Feralas.",
    ["D"] = "Es ist etwa eine Woche her, seit der Kurier Raschal sich nach Thalanaar aufgemacht hat. Er kennt Feralas gut; er ist schon bei uns, seit General Mondfeder das Expeditionskorps hergeführt hat. Ich weiß, dass er vorhatte, mit einem Ruderboot die Küste entlangzufahren und Feralas im Süden zu durchqueren, um das Lager der Horde zu umgehen.$B$BIch konnte sein Boot nicht finden, aber vielleicht findet Ihr es ja. Vielleicht liegt es irgendwo versteckt...oder vielleicht wurde es auch versenkt. Wie dem auch sei, möglicherweise enthält es einen Hinweis darauf, wo Raschal ist. Wenn Ihr irgendetwas entdeckt, lasst es mich bitte wissen.",
  },
  [4126] = {
    ["T"] = "Hurley Pestatem",
    ["O"] = "Bringt Ragnar Donnerbräu in Kharanos das gestohlene Donnerbräurezept.",
    ["D"] = "Die Dunkeleisenzwerge, angeführt von dem Bösewicht Hurley Pestatem, haben eines der besten Rezepte der Donnerbräus gestohlen, jenes für das Donnerbräu Lagerbier. Diese Schurken! Sie verdienen ein solch edles Gebräu nicht!$B$B$n, ich habe eine schwierige Aufgabe für Euch. Geht zu den Schwarzfelstiefen in der Brennenden Steppe, zerstört alle Fässer mit Donnerbräu Lagerbier, die Ihr finden könnt, und bringt unser Donnerbräurezept zurück!$B$BBitte, bringt das Rezept unbedingt zurück, und wenn Ihr dafür die ganzen Schwarzfelstiefen umkrempeln müsstet!",
  },
  [4127] = {
    ["T"] = "Havarie",
    ["O"] = "Kehrt zur Mondfederfeste zurück und zeigt Ginro Herdfeuer das gefundene Messer.",
    ["D"] = "Als Ihr mit der Hand durch den Matsch fahrt, fühlt Ihr plötzlich den Griff eines Messers unter Euren Fingern. Es sieht gut gepflegt aus und dürfte mit Sicherheit jemandem gehört haben, der auf dem Boot gewesen ist. Vielleicht hat das große Stück des verschwundenen Bootes irgendetwas mit seinem Verschwinden zu tun.$B$BIhr nehmt das Messer an Euch, denn Ginro Herdfeuer in der Mondfederfeste hatte Euch angewiesen, ihm jegliche Hinweise sofort zu bringen. Allerdings werdet Ihr das ungute Gefühl nicht los, dass Ihr in diesem Gewässer nicht allein seid...",
  },
  [4128] = {
    ["T"] = "Ragnar Donnerbräu",
    ["O"] = "Sprecht mit Ragnar Donnerbräu.",
    ["D"] = "Hallo, $n. Wie ich hörte, soll Ragnar Donnerbräu außer sich vor Wut sein, dass jemand sein Bier gestohlen hat. Er sucht nach wackeren Abenteurern und ist gewillt, sie reichlich zu entlohnen.$B$BSprecht mit Ragnar, wenn Ihr Interesse habt. Ihr findet ihn in der Brauerei Donnerbräu in Kharanos in Dun Morogh.",
  },
  [4129] = {
    ["T"] = "Das wieder aufgetauchte Messer",
    ["O"] = "Bringt Quintis Lebenshand in der Mondfederfeste das gefundene Messer.",
    ["D"] = "Der Hausdruide der Festung, Quintis Lebenshand, verfügt über ein paar... ungewöhnliche Fertigkeiten. Eine davon ist die Psychometrie - die Fähigkeit, durch Berührung eines Objekts etwas über seine Geschichte zu erfahren. Fragt mich nicht, wie er das bewerkstelligt. Es muss etwas mit Magie zu tun haben, wovon ich nichts verstehe und womit ich auch nichts zu tun haben will. Zwei Dinge weiß ich jedoch: Erstens, ich habe gesehen, dass es funktioniert, und zweitens, der Gauner schuldet mir etwas.$B$BQuint lebt in einem kleinen Baumhaus. Bringt ihm das Messer und richtet ihm von mir aus, dass seine Kalimdor-Poker-Schulden bei mir beglichen sind, wenn er seinen Hokuspokus bei dem Messer wirkt.",
  },
  [4130] = {
    ["T"] = "Psychometrie-Sitzung",
    ["O"] = "Sprecht mit Ginro Herdfeuer in der Mondfederfeste.",
    ["D"] = "Entschuldigt bitte, mir ist immer noch ein bisschen schwummrig. Wie dem auch sei, ich hatte ein paar nützliche Visionen.$B$BRaschal nimmt zwei lederne Rucksäcke aus seinem Boot und geht landeinwärts. Dann jedoch kehrt er zu seinem Boot zurück. Ich sehe Gnollskalps... er ist auf ein paar Gnolle gestoßen - Waldpfoten nennt man sie, glaube ich. Er ist sehr besorgt - fast schon panisch - wegen etwas. Und das so sehr, dass er nicht bemerkt, wie sich Wasserelementare hinter seinem Rücken manifestieren. Hier enden die Visionen.$B$BIch muss mich ausruhen, tut mir leid, $n. Erzählt Gin, was ich gesehen habe. Er muss das wissen.",
  },
  [4131] = {
    ["T"] = "Die Waldpfotengnolle",
    ["O"] = "Durchsucht die Lager der Waldpfotengnolle im Süden von Feralas nach Hinweisen auf den Verbleib des Kuriers oder seiner Habseligkeiten.",
    ["D"] = "Unsere \'Freunde\', die Waldpfoten, mögen Raschal am Ende vielleicht zwar erwischt haben, aber irgendwie habe ich das Gefühl, als hätte sich da etwas anderes ereignet. Wie dem auch sei, es dürfte allerdings der beste Anhaltspunkt sein, um mit der Suche zu beginnen.$B$BIch wünschte nur, ich wüsste genauer, was zu tun ist, aber momentan kann ich lediglich sagen: \'Durchsucht die Lager der Waldpfoten. Macht Euch auf den Weg.\' Ich weiß, dass Gnolle Gegenstände, die sie ergattern, behalten und um sie zanken. Vielleicht haben sie ja auch die Rucksäcke gefunden, die Quint erwähnte. Ohne Quints Visionen hätte ich bei früheren Suchaktionen niemals eine direkte Konfrontation mit ihnen gesucht, aber jetzt würde ich sagen: Auf sie mit Gebrüll!",
  },
  [4132] = {
    ["T"] = "Operation: Tod dem Zornesschmied",
    ["O"] = "Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt.",
    ["D"] = "Ihr habt bemerkenswerte Arbeit geleistet, $n. Jetzt ist es an der Zeit, Euch auf Eure letzte Mission zu schicken.$B$BGeneral Zornesschmied, der für die Koordination der Angriffe auf die 109. Division des KEK und die massive Dezimierung unserer Truppen verantwortliche Dunkeleisenzwerg, muss zur Rechenschaft gezogen werden.$B$BDies wird zweifellos Eure schwerste Mission werden, aber solltet Ihr erfolgreich sein, wird man Euch reich entlohnen.",
  },
  [4133] = {
    ["T"] = "Vivian Lagrave",
    ["O"] = "Sprecht mit Schattenmeisterin Vivian Lagrave in Kargath.",
    ["D"] = "Schattenmeisterin Vivian Lagrave benötigt Eure Dienste. Sie lebt im Ödland, im orcischen Außenposten Kargath, wo sie dem Gerücht nachgeht, nach dem die Dunkeleisenzwerge der Schwarzfelstiefen angeblich ein bestimmtes Gebräu herstellen.$B$BSprecht mit Vivian. Sie erwartet Euch.",
  },
  [4134] = {
    ["T"] = "Verlorenes Donnerbräurezept",
    ["O"] = "Bringt Vivian Lagrave in Kargath das gestohlene Donnerbräurezept.",
    ["D"] = "Es geht das Gerücht umher, dass der Dunkeleisenzwerg Hurley Pestatem ein Rezept zum Brauen von Bier gestohlen habe. Dieses Bier, das Donnerbräu Lagerbier, soll angeblich jeden, der es trinkt, mit großer Kraft und Mut erfüllen. Wir möchten dieses Getränk gern untersuchen. Vielleicht finden wir ja noch andere Anwendungsgebiete für seine Vorzüge... Anwendungsgebiete, die eher im Einklang mit den Zielen der Verlassenen sind.$B$BDurchforstet die Schwarzfelstiefen, findet Hurley, nehmt das Donnerbräurezept von ihm und bringt es mir.$B$BUnd um ihn zu finden, müsst Ihr ihn möglicherweise locken, indem Ihr sein wertvolles Bier bedroht.",
  },
  [4135] = {
    ["T"] = "Die Gewundene Tiefe",
    ["O"] = "Da Ihr jetzt Raschals letzten bekannten Aufenthaltsort in Erfahrung gebracht habt, setzt Ihr die Suche nach ihm oder seinen sterblichen Überresten in der Gewundenen Tiefe fort. Laut Notiz soll dieser Ort südlich der Lager der Waldpfotengnolle liegen.",
    ["D"] = "In den Rucksäcken befinden sich zwei Gegenstände: Der erste ist ein für Thalanaar bestimmtes Paket mit Waren, der zweite eine hastig hingekritzelte Notiz, die an einen unbekannten Empfänger gerichtet ist.$B$BDie Notiz spricht von der Entdeckung heimtückischer käferartiger Kreaturen südlich des Gnoll-Lagers an einem Ort, der Gewundene Tiefe genannt wird. Der Verfasser wollte die Sache persönlich untersuchen, da er dies als ernsthafte Bedrohung für ganz Feralas befand. Die Notiz ist mit \'Raschal von Mondfeder\' unterzeichnet.$B$BOffenbar habt Ihr jetzt herausgefunden, wohin Raschal letztlich verschwunden ist.",
  },
  [4136] = {
    ["T"] = "Ribbly Schraubstutz",
    ["O"] = "Bringt Yuka Schraubstutz in der Brennenden Steppe Ribblys Kopf.",
    ["D"] = "Mein Bruder Ribbly hat unserer Familie schon immer auf der Tasche gelegen, unser Geld genommen und es für eine Dummheit nach der anderen ausgegeben.$B$BTja, seine letzte Dummheit muss wohl seine bislang schlimmste gewesen sein, denn Baron Revilgaz von Beutebucht hat ein Kopfgeld auf den armen Ribbly ausgesetzt. Ich kann Euch gar nicht sagen, wie glücklich das die Schraubstutzs macht! Endlich ist unser kleiner Ribbly mal etwas wert!$B$BEr versteckt sich jetzt allerdings ganz weit unten in den Schwarzfelstiefen. Sucht ihn doch bitte und bringt ihn zu mir!$B$BUnd falls er nicht mitkommen will, ist das auch kein Problem...bringt mir einfach seinen Kopf.",
  },
  [4141] = {
    ["T"] = "Muigin und Larion",
    ["O"] = "Sammelt 15 Blutblüten und kehrt zu Muigin zurück.",
    ["D"] = "Seht Ihr Larion da drüben? Es ist SEINE Schuld, dass wir hier sind!$B$BIch hab ihm noch gesagt, dass wir in die falsche Richtung gehen, aber hat er auf mich gehört? Und stellt Euch vor, jetzt spricht er nicht mehr mit mir. Na ja, erlauben wir uns doch ein kleines Späßchen mit ihm, bis er es sich anders überlegt.$B$BSind Euch die seltsamen pflanzenartigen Kreaturen in dieser Gegend hier aufgefallen? Die machen nichts als Ärger, wirklich, aber ich glaube, wenn man sie ordentlich abrichten würde, könnte man sie dazu bringen, Befehlen zu gehorchen.$B$BAls Erstes bräuchte ich allerdings ein paar davon, um sie zu studieren. Meint Ihr, Ihr könntet mir ein paar bringen?",
  },
  [4142] = {
    ["T"] = "Ein Besuch bei Gregan",
    ["O"] = "Bringt Gregan Hopfenspei in Feralas eine Blutblüte.",
    ["D"] = "Ich weiß nicht so recht, was ich davon halten soll, $n. Die Pflanzen... sie scheinen ein gewisses Maß an Intelligenz zu besitzen.$B$BAllerdings bin ich auf diesem Gebiet kein Experte. Aber ich kenne einen!$B$BGeht nach Nord-Feralas und sucht einen Freund von mir auf, er heißt Gregan Hopfenspei. Er kennt sich mit solchen Dingen bestens aus.$B$BGregan kann uns bestimmt weiterhelfen. Ihr findet ihn draußen in der Wildnis, denn für das Großstadtleben hat er nicht viel übrig.",
  },
  [4143] = {
    ["T"] = "Der Dunst des Bösen",
    ["O"] = "Sammelt 5 Proben Dunst der Atal\'ai und bringt sie Muigin im Krater von Un\'Goro.",
    ["D"] = "Um ehrlich zu sein, $n, solche Pflanzen habe ich auf meinen Reisen noch nie gesehen. Vielleicht sollte ich mal einen Ausflug zu diesem Krater machen...$B$BEgal, ich weiß vermutlich, was Muigin benötigt - Dunst der Atal\'ai. Das ist ein abscheuliches Zeug, sehr schwer zu sammeln, aber es ist genau das, was er benötigt, um diese kleinen Monster unter Kontrolle zu bringen.$B$BIhr müsst zum Tempel von Atal\'Hakkar in den Sümpfen des Elends gehen. Den Dunst der Atal\'ai bekommt Ihr von Tieflauerern, Düsterwürmern oder Schlammen, die man dort findet. Viel Glück, $n!",
  },
  [4144] = {
    ["T"] = "Blutblütensprösslinge",
    ["O"] = "",
    ["D"] = "",
  },
  [4145] = {
    ["T"] = "Larion und Muigin",
    ["O"] = "Jagt 5 Blutblütenpeitscher, 5 Blutblütendrescher, 5 Blutblütenschinder und 5 Blutblütenfallen für Larion in Marschalls Zuflucht.",
    ["D"] = "Seht Ihr Muigin da drüben? Es ist SEINE Schuld, dass wir uns verirrt haben!$B$BIch kannte den richtigen Weg, aber er bestand darauf, eine Abkürzung zu nehmen, und so sind wir hier gelandet!$B$BUnd um die Lage noch zu verschlimmern, schickt er mir jetzt auch noch seine verrückten Pflanzen, um mich zu ärgern. Ich bin so wütend, dass ich bis auf weiteres kein Wort mehr mit ihm reden werde, aber diese Quälgeister sind wirklich unerträglich.$B$BWarum versuchen wir nicht als Erstes, seinen Bestand ein bisschen zu dezimieren? Geht hinaus und jagt einige Blutblüten und kehrt dann zu mir zurück.",
  },
  [4146] = {
    ["T"] = "Treibstoff für die Unkrautvernichtung",
    ["O"] = "Bringt Larion in Marschalls Zuflucht den ungeladenen Unkrautvernichter und 5 Proben Dunst der Atal\'ai.",
    ["D"] = "Ihr habt Glück, $n! Während Marvon fort war, habe ich an einigen meiner eigenen Experimente gearbeitet, eines davon ist ein Unkrautvernichter mit kurzer Reichweite. Nur noch ein paar kleine Änderungen, dann ist er perfekt, um damit diese Blutblütenplagen zu beseitigen.$B$BIhr könnt gern einen meiner Prototypen haben, aber Ihr benötigt noch etwas Treibstoff dafür. Den für den Vernichter benötigten Dunst der Atal\'ai findet Ihr im Tempel von Atal\'Hakkar. Ihr bekommt ihn von Tieflauerern, Düsterwürmern oder Schlammen.",
  },
  [4147] = {
    ["T"] = "Marvons Werkstatt",
    ["O"] = "Reist zu Marvon Nietensuchers Werkstatt in Ratschet.",
    ["D"] = "Ich habe herausgefunden, was Muigin so treibt. Er züchtet da drüben doch tatsächlich diese verdammten Dinger!$B$BIch glaube, die einzige Lösung des Problems besteht darin, mit... nein, nicht mit ihm zu sprechen... Was für ein Blödsinn! Die Lösung ist zurückzuschlagen!$B$BWir brauchen die Hilfe meines Freundes Marvon, der in Ratschet eine Werkstatt hat. Er kann bestimmt irgendein Gerät zusammenbasteln, das uns helfen wird.",
  },
  [4148] = {
    ["T"] = "Blutblütenvernichter",
    ["O"] = "",
    ["D"] = "",
  },
  [4161] = {
    ["T"] = "Rezept der Kaldorei",
    ["O"] = "Sammelt 7 kleine Spinnenbeine.",
    ["D"] = "Vor langer Zeit nannte man die Nachtelfen \'Kaldorei\', was übersetzt \'Kinder der Sterne\' bedeutet. Für Euch als $C ist es besonders wichtig, etwas über die Vergangenheit zu erfahren, also hört gut zu, was ich Euch zu sagen habe, $n.$B$BDie Kaldorei waren schon immer stolz auf ihre harmonische Beziehung zur Natur. Das bedeutet, dass wir von der Natur nur nehmen, was notwendig ist, und es in gleichem Maße vergelten. Dieses Gleichgewicht bedeutet uns viel, $n.$B$BGeht und sammelt sieben kleine Spinnenbeine - und nicht mehr, und kommt dann zu mir zurück.",
  },
  [4181] = {
    ["T"] = "Gobliningenieurskunst",
    ["O"] = "Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan.",
    ["D"] = "Bei der Experteningenieurskunst gibt es zwei unterschiedliche Disziplinen: Gnom und Goblin. Ich erwähne die Ingenieurskunst der Goblins nur, weil ich ein gerechter und unvoreingenommener Lehrer bin. Sie machen gern Dinge, die in die Luft gehen und es ist ihnen dabei meistens egal, ob dabei was im Weg herumsteht - auch wenn sie es selbst sind.$B$BLest dieses Handbuch, $n. Wenn Ihr lernen wollt, wie Ihr Euch selbst in die Luft jagt, dann bringt es zu Nixx Sprossfeder nach Gadgetzan. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.",
  },
  [4182] = {
    ["T"] = "Drachkin-Bedrohung",
    ["O"] = "Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 schwarze Wyrmkin und 1 Schwarzdrachen.",
    ["D"] = "Das Leben in der Brennenden Steppe ist voller Konflikte.$B$BSeht Euch nur mal diesen Ort an! Wir wissen nur sehr wenig darüber, was mit diesen Gebäuden geschehen ist, aber wir wissen, dass all das hier irgendwann vor der großen Explosion einmal Teil der Rotkammbergkette war.$B$BAber genug gejammert! Wir haben ein Problem, $n. Vielleicht sind Euch die Drachkin am Fuß dieses Bergkamms aufgefallen. Wir brauchen Hilfe, um sie zurückzuschlagen. Tötet so viele, wie Ihr könnt, und kommt dann zu mir zurück. Wenn Ihr erfolgreich seid, können wir uns vielleicht noch über andere Dinge unterhalten.",
  },
  [4183] = {
    ["T"] = "Die wahren Meister",
    ["O"] = "Reist nach Seenhain im Rotkammgebirge und händigt Magistrat Solomon Helendis Flusshorns Brief aus.",
    ["D"] = "Bislang nahmen wir an, dass die Schwarzfelsorcs das Ergebnis ihrer primitiven Erziehung seien. Was wir jedoch hier, in ihrem Heimatland, entdeckt haben, erzählt uns eine ganz andere Geschichte.$B$BAngetrieben werden sie zwar tatsächlich von Kampfrausch und Streitlust, aber der schwarze Drachenschwarm ist es, der die Orcs des Schwarzfels beherrscht. Wir vermuten, dass es auch der schwarze Drachenschwarm ist, der ihre Wut und Feindseligkeit schürt.$B$BBringt diesen Brief zu Magistrat Solomon. Er wird wissen, was zu tun ist.$B$B<Helendis gibt Euch einen versiegelten Brief.>",
  },
  [4184] = {
    ["T"] = "Die wahren Meister",
    ["O"] = "Reist nach Sturmwind und tragt Solomons Hilfegesuch dem König Varian Wrynn vor.$B$BDer König residiert in Burg Sturmwind.",
    ["D"] = "Das sind schlimme Neuigkeiten, $n. Zu entdecken, dass unsere Todfeinde nicht mehr als Marionetten einer viel größeren Bedrohung sind, ist wahrlich entmutigend. Es ist unmöglich, dass Seenhain einen Angriff durch die Schwarzfelsorcs UND den schwarzen Drachenschwarm überleben könnte. Wenn Seenhain dieser Gefahr anheim fällt, dann fällt bald auch Dunkelhain, und was dann? Goldhain? Auch Sturmwind?$B$BIhr müsst diese Information unbedingt sofort König Varian Wrynn in Sturmwind überbringen! Wenn dies nicht die Unterstützung Sturmwinds rechtfertigt, dann ist alles verloren.",
  },
  [4185] = {
    ["T"] = "Die wahren Meister",
    ["O"] = "Sprecht zuerst mit Lady Katrana Prestor, dann mit Hochlord Bolvar Fordragon.",
    ["D"] = "Solomon malt da ein recht düsteres Bild aus. Aber wir können es uns leider nicht leisten, unsere dünn gesäten Truppen aufgrund von reinen Vermutungen nach Seenhain zu schicken. Ich brauche einen Beweis für diese Behauptungen, bevor ich handeln kann.$B$BSprecht mit Lady Prestor, $n. Sie ist eine kluge Frau und weiß viel über Großdrachen und Drachkin. Vielleicht kann sie ein bisschen Licht in die Sache bringen.",
  },
  [4186] = {
    ["T"] = "Die wahren Meister",
    ["O"] = "Händigt Wrynns Erlass an Magistrat Solomon in Seenhain aus.",
    ["D"] = "Ich ernenne Euch hiermit zum kommissarischen Stellvertreter von Sturmwind, $n.$B$BKehrt zu Magistrat Solomon in Seenhain zurück und übergebt ihm diesen Erlass.",
  },
  [4201] = {
    ["T"] = "Der Liebestrank",
    ["O"] = "Bringt 4 Gromsblut-Kräuter, 10 Riesensilbervenen und Nagmaras gefüllte Phiole zu Herrin Nagmara in den Schwarzfelstiefen.",
    ["D"] = "Oh, wie schmerzhaft doch die Liebe ist! Mein geliebter Gefreiter Rocknot ist immun gegen meinen Charme. Er verschmäht mich! Es ist zum Verrücktwerden!$B$BIhr müsst mir helfen! Wir Sukkuben wissen viel über die Liebe und ich kann einen Trank herstellen, der den eisernen Panzer um Rocknots Herz erweichen könnte. Dazu benötige ich folgende Dinge: das Kraut Gromsblut, Riesensilbervenen von den Klippenriesen Azsharas sowie Wasser aus der Quelle des Golakkakraters in Un\'Goro. Ihr könnt das Wasser in diese Phiole füllen.$B$BBitte, $n, verhelft einer armen Seele zum Glück!",
  },
  [4221] = {
    ["T"] = "Verderbte Windblüte",
    ["O"] = "",
    ["D"] = "",
  },
  [4222] = {
    ["T"] = "Verderbte Windblüte",
    ["O"] = "",
    ["D"] = "",
  },
  [4223] = {
    ["T"] = "Die wahren Meister",
    ["O"] = "Sprecht mit Marschall Maxwell in der Brennenden Steppe.",
    ["D"] = "<Magistrat Solomon seufzt.>$B$BIhr müsst in die Brennende Steppe zurückkehren, $n. Als Stellvertreter von Sturmwind erwartet man von Euch, dass Ihr Euch bei Marschall Maxwell meldet. Viel Glück.",
  },
  [4224] = {
    ["T"] = "Die wahren Meister",
    ["O"] = "Sprecht mit dem struppigen John.",
    ["D"] = "Was? Ihr dachtet, Ihr wärt der Erste? Wie viele Soldaten müssen wir noch verlieren, bevor Sturmwind handelt?$B$BWir hatten riesige Mengen an Informationen über die Schwarzfelsbedrohung zusammengetragen, doch dann verloren wir unseren befehlshabenden Offizier, Marschall Windsor. Zusammen mit Marschall Windsor gingen auch all unsere wertvollen Informationen verloren.$B$BWas wir brauchen, ist diese Dokumentation. Sprecht mit dem struppigen John, $n. Er war mit Windsor zusammen, als sie von einem Stoßtrupp der Schwarzfelsorcs überfallen wurden. Er wurde zuletzt in einer Höhle im Norden gesehen.",
  },
  [4241] = {
    ["T"] = "Marschall Windsor",
    ["O"] = "Reist zum Schwarzfels im Nordwesten und dann weiter zu den Schwarzfelstiefen. Findet heraus, was aus Marschall Windsor geworden ist.$B$BIhr erinnert Euch daran, dass der struppige John sagte, man hätte Windsor in ein Gefängnis verschleppt.",
    ["D"] = "Dieser nichtsnutzige Feigling behauptet also, Windsor sei noch am Leben, was?$B$BMacht Euch bereit, Stellvertreter, Ihr werdet ausziehen, um ihn zu suchen. Falls Windsor noch am Leben ist, müsst Ihr ihn zurückholen. Sollte er tot sein, brauche ich einen Beweis dafür.$B$B<Maxwell lacht.>$B$BIch wette, das könnt Ihr langsam schon nicht mehr hören, was?",
  },
  [4242] = {
    ["T"] = "Verlorene Hoffnung",
    ["O"] = "Überbringt Marschall Maxwell bei Morgans Wacht in der Brennenden Steppe die schlechten Neuigkeiten.",
    ["D"] = "Bolvar ist ein Dummkopf. Ich war gerade dabei, ihm seinen kostbaren \'Beweis\' zu beschaffen, um ihm diesen in den Hals stopfen zu können.$B$BEr ist ein blinder Blödmann. Selbst wenn der Beweis ihn in die Nase beißt, würde er ihn nicht sehen.$B$BWas meine Informationen angeht, die sind verloren. Unwiderbringlich.$B$BSollte ich Eisenfeind jemals finden und es aus diesem Labyrinth herausschaffen, wird mein erster Halt Sturmwind sein, damit ich diesem Reptil meinen Hammer über den Schädel ziehen kann.$B$BGeht jetzt, $n.",
  },
  [4243] = {
    ["T"] = "Chasing A-Me 01",
    ["O"] = "Sucht im Krater von Un\'Goro nach A-Me 01.",
    ["D"] = "Irgendetwas muss meiner bislang besten Erfindung zugestoßen sein. Ihr habt doch schon mal mechanische Eichhörnchen gesehen, richtig? Tja, um näher an die Affen des Kraters von Un\'Goro heranzukommen, habe ich eine mechanische Äffin geschaffen, die sich unbemerkt unter sie mischen kann - ich nenne sie A-Me 01.$B$BSie hat Wahrsagesteine als Augen, so konnte ich alles sehen, was auch sie sah. Den Fungusfels hat sie wohlbehalten erreicht - das ist das Versteck der Affen, gleich nördlich von hier. Und dort habe ich dann den Kontakt zu ihr verloren. Das Letzte, was ich sah, war eines der Alphamännchen, das auf sie zugestürmt kam, um sie zu begrüßen!$B$BKönntet Ihr mir wohl helfen, sie zu finden?",
  },
  [4244] = {
    ["T"] = "Chasing A-Me 01",
    ["O"] = "Sucht ein Mithrilgehäuse und bringt es zu A-Me 01 im Krater von Un\'Goro.",
    ["D"] = "Ihr setzt Eure Untersuchungen fort und findet eine Abdeckung, die sich aufschieben lässt. Oberhalb der Verkabelung steht in gut lesbarer Schrift geschrieben: \'Mithrilgehäuse ersetzen und Strom erst ausschalten, dann einschalten.\'$B$BEs dauert einen Moment, bis Ihr die Abdeckung wieder angebracht habt, und Ihr fragt Euch, ob es wirklich so einfach ist, A-Me 01 zu reparieren und zurück zu Karna Wirrbart in der Nähe von Marschalls Zuflucht zu bringen.$B$BTja, probieren geht über studieren...",
  },
  [4245] = {
    ["T"] = "Chasing A-Me 01",
    ["O"] = "Begleitet A-Me 01 zu Karna Wirrbart im Krater von Un\'Goro.",
    ["D"] = "Nachdem Ihr Euch vergewissert habt, dass Ihr bereit seid, A-Me 01 zu Karna Wirrbart zurückzubringen, knipst Ihr den Schalter auf \'Aus\'. Ihr seht Euch nochmals um, um sicherzugehen, dass sich keine Gorillas nähern, und fragt Euch, was wohl geschieht, wenn Ihr den Schalter wieder auf die \'Ein\'-Position stellt.$B$BDas ist Eure letzte Chance...wollt Ihr das wirklich tun?",
  },
  [4261] = {
    ["T"] = "Alter Geist",
    ["O"] = "Bringt Arei in Sicherheit und sprecht dann mit Kayneth Stillwind in Waldeslied, im Osten des Eschentals.",
    ["D"] = "Der Teufelswald war einst ein friedliches und ruhiges Land... bis die Legion hier eingefallen ist. Die Höllenbestien, Zerstörung, Chaos...$B$BDer Schädel des Gul\'dan hat dafür gesorgt, dass hier niemand ungeschoren davonkam. Alles fiel der Verderbnis zum Opfer.$B$BSogar jetzt, da ich ein Geist bin, bin ich immer noch gefangen und muss die Leiden dieses Landes ertragen. Helft mir, den Weg nach Hause zu finden. Bitte, $n, sagt mir, dass Ihr einen alten Geist weg von hier führen werdet, dorthin, wo ich endlich ruhen kann.$B$BSobald ich frei sein werde, solltet Ihr Kayneth Stillwind aufsuchen, einen Druiden, der in Waldeslied im Osten lebt.",
  },
  [4262] = {
    ["T"] = "Übermeister Pyron",
    ["O"] = "Erschlagt Übermeister Pyron.$B$BIhr erinnert Euch an Jalindas Worte, dass Pyron den Steinbruch bewachen würde. Vielleicht solltet Ihr dort nachsehen?",
    ["D"] = "Die letzten fünf meiner Magier, die ausgezogen waren, um sich auf dem Schwarzfels umzusehen, kamen knusprig geröstet zurück, mit einem dauerhaften orangefarbenen Gnomenafro - oder noch schlimmer.$B$B<Jalinda nimmt etwas Asche vom Boden auf und lässt sie vom Wind davontragen.>$B$BEin besonders mieses Individuum, Übermeister Pyron, scheint hier eine Menge Ärger zu verursachen. Dieser Elementar muss vernichtet werden, damit unser Expeditionsteam in die Schwarzfelstiefen reisen kann!$B$BEr bewacht den Eingang am Schwarzfelssteinbruch.",
  },
  [4263] = {
    ["T"] = "Incendius!",
    ["O"] = "Sucht Lord Incendius in den Schwarzfelstiefen und vernichtet ihn!",
    ["D"] = "Seid Ihr sicher, dass Pyron \'Incendius\' sagte, als er starb?$B$B<Jalinda schluckt schwer.>$B$BLord Incendius soll angeblich einer von Ragnaros Dienern sein! Oje, oje... was sollen wir jetzt nur tun?$B$BMeint Ihr, Ihr könntet noch eine weitere Mission bewältigen? Ich habe niemand anderen, den ich schicken könnte, $n!$B$BWir werden niemals eine Gruppe hineinbringen, solange Incendius Pyron aus der Asche immer wieder neu erstehen lässt.$B$BIhr müsst Lord Incendius suchen und vernichten!",
  },
  [4264] = {
    ["T"] = "Eine zusammengeknüllte Notiz",
    ["O"] = "Soeben seid Ihr auf etwas gestoßen, das Marschall Windsor mit Sicherheit sehr interessiert. Vielleicht besteht ja doch noch Hoffnung.",
    ["D"] = "Die Notiz ist mit Flecken übersät. Der Text ist kaum noch lesbar, aber Ihr könnt Folgendes erkennen:$B$B... süße Ironie...$B$B... der Sturz der zerbrochenen Allianz...$B$B... ohne General Angerfords Sorgfalt hätten wir das Schema der Verschlüsselung niemals herausgefunden...$B$B... Argelmach hat Windsors Notizen fast entschlüsselt.$B$BUnser Lord wird sich aus den Tiefen erheben und alle zerschmettern, die noch übrig sind...$B$BImperator Dagran Thaurissan",
  },
  [4265] = {
    ["T"] = "Flucht aus dem Schwarmbau",
    ["O"] = "Begleitet Raschal sicher aus dem Schwarmbau der Zukk\'ash hinaus.$B$BKehrt zu Ginro Herdfeuer in der Mondfederfeste zurück und lasst ihn wissen, dass Raschal am Leben ist und dass es ihm gut geht.",
    ["D"] = "Die Kapsel platzt auf, als Ihr sie berührt, und zum Vorschein kommt ein Nachtelf, der überraschenderweise noch am Leben ist. Er scheint bei guter Gesundheit zu sein, ist jedoch sehr benommen. Als Ihr ihm aus der Kapsel heraushelft, regt sich Leben in ihm.$B$B\"Elune sei Dank! Ihr habt mich gerettet! Ich... Mein Name ist Raschal. Ich erwachte völlig gelähmt in dieser Kapsel, nachdem mich diese Käfer überwältigt hatten. Wurdet Ihr von der Festung geschickt?\"$B$BAnscheinend habt Ihr den vermissten Kurier gefunden!",
  },
  [4266] = {
    ["T"] = "Der Empfang eines Helden",
    ["O"] = "Sprecht mit Shandris Mondfeder in der Mondfederfeste.",
    ["D"] = "$n, General Shandris Mondfeder möchte unter vier Augen mit Euch sprechen. Sie möchte Euch bestimmt für Eure Dienste zum Wohle der Festung danken, aber ich weiß auch, dass sie außerdem mit Euch über diese Insektenwesen sprechen will, auf die Ihr und Raschal im Süden von Feralas gestoßen seid. Die Berichte, die diese Insekten der Zukk\'ash betreffen, sind...mehr als beängstigend.$B$BGeneral Mondfeder wartet im Haupttruppenlager auf Euch.",
  },
  [4267] = {
    ["T"] = "Aufstieg der Silithiden",
    ["O"] = "Bringt Raschals Bericht zu Gracina Glaubensmacht in Darnassus.",
    ["D"] = "Ich vertraue Euch eine Kopie des Berichts an, den Raschal über die Plage im Südosten Feralas\' angefertigt hat. Der eigentliche Bericht befindet sich in den Akten in Darnassus, aber ich möchte, dass Ihr diese Kopie an eine Vertraute des Protektorats überbringt. Ihr Name lautet Gracina Glaubensmacht. Sie ist eine Priesterin, die im Tempel des Mondes in Darnassus lebt. Sie verfügt über großes Wissen, solche Kreaturen betreffend, und wenn sie diesen Bericht erhalten würde, käme es uns allen sehr zugute.$B$BMacht Euch auf den Weg, und viel Glück, $n. Möge Elune über uns alle wachen.",
  },
  [4281] = {
    ["T"] = "Thalanaar-Lieferung",
    ["O"] = "Bringt das nicht ausgelieferte Paket zu Falfindel Wegeshut in Thalanaar.",
    ["D"] = "Dieses Paket mit Waren, das Ihr in den Lagern der Waldpfotengnolle gefunden habt, war für den kleinen Streckenposten Thalanaar im Südosten Feralas\' bestimmt. Das Paket, ungeachtet des Schicksals des vermissten Kuriers, harrt immer noch der Auslieferung.$B$BIhr untersucht das Paket genauer und entdeckt den Namen \'Falfindel Wegeshut\' handschriftlich auf der Verpackung vermerkt. Vielleicht ist er der eigentliche Empfänger des Pakets.",
  },
  [4282] = {
    ["T"] = "Ein Funken Hoffnung",
    ["O"] = "Holt Marschall Windsors verloren gegangene Informationen zurück.$B$BMarschall Windsor glaubt, dass sich die Informationen in den Händen des Golemlords Argelmach und des Generals Zornesschmied befinden.",
    ["D"] = "<Marschall Windsor wedelt aufgeregt mit der Notiz herum.>$B$BWisst Ihr, was das bedeutet, $n?$B$BVielleicht ist doch noch nicht alles verloren! Soweit ich das erkennen kann, wird die Information von zwei Dunkeleisenzwergen verwahrt: General Zornesschmied und dem Golemlord Argelmach. Ich habe das Gefühl, als würden sie die Information nicht einfach so herausrücken, wenn wir sie freundlich darum bitten.$B$BAlso hoffe ich, dass Ihr auf einen Kampf vorbereitet seid.$B$BZieht aus und findet die beiden Bösewichte! Vernichtet sie und bringt mir meine verloren gegangenen Informationen zurück.",
  },
  [4283] = {
    ["T"] = "FÜNFZIG! Jawoll!",
    ["O"] = "Beschafft Euch 50 Medaillons des Schwarzfelsklans.",
    ["D"] = "Ich werde Euch ein kleines Geheimnis verraten, Soldat: Kommt näher.$B$BICH BIN HIER DER EINZIGE, DER NOCH BEI VERSTAND IST!$B$BSeht Euch nur all diese Leute an! Ist es zu fassen, dass sie alle irre sind!? Oh ja, das sind sie! DURCHGEDREHT! WAHNSINNIG!$B$B<Oralius senkt die Stimme.>$B$BDas bleibt aber unter uns: mir, Euch und meinem Kumpel Käpt\'n Winky.$B$B<Oralius greift in die Luft und drückt zu.>$B$BWie auch immer...Ich sammle Abzeichen, die gut zu meiner Sammlung Ogerohren passen. Orcs sind der letzte Schrei. Fünfzig. LOS!$B$B<Oralius stößt in ein Horn.>$B$BAB DAMIT!",
  },
  [4284] = {
    ["T"] = "Machtkristalle",
    ["O"] = "Sammelt 7 Machtkristalle von jeder Farbe: rot, blau, gelb und grün. Bringt sie zu J. D. Collie in Marschalls Zuflucht.",
    ["D"] = "Ich bin sicher, Ihr habt sie bemerkt, $n. Sie sind überall!$B$BWovon ich rede? Na, von den Kristallen natürlich!$B$BDie Kristalle... Sie liegen überall im Krater herum. Die meisten Leute ignorieren sie, aber ich habe das seltsame Gefühl, als besäßen sie eine gewisse Macht. Ich habe eine Weile mit ihnen herumexperimentiert, aber ich habe noch nichts herausgefunden. Tja, bis jetzt jedenfalls.$B$BAber hört mal, mir gehen langsam die Kristalle aus - meint Ihr, Ihr könntet noch ein paar für mich sammeln?",
  },
  [4285] = {
    ["T"] = "Der Nordpylon",
    ["O"] = "Findet den Standort des nördlichen Kristallpylonen im Krater von Un\'Goro.",
    ["D"] = "Wofür die wohl gut sind? Ich muss ständig daran denken!$B$BEs macht mich verrückt, dass all die Gerüchte über die Kristallpylonen wahr sind. Nennt mich leichtgläubig, aber ich glaube gewöhnlich, was mir die Leute erzählen.$B$BWie dem auch sei, ich habe gehört, dass sich einer dieser Kristallpylonen hier ganz in der Nähe befinden soll. Irgendwo im Norden... Sucht ihn doch bitte, $n. Ich weiß genau, dass es die Dinger gibt.",
  },
  [4286] = {
    ["T"] = "Das gute Zeug",
    ["O"] = "Beschafft Euch 20 Dunkeleisengürteltaschen.",
    ["D"] = "Käpt\'n Winky sagt mir, dass die Dunkeleisenzwerge in den Tiefen das ganze gute Zeug haben. Stimmt\'s nicht, Winky?$B$B<Oralius versucht, so gut es geht, einen Piraten zu imitieren.>$B$BHARRR, KUMPEL! DAS STIMMT!$B$BDa hört Ihr\'s. Ihr müsst Euch dorthin begeben und mir etwas von dem guten Zeug besorgen.$B$BGeht da rein und holt es Euch!",
  },
  [4287] = {
    ["T"] = "Der Ostpylon",
    ["O"] = "Findet den Standort des östlichen Kristallpylonen im Krater von Un\'Goro.",
    ["D"] = "Während meines Aufenthaltes hier sind mir seltsame Gerüchte über rätselhafte Pylonen zu Ohren gekommen, die es angeblich draußen in der Wildnis des Kraters geben soll, $n. Ich nehme an, sie sind ziemlich groß, mit Kristallen an den Innenseiten.$B$BIch habe zwar noch nie welche gesehen, aber ich komme nicht umhin zu glauben, dass die Gerüchte wahr sind - und dass die Pylonen irgendetwas mit all den Kristallen zu tun haben, die wir gefunden haben.$B$BAlso, warum helft Ihr mir nicht, meine Neugier zu stillen? Angeblich soll es einen im östlichen Teil des Kraters geben. Meint Ihr, Ihr könntet ihn finden?",
  },
  [4288] = {
    ["T"] = "Der Westpylon",
    ["O"] = "Findet den Standort des westlichen Kristallpylonen.",
    ["D"] = "Meine Experimente haben bis jetzt noch keine positiven Ergebnisse erzielt, $n. Ich kann nur hoffen, dass Ihr eine Lösung findet, indem Ihr einen der Pylonen entdeckt, von denen ich gehört habe.$B$BWas, wenn wir bestimmte Farben mit ihnen kombinieren würden... ach, eines nach dem anderen. Zuerst müssen wir sie finden!$B$B$n, sucht im westlichen Teil des Kraters. In diesem Gebiet soll sich irgendwo ein Pylon befinden.",
  },
  [4289] = {
    ["T"] = "Die Affen von Un\'Goro",
    ["O"] = "Bringt Torwa Pfadfinder am Eingang zum Krater von Un\'Goro 2 Gorillapelze von Un\'Goro, 2 Stampferpelze von Un\'Goro und 2 Donnererpelze von Un\'Goro.",
    ["D"] = "In diesem Krater gibt es viele seltsame Kreaturen - sie scheinen edler zu sein als ihre Verwandten, die man überall in meinem Heimatland und jenseits davon findet. Ich habe keine Erklärung dafür, aber dieser Ort trägt die Handschrift der Erdenmutter. Immer, wenn ich auf eines ihrer wundervollen Geschöpfe in diesem Krater stoße, erweise ich ihr meine Ehrerbietung.$B$BWenn auch Ihr diese Erleuchtung spüren möchtet, die eine solche Tat begleitet, dann geht nach Norden. Dort findet Ihr große Affen, die die Kraft eines jeden Sterblichen auf eine harte Probe stellen. Bringt mir ihre Pelze...beweist mir Eure Kraft.",
  },
  [4290] = {
    ["T"] = "Lar\'korwis Mahl",
    ["O"] = "Sucht das Gerippe von Lar\'korwis letzter Jagdbeute und stehlt einen Teil eines Dreschadongerippes; kehrt dann zu Torwa Pfadfinder im Krater von Un\'Goro zurück.",
    ["D"] = "Einst, vor sehr langer Zeit, stahl einer meiner Brüder ein Stück Fleisch aus Lar\'korwis Jagdbeute. Damals wusste er es nicht, aber Lar\'korwi hat ihn vom Wald aus beobachtet. Lar\'korwi folgte meinem Bruder tagelang und vernichtete alles, was er berührte. Mein Bruder, von Angst erfüllt, entschloss sich schließlich, aus dem Krater zu fliehen, um Lar\'korwi zu entkommen.$B$BLar\'korwi tötete ihn, nicht weit von dieser Stelle entfernt.$B$BWenn Ihr Lar\'korwi provozieren wollt, sucht nach seiner letzten Jagdbeute und stehlt ihm etwas Fleisch.$B$BKommt dann zu mir zurück.",
  },
  [4291] = {
    ["T"] = "Lar\'korwis Fährte",
    ["O"] = "Bringt Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un\'Goro 2 Pheromondrüsen eines Ravasaurus.",
    ["D"] = "Ich bin wirklich erstaunt, dass er noch nicht hinter Euch her ist, $n. Alles läuft jedoch gut, denn ich habe einen letzten Plan, mit dem wir das Monster zu uns locken können.$B$BAls dominantem Männchen steht es Lar\'korwi zu, sich mit jedem Weibchen zu paaren, das ihm gefällt - und ihm gefallen viele. Westlich von hier werdet Ihr eine Menge Ravasauruseier finden. Tretet dicht an sie heran, um damit einen Angriff seiner Artgenossen zu provozieren. Die Weibchen produzieren in einer Drüse an ihrem Kopf einen abscheulichen Gestank, der die Männchen wissen lässt, dass sie paarungsbereit sind. Bringt mir zwei dieser Drüsen.",
  },
  [4292] = {
    ["T"] = "Ein Köder für Lar\'korwi",
    ["O"] = "Beschwört Lar\'korwi mit dem Inhalt von Torwas Beutel und besiegt ihn. Bringt dann Lar\'korwis Kopf zu Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un\'Goro.",
    ["D"] = "Das hier wird der schwierigste Teil werden, denn der Plan ist etwas kompliziert. Nördlich von hier findet Ihr einen kleinen Pfad, der in die Berge hinaufführt. Dort habe ich Lar\'korwi zuletzt gesehen. Ihr werdet wissen, dass Ihr im richtigen Gebiet seid, wenn Ihr all die Knochen der großen Kreaturen findet, die es dort einst gab. Nehmt das Fleisch, das Ihr Euch besorgt habt, und legt es dort ab. Reibt das Fleisch dann mit der Drüse ein. Wenn es mit beiden Lockmitteln zusammen nicht gelingt, Lar\'korwi zu Euch zu locken, dann weiß ich auch nicht weiter.",
  },
  [4293] = {
    ["T"] = "Eine Schleimprobe...",
    ["O"] = "Sammelt im Teufelswald Proben von verschiedenen Schleimen. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am verderbtesten sind. Bringt 5 der verderbten Proben aus dem Teufelswald, die Ihr findet, zu Chemiker Brenner.",
    ["D"] = "Der Teufelswald ist ein Wald weit im Norden des Brachlandes, sogar noch weiter weg als das Eschental. Um die dortigen Teiche herum, die durch dämonische Energie gesegnet wurden, befinden sich überall Schlamme. Die Geißel hat mit den Angriffen von Höllenbestien und anderer Dämonenmagie die Gegend stark verwüstet - die Schleime sind angeblich während dieser Angriffe aus der Erde an die Oberfläche gekrochen.$B$BSammelt einige Proben von Teufelswaldschleimen, dann verwenden wir die Testausrüstung hier neben mir, um eine Kontrollgruppe zusammenzustellen, mit der wir die anderen Proben, die Ihr eingesammelt habt, vergleichen können.",
  },
  [4294] = {
    ["T"] = "...und eine Menge Schlamm",
    ["O"] = "Sammelt im Un\'Goro Proben von verschiedenen Schleimen. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am reinsten sind. Bringt 5 der reinen Proben aus dem Un\'Goro, die Ihr findet, zu Chemiker Brenner.",
    ["D"] = "Habt Ihr schon einmal vom Krater von Un\'Goro gehört, $n? Er liegt in der Nähe der Tanariswüste und es ist ein großartiger Ort: üppig, von Leben sprühend, voller Kreaturen und Pflanzen.$B$BDorthin müsst Ihr Euch begeben. Ich habe mir sagen lassen, dass der Krater noch völlig unbesudelt und frei von Einflüssen von außen sei. Erst jetzt beginnen Forscher, seine Geheimnisse zu entdecken.$B$BIch würde die dortigen Schleime und Schlamme gern testen. Wenn es stimmt, dass sie nicht verderbt sind, dann würde ich gern wissen, wie sich solche Kreaturen entwickelt haben.",
  },
  [4295] = {
    ["T"] = "Rocknots Bier",
    ["O"] = "",
    ["D"] = "",
  },
  [4296] = {
    ["T"] = "Schrifttafel der Sieben",
    ["O"] = "Beschafft Euch die Abschrift der Schrifttafel.",
    ["D"] = "Die Legenden der Sieben führten mich in die Brennende Steppe. Sie waren hoch gebildete und mächtige Zwerge, und wenn man über Wissen und Macht verfügt, so ist das der Schlüssel zum Reichtum.$B$BUnd genau das ist es, was ich will. Reichtum!$B$BWenn wir mehr über die Sieben in Erfahrung bringen könnten, würde das uns beide vielleicht reich machen. In den Ruinen von Thaurissan soll sich angeblich die Schrifttafel der Sieben befinden, auf der das Wissen über die alten Zwerge geschrieben steht. Sucht diese Schrifttafel, kopiert den Text und bringt ihn mir. Lasst uns deren Geheimnisse gemeinsam entschlüsseln!",
  },
  [4297] = {
    ["T"] = "Essen für das Baby",
    ["O"] = "Bringt Agnar Tiermeister im Hinterland 5 Silbermähnenpirscherflanken.",
    ["D"] = "Grimmlingflitzer sind Allesfresser - das bedeutet, dass sie sowohl Fleisch als auch Grünzeug fressen. Wenn dieses Junge hier schlüpft, sollten wir vorbereitet sein. Es ist kaum zu glauben, aber diese kleinen Kerlchen ernährt man am besten mit Fleischstücken, roh und saftig. Ich habe nie herausgefunden, warum das so ist, aber es muss wohl etwas mit Elunes Plan für die Elfen zu tun haben... vielleicht waren sie als Beschützer oder so etwas vorgesehen, ich weiß es nicht.$B$BIch kümmere mich vorerst um das Ei. Warum besorgt Ihr nicht ein paar Silbermähnenpirscherflanken, mit denen wir das Junge füttern können, sobald es geschlüpft ist?",
  },
  [4298] = {
    ["T"] = "Elternfreuden",
    ["O"] = "Sprecht mit Agnar Tiermeister im Hinterland.",
    ["D"] = "He, es war wohl schon weiter, als ich dachte...",
  },
  [4300] = {
    ["T"] = "Knochenklingenwaffen",
    ["O"] = "Bringt Jes\'rimon in Orgrimmar 8 weiße Ravasaurusklauen.",
    ["D"] = "Hmm, jetzt, wo ich darüber nachdenke, könnte es doch etwas geben, das Ihr für die Hand erledigen könntet. Ja, Jes\'rimon könnte bestimmt Eure Hilfe brauchen.$B$BIm Krater von Un\'Goro gibt es Raptoren mit tödlichen Klauen. Manche dieser Raptoren haben weiße Klauen statt schwarze wie der Tod. Die Mitglieder der Hand haben...Kunden..., denen aus diesen Klauen gefertigte Waffen gefallen. Bringt mir reichlich von diesen weißen Klauen, dann werde ich Euch gut entlohnen. Wenn Ihr das schnell genug tut, dann kann ich Euch vielleicht noch auf andere Weise helfen.",
  },
  [4301] = {
    ["T"] = "Der mächtige U\'cha",
    ["O"] = "Bringt U\'chas Pelz zu Torwa Pfadfinder am Eingang des Kraters von Un\'Goro.",
    ["D"] = "Während Ihr Un\'Goro erkundet habt, sind mir ein paar andere Dinge aufgefallen, die einem mächtigen Jäger wie Euch sicher gefallen würden. Zum Beispiel gibt es da einen gewaltigen Affen unter den Gorillas im Norden. Ihr seid schon einmal in dieser Höhle gewesen, aber dieses Mal frage ich mich doch, ob Ihr die Begegnung mit ihm wohl überleben würdet, solltet Ihr meine Aufgabe annehmen.$B$BIch nenne ihn U\'cha und er ist einer der größten Söhne der Erdenmutter, da könnt Ihr sicher sein. Besiegt U\'cha und bringt mir seinen Pelz. Wenn Euch das gelingt, habt Ihr das Recht darauf, ein großer Jäger genannt zu werden.",
  },
  [4321] = {
    ["T"] = "Die Sache verstehen",
    ["O"] = "Sprecht demnächst mit J. D. Collie.",
    ["D"] = "Die Information über die Pylonen, die Ihr mir gabt, ist sehr interessant...$B$BGebt mir einen Moment Zeit, um ein paar Berechnungen anzustellen, $n. Sprecht mich demnächst noch einmal an, dann sollte ich Euch die Antwort geben können, nach der wir gesucht haben.",
  },
  [4322] = {
    ["T"] = "Gefängnisausbruch!",
    ["O"] = "Helft Marschall Windsor, seine Ausrüstung zurückzuholen und seine Freunde zu befreien. Kehrt zu Marschall Maxwell zurück, wenn Ihr Erfolg hattet.",
    ["D"] = "Wir müssen hier weg, $n. Helft mir, meine Ausrüstung aus dem Vorratsbereich zu holen und meine Freunde zu befreien. Falls wir hier rauskommen, dann müsst Ihr Euch weiter nach Morgans Wacht durchschlagen und mit Marschall Maxwell sprechen. Er wird Euch weitere Anweisungen geben.",
  },
  [4324] = {
    ["T"] = "Yuka Schraubstutz",
    ["O"] = "Sprecht mit Yuka Schraubstutz in der Brennenden Steppe.",
    ["D"] = "Mein Bruder Ribbly war sein ganzes Leben lang zu nichts nütze; aber jetzt, glaube ich, hat er endlich einen gewissen Wert! Aber... wir werden Eure Hilfe brauchen, um aus Ribblys Wert Kapital zu schlagen.$B$BMeine Schwester Yuka hat ihn wochenlang gesucht und endlich in der Brennenden Steppe gefunden. Sie hat mich wissen lassen, dass sie ein paar Helfer brauchen könnte.$B$BAlso, was meint Ihr? Wollt Ihr uns helfen? Falls ja, sprecht mit meiner Schwester. Ihr findet sie im Flammenkamm versteckt, in einer Höhle nordwestlich des Schreckensfelsens.",
  },
  [4341] = {
    ["T"] = "Kharan Hammermacht",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und findet Kharan Hammermacht.$B$BDer König erwähnte, dass Kharan dort gefangen sei - vielleicht solltet Ihr nach einem Gefängnis Ausschau halten.",
    ["D"] = "Seid Ihr in den rauchenden Ruinen von Thaurissan gewesen? Seid Ihr dort zufällig einem erbärmlichen Tölpel namens Kharan Hammermacht begegnet? Dem Zwerg, der eigentlich über mein kleines Mädchen wachen sollte?$B$B<Der König schluchzt.>$B$BUnd jetzt ist sie verschwunden, $n!$B$BIch habe meine Leute die gesamte Steppe durchkämmen lassen. Doch der einzige Hinweis, den sie über den Verbleib meiner Tochter finden konnten, war, dass sie vielleicht in den Tiefen ist. Möglicherweise ist sie sogar schon tot!$B$BAngeblich wird Kharan dort als Gefangener festgehalten...findet ihn! Ich will Antworten!",
  },
  [4342] = {
    ["T"] = "Kharans Geschichte",
    ["O"] = "Lasst Kharan Hammermacht seine Geschichte erzählen.",
    ["D"] = "Bitte hört mich an, ehe Ihr ein Urteil fällt!",
  },
  [4343] = {
    ["T"] = "Verderbte Windblüte",
    ["O"] = "",
    ["D"] = "",
  },
  [4361] = {
    ["T"] = "Der Überbringer schlechter Botschaften...",
    ["O"] = "Kehrt nach Eisenschmiede zurück und überbringt König Magni Bronzebart die schlechten Nachrichten.",
    ["D"] = "Ihr müsst König Magni diese Informationen bringen, $n. Er wird wissen, was zu tun ist!",
  },
  [4362] = {
    ["T"] = "Das Schicksal des Königreichs",
    ["O"] = "Kehrt in die Schwarzfelstiefen zurück und rettet Prinzessin Moira Bronzebart aus den Fängen des bösen Imperators Dagran Thaurissan.",
    ["D"] = "Es scheint, als habe mein alter Widersacher, Dagran Thaurissan, mich und das ganze Königreich Eisenschmiede in seiner Gewalt.$B$BVielleicht seid Ihr meine letzte Hoffnung, $n. Ihr müsst meine Tochter Moira retten!$B$BEs gibt nur einen Weg, wie der Zauber, mit dem Thaurissan Moira belegt hat, wirklich zuverlässig gebrochen werden kann: Ihr müsst ihn töten.$B$BAber, $n, meiner Tochter dürft Ihr kein Leid zufügen! Vergesst nicht, dass sie Thaurissans Kontrolle unterliegt! Alles, was sie vielleicht sagt oder tut, geschieht aufgrund von Thaurissans bösem Zauber!",
  },
  [4363] = {
    ["T"] = "Die Überraschung der Prinzessin",
    ["O"] = "Kehrt nach Eisenschmiede zurück und sprecht mit König Magni Bronzebart.",
    ["D"] = "Thaurissan war ein großer und ehrenhafter Zwerg. Ihr habt mein Leben zerstört - meines und das meines ungeborenen Kindes!$B$B<Ihr habt den Eindruck, als leide Prinzessin Bronzebart noch immer unter den letzten Auswirkungen des vom Imperator gewirkten Zaubers.>$B$BKehrt nach Eisenschmiede zurück und sagt meinem Vater, dass der Erbe des Königreichs Eisenschmiede ein Dunkeleisenzwerg sein wird.$B$BOb er dies billigt oder nicht, so wird es sein.",
  },
