  [4485] = {
    ["T"] = "Der Foliant des Adels",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["D"] = "Hallo, $n. Wie ich sehe, ist Eure Hingabe weiterhin stark, ebenso wie Euer Glaube.$B$BIch denke, es ist an der Zeit, dass Ihr mit Duthorian Rall in Sturmwind sprecht. Es gibt noch einige Lektionen, die Ihr lernen müsst, und ich spüre, dass Ihr dazu bereit seid.$B$BSucht ihn in der Kathedrale des Lichts auf... und, $n, nehmt diese Lektionen ernst! Ihr habt Euren derzeitigen Rang durch harte Arbeit und Demut erreicht, enttäuscht uns jetzt nicht, indem Ihr überheblich werdet.$B$BGehabt Euch wohl.",
  },
  [4486] = {
    ["T"] = "Der Foliant des Adels",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["D"] = "Nee, nee, nee. Jetzt sieh sich nur mal einer den mächtigen Paladin in all seinem Glanz an.$B$BTut mir leid, $n, war nicht böse gemeint... Ich werde nur manchmal ein bisschen neidisch, wenn ich sehe, was Ihr für tolle Taten vollbracht habt. Tiza hat gesagt, das sei meine Schwäche, und ich habe ihr geschworen, dass ich daran arbeiten würde. Aber manchmal ist das einfach schwer, wisst Ihr.$B$BAber der Grund, warum ich mit Euch sprechen wollte, war, dass Duthorian Rall in Sturmwind gesagt hat, Ihr solltet ihn in der Kathedrale des Lichts treffen, wenn Ihr bereit seid. Klang ziemlich wichtig. Klang, als sei irgendwas los.",
  },
  [4487] = {
    ["T"] = "Teufelsross beschwören",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["D"] = "Ah, gut, da seid Ihr ja. Ich habe Euch $geinen Sukkubus:eine Höllenbestie; hinterhergeschickt, um Eure Aufmerksamkeit zu erregen, aber es scheint, als sei es Euch gelungen herzukommen, ehe $ger:sie; Euch gefunden hat. Einerlei.$B$BIn letzter Zeit gab es einiges an Gerede über Eure wachsende Macht - das ist gut für Euch und möglicherweise schlecht für andere. Doch das sind alles Nebensächlichkeiten. Wichtig ist nur, dass ich denke, dass Ihr Euch bewiesen habt und deswegen... hihi... einen gewissen Segen verdient.$B$BOberhalb von Ratschet im Brachland findet Ihr einen Mann namens Strahad. Ich glaube, Eure Wege haben sich bereits gekreuzt.",
  },
  [4488] = {
    ["T"] = "Teufelsross beschwören",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["D"] = "Ah, $n, wie ich sehe, beehrt Ihr uns mit Eurer Anwesenheit. Wie wunderbar!$B$BIch hoffe, Ihr erwartet nicht auch noch, dass wir Euch von Kopf bis Fuß bedienen. Ihr mögt ja vielleicht schnell an Macht gewinnen, doch Ihr seid noch immer ein Kleinkind, was das wahre Wissen und das wahre Verständnis angeht. Wenn Ihr nicht aufpasst, wird die Verderbnis Euch und Euren schwachen Willen überwältigen.$B$BAber ich sollte zum Thema kommen: Strahad möchte mit Euch sprechen. Er wohnt noch immer im Brachland in seinem Turm, der Ratschet überblickt. Ich schlage vor, Ihr säumt nicht zu lange.",
  },
  [4489] = {
    ["T"] = "Teufelsross beschwören",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["D"] = "Ihr da, $R! Kommt her!$B$BIhr werdet $n gerufen, nicht wahr?$B$BDas habe ich mir gedacht. Ich fasse mich kurz: Ihr gewinnt an Macht und das ist nicht unbeobachtet geblieben. Und nun ist die Zeit gekommen, dass Ihr für Eure Anstrengungen belohnt werdet.$B$BBegebt Euch ins Brachland. Oberhalb von Ratschet findet Ihr dort Strahad Farsan - vielleicht erinnert Ihr Euch an ihn? Er möchte mit Euch sprechen. Ich würde nicht säumen.",
  },
  [4490] = {
    ["T"] = "Teufelsross beschwören",
    ["O"] = "Sprecht in Ratschet mit Strahad Farsan, um zu lernen, wie man ein Teufelsross beschwört.",
    ["D"] = "Ich möchte Euch eine Belohnung für Eure Geduld und Eure harte Arbeit geben. Doch wisset, dass sich die Fähigkeit, ein Teufelsross zu beschwören, in Zukunft wesentlich schwieriger gestalten wird. Ihr müsst sie Euch verdienen - und es wird nicht leicht sein.$B$BDoch geht nun fürs Erste, nehmt die Macht mit Euch, eine solche Kreatur zu beschwören, und verwendet diese Macht, wie Ihr es für richtig haltet.",
  },
  [4491] = {
    ["T"] = "Etwas Hilfe von guten Freunden",
    ["O"] = "Führt Ringo zu Spraggel Frock in Marschalls Zuflucht.$B$BFalls Ringo ohnmächtig werden sollte, verwendet Spraggels Feldflasche, um ihn zu beleben.",
    ["D"] = "Argh... es ist so heiß...$B$BIch war gerade dabei, die Gegend zu erkunden, als die Hitze plötzlich unerträglich wurde. Ich muss wohl einen Hitzschlag haben.$B$BBitte, bringt mich zu meiner Freundin Spraggel in Marschalls Zuflucht zurück. Ich glaube, es geht mir noch gut genug, um Euch dorthin zu folgen.$B$BAber ich fühle mich ziemlich schwach. Wenn ich also noch mal in Ohnmacht fallen sollte, dann bespritzt mich einfach mit etwas Wasser aus Spraggles Feldflasche. Das sollte helfen, hoffe ich...",
  },
  [4492] = {
    ["T"] = "Verloren!",
    ["O"] = "Sucht Ringo am Feuersäulengrat.",
    ["D"] = "Ihr müsst mir helfen, $n! Mein Freund Ringo ist vor einer Weile aufgebrochen, um den Vulkan südlich von hier zu erforschen, und er ist schon viel zu lange weg. Ich mache mir große Sorgen um ihn.$B$BMeint Ihr, Ihr könnt ihn suchen? Nehmt doch meine Feldflasche mit - wenn Ihr Ringo tatsächlich findet, dann hat er sie bestimmt nötig!",
  },
  [4493] = {
    ["T"] = "Der Marsch der Silithiden",
    ["O"] = "Sprecht in Gadgetzan mit Alchemist Stößelbruch.",
    ["D"] = "Es scheint, als sei es die Absicht der Silithiden – nein, ihr ganzer Lebenszweck! –, alles zu verschlingen, was ihnen im Weg steht und was nicht zu ihrer eigenen Ökologie gehört. Nur darauf konzentrieren sie sich und darum stellen sie eine große Gefahr für alles Leben auf Azeroth dar.$B$BBis jetzt haben wir nur die Anfänge gesehen. Ich vermute, dass sie ihre Kraft aus einem Ort tiefer drinnen in Kalimdor beziehen: aus dem Krater von Un\'Goro. Geht nach Gadgetzan in der Tanariswüste und sprecht mit Alchemist Stößelbruch. Erzählt ihm von meiner Theorie; er wird uns bestimmt dabei helfen, die Silithiden zu bekämpfen.",
  },
  [4494] = {
    ["T"] = "Der Marsch der Silithiden",
    ["O"] = "Sprecht in Gadgetzan mit Alchemist Stößelbruch.",
    ["D"] = "Es scheint, als sei es die Absicht der Silithiden – nein, ihr ganzer Lebenszweck! –, alles zu verschlingen, was ihnen im Weg steht und was nicht zu ihrer eigenen Ökologie gehört. Nur darauf konzentrieren sie sich und darum stellen sie eine große Gefahr für alles Leben auf Azeroth dar.$B$BBis jetzt haben wir nur die Anfänge gesehen. Ich vermute, dass sie ihre Kraft aus einem Ort tiefer drinnen in Kalimdor beziehen: aus dem Krater von Un\'Goro. Geht nach Gadgetzan in der Tanariswüste und sprecht mit Alchemist Stößelbruch. Erzählt ihm von meiner Theorie; er wird uns bestimmt dabei helfen, die Silithiden zu bekämpfen.",
  },
  [4495] = {
    ["T"] = "Ein guter Freund",
    ["O"] = "Sucht Iverron an der Höhle im Norden.",
    ["D"] = "Ich habe einen Freund, Iverron, der mich normalerweise jeden Tag um dieselbe Zeit besucht. Komischerweise ist er heute noch nicht da gewesen - genau genommen ist er sogar schon etliche Stunden über seiner sonstigen Zeit.$B$BIch muss zugeben, ich bin etwas besorgt, $n. Iverron verbringt viel Zeit bei der Spinnenhöhle oben im Norden und ich bin sicher, Ihr wisst, wie gefährlich es dort ist - überall Spinnen!$B$BFalls Ihr zufällig dort vorbeikommen solltet, könntet Ihr dann nach ihm Ausschau halten?",
  },
  [4496] = {
    ["T"] = "Dschungelei",
    ["O"] = "Bringt eine Gorishiduftdrüse und 5 Erdproben von Un\'Goro zu Alchemist Stößelbruch in Gadgetzan.",
    ["D"] = "Meine Forschungen haben ergeben, dass die Silithiden den Bienen ähneln: Wenn man die Königin des Schwarmbaus tötet, dann sollte das den Rest in völliges Chaos stürzen. Ich werde einen Köder brauen, mit dem wir die Königin herauslocken können, und sobald sie draußen ist, streckt Ihr sie nieder.$B$BDoch zuerst brauchen wir einige Reagenzien; wir wollen doch nicht, dass Ihr völlig unvorbereitet im Dschungel umherstolpert. Geht nach Westen, sucht den Schwarmbau der Silithiden im Krater von Un\'Goro und beschafft Euch eine Duftdrüse von einem der Viecher. Außerdem brauche ich einige Erdproben aus Un\'Goro.",
  },
  [4501] = {
    ["T"] = "Vorsicht, Pterrordax!",
    ["O"] = "Tötet 10 rasende Pterrordax und sprecht dann mit Spraggel Frock in Marschalls Zuflucht.",
    ["D"] = "Gesucht: Ein fähiger Kämpfer, der sich der Bedrohung durch die Pterrordax im Krater von Un\'Goro annimmt. Ihre Zahl wächst ständig und sie werden zu einer Bedrohung für die Reisenden in der Gegend.$B$BDezimiert den Bestand, indem Ihr 10 rasende Pterrordax tötet.$B$BSprecht mit Spraggel Frock, nachdem Ihr die Aufgabe erledigt habt, dann erhaltet Ihr eine Belohnung.",
  },
  [4502] = {
    ["T"] = "Vulkanische Aktivität",
    ["O"] = "Sammelt 9 Proben der Asche von Un\'Goro von den Feuerelementaren am Vulkan im Krater von Un\'Goro ein und bringt die Proben zu Liv Ritzelflick in Ratschet.",
    ["D"] = "Als Lehrling von Marvon Nietensucher habe ich unglaublich viel gelernt. Aber er ist jetzt schon sehr lange fort. Daher denke ich, werde ich an ein paar eigenen Experimenten arbeiten.$B$BIch habe kürzlich etwas über den Krater von Un\'Goro gelesen und über die eigentümliche Vulkanaktivität, die in der Mitte des Kraters zu erkennen ist. Ich glaube, ich könnte eine Menge lernen, wenn ich nur etwas Asche von den Elementaren dort haben könnte.$B$BWürde es Euch etwas ausmachen, etwas Asche für mich einzusammeln, falls Ihr dort entlangkommen solltet?",
  },
  [4503] = {
    ["T"] = "Sappalots Flieger",
    ["O"] = "Sammelt 8 eingesponnene Dimetrodonschuppen und 8 eingesponnene Pterrordaxschuppen für Sappalot in Marschalls Zuflucht.",
    ["D"] = "Ich baue einen Flugapparat!$B$BIch bin hier gestrandet und habe leider absolut überhaupt keinen Orientierungssinn. Ich würde ja einen Greifen oder Windreiter zurück nach Gadgetzan nehmen, aber diese Viecher jagen mir einfach eine höllische Angst ein.$B$BAlso, werdet Ihr mir helfen? Ich muss nur noch die Flügel bauen und ich glaube, dass sich die eingesponnenen Schuppen von Pterrordax und Dimetrodonten perfekt dafür eignen würden. Ich werde mich zwar garantiert nicht mit denen anlegen, aber Ihr seht ziemlich zäh aus. Wollt Ihr es nicht versuchen?",
  },
  [4504] = {
    ["T"] = "Superkleber",
    ["O"] = "Sammelt 12 Proben superklebrigen Teer für Tran\'rek in Gadgetzan.",
    ["D"] = "Ich bin immer auf der Suche nach neuen Methoden, um schnelles Gold zu verdienen, $n. Wenn Ihr mir bei meiner neuesten Idee helft, dann gebe ich Euch ein bisschen was vom Gewinn ab. Na, was sagt Ihr dazu?$B$BDas hab ich mir gedacht!$B$BGeht zu den Teergruben im Norden des Kraters von Un\'Goro und beschafft mir etwas Teer. Aber nicht jeden x-beliebigen Teer, versteht Ihr? Ihr müsst den von den Kreaturen nehmen, die an den Teergruben leben. Normaler Teer ist einfach nicht klebrig genug.$B$BDie pflanzenartigen Kreaturen jedoch haben Chlorophyll in ihrer Haut, und wenn man das mit dem Teer mischt, dann wird das superklebrig!",
  },
  [4505] = {
    ["T"] = "Brunnen der Verderbnis",
    ["O"] = "Beschafft eine Probe verderbten Wassers vom Mondbrunnen der Jadefeuersatyrn und bringt sie zu Winna Hazzard am Blutgiftposten.",
    ["D"] = "Manche Leute sagen vielleicht, ich sei außergewöhnlich grausam... Aber in Wirklichkeit bin ich doch nur von dem Wunsch beseelt zu sehen, wie die Horde einen Vorteil gegenüber der Allianz erringt, und zwar auf jede nur mögliche Weise.$B$BSeit Kurzem interessiere ich mich für einen Mondbrunnen in den Ruinen von Constellas. Er soll südlich von hier liegen und von den Jadefeuersatyrn gepflegt werden. Der Mondbrunnen, der einst ein Symbol der Erneuerung für die unerträglichen Nachtelfen war, ist jetzt verderbt und wird dazu benutzt, neue Satyrn hervorzubringen.$B$BIch habe da eine Idee, $n, und ich hätte gern eine Probe von dem Wasser... für meinen kleinen Katzenfreund hier.",
  },
  [4506] = {
    ["T"] = "Verderbte Säbler",
    ["O"] = "Bringt Winnas Kätzchen zum verderbten Mondbrunnen, lasst es dort frei und bringt es dann wieder zu Winna zurück.$B$BSobald Ihr wieder bei Winna seid, klickt auf die Katze, um sie Winna zu übergeben.",
    ["D"] = "Dieses verderbte Wasser können wir zu unserem Vorteil verwenden, $n. Mit nur einer kleinen Menge Wasser wurde mein Kätzchen doppelt so groß und scheint jetzt irgendwie... stärker als vorher zu sein. Solche Katzen könnten sich noch als nützlich erweisen...$B$BHier, $n. Nehmt diesen Katzentransportkorb und lasst das Kätzchen darin neben dem Mondbrunnen in den Ruinen von Constellas frei. Hi, hi... Ich bin mir sicher, das Ergebnis wird Euch gefallen!$B$BBringt mir danach die Katze zurück, dann werde ich mich für Eure Zeit und Mühe erkenntlich zeigen.",
  },
  [4507] = {
    ["T"] = "Bauer schlägt Dame",
    ["O"] = "Besiegt die Schwarmkönigin der Gorishi, sobald sie erscheint.$B$BBringt das Gehirn der Schwarmkönigin zu Alchemist Stößelbruch nach Gadgetzan.",
    ["D"] = "Hier ist der Köder. Im Schwarmbau sollte sich ein Kristall befinden. Wir glauben, dass die Silithiden den Kristall als eine Art Einstellungsgerät verwenden, das den ganzen Schwarmbau verbindet. Wo sich dieser Kristall befinden könnte, wollt Ihr wissen? Ich würde vorschlagen, Ihr versucht es in der Brutstätte innerhalb des Schwarmbaus.$B$BWenn Ihr den Köder bei dem Kristall benutzt, sollte das die Königin aufscheuchen, aber geht acht – sie ist wahrscheinlich von mehreren Wachen umgeben. Schaltet die Königin aus und entfernt ihr Gehirn. Ich weiß, das klingt eklig, aber wir müssen es unbedingt untersuchen. Viel Glück, $n!",
  },
  [4508] = {
    ["T"] = "Die Ruhe vor dem Sturm",
    ["O"] = "Bringt Stößelbruchs Bericht von Un\'Goro zu Gracina Glaubensmacht in Darnassus.",
    ["D"] = "Meine jüngsten Ergebnisse und das Wissen, das wir heute hier gesammelt haben, müssen in die Hände von Leuten gelangen, die etwas damit anfangen können. So einfallsreich wir hier in Gadgetzan auch sein mögen, das hier können wir nicht allein schaffen.$B$BBringt meinen jüngsten Bericht zu Gracina Glaubensmacht nach Darnassus; sie hält sich doch immer noch im Tempel des Mondes auf, oder? Sie hat Freunde, und zwar ziemlich hohe Tiere innerhalb der Allianz; wenn wir die in diese Sache mit hineinziehen können, $n, dann haben wir vielleicht eine Chance, dies alles zu überleben.",
  },
  [4509] = {
    ["T"] = "Die Ruhe vor dem Sturm",
    ["O"] = "Bringt Stößelbruchs Bericht von Un\'Goro zu Zilzibin Tomtom in Orgrimmar.",
    ["D"] = "Meine jüngsten Ergebnisse und das Wissen, das wir heute hier gesammelt haben, müssen in die Hände von Leuten gelangen, die etwas damit anfangen können. So einfallsreich wir hier in Gadgetzan auch sein mögen, das hier können wir nicht allein schaffen.$B$BBringt meinen jüngsten Bericht zu Zilzibin Tomtom nach Orgrimmar; er lebt doch immer noch im oberen Teil der Gasse, oder? Er hat Freunde ziemlich weit oben in der Horde; wenn wir die mit in diese Sache hineinziehen können, $n, dann haben wir vielleicht eine Chance, dies alles zu überleben.",
  },
  [4510] = {
    ["T"] = "Die Ruhe vor dem Sturm",
    ["O"] = "Bringt den Bankgutschein zu Idriana in der Bank von Darnassus.",
    ["D"] = "$n, Eure Hilfe war wirklich unschätzbar wertvoll. Es wird zweifellos der Moment kommen, an dem ich erneut um Euren Beistand im Kampf gegen die Silithiden bitten muss. Wir müssen erst verarbeiten, was wir im Krater von Un\'Goro gelernt haben - ich fürchte, dass meine ursprüngliche Annahme, die Silithiden kämen aus dem Un\'Goro, falsch war... dass an einem anderen Ort tatsächlich eine wesentlich stärkere Präsenz existiert.$B$BBringt diese Nachricht zu Idriana in der Bank. Sie wird Euch etwas aus meinem Gewölbe geben, das Ihr als Zeichen meiner Dankbarkeit ansehen sollt. Ich danke Euch für Eure mutige Unterstützung, $C.",
  },
  [4511] = {
    ["T"] = "Die Ruhe vor dem Sturm",
    ["O"] = "Bringt den Bankgutschein zu Karus in der Bank von Orgrimmar.",
    ["D"] = "$n, ohne Euch wären wir niemals so weit gekommen. Es wird zweifellos der Moment kommen, an dem ich erneut um Eure Hilfe im Kampf gegen die Silithiden bitten muss. Wir müssen erst verarbeiten, was wir im Krater von Un\'Goro gelernt haben - ich fürchte, dass meine ursprüngliche Annahme, die Silithiden kämen aus dem Un\'Goro, falsch war... dass an einem anderen Ort tatsächlich eine wesentlich stärkere Präsenz existiert.$B$BBringt diese Nachricht zu Karus in der Bank. Er wird Euch etwas aus meinem Gewölbe geben, das Ihr als Zeichen meiner Dankbarkeit ansehen sollt. Danke für Eure tatkräftige Unterstützung, $C.",
  },
  [4512] = {
    ["T"] = "Schlamm auf Abwegen",
    ["O"] = "Bringt 6 mit verfluchtem Schlamm gefüllte Gläser und 6 mit besudeltem Schlamm gefüllte Gläser zu Laris Bummelritzel in Eisenschmiede.",
    ["D"] = "Aber womit ich mich am meisten beschäftigt habe, das sind Schlamme. Ich weiß, das klingt seltsam, aber die sind wirklich überall!$B$BVor Kurzem habe ich einer Dame in Menethil dabei geholfen, ihre Tasche wiederzufinden - ein Schlamm hatte sie gefressen. Ich habe auch gesehen, wie Mitglieder der Königlichen Apothekervereinigung aus Unterstadt Proben von dem Zeug sammelten. Ich möchte wissen, wieso! Wo kommt es her?$B$BIch habe auch gehört, dass es im Teufelswald noch mehr davon geben soll. Ich brauche einige Proben von den Schlammen dort, damit ich meine Forschungen weiter vorantreiben kann. Ich gebe Euch einige Behälter mit; verwendet diese, um eine Probe von den toten Kreaturen einzusammeln.",
  },
  [4513] = {
    ["T"] = "Schlamm auf Abwegen",
    ["O"] = "Bringt 10 gefüllte reine Probengläser zu Laris Bummelritzel in Eisenschmiede.$B$BFalls Ihr Probleme habt, fündig zu werden, dann kann Laris Euch vielleicht noch den einen oder anderen Hinweis geben.",
    ["D"] = "Ich brauche noch mehr Proben, $n. Aber - und ich weiß nicht so recht, wie ich das ausdrücken soll - es müssen reine Proben sein. Ich weiß, ich weiß. \"Was meint Ihr mit \'rein\', Laris?\", fragt Ihr. Ich kann es nicht genau sagen. Ich weiß, dass die hier verderbt sind... genau wie im Teufelswald. Und genau deswegen ist meine Erkenntnis so wichtig. Die Schlamme scheinen sich anzupassen... die Eigenschaften der Gegend anzunehmen, in der sie sich befinden. Ich muss Proben finden, die nicht verunreinigt sind. Aber wo? Wo?!$B$BPah! $n, nehmt diese Behälter einfach mit, nur für den Fall, dass Ihr irgendwo welche finden solltet.",
  },
  [4521] = {
    ["T"] = "Wilde Wächter",
    ["O"] = "Trull Scheiterbann im Teufelswald möchte, dass Ihr 15 tobende Eulenbestien und 15 struppige Eulenbestien tötet.",
    ["D"] = "Winterquell liegt gleich hinter der Holzschlundfeste, im Nordosten des Teufelswalds. Seid Ihr schon mal dort gewesen?$B$BIch habe gehört, dass es in jener Region nur so von Kreaturen wimmeln soll, die Wildekin genannt werden. Diese Bestien sind dafür bekannt, dass sie Geheimnisse bewachen, und zwar Geheimnisse der Nachtelfen.$B$BWir müssen herausfinden, was in Winterquell verborgen liegt.$B$BIch habe gehört, die struppigen Eulenbestien findet man südlich von der Straße, und außerdem soll es noch so genannte tobende Eulenbestien geben, die im Zentrum von Winterquell zu finden sind. Räumt sie aus dem Weg und verschafft Euch so Zutritt zu dem Gebiet.",
  },
  [4542] = {
    ["T"] = "Nachricht zum Freiwindposten",
    ["O"] = "Bringt Klippenbehüter Langhorn am Freiwindposten die dringende Nachricht.",
    ["D"] = "$gTapferer Reisender:Tapfere Reisende;, die Zentauren haben ihre Angriffe in diesem Gebiet verstärkt. Der Freiwindposten muss umgehend über diese erneuten Angriffe informiert werden! Sucht Klippenbehüter Langhorn am Freiwindposten im Südosten auf und gebt ihm diese dringende Nachricht.$b$bDoch seht Euch vor und geht dem hier in der Nähe herumlungernden Grimmtotemklan aus dem Weg... in letzter Zeit benehmen die Leute sich seltsam uns gegenüber.",
  },
  [4561] = {
    ["T"] = "Test auf Unreinheiten - Krater von Un\'Goro",
    ["O"] = "",
    ["D"] = "",
  },
  [4581] = {
    ["T"] = "Kayneth Stillwind",
    ["O"] = "Bringt Shindrells Notiz zu Kayneth Stillwind in Waldeslied.",
    ["D"] = "Der Druide Kayneth Stillwind hat sich bei mir gemeldet. Er hätte gern einen Bericht über das, was an unserer Küste und in den Wäldern um Astranaar vorgeht. Er befürchtet, dass eine Verderbnis durch das Eschental zieht... und vielleicht hat er Recht damit.$B$BHier, bringt ihm meinen Bericht. Ihr werdet ihn im Schrein von Waldeslied finden, im Nachtweisenwald im Osten.",
  },
  [4601] = {
    ["T"] = "Der Funkelmat 5200!",
    ["O"] = "Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren.",
    ["D"] = "Dieser Apparat ist etwas ganz Besonderes unter den Gnomen, er bietet dem Wahnsinn außerhalb dieses Raums tapfer die Stirn. Eine kleine Plakette auf dem Gerät besagt: \"Der Funkelmat 5200\", gefolgt von einem kurzen Text:$B$B\"Schmutz und Verkrustungen ruinieren Euer Funkelzeug? Der Funkelmat 5200 definiert das Wort Glanz neu! Gebt den Gegenstand hinein, den Ihr gesäubert haben wollt, und werft drei Silbermünzen in den Münzschlitz. Lasst den Funkelmat 5200 einen Moment arbeiten - und voilà! Eure Wertgegenstände werden sauber und glänzend herauskommen!\"",
  },
  [4602] = {
    ["T"] = "Der Funkelmat 5200!",
    ["O"] = "Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren.",
    ["D"] = "Dieser Apparat ist etwas ganz Besonderes unter den Gnomen, er bietet dem Wahnsinn außerhalb dieses Raums tapfer die Stirn. Eine kleine Plakette auf dem Gerät besagt: \"Der Funkelmat 5200\", gefolgt von einem kurzen Text:$B$B\"Schmutz und Verkrustungen ruinieren Euer Funkelzeug? Der Funkelmat 5200 definiert das Wort Glanz neu! Gebt den Gegenstand hinein, den Ihr gesäubert haben wollt, und werft drei Silbermünzen in den Münzschlitz. Lasst den Funkelmat 5200 einen Moment arbeiten - und voilà! Eure Wertgegenstände werden sauber und glänzend herauskommen!\"",
