    ["D"] = "",
  },
  [6548] = {
    ["T"] = "Rächt mein Dorf",
    ["O"] = "Tötet 8 Grobiane der Grimmtotem und 6 Söldner der Grimmtotem. Kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.",
    ["D"] = "Der Grimmtotemklan hat mein Dorf überfallen und fast alle getötet. Ich habe so viele ich konnte niedergestreckt, bin aber nur um Haaresbreite lebend entkommen.$b$bJetzt wünsche ich mir nichts mehr, als noch mehr von ihnen tot zu sehen, $n. Ihr findet sie gleich westlich von hier.",
  },
  [6561] = {
    ["T"] = "Schurkerei in der Tiefschwarzen Grotte",
    ["O"] = "Bringt den Kopf von Zwielichtfürst Kelris zu Bashana Runentotem in Donnerfels.",
    ["D"] = "Die Kraft hat mich verlassen. Ich benötige Eure Hilfe!$b$bDiese Stätte war einst ein großer Tempel von Elune. Doch das Unglück führte zum Untergang, als die Verderbnis eines Alten Gottes unter der Erde emporwallte und den heiligen Mondbrunnen besudelte.$b$bAku\'mai, die Dienerin der Alten Götter, erhob sich aus den Wassern.$b$bDie Kultisten des Schattenhammers haben sich mit den Naga verschworen, um diese Ländereien zu besetzen. Die Kultisten opfern unter Führung von Kelris Unschuldige für Aku\'mai, um Macht zu erlangen.$b$bBitte, erschlagt Kelris und bringt Bashana in Donnerfels seinen Kopf, $n.",
  },
  [6562] = {
    ["T"] = "Ärger in der Tiefe",
    ["O"] = "Sprecht im Eschental mit Je\'neu Sancrea.",
    ["D"] = "Je\'neu Sancrea braucht Hilfe. Er wartet am Eingang zur Tiefschwarzen Grotte im Eschental. Der Nachtelfenwald liegt im Norden des Steinkrallengebirges. Ihr erreicht ihn, indem Ihr über die Scherwindklippe oder nördlich durchs Brachland reist. Der Zoramstrand liegt weit im Westen des Waldes. Seid vorsichtig, $n. Dort gibt es Mächte, denen Eure Anwesenheit nicht gefallen wird.",
  },
  [6563] = {
    ["T"] = "Die Essenz von Aku\'mai",
    ["O"] = "Bringt 20 Saphire von Aku\'mai zu Je\'neu Sancrea im Eschental.",
    ["D"] = "Im Eingangsbereich des Tunnels der Tiefschwarzen Grotte findet man überall an den Höhlenwänden große Saphiransammlungen. Kürzlich ist aufgefallen, dass die Naga diese Kristalle abbauen. Bis wir herausgefunden haben, warum sie das tun, sollten wir die Naga einstweilen davon abhalten. Die Kristalle besitzen große Elementarkraft und wenn sie unangemessen eingesetzt werden, könnte man daraus eine mächtige arkane Komponente herstellen.$B$BWerdet Ihr mir helfen, $n? Die Tiefschwarze Grotte liegt am Zoramstrand nördlich unseres Außenpostens.",
  },
  [6564] = {
    ["T"] = "Treue zu den Alten Göttern",
    ["O"] = "Bringt die durchfeuchtete Notiz zu Je\'neu Sancrea im Eschental.",
    ["D"] = "Die zerknitterte Notiz trägt eine kaum leserliche Handschrift:$B$B\"Ja, sagt allen Priesterinnen, dass sie mir die Kristalle bringen sollen. Die Wasserelementare sind stark in ihnen und sie werden unseren Aquamanten bei ihren Beschwörungen ungemein dienlich sein.$B$BIndem wir die Kraft dieser Kristalle beherrschen, beschwichtigen wir Aku\'mai. Eines Tages werden wir genug Macht haben, um Aku\'mai zu uns zurückzuholen. Dann wird die Welt wieder zur Ära ihrer einstigen Größe zurückkehren! Eine Ära, in der die Alten Götter herrschen!\"",
  },
  [6565] = {
    ["T"] = "Treue zu den Alten Göttern",
    ["O"] = "Tötet Lorgus Jett.",
    ["D"] = "Dieser Anhänger des Schattenhammerklans darf seinen Plan nicht in die Tat umsetzen. Die Schattenhammerkultisten verstehen die Geister der Natur nicht. Sie glauben an die Alten Götter - Kreaturen des Chaos und der Zerstörung, die vor langer Zeit besiegt wurden. Dieser Lorgus darf keinen Erfolg haben. Ihr müsst ihn aufhalten, $n. Ich kann mich leider nicht selbst darum kümmern... ich bin zu schwach.$B$BGeht zur Tiefschwarzen Grotte, findet ihn und tötet ihn. Kehrt hierher zu mir zurück.",
  },
  [6566] = {
    ["T"] = "Was der Wind erzählt",
    ["O"] = "Hört Thrall zu.",
    ["D"] = "Schön, Euch wiederzusehen, $n. Der Wind führt Kunde aus den Östlichen Königreichen mit sich.$B$BSetzt Euch und lauscht.",
  },
  [6567] = {
    ["T"] = "Der Champion der Horde",
    ["O"] = "Sucht in Schattenflucht in Desolace nach Rokaro.",
    ["D"] = "Rexxar, einer meiner treuesten Helden, hat einen Weg in den Hort des Drachen gefunden. Leider ist er mit unserer Expedition in die Scherbenwelt aufgebrochen.$B$BEr hat jedoch zuvor seinem Freund Rokaro in Schattenflucht die Einzelheiten mitgeteilt. Sucht nach ihm!",
  },
  [6568] = {
    ["T"] = "Meisterin der Illusionen",
    ["O"] = "Überbringt Myranda der Vettel in den Westlichen Pestländern Rokaros Brief.",
    ["D"] = "Illusion und Täuschung. So werdet Ihr es schaffen, den schwarzen Drachenschwarm zu überlisten und zu erlangen, was Ihr sucht.$B$BAuch wenn ich Euch in der Kunst der Täuschung unterrichten könnte, werdet Ihr die Hilfe eines Meistern benötigen.$B$BIch kenne jemanden, der Euch dabei helfen kann. In den Westlichen Pestländern findet Ihr Myranda die Vettel, eine Meisterin der Illusionen aus Lordaeron. Sie hat uns in der Vergangenheit bereits geholfen. Wenn Ihr sie davon überzeugen könnt, dass Eure Sache es wert ist, wird sie Euch vielleicht unterstützen.$B$BBringt diese Nachricht zu ihr.",
  },
  [6569] = {
    ["T"] = "Oculus-Illusionen",
    ["O"] = "Reist zur Schwarzfelsspitze und sammelt 20 schwarze Drachenbrutaugen. Kehrt zu Myranda der Vettel zurück, sobald Ihr die Aufgabe erfüllt habt.",
    ["D"] = "Ihr möchtet also einer vom schwarzen Drachenschwarm werden, was, $R?$B$BDie Erschaffung einer Illusion, wie Ihr sie wünscht, ist eine Kleinigkeit, aber die für den Zauber erforderlichen Komponenten sind ziemlich schwierig zu beschaffen. Der Kriegshäuptling hat Euch mit dieser Mission betraut, also muss Myranda wohl annehmen, dass Ihr fähig dazu seid.$B$BReist zur oberen Zitadelle der Schwarzfelsspitze und tötet genug Mitglieder der schwarzen Drachenbrut, um eine Eurer Taschen mit deren Augen zu füllen.$B$BKehrt zu Myranda zurück, wenn Ihr genügend von dem Reagenz gesammelt habt.",
  },
  [6570] = {
    ["T"] = "Aschenschwinge",
    ["O"] = "Reist zum Drachensumpf in den Düstermarschen und begebt Euch in Aschenschwinges Bau. Sobald Ihr darin seid, tragt das Amulett der drachischen Subversion und sprecht mit Aschenschwinge.",
    ["D"] = "Ich habe dieses Medaillon mit der Magie versehen, die notwendig ist, um eine Illusion zu erschaffen und sie innerhalb der Einfriedung des Höhlenbaus von Aschenschwinge aufrechtzuerhalten.$B$BAschenschwinge ist ein uralter Drache, ein alter Torwächter Nefarians, der die schwere Bürde auf seinen Schultern trägt, jene der Drachenbrut auf die Probe zu stellen, die in den Rang einer Elitewache Onyxias aufsteigen wollen.$B$BReist zum Drachensumpf in den Düstermarschen und begebt Euch in Aschenschwinges Bau. Sobald Ihr dort seid, legt Ihr das Amulett an und folgt Eurem Schicksal, wo immer es Euch auch hinführen mag!",
  },
  [6571] = {
    ["T"] = "Nachschub des Kriegshymnenklans",
    ["O"] = "Tragt den Nachschub des Kriegshymnenklans zusammen: Sägeblätter des Kriegshymnenklans, ein Holzfällerseil, Öl des Kriegshymnenklans und die Axtlieferung des Kriegshymnenklans für Locke Okarr im Splitterholzposten.",
    ["D"] = "Uns hier im Holzfällerlager des Kriegshymnenklans gehen langsam einige Dinge aus!$B$BUnsere Lieferung Sägeblätter liegt bei Pixel im Splitterholzposten. Jemand muss auch nach Beutebucht reisen, um eine Kiste mit Äxten bei Werftmeister Lozgil abzuholen.$B$BBei unserer Öl- und Seillieferung sieht es jedoch schon schwieriger aus. Die Furbolgs haben die Seile geraubt und die Satyrn haben das Öl abgefangen und nutzen es jetzt für abscheuliche Rituale in der Nachtflucht und Satyrnaar!$B$BIch komme in große Schwierigkeiten, wenn ich die Sachen nicht bald ausliefere... könnt Ihr mir helfen?",
  },
  [6581] = {
    ["T"] = "Sägeblätter des Kriegshymnenklans",
    ["O"] = "",
    ["D"] = "",
  },
  [6582] = {
    ["T"] = "Die Prüfung der Schädel, Seher",
    ["O"] = "Ihr müsst Seher, den Champion der blauen Großdrachen, finden und ihn töten. Schlagt seinem Kadaver den Schädel ab und bringt ihn zu Aschenschwinge.$B$BIhr wisst, dass Seher in Winterquell zu finden ist.",
    ["D"] = "Wir sind Kinder von Todesschwinge, Welpe. Wir müssen die Herrschaft über die niederen Aspekte durchsetzen.$B$BWenn Ihr wirklich eines Aufstiegs würdig seid, werden die Geringeren des Drachenschwarms vor Euch niedersinken.$B$BFindet deren Champions und vernichtet sie im Namen unseres Vaters!$B$BBegebt Euch nach Winterquell und spürt den mächtigen Blaudrachen Seher auf. Zerschmettert ihn und alle anderen des blauen Drachenschwarms, die sich Euch in den Weg stellen. Entreißt ihm seinen Schädel und bringt ihn mir.",
  },
  [6583] = {
    ["T"] = "Die Prüfung der Schädel, Somnus",
    ["O"] = "Vernichtet den Drachenchampion des grünen Drachenschwarms, Somnus. Nehmt seinen Schädel und bringt ihn zu Aschenschwinge zurück.",
    ["D"] = "Genau wie unser Vater die Aspekte zerschmettert, müssen wir danach trachten, Chaos und Zerstörung unter ihren Kindern anzurichten.$B$BIhr müsst Eure Leistungsfähigkeit im Kampf gegen die schlaflosen Träumer auf die Probe stellen: die Kinder des Gründrachenschwarms.$B$BBegebt Euch zu den Sümpfen des Elends und spürt Somnus auf, den Drachenchampion des Gründrachenschwarms. Lasst Eurem Hass freien Lauf und bezwingt das Insekt...Kommt mit seinem Schädel zu mir zurück...",
  },
  [6584] = {
    ["T"] = "Die Prüfung der Schädel, Chronalis",
    ["O"] = "Chronalis, ein Kind Nozdormus, bewacht die Höhlen der Zeit in der Tanariswüste. Vernichtet ihn und bringt seinen Schädel zu Aschenschwinge.",
    ["D"] = "Die Zeitbehüter, Kinder Nozdormusss - Lord der Jahrhunderte...$B$BEs sind derer drei, die die Höhlen der Zeit bewachen, aber nur einer, an dem der schwarze Drachenschwarm interessiert ist: Chronalisss, der Zeitverschieber, bevorzugter Drachling Nozdormusss.$B$BBesssiegt diesssen Champion, denn das wäre ein Schlag, der sssogar den stoischen Sand der Zzzeit erschüttern würde. Kehrt mit seinem Schädel zu mir zzzurück.",
  },
  [6585] = {
    ["T"] = "Die Prüfung der Schädel, Axtroz",
    ["O"] = "Begebt Euch nach Grim Batol und spürt Axtroz auf, den Drachenchampion des roten Drachenschwarms. Vernichtet ihn und nehmt seinen Schädel. Bringt den Schädel zu Aschenschwinge zurück.",
    ["D"] = "Es war in den Bergen von Grim Batol, wo unser Vater gegen die vereinte Macht der Aspekte wütete. Die feige Tat der Verräterin Alexstrasza verschaffte den übrigen Aspekten genug Zeit, um dem Zorn des Vaters zu entkommen.$B$BWasss für ein schändlicher Akt...$B$BDer rote Drachenschwarm bewacht heute Grim Batol und ihr Leutnant Axtroz muss vernichtet werden. Bringt mir Axtroz\' Schädel. Für den Vater, Welpe!",
  },
  [6601] = {
    ["T"] = "Aufstieg...",
    ["O"] = "Mir scheint, als sei die Charade jetzt vorüber. Ihr wisst, dass das Amulett der drachischen Unterwanderung, das Myranda die Vettel für Euch erschuf, auf der Schwarzfelsspitze nicht funktioniert. Vielleicht solltet Ihr Rokaro aufsuchen und ihm Eure Notlage erklären. Zeigt ihm das glanzlose Drachenfeueramulett. Er wird hoffentlich wissen, was als Nächstes zu tun ist.",
    ["D"] = "Ein Medaillon, aus den Schädeln Eurer Feinde gefertigt. Ihr kennt dieses Medaillon, nicht wahr? Zweifellos habt Ihr gesehen, wie Eure Ältesten es trugen.$B$BNehmt es, Welpe. Begebt Euch zur Spitze zurück und zeigt es General Drakkisath. Der General wird das Medaillon mit der letzten Verzauberung versehen und das Schmuckstück auf Euren Geist abstimmen.$B$BIhr werdet das fertige Medaillon als Ehrenabzeichen tragen, als Symbol für Euren Aufstieg in unsere am höchsten geschätzten Reihen: Wächter der Brutmutter.$B$BGeht!",
  },
  [6602] = {
    ["T"] = "Blut des schwarzen Großdrachenhelden",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und tötet General Drakkisath. Sammelt sein Blut und bringt es zu Rokaro.",
    ["D"] = "Ihr werdet dem General einen Besuch abstatten, jawohl, aber nicht als einer des schwarzen Drachenschwarms.$B$BWisst Ihr, Zeremonie ist für den schwarzen Drachenschwarm nur ein anderer Begriff für Aderlass.$B$BDas ruhende Amulett benötigt nur das Blut des Generals, um wieder aktiv zu sein. Das ist einer ihrer primitiven, ausfallsicheren Mechanismen.$B$BKehrt zur Schwarzfelsspitze zurück und vernichtet Drakkisath. Bringt sein Blut hierher zurück, dann aktiviere ich den Schlüssel zu Onyxias Hort.",
  },
  [6603] = {
    ["T"] = "Probleme in Winterquell!",
    ["O"] = "Findet Donova Schneeweh bei den Thermalquellen in Winterquell.",
    ["D"] = "In Winterquell gibt es Probleme!$B$BSicher, ich habe einige Zeit da draußen in der Wildnis zugebracht und weiß um diese Dinge... Es macht mich immer ganz stolz, das zu sagen. Für mich waren die Stunden, die ich dort draußen in den Hügeln verbrachte, etwas ganz Besonderes. Es hilft einem, blitzschnelle Reflexe zu entwickeln, einen kühlen Kopf und ein wachsames Auge zu bewahren...$B$BMan braucht Urteilsvermögen, Grips und Reife, um die Wildnis so schätzen zu können, wie ich es tue.$B$BAlso, worüber wollte ich eigentlich sprechen?$B$BAch ja, Probleme. Sprecht mit Donova Schneeweh bei den Thermalquellen. Sie wird Euch mehr sagen können.",
  },
  [6604] = {
    ["T"] = "Wütende Wildekin",
    ["O"] = "Sprecht mit Jaron Steinschleifer in Sternfall.",
    ["D"] = "Ist Euch das verlassene Lager südlich von hier aufgefallen? Es scheint, als wären die Wildekin verrückt geworden und haben so gut wie alles zerstört, was sie finden konnten!$B$BIch hab Gerüchte gehört, wonach einer der Überlebenden des Angriffs sich in Sternfall aufhalten soll, das liegt westlich von hier. Ich bin neugierig, was wohl geschehen sein mag...Ihr nicht auch?",
  },
  [6605] = {
    ["T"] = "Eine seltsame Person",
    ["O"] = "Sprecht im Teufelswald mit Winna Hazzard.",
    ["D"] = "Ich bin gerade erst vom Teufelswald zurückgekehrt, $n. Unterwegs habe ich im Blutgiftposten Halt gemacht und eine wirklich eigenartige Bekanntschaft gemacht... Sie schien ganz besessen von einer Sache gewesen zu sein, und ich könnte Euch wohl erzählen, um was es sich handelt, aber ich denke, das sollte sie Euch selbst erklären.$B$BGeht zum Blutgiftposten im Westen des Teufelswalds und fragt nach Winna Hazzard, falls Ihr Interesse habt.$B$BWer weiß, vielleicht braucht sie ja Eure Hilfe...",
  },
  [6606] = {
    ["T"] = "Ein bisschen Glück",
    ["O"] = "Sprecht in der Ewigen Warte mit Hexendoktorin Mau\'ari.",
    ["D"] = "Ein bisschen Glück kann man immer gebrauchen, nicht wahr? Ein kleiner Vorteil ist alles, was man braucht, um eine günstige Wendung herbeizuführen...$B$BIch habe vor einiger Zeit eine ziemlich exzentrische Hexendoktorin kennen gelernt. Sie fertigte einen Glücksbringer für mich... nicht gerade billig. Aber ich sage Euch, er war es auch wert!$B$BMöchtet Ihr vielleicht selbst gern einen haben? Sucht Hexendoktorin Mau\'ari in der Ewigen Warte auf. Meinen bekommt Ihr nämlich nicht!",
  },
  [6607] = {
    ["T"] = "Nat Pagle, Extremangler",
    ["O"] = "Nat Pagle möchte, dass Ihr folgende Fische fangt:$B$BNebelschilf-Mahi Mahi vom Nebelschilfstrand in den Sümpfen des Elends.$B$BEinen Sar\'therisbarsch vom Sar\'therisstrand in Desolace.$B$BFeralas-Ahi aus dem Verdantis in Feralas.$B$BBlauwimpel von der Ungezähmten Küste im Schlingendorntal.$B$BKehrt zu Nat Pagle zurück, wenn Ihr alle an Land gezogen habt!",
    ["D"] = "Ich kann nicht behaupten, dass ich gern angele. Ich glaub, ich mach das jetzt seit gut 20 oder 30 Jahren.$B$BEs gibt wohl viele Gründe, die einen dazu treiben, etwas zu tun.$B$BIch will damit jetzt nicht sagen, dass Ihr 30 Jahre lang angeln oder jeden einzelnen Fisch im Meer fangen müsst, um ein Meisterangler zu werden. Man muss einfach nur entschlossen sein - entschlossen genug, um stundenlang auf dem Hintern zu sitzen und nichts zu tun.$B$BIch sag Euch was: Fangt mir ein paar seltene Fische, dann bring ich Euch das eine oder andere bei. Hier habt Ihr eine Liste:",
  },
  [6608] = {
    ["T"] = "Ihr zu gut",
    ["O"] = "Ihr nehmt an, dass Lumak Euch zu erklären versucht, dass er nicht mehr tun kann, um Eure Angelfertigkeit zu verbessern. Ihr solltet seinen Freund aufsuchen, diesen Nat Pagle, einen Menschen, der auf einer der Inseln südlich von Theramore (jedenfalls glaubt Ihr, dass Lumak das gemeint hat) auf Fischfang geht.",
    ["D"] = "Lumak können $n nix mehr beibringen. Ihr zu gut! Finden Freund von Lumak, Nat Pagle. Er sein Mensch, aber nix Futter! Ist Freund von Lumak! Nat Euch zeigen, wie besserer angeln.$B$BIhr finden Nat in den Düstermarschen, Süden von Thermoro-odersoähnlich. Nat bestimmt angeln. Ihr jetzt gehen!",
  },
  [6609] = {
    ["T"] = "Ich hab nix mehr!",
    ["O"] = "Reist nach Kalimdor zur Küstenstadt Theramore. Dort müsst Ihr nach einer Insel suchen, die südlich der Hauptstadt liegt. Nat Pagle erwartet Euch!",
    ["D"] = "Ich bin ein Zwerg, zum Donnerwetter nochmal, $g Jungchen:junge Dame;! Ich hab mit Angeln nichts am Hut! Ich meine, seht mich doch mal an! Ich bestehe praktisch aus Stein.$B$BIch hab nichts mehr - Ihr habt mich ausgelaugt. Ich hab Euch alles beigebracht, was ich weiß, flipflop!$B$BKapiert Ihr\'s nicht? Ah, gut. Ich hab in Kalimdor einen alten Freund, der Euch weiterhelfen kann. Heißt Nat Pagle, guter Junge. Findet ihn auf den Inseln südlich von Theramore. Jetzt verfatzt Euch!",
  },
  [6610] = {
    ["T"] = "Muschelette Surprise",
    ["O"] = "Dirge Schnetzelhack möchte, dass Ihr folgende Dinge besorgt:$B$B10 Rieseneier. Ihr findet sie bei den Rocs, die Tanaris durchstreifen, oder anderen Großvogelarten.$B$B10 Stück scharfes Muschelfleisch. Das findet Ihr natürlich in Muscheln!$B$B20 Stück feiner alter Cheddar. Begebt Euch zu einer Käserei!$B$BSammelt all diese Gegenstände und kehrt dann zu Dirge zurück.",
    ["D"] = "Schon mal von einem Muschelette gehört? Natürlich nicht! Das ist mein neuestes kulinarisches Meisterwerk. Oh, $n, mir läuft das Wasser im Mund zusammen, wenn ich nur an ein Muschelette denke.$B$BLeider habe ich nichts mehr hier, um eins zu machen! Wie wär\'s mit einem Handel? Ihr besorgt die Zutaten für ein Muschelette und ich bring Euch die Feinheiten der exotischen Küche bei und leg\' sogar noch ein paar Muschelettes drauf! Abgemacht?$B$BUnd das brauche ich dafür:",
  },
  [6611] = {
    ["T"] = "Ab mit Euch nach Gadgetzan!",
    ["O"] = "Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere.",
    ["D"] = "Erwartet Ihr etwa, dass Zamja Euch noch irgendwas beibringt? Ihr kennt schon alle von Zamjas Küchentricks und verlangt immer noch nach mehr!$B$BZamja kann nicht helfen. $gFetter:Fette:r; $R muss nach Gadgetzan gehen. Dort findet Ihr einen kleinen Goblin namens Dirge - er wird Euch helfen, Dickerchen.$B$BWenn Ihr dann ein richtiger Sternekoch seid, kommt Ihr zurück und gebt Zamja Unterricht, okay? Und jetzt geht!",
  },
  [6612] = {
    ["T"] = "Ich kenne einen Typen...",
    ["O"] = "Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere.",
    ["D"] = "Das seh ich doch an Eurem Kochlöffel, dass Ihr wisst, was Ihr tut. Leider kann ich Euch nichts beibringen.$B$BGlück für Euch, ich kenn da einen Typen. Er ist ein Goblin und wahrscheinlich der beste Koch von ganz Kalimdor.$B$BHajo, und jetzt die schlechte Nachricht, $gJungchen:Mädel; - Ihr müsst nach Kalimdor reisen. Geht nach Gadgetzan und sucht nach Dirge Schnetzelhack. Er wird\'s Euch beibiegen.",
  },
  [6621] = {
    ["T"] = "König der Fäulnisklauen",
    ["O"] = "Platziert Senanis Banner auf dem Totemhügel der Fäulnisklauen. Die Furbolgs dürfen das Banner nicht zerstören. Besiegt Häuptling Murgut und bringt Murguts Totem zu Senani in der Silberwindzuflucht.",
    ["D"] = "Meine Forschungen haben sich bezahlt gemacht.$b$bIch glaube, dass eine Machtdemonstration die Angriffslust der Furbolgs verringern wird.$B$BNehmt dieses Banner, geht ein paar Schritte in Richtung Nordosten und platziert es auf ihrem Totemhügel. Sie werden versuchen, das Banner zu zerstören, aber Ihr müsst Eure Stellung halten, bis Häuptling Murgut auftaucht, um das Gesicht seines Stammes zu wahren. Bringt mir das Totem, das er trägt.$B$BVersammelt Eure Verbündeten, $n. Dies ist keine Aufgabe, die $gein einzelner:eine einzelne:c; $C bewältigen kann.",
  },
  [6622] = {
    ["T"] = "Triage",
    ["O"] = "Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.$B$BDenkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt.",
    ["D"] = "Guten Tag, Doktor. Heute Nachmittag werden wir Eure Fähigkeit auf die Probe stellen, Patienten auszuwählen. Solltet Ihr das Examen bestehen, gehört Ihr zu den angesehenen Traumachirurgen der Horde.$B$BUnd jetzt passt gut auf! Die Schwerstverletzten müssen zuerst versorgt werden. Nach den Schwerstverletzten kommen die Schwerverletzten. Die Leichtverletzten kommen zuletzt an die Reihe. Ihr müsst 15 Patienten retten, um dieses Examen zu bestehen. Ihr fallt durch, wenn 6 Eurer Patienten durch Eure falsche Wahl sterben.$B$BVerbände werden gestellt.$B$BViel Glück, Doktor.",
  },
  [6623] = {
    ["T"] = "Traumachirurg der Horde",
    ["O"] = "Reist nach Hammerfall im Arathihochland und sprecht mit Doktor Gregory Victor darüber, dass Ihr auch ein Traumachirurg der Horde werden wollt.",
    ["D"] = "Doktor $n, nehme ich an. Es ist mir eine Freude, Euch wiederzusehen.$B$BIch habe wunderbare Neuigkeiten für Euch! Doktor Gregory Victor, Chefarzt der Traumachirurgen der Horde, hat Euch persönlich angefordert, damit Ihr Euch ihm in Hammerfall anschließt. Das könnte eine einmalige Gelegenheit sein! Ich würde alles darum geben, bei dem großen Doktor Gregory Victor studieren zu dürfen! Diese Gelegenheit dürft Ihr Euch nicht entgehen lassen!",
  },
  [6624] = {
    ["T"] = "Triage",
    ["O"] = "Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.$B$BDenkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt.",
    ["D"] = "Guten Tag, Doktor. Heute Nachmittag werden wir Eure Fähigkeit auf die Probe stellen, Patienten auszuwählen. Solltet Ihr das Examen bestehen, gehört Ihr zu den angesehenen Traumachirurgen der Allianz.$B$BUnd jetzt passt gut auf! Die Schwerstverletzten müssen zuerst versorgt werden. Nach den Schwerstverletzten kommen die Schwerverletzten. Die Leichtverletzten kommen zuletzt an die Reihe. Ihr müsst 15 Patienten retten, um dieses Examen zu bestehen. Ihr fallt durch, wenn 6 Eurer Patienten durch Eure falsche Wahl sterben.$B$BViel Glück, Doktor.",
  },
  [6625] = {
    ["T"] = "Traumachirurg der Allianz",
    ["O"] = "Reist nach Theramore in die Düstermarschen und sprecht mit Doktor Gustav van Howzen darüber, dass Ihr auch ein Traumachirurg der Allianz werden wollt.",
    ["D"] = "Doktor $n, nehme ich an. Es ist mir eine Freude, Euch zu sehen.$B$BIch habe wunderbare Neuigkeiten für Euch! Gustav van Howzen, Chefarzt der Traumachirurgen der Allianz, hat Euch persönlich angefordert, damit Ihr Euch ihm in Theramore anschließt. Das könnte eine einmalige Gelegenheit sein! Ich würde alles darum geben, bei dem großen Doktor Gustav van Howzen studieren zu dürfen! Diese Gelegenheit dürft Ihr Euch nicht entgehen lassen, Doktor!",
  },
  [6626] = {
    ["T"] = "Ein Hort des Bösen",
    ["O"] = "Tötet 8 Schlachtwachen der Klingenhauer, 8 Dornenwirker der Klingenhauer und 8 Kultistinnen der Totenköpfe und kehrt dann zu Myriam Mondsang nahe dem Eingang zu den Hügeln der Klingenhauer zurück.",
    ["D"] = "Die Verlassenen geben ihre... Krankheit... nicht weiter, soweit ich das sehe. Dieser Diplomat scheint Untote als Möchtegernanhänger zu betrachten. Ich glaube langsam, dass er der Geißel angehört.$B$BUm ihn mache ich mir allerdings momentan keine Gedanken, sondern um die wachsende Schar um ihn herum. Tag für Tag kriechen sie aus den Schatten und betteln ihn nahezu um mehr Macht an.$B$BIch bin nicht stark genug, um sie allein zu bekämpfen, $n. Was meint Ihr? Hättet Ihr Lust, auf die Jagd zu gehen?",
  },
  [6627] = {
    ["T"] = "Test der Lehre",
    ["O"] = "Beantwortet Braug Dämmergeists Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch im Steinkrallengebirge sein, wenn Ihr bereit seid.",
    ["D"] = "Ihr habt mir das Buch gebracht, und jetzt ist es an der Zeit für Eure Befragung. Seid Ihr bereit?$B$BZeit spielt keine Rolle, $C, also fühlt Euch nicht unter Druck gesetzt. Ihr könnt Euch mit der Antwort so viel Zeit lassen, wie Ihr möchtet.",
  },
  [6628] = {
    ["T"] = "Test der Lehre",
    ["O"] = "Beantwortet Parqual Fintallas\' Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch in Unterstadt sein, wenn Ihr bereit seid.",
    ["D"] = "Die Zeit ist also wieder einmal gegen uns. Für einen Studenten der Lehre habt Ihr große Hartnäckigkeit bewiesen, indem Ihr meine Aufgabe erledigt habt.$B$BUnd deshalb werde ich Euch eine Frage anbieten. Wenn Ihr bereit seid, sprecht mich wieder an, und lasst Euch so viel Zeit, wie Ihr braucht, $n - es besteht keine Notwendigkeit, schnell zu antworten.",
  },
  [6629] = {
    ["T"] = "Tötet Grundig Finsterwolke",
    ["O"] = "Tötet Grundig Finsterwolke und 6 Schläger der Grimmtotem und kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.",
    ["D"] = "$n, Ihr habt gute Arbeit geleistet, indem Ihr Grimmtotem tötetet. Grundig Finsterwolke und seine persönliche Schlägerbande sind bei weitem die Schlimmsten von allen, wenn Ihr Euch traut. Er war einer derjenigen, die den brutalen Angriff auf mein Dorf anführten.$b$bIhr findet ihn im Grimmtotemposten ein Stück weiter den Weg nach Westen hinauf. Tötet ihn, dann werde ich Euch immer dankbar sein.",
  },
  [6641] = {
    ["T"] = "Vorsha die Peitscherin",
    ["O"] = "Bezwingt Vorsha die Peitscherin.",
    ["D"] = "Vorsha die Peitscherin... die satanische Hydra, die die Meere durchstreift...$B$BSie hat unseren Außenposten angegriffen, völlig ohne Grund, und dabei alles verheert, was auf ihrem Weg lag.$B$BAber ich bin bereit, $n. Ich bin bereit, die Hydra zu besiegen und den Angriffen auf Zoram\'gar ein Ende zu setzen.$B$BDie Naga verehren die tückische Natur dieser Bestie mit einer Kohlenpfanne, die auf einer Insel gleich vor der Küste immer am Brennen gehalten wird. Ich denke, dass Vorsha erzürnt auftauchen wird, wenn wir sie löschen.$B$BWir werden jedoch Hilfe brauchen, also solltet Ihr ein paar starke Verbündete mitbringen.",
  },
