  [6661] = {
    ["T"] = "Tiefenbahn-Ratten-Razzia",
    ["O"] = "Fangt mithilfe der Rattenfängerflöte 5 Untergrundratten in der Tiefenbahn. Führt die Ratten zu Monty.",
    ["D"] = "Haltet ein, Taugenichts. Arrrr... Sucht Ihr Arbeit? Wir ha\'m hier ein echtes Rattenproblem und nicht genügend Leute auf dem Hüttendeck.$B$BWas sagt Ihr? Ihr müsst nur diese Rattenfängerflöte nehmen und dem Ungeziefer ein Liedchen vorspielen. Die folgen Euch dann bis ans Ende der Welt!$B$BFangt mir fünf von den kleinen Mistviechern und bringt sie mir. Ich brauch sie lebendig.$B$BArrrrr...",
  },
  [6662] = {
    ["T"] = "Mein Bruder Nipsy...",
    ["O"] = "Bringt den Karton mit eigenartigem Fleisch zu Nipsy im Tiefenbahndepot von Sturmwind.",
    ["D"] = "Mein Bruder Nipsy hat \'n Rattengeschäft auf der anderen Seite der Tiefenbahn. Ihr müsst die Kiste mit Ratten hier zu ihm bringen, bevor sie schlecht werden. Zackig!$B$BIhr seid doch wohl schon mal mit \'ner Bahn gefahren, oder? Ganz einfach. Steigt einfach ein, wenn sie kommt, und genießt die Fahrt. Und haltet Eure Arme und Beine während der Fahrt drinnen, wenn Ihr keine Holzgräten haben wollt.",
  },
  [6681] = {
    ["T"] = "Das Anwesen, Rabenholdt",
    ["O"] = "Bringt das Siegel von Rabenholdt zu Fahrad im Rabenholdtanwesen, das versteckt in den Hügeln des Hügellands liegt.$B$BUnterwegs wird man Eure Schlauheit testen. Seid darauf gefasst.",
    ["D"] = "$n,$B$BRabenholdt interessiert sich für Euch, junger Dieb. Wir haben Euch beobachtet. Euer Stil ist ein bisschen unbeholfen, aber daran können wir arbeiten.$B$BWenn Ihr bereit seid, eine Karriere als Schüler von Meister Rabenholdt in Angriff zu nehmen und Mitglied der Auftragsmörderliga zu werden, müsst Ihr nur das Siegel von Rabenholdt (diesem Brief beigefügt) zum Rabenholdtanwesen bringen.$B$BEure erste Aufgabe ist es, das Anwesen zu finden, das versteckt in den Hügeln des Hügellands liegt, um Eure Schlauheit zu demonstrieren.$B$BHochachtungsvoll,$B$BFahrad",
  },
