  [6761] = {
    ["T"] = "Die neue Grenze",
    ["O"] = "Sprecht mit Mathrengyl Bärenfährte in der Enklave des Cenarius von Darnassus.",
    ["D"] = "Ich habe meinen Stellvertreter Mathrengyl Bärenfährte beauftragt, sich um die Bitte um Unterstützung der Mondlichtung zu kümmern. Sprecht mit ihm über diese Angelegenheit - NICHT mit mir.$B$BLasst mich Euch einen... gut gemeinten... Rat geben. ICH habe uns aus der Dunkelheit geführt - NICHT der Zirkel des Cenarius auf der Mondlichtung -, als für unser Volk alles verloren war. Teldrassil gibt uns allen neues Leben! Sie wollen Euch anderes glauben machen, und ihre Propaganda zu glauben, wäre reine Blauäugigkeit.$B$BIch werde es Euch überlassen, ob Ihr ein Narr sein wollt oder nicht.",
  },
  [6762] = {
    ["T"] = "Rabine Saturna",
    ["O"] = "Sprecht mit Rabine Saturna im Dorf Nachthafen auf der Mondlichtung. Die Mondlichtung liegt zwischen dem Teufelswald und Winterquell, und Ihr erreicht es über den Pfad, der aus der Holzschlundfeste herausführt.",
    ["D"] = "Für diese Aufgabe sucht Majordomus Rabine Saturna für Bewahrer Remulos bei der Mondlichtung Unterstützung bei der Erforschung des riesigen Ödlands weit im Westen von Tanaris... und noch weiter im Westen von Un\'Goro. Ihr solltet ihn in Nachthafen finden, dem Hauptort der Mondlichtung.$B$BEure Arbeit wird zwar zweifellos sehr vorteilhaft für uns sein, dennoch rate ich Euch, Vorsicht walten zu lassen. Es gibt Spannungen zwischen Darnassus und der Mondlichtung... Spannungen, die im Laufe der Zeit wachsen könnten.",
  },
