  [6844] = {
    ["T"] = "Umber, Archivar",
    ["O"] = "Bringt das verkrustete Silithidenobjekt zu Umber, dem Archivar des Zirkels des Cenarius, ins Dorf Nachthafen auf der Mondlichtung.",
    ["D"] = "Hier gibt es immer noch viel zu tun, aber ich denke, es ist wichtig, dies zur Analyse zum Zirkel zurückzubringen. Vertraut mir - es wird immer noch Arbeit geben, wenn das alles erledigt ist. Wenn sich in diesem klebrigen Chitin ein Tagebuch befindet, hätte der Zirkel endlich ein Fenster zur Vergangenheit geöffnet!$B$BBringt dies zum Archivar des Zirkels in Nachthafen. Sein Name ist Umber und er ist brillant. Er ist etwas eigenartig, sicher, aber trotzdem brillant! Umber hat schon Folianten gerettet, die in wesentlich schlechterem Zustand waren als dieser hier. Wenn dieser gerettet werden kann, dann schafft er es.",
  },
  [6845] = {
    ["T"] = "Enthüllung von Geheimnissen der Vergangenheit",
    ["O"] = "Sprecht mit Rabine Saturna im Dorf Nachthafen auf der Mondlichtung.",
    ["D"] = "Tja, Ihr solltet möglichst bald wieder zu mir kommen... falls Ihr wissen wollt, was sich hier drinnen befand. Sobald ich herausfinde, was es ist, und falls es in der Tat ein Buch ist, werde ich diese Informationen gern mit Euch teilen.$B$BIn der Zwischenzeit solltet Ihr Euch an die Höhlenmutter dieses kleinen Bärenlagers wenden, Rabine. Es ist bestimmt für alle interessant zu wissen, was für verrückte Dinge Ihr so erlebt habt. Lasst die Höhlenmutter niemals warten, $n... zumindest nicht, wenn Ihr nicht in Honig getaucht werden wollt.$B$BDas ist schon passiert, glaubt mir.",
  },
  [6846] = {
