  [6847] = {
    ["T"] = "Meister Rysons Allsehendes Auge",
    ["O"] = "Holt das allsehende Auge von Meister Ryson aus den Höhlen der Winterax und kehrt damit zu Meister Ryson zurück.$B$BSeid gewarnt, man sagt, dass das allsehende Auge von Meister Ryson einen schrecklicken Fluch mit sich brächte!",
    ["D"] = "Euer Kampf im Herzen von Alterac bedeutet dem Syndikat nichts, $C. Wir werden so handeln, wie wir es immer getan haben: Warten, bis sich ein klarer Sieger aus der Asche und dem Geröll des Wahnsinns erhebt, und dann zuschlagen.$B$BAber darum sind wir nicht hier. Das allsehende Auge ging verloren - es wurde von den Winteraxtrollen der Region gestohlen.$B$BBeschafft das Auge wieder und unsere Dienste werden Euch gehören - für die Zeit, die Eurer Seite noch verbleibt.$B$BKehrt zu mir zurück, wenn Ihr das Auge beschafft habt, damit ich es untersuchen kann.$B$B<Meister Ryson grinst.>",
  },
  [6848] = {
    ["T"] = "Meister Rysons Allsehendes Auge",
    ["O"] = "Holt das allsehende Auge von Meister Ryson aus den Höhlen der Winterax und kehrt damit zu Meister Ryson zurück.$B$BSeid gewarnt, man sagt, dass das allsehende Auge von Meister Ryson einen schrecklicken Fluch mit sich brächte!",
    ["D"] = "Euer Kampf im Herzen von Alterac bedeutet dem Syndikat nichts, $C. Wir werden so handeln, wie wir es immer getan haben: Warten, bis sich ein klarer Sieger aus der Asche und dem Geröll des Wahnsinns erhebt, und dann zuschlagen.$B$BAber darum sind wir nicht hier. Das allsehende Auge ging verloren - es wurde von den Winteraxtrollen der Region gestohlen.$B$BBeschafft das Auge wieder und unsere Dienste werden Euch gehören - für die Zeit, die Eurer Seite noch verbleibt.$B$BKehrt zu mir zurück, wenn Ihr das Auge beschafft habt, damit ich es untersuchen kann.$B$B<Meister Ryson grinst.>",
  },
  [6861] = {
    ["T"] = "Zinnzischelix\'s tragbare Schreddereinheit",
    ["O"] = "Meisteringenieur Zinnzischelix möchte, dass Ihr ihm Folgendes bringt:$B$B*30 Thoriumbarren.$B$B*50 Mithrilbarren.$B$B*75 Eisenbarren.$B$B*1 Dampfsäge.",
    ["D"] = "Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, $R, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!$B$BUm genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.$B$BAls ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.",
  },
  [6862] = {
    ["T"] = "Zinnzischelix\'s tragbare Schreddereinheit",
    ["O"] = "Meisteringenieur Zinnzischelix möchte, dass Ihr ihm Folgendes bringt:$B$B*30 Thoriumbarren.$B$B*50 Mithrilbarren.$B$B*75 Eisenbarren.$B$B*1 Dampfsäge.",
    ["D"] = "Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, $R, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!$B$BUm genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.$B$BAls ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Frostwölfe aufgefallen.",
  },
