  [6961] = {
    ["T"] = "Altvater Winter ist hier!",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.",
    ["D"] = "Ich wünsche ein wunderbares Winterhauchfest! Vergesst nicht, Altvater Winter zu begrüßen! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!$B$BAchtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!",
  },
  [6962] = {
    ["T"] = "Leckereien für Altvater Winter",
    ["O"] = "Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Orgrimmar.",
    ["D"] = "He, könnt Ihr Altvater Winter einen Gefallen tun, ähm, $gKleiner :Kleines;?$B$BIch will ja nicht überflüssigerweise über mich selbst in der dritten Person sprechen, aber Altvater Winter arbeitet schon den ganzen Tag hier und könnte selbst eine kleine Leckerei brauchen. Ein paar Lebkuchen und etwas eiskalte Milch, um sie runterzuspülen, wären genau das Richtige. Glaubt Ihr, Ihr könntet dem Altvater eine Freude machen und das für mich besorgen?",
  },
  [6963] = {
    ["T"] = "Gestohlene Winterhauchleckereien",
    ["O"] = "Sucht den Ermittler von Kokelwälder, der in den verschneiten Regionen des Vorgebirges des Hügellands vermisst wird.",
    ["D"] = "Hört zu... ich will Euch ja den Zauber der Feiertage nicht verderben, aber Kokelwälder könnte etwas Hilfe brauchen... von der Art, für die Ihr Abenteurer bekannt seid.$B$BUns ist eine wichtige Lieferung mit Leckereien abhandengekommen, mit der wir die Vorräte für diese Feiertage aufstocken wollten, und jetzt ist auch noch der Agent von Kokelwälder verschwunden, den wir losgeschickt haben, um sie zu suchen! Also - die letzten Informationen über die Lieferung stammen aus dem Alteracgebirge, das an das Vorgebirge des Hügellands angrenzt und dort wollte unser Mann danach suchen.$B$BSeht nach, ob sowohl Mann als auch Material dort zu finden sind, und zwar gesund und munter.",
  },
  [6964] = {
    ["T"] = "Der Anlass für die Feiertage",
    ["O"] = "Sprecht mit Sagorne Gratläufer im Tal der Weisheit von Orgrimmar über das Winterhauchfest.",
    ["D"] = "Gaben, Leckereien, Geschenke, pah!$B$BDiese Goblins beschmutzen die wahre Bedeutung dieser Feiertage - des Winterhauchfests. Dies ist eine Zeit der Erneuerung. Das Land schlummert unter einer Schneedecke, die Altvater Winter gebracht hat, und das ist NICHT der Wichtigtuer da drüben in dem roten Aufzug!$B$BFalls Ihr erfahren wollt, worum es bei dem Fest wirklich geht, dann fragt Sagorne Gratläufer. Es dürfte ihn bestimmt überraschen, dass sich jemand für Geschichte statt für Geschenke interessiert.",
  },
  [6981] = {
    ["T"] = "Der leuchtende Splitter",
    ["O"] = "Begebt Euch nach Ratschet, um jemanden zu finden, der Euch mehr über den leuchtenden Splitter sagen kann.$B$BLiefert dann den Splitter ab, wie Ihr angewiesen wurdet.",
    ["D"] = "Der Splitter ist von einem glänzenden Grün, und obwohl er massiv ist, scheint etwas wie Rauch knapp unterhalb der Oberfläche herumzuwirbeln. Ihr fühlt den Zwang, in die Tiefen des Kristalls zu schauen, aber gleichzeitig fühlt Ihr auch, dass etwas sehr Beunruhigendes von ihm ausgeht.$B$BVielleicht solltet Ihr in Ratschet nach jemandem suchen, der Euch mehr über diesen seltsamen Splitter erzählen kann.",
  },
  [6982] = {
    ["T"] = "Vorräte der Eisbeißermine",
    ["O"] = "Bringt 10 Vorräte der Eisbeißermine zum Rüstmeister der Allianz in Dun Baldar.",
    ["D"] = "Die Eisbeißermine ist ein Vorratslager für die Horde. Ich vertrage das Essen nicht, das sie ihren Truppen reichen und der Zustand ihrer Waffen und Rüstungen ist grauenhaft! Aber... in Zeiten wie diesen, in denen Vorräte so wichtig sind, können wir wirklich alles verwenden, was sie haben. $B$BBetretet die Eisbeißermine und bringt mir ihre Vorräte. Dies sollte der Allianz helfen sich weiterhin gut im Kampf zu bewähren und die Soldaten der Horde schwächen.$B$BViel Erfolg, $n. Die Mine ist im entfernten Süden, tief im Territorium der Horde.",
  },
  [6983] = {
    ["T"] = "So was Gemeines...",
    ["O"] = "Sucht die gestohlenen Leckereien und befreit Metzen das Rentier, nachdem Ihr den monströsen Griesgram besiegt habt. Er verbirgt sich nahe der Eisfellhöhle im Vorgebirge des Hügellandes.",
    ["D"] = "Eine Kreatur namens \"Der monströse Griesgram\" hat unsere Lieferung mit Leckereien gestohlen, $n. Seid aber vorsichtig! Er ist bösartig und befiehlt über ein paar dieser kleinen grünen Gnome!$B$BIhr werdet Hilfe brauchen, wenn Ihr ihm gegenübertretet. Ich habe gehört, dass er unsere Leckereien und Metzen, unser geliebtes Rentier, in die Eisfellhöhle im Vorgebirge des Hügellandes gebracht hat. Findet die Leckereien, $n, und bringt sie zurück nach Orgrimmar, bevor das Fest ruiniert ist und all die Kinder enttäuscht werden!",
  },
  [6984] = {
    ["T"] = "Ein Dankeschön von Kokelwälder!",
    ["O"] = "Sprecht mit Altvater Winter in Orgrimmar.",
    ["D"] = "Wir von Kokelwälder sind dankbar für die Wiederbeschaffung der gestohlenen Leckereien, $n. Dafür möchten wir Euch ein besonderes Geschenk anbieten... präsentiert von niemand Geringerem als Altvater Winter persönlich!$B$BBitte, sprecht mit Altvater Winter und er wird Euch Euer Geschenk zum Winterhauchfest von uns hier bei Kokelwälder überreichen. Frisch von unserem Hof auf Euren Tisch - beste Kokelwälder Vollwertkost... dank Euch natürlich!",
  },
  [6985] = {
    ["T"] = "Vorräte der Eisenschachtmine",
    ["O"] = "Bringt 10 Vorräte der Eisenschachtmine zum Rüstmeister der Horde in die Burg Frostwolf.",
    ["D"] = "Die Zwerge der Sturmlanzen benutzen die Eisenschachtmine als ein Vorratslager. Ich möchte, dass Ihr diese Mine überfallt und mir ihre Vorräte bringt. Dies sollte die Bemühungen der Sturmlanzen im Alteractal behindern und uns mit der nötigen Ausrüstung versorgen!$B$BSeid vorsichtig, $n. Die Eisenschachtmine liegt tief im Territorium der Allianz, und die Kontrolle über die Mine ändert sich so oft wie neuer Schnee in Alterac fällt.",
  },
  [7001] = {
    ["T"] = "Verwaiste Ställe",
    ["O"] = "Findet einen Frostwolf im Alteractal. Wenn Ihr Euch in der Nähe eines Frostwolfs befindet, benutzt den Frostwolfmaulkorb um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Frostwolf bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden.",
    ["D"] = "Der Krieg hier im Tal hat viele Opfer gefordert. Unsere Streitkräfte müssen dringend wieder aufgebaut werden!$B$BWenn wir es schaffen unsere Ställe wieder zu füllen, können unsere glorreichen Wolfsreiter wieder in die Schlacht reiten.$B$BNehmt diesen Maulkorb und begebt Euch in die nahegelegenen Wälder. Sucht in der Umgebung nach herumstreifenden Frostwölfen und fangt sie mit dem Maulkorb ein. Wenn Ihr einen Wolf gezähmt habt, bringt ihn zu mir und ich werde ihn in unseren Ställen unterbringen.$B$BSobald wir wieder über ausreichend Wölfe verfügen, werden die Wolfsreiter ihre Frontpatrouillen fortsetzen.",
  },
  [7002] = {
    ["T"] = "Widderledernes Zaumzeug",
    ["O"] = "null",
    ["D"] = "While some of our soldiers are busy capturing wolves for the stable master, others must provide for the simple necessities that riding requires. I am speaking of riding harnesses, of course.$B$BYou must strike at the indigenous rams of the region. The very same rams that the Stormpike cavalry uses as mounts!$B$BSlay them and return to me with their hides. Once we have gathered enough hides, we will fashion harnesses for the riders. The Frostwolf Wolf Riders will ride once more!",
  },
  [7003] = {
    ["T"] = "Geschrumpfte Riesen",
    ["O"] = "Benutzt Zorbins Ultraschrumpfer um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt Zorbin von Schiller 15 Schrumpfüberreste von diesen Riesen. Ihr findet ihn bei den Docks, an der Vergessenen Küste in Feralas.",
    ["D"] = "Ich muss etwas gegen die Riesen unternehmen, wenn ich hier draußen bleibe! Sie sind sicherlich nicht gut fürs Geschäft, oder nicht?$B$BIch habe mich an einer Variante von Gnomischen Schrumpfstrahlen versucht, benutze jetzt aber überlegene GOBLIN Technologien für die Herstellung! Im Zuge von Bemühungen, Unannehmlichkeiten wie meinen Tod beispielsweise zu vermeiden, möchte ich Euch bitten es einmal an diesen Riesen in Feralas zu testen. Ihr solltet nach dem Prozess Schrumpfüberreste auf ihnen finden, bringt mir bitte etwas davon!",
  },
  [7021] = {
    ["T"] = "Altvater Winter ist hier!",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.",
    ["D"] = "Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Orgrimmar befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!$B$BAchtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!",
  },
  [7022] = {
    ["T"] = "Altvater Winter ist hier!",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Eisenschmiede.",
    ["D"] = "Ich wünsche ein wunderbares Winterhauchfest! Vergesst nicht, Altvater Winter zu begrüßen! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!$B$BAchtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!",
  },
  [7023] = {
    ["T"] = "Altvater Winter ist hier!",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Eisenschmiede.",
    ["D"] = "Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Eisenschmiede befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!$B$BAchtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!",
  },
  [7024] = {
    ["T"] = "Altvater Winter ist hier!",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.",
    ["D"] = "Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Orgrimmar befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!$B$BAchtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!",
  },
  [7025] = {
    ["T"] = "Leckereien für Altvater Winter",
    ["O"] = "Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Eisenschmiede.",
    ["D"] = "He, könnt Ihr Altvater Winter einen Gefallen tun, ähm, $gKleiner :Kleines;?$B$BIch will ja nicht überflüssigerweise über mich selbst in der dritten Person sprechen, aber Altvater Winter arbeitet schon den ganzen Tag hier und könnte selbst eine kleine Leckerei brauchen. Ein paar Lebkuchen und etwas eiskalte Milch, um sie runterzuspülen, wären genau das Richtige. Glaubt Ihr, Ihr könntet dem Altvater eine Freunde machen und das für mich besorgen?",
  },
  [7026] = {
    ["T"] = "Widderzaumzeug",
    ["O"] = "null",
    ["D"] = "",
  },
  [7027] = {
    ["T"] = "Verwaiste Ställe",
    ["O"] = "Findet einen Alteracwidder im Alteractal. Wenn Ihr Euch in der Nähe eines Alteracwidders befindet, benutzt das Zähmungshalsband der Sturmlanzen um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Alteracwidder bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden.",
    ["D"] = "Seht Euch nur unsere Ställe an, $n! Völlig leer! Die Sturmlanzengarde hat keine Widder mehr, die sie reiten könnte, die Kavallerie ist praktisch nicht mehr existent.$B$BIhr müsst uns helfen, Soldat. Nehmt dieses Zähmungshalsband und begebt Euch in die Wildnis dieses Tals. Sucht in der Umgebung nach herumstreifenden Alteracwiddern und fangt sie mit dem Zähmungshalsband ein. Wenn Ihr einen Widder eingefangen habt, bringt ihn zu mir und ich werde ihn in unseren Ställen unterbringen. Sobald wir wieder über ausreichend Widder verfügen, werde ich den Kommandanten der Widderreiter informieren.$B$BMit Eurer Hilfe wird die Kavallerie neu entstehen!",
  },
  [7028] = {
    ["T"] = "Dunkles Böses",
    ["O"] = "Sammelt 15 theradrische Kristallschnitzereien für Trista in Desolace.",
    ["D"] = "Die Kristallhöhlen von Terramok liegen im Westen, tief in Maraudon... Das ist ein uralter Ort mit einer langen Geschichte. Und ich will einiges davon in die Hände bekommen, $C. Relikte der Alten Götter liegen hinter den riesigen Türen verborgen.$B$BWenn Ihr bereit seid, dem dunklen Bösen zu trotzen, das sich in Maraudon findet, wird es Euer Schade bestimmt nicht sein. Sammelt die theradrischen Kristallschnitzereien ein, die Ihr aufstöbern könnt, und bringt sie mir.$B$BSobald ich die Schnitzereien habe, kann ich sie... studieren. Diese Mächte haben schon zu lange geruht...",
  },
  [7029] = {
    ["T"] = "Schlangenzunges Verderbnis",
    ["O"] = "Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.$B$BBenutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.$B$BHeilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Vark Schlachtnarbe in Schattenflucht zurück.",
    ["D"] = "Ein Satyr, der unter dem Namen Schlangenzunge bekannt ist, hat die Höhlen von Maraudon verderbt und so das Wachstum vieler Pflanzen im Inneren behindert.$B$BNehmt diese Phiole, $n. Füllt sie am orangefarbenen Kristallteich, den Ihr in den Höhlen findet. Die Beschichtung in der Phiole wird den Toxinen entgegenwirken und sie in einen Heilstoff verwandeln.$B$BSucht die Schlangenstrunkranken in den orangefarbenen Höhlen und wendet die geläuterte Flüssigkeit auf sie an. Die Pflanze wird damit von der Verderbnis gereinigt, doch Ihr müsst wissen, dass Schlangenzunge sie immer noch kontrolliert. Er kann sie gegen Euch einsetzen...",
  },
  [7041] = {
    ["T"] = "Schlangenzunges Verderbnis",
    ["O"] = "Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.$B$BBenutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.$B$BHeilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Talendria an der Nijelspitze zurück.",
    ["D"] = "Ein Satyr, der unter dem Namen Schlangenzunge bekannt ist, hat die Höhlen von Maraudon verderbt und so das Wachstum vieler Pflanzen im Inneren behindert.$B$BNehmt diese Phiole, $n. Füllt sie am verderbten orangefarbenen Kristallteich, den Ihr in den Höhlen findet. Die Beschichtung in der Phiole wird den Toxinen entgegenwirken und sie in einen Heilstoff verwandeln.$B$BSucht die Schlangenstrunkranken in den orangefarbenen Höhlen und wendet die geläuterte Flüssigkeit auf sie an. Die Pflanze wird damit von der Verderbnis gereinigt, doch Ihr müsst wissen, dass Schlangenzunge sie immer noch kontrolliert. Er kann sie gegen Euch einsetzen...",
  },
  [7042] = {
    ["T"] = "Gestohlene Winterhauchleckereien",
    ["O"] = "Sucht den Ermittler von Kokelwälder, der in den verschneiten Regionen des Vorgebirges des Hügellands vermisst wird.",
    ["D"] = "Hört zu... ich will Euch ja den Zauber der Feiertage nicht verderben, aber Kokelwälder könnte etwas Hilfe brauchen - von der Art, für die Ihr Abenteurer bekannt seid.$B$BUns ist eine wichtige Lieferung mit Leckereien abhandengekommen, mit der wir die Vorräte für diese Feiertage aufstocken wollten, und jetzt ist auch noch der Agent von Kokelwälder verschwunden, den wir losgeschickt haben, um sie zu suchen! Also - die letzten Informationen über die Lieferung stammen aus dem Alteracgebirge, das an das Vorgebirge des Hügellands angrenzt und dort wollte unser Mann danach suchen.$B$BSeht nach, ob sowohl Mann als auch Material dort zu finden sind, und zwar gesund und munter.",
  },
  [7043] = {
    ["T"] = "So was Gemeines...",
    ["O"] = "Sucht die gestohlenen Leckereien und befreit Metzen das Rentier, nachdem Ihr den monströsen Griesgram besiegt habt. Er verbirgt sich nahe der Eisfellhöhle im Vorgebirge des Hügellandes.",
    ["D"] = "Eine Kreatur namens \"Der monströse Griesgram\" hat unsere Lieferung mit Leckereien gestohlen, $n. Seid aber vorsichtig! Er ist bösartig und befiehlt über ein paar dieser kleinen grünen Gnome!$B$BIhr werdet Hilfe brauchen, wenn Ihr ihm gegenübertretet. Ich habe gehört, dass er unsere Leckereien und Metzen, unser geliebtes Rentier, in die Eisfellhöhle im Vorgebirge des Hügellandes gebracht hat. Findet die Leckereien, $n, und bringt sie zurück, bevor das Fest ruiniert ist und all die Kinder von Sturmwind enttäuscht werden!",
  },
  [7044] = {
    ["T"] = "Legenden von Maraudon",
    ["O"] = "Beschafft die beiden Teile des Szepters von Celebras: den Celebriangriff und den Celebriandiamanten.$B$BFindet einen Weg, um mit Celebras zu sprechen.",
    ["D"] = "Ein dunkler Satyr namens Lord Schlangenzunge hat sein Böses in diesen verschlungenen Höhlen verbreitet und die Gedanken aller im Inneren vergiftet. Er residiert noch jenseits der lila Kristalle.$B$BSchlangenzunge hat des Weiteren ein lebendes Symbol seiner Verderbnis namens Noxxion geschaffen, das sich hinter den orangefarbenen Kristallen befindet. Zusammen haben sie die beiden Teile des Szepters meines Bruders gestohlen. Celebras, mein Bruder... wandert blind im Inneren umher, verflucht durch die Verderbnis.$B$BIhr müsst ihm helfen! Findet die Stücke und sprecht mit meinem Bruder...irgendwie.",
  },
  [7045] = {
    ["T"] = "Ein Dankeschön von Kokelwälder!",
    ["O"] = "Sprecht mit Altvater Winter in Eisenschmiede.",
    ["D"] = "Wir von Kokelwälder sind dankbar für die Wiederbeschaffung der gestohlenen Leckereien, $n. Dafür möchten wir Euch ein besonderes Geschenk anbieten... präsentiert von niemand Geringerem als Altvater Winter persönlich!$B$BBitte, sprecht mit Altvater Winter und er wird Euch Euer Geschenk zum Winterhauchfest von uns hier bei Kokelwälder überreichen. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost... dank Euch natürlich!",
  },
  [7046] = {
    ["T"] = "Das Szepter von Celebras",
    ["O"] = "Helft Celebras dem Erlösten, während er das Szepter von Celebras herstellt.$B$BSprecht mit ihm, nachdem das Ritual vollendet ist.",
    ["D"] = "Mein Szepter war einst eine Quelle der Hoffnung für mich. Es gewährte mir einst die Macht über diese Höhlen. Ich hatte gehofft, dem Geist meines Onkels Frieden bringen zu können...$B$BDamit die beiden Teile wieder eins werden können, benötige ich Eure Hilfe bei der Durchführung des Rituals. Folgt mir bitte und hört auf meine Anweisungen, während ich die Energie kanalisiere, die erforderlich ist, um den Griff wieder mit dem Diamanten zu vereinigen.$B$BZusammen werden wir das Szepter von Celebras wieder erschaffen!",
  },
  [7061] = {
    ["T"] = "Das Winterhauchfest",
    ["O"] = "Ihr könnt gern das Buch \'Das Winterhauchfest\' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, bringt es zu Baine Bluthuf in Donnerfels.",
    ["D"] = "Hier - wenn Ihr daran interessiert seid, mehr über das Winterhauchfest zu erfahren, dann lest dieses Buch. Ich glaube zwar, dass unser Verständnis der Legende zutreffend ist, aber als jemand, der Legenden studiert, weiß ich durchaus eine Sammlung aller Informationen in einer einzelnen Quelle zu schätzen.$B$BWenn Ihr fertig seid, bringt das Buch zu Baine Bluthuf in Donnerfels. Ich weiß mit Bestimmtheit, dass es ihm nicht gleichgültig ist, wenn auch andere sich für die Lehre unseres Volkes interessieren.",
  },
  [7062] = {
    ["T"] = "Der Anlass für die Feiertage",
    ["O"] = "Sprecht mit dem Historiker Karnik in der Forscherliga von Eisenschmiede über das Winterhauchfest.",
    ["D"] = "Gaben, Leckereien, Geschenke, pah!$B$BDiese Goblins beschmutzen die wahre Bedeutung dieser Feiertage - des Winterhauchfests. Dies ist eine Zeit der Erneuerung. Das Land schlummert unter einer Schneedecke, die Altvater Winter gebracht hat, und das ist NICHT der Wichtigtuer da drüben in dem roten Aufzug!$B$BFalls Ihr erfahren wollt, worum es bei dem Fest wirklich geht, dann befragt den Historiker Karnik in der Forscherliga. Es dürfte ihn bestimmt überraschen, dass sich jemand für Geschichte statt für Geschenke interessiert.",
  },
  [7063] = {
    ["T"] = "Das Winterhauchfest",
    ["O"] = "Ihr könnt gern das Buch \'Das Winterhauchfest\' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, dann bringt es zu Muradin Bronzebart nach Eisenschmiede.",
    ["D"] = "Hier - wenn Ihr daran interessiert seid, mehr über das Winterhauchfest zu erfahren, dann lest dieses Buch. Ich glaube zwar, dass unser Verständnis der Legende zutreffend ist, aber als Gelehrter, der Legenden studiert, weiß ich durchaus eine Sammlung aller Informationen in einer einzelnen Quelle zu schätzen.$B$BWenn Ihr fertig seid, dann bringt das Buch zu Muradin Bronzebart. Ich weiß mit Bestimmtheit, dass es ihn sehr interessieren wird, wenn auch andere Interesse für die alten Legenden an den Tag legen.",
  },
  [7064] = {
    ["T"] = "Verderbnis von Erde und Samenkorn",
    ["O"] = "Tötet Prinzessin Theradras und kehrt zu Selendra in der Nähe von Schattenflucht in Desolace zurück.",
    ["D"] = "Tief in Maraudon lebt eine böse Kreatur des Chaos - Prinzessin Theradras ist eine Naturgewalt der Erde, die mit den Alten Göttern verwandt ist. Vor langer Zeit gingen sie und Zaetar, der erstgeborene Sohn von Cenarius, eine Beziehung ein. Aus ihrer gemeinsamen Zeit entsprangen Wesen, die unter den Menschen von Kalimdor als Zentauren bekannt wurden. Diese undankbaren Kreaturen töteten Zaetar und bewachen nun seine Überreste. Meine Aufgabe besteht darin, jemanden zu finden, der mächtig genug ist, um Theradras zu erschlagen, damit wir Zaetars Überreste holen können, bevor wir in das Steinkrallengebirge zurückkehren.",
  },
  [7065] = {
    ["T"] = "Verderbnis von Erde und Samenkorn",
    ["O"] = "Erschlagt Prinzessin Theradras und kehrt zum Bewahrer Marandis an der Nijelspitze in Desolace zurück.",
    ["D"] = "In den Tiefen von Maraudon lebt eine Kreatur des Chaos und des Bösen, $n. Prinzessin Theradras ist eine Naturgewalt der Erde, die mit den Alten Göttern verwandt ist. Vor langer Zeit stattete Zaetar, der erstgeborene Sohn von Cenarius, ihr einen Besuch ab. Die Nachkommen ihrer...Beziehung sind jene Kreaturen, die unter dem Namen Zentauren bekannt sind. Diese undankbaren Kreaturen töteten Zaetar und Theradras bewacht nun seine Überreste. Meine Aufgabe besteht darin, jemanden zu finden, der mächtig genug ist, um Theradras zu erschlagen, damit wir Zaetars Überreste holen können, bevor wir in das Steinkrallengebirge zurückkehren.",
  },
  [7066] = {
    ["T"] = "Samenkorn des Lebens",
    ["O"] = "Sucht Remulos bei der Mondlichtung auf und gebt ihm das Samenkorn des Lebens.",
    ["D"] = "Diese missratenen Zentauren - meine Söhne und Töchter - brauchen ihren Vater.$B$BSeht Euch um - dieser Ort hallt wider von Hoffnung und all jenem, was mein Vater Cenarius predigt. All das entsprang aus mir! Und ich kann ihnen das nicht nehmen... nicht einmal ob ihrer Verbrechen gegen mich.$B$BEines jedoch möchte ich von Euch erbitten, damit mein Bruder Ruhe findet. Nehmt dies... es ist das erste Samenkorn, das aus dem Leben entstand, das meine Überreste nährten. Sagt ihm, dass mein Geist hier bleibt und dass ich weiterlebe. Er wird es sicherlich verstehen.",
  },
  [7067] = {
    ["T"] = "Die Anweisungen des Pariahs",
    ["O"] = "Lest die Anweisungen des Pariahs. Beschafft Euch danach das Amulett der Vereinigung von Maraudon und bringt es dem Zentaurenpariah im südlichen Desolace.",
    ["D"] = "Obwohl ich als Ketzer verbannt wurde, bleibe ich meinem Glauben treu - das allein treibt mich voran. Aber damit mein Schicksal sich erfüllt, benötige ich die Hilfe eines Außenstehenden... jemanden, der keinem Zentaurenstamm angehört.$B$BFür diese Aufgabe muss jemand die heiligen Hallen von Maraudon betreten - das ist ein Tempel, den mein Volk schützt, und nur die Gläubigsten dürfen ihn betreten.$B$BWenn Ihr einwilligt, mir zu helfen, dann müsst Ihr nur nicken. Ich werde Euch ein Pergament mit meinen Anweisungen geben. Lest es durch und denkt über meine Worte nach.",
  },
  [7068] = {
    ["T"] = "Schattensplitter",
    ["O"] = "Sammelt 10 Schattensplitter aus Maraudon und bringt sie zu Uthel\'nay nach Orgrimmar.",
    ["D"] = "Man findet starke Magie am heiligen Ort der Zentauren... dem heiligen Ort von Maraudon. Die Desolace-Stämme der Zentauren schützen diesen Ort mit ihrem Leben, aber sie können uns nicht davon abhalten, das zu nehmen, was wir brauchen.$B$BBegebt Euch zu diesem Maraudon-Ort und sucht nach den Felskreaturen, die aus Schattenstein bestehen. Bringt mir die lila Kristalle und Uthel\'nay wird Euch Eure Mühe vergelten.$B$BDer Ort ist gefährlich, daher solltet Ihr einige Freunde mitnehmen.",
  },
  [7070] = {
    ["T"] = "Schattensplitter",
    ["O"] = "Sammelt 10 Schattensplitter in Maraudon und bringt sie zu Erzmagier Tervosh in den Düstermarschen.",
    ["D"] = "Ich habe kürzlich begonnen, verschiedene Kristalle und ihre arkanen Eigenschaften zu testen. Lady Prachtmeer hat mir die Erlaubnis erteilt, Reisende, die nach Desolace ziehen, zu bitten, einen Ort zu suchen, der Maraudon genannt wird. In dem zerfallenen Tempel dort gibt es große Elementare, die aus lila Steinen bestehen. Ich habe sie Schattensplitter getauft.$B$BFalls Ihr beschließt, diese Richtung einzuschlagen, und dort einige dieser Kristalle findet, wäre ich sehr dankbar. Ich würde Euch sogar ein Schmuckstück daraus herstellen, wenn Ihr dies wünscht.",
  },
  [7081] = {
    ["T"] = "Die Friedhöfe im Alteractal",
    ["O"] = "Erobert einen Friedhof und kehrt zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück.",
    ["D"] = "Die Friedhöfe im Alteractal sind äußerst wertvolle Gebiete. Beide Seiten, die Horde und die Allianz, kämpfen verbissen um diese geheiligten Stätten. $n, seid Ihr bereit die Kämpfe zu unterstützen?$B$BIch möchte, dass Ihr einen gegnerischen Friedhof besetzt. Sucht einen Friedhof mit einem Banner der Horde in seiner Mitte und reißt es heraus! Wenn Ihr das schafft, ist meine Aufgabe an Euch erfüllt...$B$BSolltet Ihr es schaffen, den Friedhof für einige Minuten zu halten, werden unsere Verstärkungstruppen eintreffen, um Euch bei der Verteidigung zu unterstützen.",
  },
  [7082] = {
    ["T"] = "Die Friedhöfe von Alterac",
    ["O"] = "Erobert einen Friedhof und kehrt zu Korporal Teeka Murrblut im Alteracgebirge zurück.",
    ["D"] = "Die Frontlinien in diesem Tal sind in ständiger Bewegung. Die Gebietsgrenzen bewegen sich hin und her, während wir unseren Feinden in tödlichen Schlachten gegenüber treten. Doch um die Friedhöfe, $n, wird am grimmigsten gekämpft.$B$BIch möchte, dass Ihr einen von der Allianz besetzten Friedhof angreift! Sucht einen Friedhof mit einem Banner der Allianz in seiner Mitte und reißt es aus dem Boden.$B$BWenn Ihr das schafft, ist meine Aufgabe an Euch erfüllt...$B$BSolltet Ihr es außerdem schaffen, den Friedhof für einige Minuten zu halten, wird dieser uns gehören und unsere Truppen werden kommen, um Euch bei der Verteidigung dieser wichtigen Position zu unterstützen.",
  },
  [7101] = {
    ["T"] = "Türme und Bunker",
    ["O"] = "Erobert einen gegnerischen Turm und kehrt anschließend zu Korporal Teeka Murrblut im Alteracgebirge zurück.",
    ["D"] = "Die Türme und Bunker in diesem Tal sind wichtige Stellungen und daher müssen wir sie möglichst alle erobern und halten! Genug geredet... Erobert einen vom Feind kontrollierten Turm oder Bunker.$B$BBetretet dafür einen mit einem Banner der Allianz. Im Inneren befindet sich ein weiteres Banner. Zieht das Banner heraus und ein weißes Banner der Horde wird an seiner Stelle erscheinen.$B$BDamit habt Ihr meinen Auftrag erfüllt. Wenn Ihr das eroberte Gebäude lange genug haltet, ohne dass die Allianz es zurückgewinnt, gehört es uns!",
  },
  [7102] = {
    ["T"] = "Türme und Bunker",
    ["O"] = "Zerstört das Banner im Inneren eines gegnerischen Bunkers oder Turms. Kehrt anschließend zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück.",
    ["D"] = "Die Türme und Bunker in diesem Tal sind Schlüsselverteidigungspositionen und daher sollten wir so viele wie möglich davon unter Kontrolle halten! Geht und erobert einen vom Feind kontrollierten Turm oder Bunker.$B$BBetretet dafür einen mit einem Banner der Horde. Im Inneren befindet sich ein weiteres Banner. Zieht das Banner heraus und ein weißes Banner der Allianz wird an seiner Stelle erscheinen.$B$BDamit habt Ihr meinen Auftrag erfüllt. Wenn Ihr das eroberte Gebäude lange genug haltet, ohne dass die Horde es zurückgewinnt, gehört es uns!",
  },
  [7121] = {
    ["T"] = "Der Rüstmeister",
    ["O"] = "Sprecht mit dem Rüstmeister der Sturmlanzen.",
    ["D"] = "$n, unser Rüstmeister hat mich angewiesen, die neuen Rekruten direkt zu ihm zu schicken. Er benötigt dringend Vorräte aus Kisten, die in den Minen von Alterac eingelagert sind und aller Wahrscheinlichkeit nach wird er Euch entsenden, um sie zu beschaffen.$B$BFolgt einfach der Straße. Ihr findet den Rüstmeister im Westen.",
  },
  [7122] = {
    ["T"] = "Eine Mine erobern",
    ["O"] = "Erobert eine Mine, die die Sturmlanzen noch nicht kontrollieren, und kehrt dann zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück.",
    ["D"] = "In den Minen des Alteractals ist noch weit mehr zu finden als bloßes Erz. Sowohl die Frostwölfe, als auch die Sturmlanzen, haben große Mengen ihrer Ausrüstung in der Eisenschacht- und der Eisbeißermine untergebracht... was eigentlich keine schlechte Idee ist, solange wir die Kontrolle über sie behalten!$B$BBesetzt eine Mine und kehrt zu mir zurück! Um eine Mine zu erobern, betretet eine vom Feind kontrollierte Mine und tötet den dortigen Anführer. Nachdem Ihr das getan habt, gehört die Mine uns und wir werden Truppen zu ihrem Schutz entsenden!",
  },
  [7123] = {
    ["T"] = "Sprecht mit unserem Rüstmeister",
    ["O"] = "Sprecht mit dem Rüstmeister der Frostwölfe.",
    ["D"] = "$n, der Rüstmeister der Frostwölfe sucht nach Lehrlingen für Aufgaben, die in die Minen des Tals von Alterac führen. Sprecht mit ihm; er wird Euch mehr berichten.$B$BFolgt der Straße westlich in den Bunker, dann nehmt den westlichen Ausgang und Ihr findet unseren Rüstmeister.",
  },
  [7124] = {
    ["T"] = "Eine Mine erobern",
    ["O"] = "Erobert eine Mine und kehrt zu Korporal Teeka Murrblut im Alteracgebirge zurück.",
    ["D"] = "So wie Kämpfe im Gebiet des Alteractals wüten, so wüten sie auch durch die Eisbeißer- und Eisenschachtminen. Beide bedeuten enorme Ressourcen an Mineralien und beide werden als Lager genutzt; daher ist es kein Wunder, dass sie so heiß begehrt sind!$B$BHelft uns in der Schlacht, $n! Erobert für die Horde eine Mine! Begebt Euch zu einer Mine, die noch nicht von uns kontrolliert wird und tötet den dortigen Anführer! Sobald Ihr das geschafft habt, schicken wir Truppen aus, um die Mine zu bewachen!",
  },
  [7141] = {
    ["T"] = "Die Schlacht um Alterac",
    ["O"] = "Betretet das Alteractal, bezwingt den Hordegeneral Drek\'thar und kehrt dann zu Ausgrabungsleiter Steinhauer im Alteracgebirge zurück.",
    ["D"] = "Die Forscherliga glaubt, dass es im Alteractal einige Artefakte von Interesse gibt. Allerdings werden uns die Orcs des Frostwolfklans wohl kaum danach forschen lassen. Wir können es der Horde jedoch nicht erlauben, unseren Forschungsbemühungen im Weg zu stehen! Vernichtet den General der Frostwölfe, Drek\'Thar. Mit seinem Tod werden die Orcs ihren Vorteil verlieren.$B$BDrek\'Thar hält sich in der Burg Frostwolf auf, im Süden des Alteractals. Bezwingt ihn und kehrt dann zu mir zurück.",
  },
  [7142] = {
    ["T"] = "Die Schlacht ums Alteractal",
    ["O"] = "Betretet das Alteractal und bezwingt den Zwergengeneral, Vanndar Sturmlanze. Kehrt anschließend zu Voggah Todesgriff im Alteracgebirge zurück.",
    ["D"] = "Die Sturmlanzenzwerge und ihre Verbündeten sind in das Alteractal eingefallen und wir müssen sie vertreiben! Um die Schlacht ums Alteractal gewinnen zu können, müssen wir ihren General töten, Vanndar Sturmlanze!$B$BIhr werdet Vanndar in Dun Baldar, im Norden des Alteractals finden.$B$BTötet ihn, $n, und kehrt dann zu mir zurück.",
  },
  [7161] = {
    ["T"] = "Flaggenjagd",
    ["O"] = "Begebt Euch in die Höhle der Wildpfoten südöstlich vom Hauptstützpunkt und findet das Banner der Frostwölfe. Bringt anschließend Kriegsmeister Laggrond das Banner.",
    ["D"] = "Seid gegrüßt, $C. Es scheint eine Ewigkeit her, seit wir zuletzt dem Ruf des Krieges gefolgt sind. Die alten Gefühle erwachen nur langsam. Doch sind sie erst einmal erwacht, beginnt das Blut erneut zu kochen. Die Kampfeslust kehrt zurück...$B$BAlle Neuankömmlinge im Tal müssen sich zuerst dem Ritus des Kampfes unterziehen.$B$BTief in der nahegelegenen Höhle der Wildpfoten findet Ihr ein Banner der Frostwölfe. Zieht los und holt es für uns zurück. Bringt es zu mir und ich gewähre Euch das Abzeichen eines Initianden der Frostwölfe.",
  },
  [7162] = {
    ["T"] = "Flaggenjagd",
    ["O"] = "Begebt Euch in die Eisschwingenhöhle südwestlich von Dun Baldar und findet das Banner der Sturmlanzen. Bringt anschließend Leutnant Haggerdin im Alteracgebirge das Banner.",
    ["D"] = "Ihr seid genau zur rechten Zeit eingetroffen, $C. Diese Wilden der Frostwölfe wollen einfach nicht aufgeben! Wir müssen ihnen die Macht der Sturmlanzen vor Augen führen! Am Ende müssen sie sich unserer Überlegenheit beugen.$B$BDoch bevor Ihr Euch auf das Schlachtfeld begebt, solltet Ihr den Reihen der Sturmlanzen beitreten.$B$BSüdwestlich von Dun Baldar befinden sich die Eisschwingenhöhlen. Tief in ihrem Inneren werdet Ihr ein Banner der Sturmlanzen finden. Begebt Euch dorthin und holt das Banner zurück. Bringt es anschließend zu mir und ich werde Euch mit dem Abzeichen eines Initianden der Sturmlanzen auszeichnen.",
  },
