  [7181] = {
    ["T"] = "Die Legende von Korrak",
    ["O"] = "Der Legende zufolge erscheint der mächtige Anführer der Winteraxtrolle nach gutdünken, um Angst und Schrecken unter den Bewohnen des Alteractals zu verbreiten.$B$BSollte Korrak auftauchen, tötet ihn und kehrt zu Kriegsmeister Laggrond zurück.",
    ["D"] = "Die angreifenden Sturmlanzen sind nicht die einzige Bedrohung in dieser Region, Soldat. Der Krieg in diesem Tal tobt an zwei Fronten. Den kannibalistischen Winteraxtrollen dürstet es ebenso nach Macht.$B$BAngeführt werden sie von Korrak dem Blutrünstigen - einem grausamen und verschlagenen Biest.$B$BEin Streich gegen Korrak könnte sich als vernichtender Schlag für den Winteraxklan erweisen. Tötet diesen Bastard und Ihr werdet entlohnt.",
  },
  [7181] = {
    ["T"] = "Die Legende von Korrak",
    ["O"] = "Der Legende zufolge erscheint der mächtige Anführer der Winteraxtrolle nach gutdünken, um Angst und Schrecken unter den Bewohnen des Alteractals zu verbreiten.$B$BSollte Korrak auftauchen, tötet ihn und kehrt zu Kriegsmeister Laggrond zurück.",
    ["D"] = "Die angreifenden Sturmlanzen sind nicht die einzige Bedrohung in dieser Region, Soldat. Der Krieg in diesem Tal tobt an zwei Fronten. Den kannibalistischen Winteraxtrollen dürstet es ebenso nach Macht.$B$BAngeführt werden sie von Korrak dem Blutrünstigen - einem grausamen und verschlagenen Biest.$B$BEin Streich gegen Korrak könnte sich als vernichtender Schlag für den Winteraxklan erweisen. Tötet diesen Bastard und Ihr werdet entlohnt.",
  },
  [7201] = {
    ["T"] = "Das letzte Element",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hingemurmelt hat.",
    ["D"] = "Es gibt Arbeit für diejenigen, die sich in die Tiefen wagen, $n.$B$BDen Dunkeleisenzwergen ist es gelungen, extrem mächtige Golems zu erschaffen.$B$BErsten Berichten unserer Spione zufolge verwenden die Zwerge eine einzigartige Kraftquelle, die ihren Schöpfungen eine unvergleichliche Kraft zum Töten gibt.$B$BStellt Euch nur einmal vor, was wir mit unseren Monstrositäten tun könnten, wenn wir diese Essenz der Elemente in die Hände bekommen könnten! Stellt die ganze Stadt auf den Kopf, wenn es sein muss, aber kommt nicht wieder, ehe Ihr nicht die Essenz gefunden habt! Die Bezahlung ist es wert, das Risiko einzugehen.",
  },
  [7202] = {
    ["T"] = "Korrak der Blutrünstige",
    ["O"] = "Der Legende zufolge erscheint der mächtige Anführer der Winteraxtrolle nach gutdünken, um Angst und Schrecken unter den Bewohnen des Alteractals zu verbreiten.$B$BSollte Korrak auftauchen, tötet ihn und kehrt zu Leutnant Haggerdin im Alteracgebirge zurück.",
    ["D"] = "Die eingeborenen Winteraxtrolle dieser Region sind unbarmherzige Wilde, die nichts lieber täten, als unsere Knochen in einer ihrer widerlichen Mahlzeiten zu sehen.$B$BWir müssen ihnen unsere Macht vor Augen führen!$B$BWir konnten einige Schriftstücke aus ihren Höhlen entwenden, die Aufzeichnungen über ihre Führungshierarchie enthalten. Diese Schriftstücke lassen vermuten, dass ihr Anführer, Korrak der Blutrünstige, sich solange versteckt hält, bis er einen Grund hat sich zu zeigen.$B$BDer Tod Korraks, würde den Tod des Winteraxklans bedeuten! Tötet ihn und kehrt zu mir zurück.",
  },
  [7202] = {
    ["T"] = "Korrak der Blutrünstige",
    ["O"] = "Der Legende zufolge erscheint der mächtige Anführer der Winteraxtrolle nach gutdünken, um Angst und Schrecken unter den Bewohnen des Alteractals zu verbreiten.$B$BSollte Korrak auftauchen, tötet ihn und kehrt zu Leutnant Haggerdin im Alteracgebirge zurück.",
    ["D"] = "Die eingeborenen Winteraxtrolle dieser Region sind unbarmherzige Wilde, die nichts lieber täten, als unsere Knochen in einer ihrer widerlichen Mahlzeiten zu sehen.$B$BWir müssen ihnen unsere Macht vor Augen führen!$B$BWir konnten einige Schriftstücke aus ihren Höhlen entwenden, die Aufzeichnungen über ihre Führungshierarchie enthalten. Diese Schriftstücke lassen vermuten, dass ihr Anführer, Korrak der Blutrünstige, sich solange versteckt hält, bis er einen Grund hat sich zu zeigen.$B$BDer Tod Korraks, würde den Tod des Winteraxklans bedeuten! Tötet ihn und kehrt zu mir zurück.",
  },
  [7223] = {
    ["T"] = "Rüstungsfetzen",
    ["O"] = "Bringt Murgot Tiefenschmied in Dun Baldar 20 Rüstungsfetzen.",
    ["D"] = "Im Alteractal wird ständig gekämpft und der Nachschub, den wir aus Eisenschmiede bekommen, reicht nicht, um unsere erfahrenen Truppen auszustatten.$B$BWenn Ihr Rüstungsfetzen auf dem Schlachtfeld findet, dann unterstützt unsere Kriegsbemühungen und bringt mir diese Stücke!",
  },
  [7224] = {
    ["T"] = "Beutezug im Feindesland",
    ["O"] = "Bringt 20 Rüstungsfetzen zu Schmied Regzar im Dorf der Frostwölfe.",
    ["D"] = "Wenn Ihr gegen den Feind kämpft, sammelt die Fetzen seiner Rüstungen ein und bringt sie zu mir. Aus den Fetzen werde ich neue Rüstungen für unsere eigenen Truppen herstellen!",
  },
  [7241] = {
    ["T"] = "Die Verteidigung der Frostwölfe",
    ["O"] = "Reist ins Alteractal im Vorgebirge des Hügellands. Sucht dort Kriegsmeister Laggrond auf und beginnt Eure Karriere als Soldat der Frostwölfe.",
    ["D"] = "In der Ferne kann man den Klang der Kriegstrommeln vernehmen, $C. Die ehrlose Sturmlanzengarde aus Eisenschmiede greift den Klan Eures Kriegshäuptlings an.$B$BIhr seid kampfgestählt, das verrät mir Euer Auftreten. Es scheint, dass Ihr wahrlich bereit seid, der Sache des Frostwolfklans zu dienen.$B$BEs liegt nun an Euch, den nächsten Schritt in Eurer Entwicklung zu gehen.$B$BReist in das Vorgebirge des Hügellands, dort befindet sich der Zugang zum Alteractal. Betretet das Tal und sucht Kriegsmeister Laggrond auf. Beeilung $C, die Schlacht hat bereits begonnen!",
  },
  [7261] = {
    ["T"] = "Die hoheitliche Anordnung",
    ["O"] = "Reist ins Alteractal im Vorgebirge des Hügellands. Wenn Ihr dort angekommen seid, meldet Euch umgehend bei Leutnant Haggerdin.$B$BFür den Ruhm von Bronzebart!",
    ["D"] = "Wir kämpfen eine blutige Schlacht in den Tälern von Alterac. Der barbarische Frostwolfklan fordert die Sturmlanzen zum Kampf heraus! Sie müssen im Namen unseres König Magni Bronzebart ausgelöscht werden! Die Eroberung und Säuberung dieses Landes ist eine herrschende und territoriale Notwendigkeit für das Königreich von Eisenschmiede.$B$BLeider wachsen gute Soldaten nicht auf Bäumen! Meldet Euch bei Leutnant Haggerdin im Alteractal, um Euren ehrenvollen Pflichten bei den Sturmlanzen nachzukommen.",
  },
  [7281] = {
    ["T"] = "Bruderliebe",
    ["O"] = "Begebt Euch ins Gebiet der Sturmlanzen und tötet Kommandant Karl Philips. Kehrt zu Kommandant Louis Philips zurück, wenn Ihr Eure Aufgabe erfüllt habt.",
    ["D"] = "Welch tragische Fügung... Mein Bruder steht mir auf dem Schlachtfeld gegenüber: Er ist einer der Kommandanten der Sturmlanzen.$B$BVon allen vier Kommandanten stellt er zweifellos die größte Gefahr dar. Falls man ihn tötet, würde die Bansheekönigin ihn vielleicht wiedererwecken, so wie sie es mit mir getan hat - und ihm die Irrtümer seines bisherigen Handelns vor Augen führen...$B$BTief im feindlichen Gebiet werdet Ihr meinen Bruder finden. Tötet ihn und bringt ihn zu mir zurück. Ich vertraue darauf, dass Ihr diese Mission erfolgreich zu Ende bringt, Soldat. $B$BUnd Soldat... geht nicht alleine.",
  },
  [7282] = {
    ["T"] = "Bruderliebe",
    ["O"] = "Begebt euch ins Frostwolfgebiet und tötet Kommandant Louis Philips. Kehrt zu Kommandant Karl Philips zurück, wenn Ihr Eure Aufgabe erfüllt habt.",
    ["D"] = "Er muss aus seinem Elend befreit werden, Soldat! Mein Bruder weiß nicht mehr, was er tut. Er ist nur noch eine willenlose Marionette der grausamen Bansheekönigin.$B$BBegebt Euch, über das Feld des Kampfes, in die Tiefen des Gebiets der Frostwölfe. Findet meinen Bruder und zerstört, was von ihm übrig ist. Seine Qualen sollen endlich ein Ende haben.",
  },
  [7301] = {
    ["T"] = "Gefallene Himmelsstürmer",
    ["O"] = "Begebt Euch ins Frostwolfterritorium und sucht nach Schwadronskommandant Slidore, Schwadronskommandant Vipore und Schwadronskommandant Ichman. Meldet Euch bei Kommandant Duffy, sobald Ihr Eure Mission erfüllt habt.",
    ["D"] = "Wir konnten sie gefangennehmen, $R. Ich rede von ihren Schwadronskommandanten. Es war eine wilde Himmelsschlacht, doch letztendlich haben wir sie geschlagen! Sie fielen wie Steine vom Himmel! Leider hat es auch viele unserer besten Piloten erwischt, als Sie die Burg Frostwolf angriffen. Ihr müsst herausfinden was aus ihnen geworden ist.$B$BDurchkämmt das Gebiet der Westturmstellung und Burg Frostwolf. Dort wurden Sie das letzte Mal gesehen. Und geht gründlich vor!",
  },
  [7302] = {
    ["T"] = "Gefallene Himmelsstürmer",
    ["O"] = "Findet heraus, was aus Schwadronskommandantin Guse, Schwadronskommandantin Jeztor und Schwadronskommandant Mulverick geworden ist. Wenn Eure Mission erfüllt ist, kehrt zu Kommandant Mulfort zurück.",
    ["D"] = "Unsere Luftunterstützung musste einen schweren Schlag hinnehmen, $n. Sämtliche Schwadronskommandanten wurden über feindlichem Gebiet abgeschossen. Ihr müsst herausfinden, was aus ihnen geworden ist, sodass wir einen Rettungsversuch unternehmen können!$B$BGuse wurde das letzte Mal gesehen, als sie über den Eisschwingenbunker hinwegflog. Jetzors Reittier wurde zuletzt am Sägewerk der Sturmlanzen gesichtet. Mulverick... dieser waghalsige Narr, stürzte in der Nähe des Nordbunkers von Dun Baldar ab.$B$BDas sind alle Informationen, die ich habe. Schwärmt aus und durchsucht das Gebiet gründlich!",
  },
  [7321] = {
    ["T"] = "Milde Schildkrötensuppe",
    ["O"] = "Bringt 10 Stücke Schildkrötenfleisch zu Christoph Jeffkott in Tarrens Mühle.",
    ["D"] = "Wer sagt denn, dass Untote keine Ahnung vom Kochen haben? Nehmt als Beispiel nur mal mein Rezept für Schildkrötensuppe. Ich habe Leute getroffen, die sogar von den Toten zurückgekehrt sind, nur um eine Schüssel voll davon zu kosten.$B$BDa wir gerade davon sprechen: In letzter Zeit konnte ich keine mehr kochen. Ich bin früher selbst bis zum Lordameresee gereist, um Schnappkiefer zu jagen, aber meine Arbeit hier beschäftigt mich mittlerweile einfach zu sehr.$B$BWenn Ihr mir etwas Schildkrötenfleisch von den Schnappkiefern bringt, dann werd ich Euch in das Geheimnis meines Rezepts einweihen!",
  },
  [7341] = {
    ["T"] = "Ein gerechter Handel",
    ["O"] = "",
    ["D"] = "",
  },
  [7342] = {
    ["T"] = "Pfeile sind was für Memmen",
    ["O"] = "",
    ["D"] = "",
  },
  [7361] = {
    ["T"] = "Die Gunst der Dunkelspeere",
    ["O"] = "Ihr wurdet beauftragt gegnerische Nachtelfenspieler im Alteractal zu töten.$B$BTötet einen Nachtelfen und bringt einen abgeschlagenen Nachtelfenkopf zu Najak Hexxen in Burg Frostwolf.",
    ["D"] = "<Najak knurrt.>$B$BBevor die Nachtelfen waren, waren die Trolle. Erst von den Trollen sind Nachtelfen gekommen.$B$BIhre aufrührerischen Angriffe gegen mein Volk können nicht toleriert werden. Hirschhaupt will uns auslöschen - sämtliche Spuren derer vernichten, die ihn und seines Gleichen ausgetragen haben. Das wird nie geschehen!$B$BFür jeden Nachtelfen, den Ihr tötet und enthauptet, wird Najak Euch die Gunst der Dunkelspeere sichern. Die Kunde Eurer Taten wird sich über ganz Kalimdor und den Östlichen Königreichen verbreiten. Geht nun und dient den Dunkelspeeren!",
  },
  [7362] = {
    ["T"] = "Verbündeter der Tauren",
    ["O"] = "Ihr wurdet beauftragt gegnerische Gnomspieler im Alteractal zu töten.$B$BTötet einen Gnom und bringt ein Büschel Gnomhaar zu Ravak Grimmtotem in Burg Frostwolf.",
    ["D"] = "Die Älteste hat mich in dieses Kriegsgebiet geschickt um die anderen Völker zu erforschen. Genau genommen bin ich hier, um Proben von den Gnomen zu nehmen. Magatha interessiert sich insbesondere für ihre Überlebensfähigkeit. Die Gnome - obwohl erbärmlich schwach und winzig - scheinen sowohl im Kampf als auch körperlich äußerst widerstandsfähig zu sein.$B$BZum Glück für Euch, suche ich nach Freiwilligen, die Magathas Anweisungen in die Tat umsetzen. Bringt mir Gnomhaarbüschel und die Tauren werden Euch als Verbündeten anerkennen.",
  },
  [7363] = {
    ["T"] = "Die menschliche Existenz",
    ["O"] = "Ihr wurdet beauftragt gegnerische Menschenspieler im Alteractal zu töten.$B$BTötet einen Menschen und bringt dessen Knochensplitter zu Kommandant Louis Philips (er bewegt sich zwischen den Frontlinien und Burg Frostwolf hin und her).$B$BDie Heilung für die menschliche Existenz ist zum Greifen nahe!",
    ["D"] = "Menschen sind schlichtweg verabscheuenswert.$B$B<Kommandant Philips schneidet eine Grimasse.>$B$BZwar entspricht es den Tatsachen, dass ich ein Soldat der Frostwölfe bin, dennoch bleibe ich ein Diener Sylvanas.$B$BNeben der Ausübung meiner Pflichten, arbeite ich auch für die Dunklen Apotheker von Unterstadt.$B$BIch bin damit beauftragt worden menschliche Knochensplitter einzusammeln, die helfen sollen, uns von der menschlichen Existenz zu befreien.$B$BMan hat mich außerdem dazu berechtigt, diese Aufgabe an meine Untergebenen weiterzureichen.$B$BFindet sie. Tötet sie. Bringt mir ihre Knochensplitter.",
  },
  [7364] = {
    ["T"] = "Gnomeregan Kopfgeld",
    ["O"] = "Ihr wurdet beauftragt gegnerische Taurenspieler im Alteractal zu töten.$B$BTötet einen Tauren und bringt dessen Taurenhuf zu Dirk Schwindel in Dun Baldar.",
    ["D"] = "Der Hochtüftler persönlich hat mich auf dieses Schlachtfeld beordert, um meinen Brüdern und Schwestern beizustehen. Es ist uns zu Ohren gekommen, dass die Tauren sich einzelne Gnome herauspicken und töten. Noch viel schlimmer ist, dass sie den Leichen ihr prächtiges Gnomhaar büschelweise abschneiden. Diese Greultaten müssen aufgehalten werden. Wir müssen Vergeltung üben!$B$BIch will sie alle enthuft sehen, $C. Bringt mir ihre Hufe! Erweist euch als Verbündeter der Gnome.",
  },
  [7365] = {
    ["T"] = "Hirschhaupts Requiem",
    ["O"] = "Ihr wurdet beauftragt gegnerische Trollspieler im Alteractal zu töten.$B$BTötet einen Troll und bringt Athramanis in Dun Baldar etwas Trollmojo der Dunkelspeere.",
    ["D"] = "Hirschhaupt hat uns Anweisung gegeben, alle Trolle auf dem Schlachtfeld auszulöschen. Ihrer lächerlichen Behauptung die Nachtelfen erschaffen zu haben, kann nur mit aller Macht und militärischen Mitteln entgegengewirkt werden.$B$BTötet sie alle und bringt mir ihr Mojo. Ich wiederhole: Stehlt ihnen ihr Mojo.",
  },
  [7366] = {
    ["T"] = "Die Gnade des Erzbischofs",
    ["O"] = "Ihr wurdet beauftragt gegnerische Verlassenen-Spieler im Alteractal zu töten.$B$BTötet einen Verlassenen und bringt dessen Herz zu Kommandant Karl Philips (er bewegt sich zwischen dem Sägewerk und der Mine hin und her).",
    ["D"] = "Der Erzbischof ist so gütig wie er weise ist. Dass ein Mann wie er derartig gnädig zu einer solch elenden Bande sein kann...$B$B<Kommandant Philips schüttelt ungläubig den Kopf.>$B$BZu gütig... einfach zu gütig.$B$BDer Erzbischof möchte, dass wir uns der Untoten erbarmen und ihnen einen schnellen und endgültigen Tod gewähren. Für diesen großen Akt der Gnade benötigen wir nur ihre schwarzen, von Krankheit besessenen Herzen.$B$BBringt mir die Herzen der Verlassenen, sodass wir sie in der Kathedrale des Lichts zeremoniell verbrennen können. Eilt euch, $n!",
  },
  [7367] = {
    ["T"] = "Die Gefahr bannen",
    ["O"] = "Findet den Sprengmeister der Frostwölfe und tötet ihn. Kehrt nach Abschluss des Auftrags zum Sprengmeister der Sturmlanzen zurück.",
    ["D"] = "Die Frostwölfe haben ihre Frontlinien und Basen allesamt vermint, $C. Achtet darauf wohin Ihr tretet, wenn Ihr in feindliches Territorium eindringt.$B$BWenn wir dem Feind einen entscheidenden Schlag versetzten wollen, müsst Ihr seine Quelle für Sprengwaffen zum Versiegen bringen - Ich rede vom feindlichen Sprengmeister! Tötet ihn und wir können alle Minen sicher entschärfen. Solange er ausfällt, fallen auch seine Minen aus.$B$BVermutlich befindet er sich in einem Turm, nahe der Eisblutgarnison. Viel Glück, Soldat!",
  },
  [7368] = {
    ["T"] = "Die Gefahr bannen",
    ["O"] = "Findet den Sprengmeister der Sturmlanzen und tötet ihn. Kehrt nach Abschluss des Auftrags zum Sprengmeister der Frostwölfe zurück.",
    ["D"] = "Ich weiß alles über Landminen. Immerhin habe ich alle Minen an unseren Frontlinien selbst verlegt.$B$BWenn Ihr das Minenfeld der Sturmlanzen unschädlich machen wollt, braucht Ihr nur eines zu tun - Tötet den feindlichen Sprengmeister! Zweifellos versteckt sich der feige Zwerg im ersten Turm, auf dem Feld des Kampfes. Schaltet ihn aus und die Landminen werden nicht zurückkommen, nachdem sie von Euch entschärft wurden.$B$BFalls Ihr es schaffen solltet den Sprengmeister zu töten, kommt zu mir zurück und lasst es mich wissen. Keine gute Tat soll unbelohnt bleiben.",
  },
  [7381] = {
    ["T"] = "Korraks Rückkehr",
    ["O"] = "Sollte Korrak der Blutrünstige wieder in den Winteraxhöhlen auftauchen, sucht ihn auf und tötet ihn.$B$BBringt Korraks Schädel zu Kriegsmeister Laggrond auf Burg Frostwolf.",
    ["D"] = "Es ist zwar eine Tatsache, dass Korrak gefallen ist, jedoch bleibt eine Frage offen: Wird er auch tot bleiben? Trolle sind berüchtigt für ihre außerweltliche Stammesmagie. Mich würde es nicht überraschen, wenn ihr toter Anführer wiedererweckt würde, um wieder einmal über seinen Klan zu herrschen.$B$BIhr wisst ja was in einem solchen Fall zu tun ist.$B$B<Kriegsmeister Laggrond deutet mit seinem Finger eine Schneidebewegung an seinem Hals an.>$B$BUnd nächstes mal möchte ich seinen Schädel. Haltet einfach die Augen offen, Soldat.$B$BWegtreten!",
  },
  [7382] = {
    ["T"] = "Korrak der Unsterbliche",
    ["O"] = "Sollte Korrak der Blutrünstige wieder in den Winteraxhöhlen auftauchen, sucht ihn auf und tötet ihn.$B$BBringt Korraks Schädel zu Leutnant Haggerdin in Dun Baldar.",
    ["D"] = "Irgendetwas stimmt an dieser Sache nicht... Hat jemand daran gedacht Korraks Körper zu verbrennen? Diese Trolle werden vor nichts halt machen, um ihren gefallenen Anführer wiederzubeleben - so wie es die blauhäutigen Heiden des öfteren tun.$B$BHaltet immer ein Auge offen, $n. Seid nicht beunruhigt, wenn Karrak überraschend wieder auftaucht. Und wenn er das tut, will ich ihn tot sehen - seinen Körper zu Asche verbrannt.$B$BObendrein, schlagt ihm seinen Kopf ab und bringt mir seinen Schädel, bevor Ihr seinen leblosen Körper verbrennt.",
  },
  [7385] = {
    ["T"] = "Eine Gallone Blut",
    ["O"] = "null",
    ["D"] = "You have the option of offering larger quantities of the blood taken from our enemies. I will be glad to accept gallon sized offerings, $N.",
  },
  [7386] = {
    ["T"] = "Haufenweise Kristalle",
    ["O"] = "null",
    ["D"] = "There are times which you may be entrenched in battle for days or weeks on end. During those longer periods of activity you may end up collecting large clusters of the Frostwolf\'s storm crystals.$B$BThe Circle accepts such offerings, $N.",
  },
  [7401] = {
    ["T"] = "GESUCHT: ZWERGE!",
    ["O"] = "Ihr wurdet damit beauftragt, alle feindlichen Zwergenspieler im Alteractal zu töten.$B$BTötet einen Zwerg und schneidet ihm sein Rückgrat heraus. Bringt anschließend das Zwergenrückgrat zu Unteroffizier Yazra Murrblut in Burg Frostwolf.",
    ["D"] = "Auf Befehl von Unteroffizier Yazra Murrblut sind alle Zwerge auf dem Schlachtfeld umgehend zu töten.$B$BSolltet Ihr einen Zwerg im Kampf töten, schneidet ihm sein Rückgrat heraus und lasst den rückgratlosen Fleischhaufen als Mahnmal für alle zurück, die sich gegen die Frostwölfe stellen.$B$BKehrt anschließend mit dem Beweis Eurer Heldentat zu Unteroffizier Yazra Murrblut zurück.",
  },
  [7402] = {
    ["T"] = "GESUCHT: ORCS!",
    ["O"] = "Ihr wurdet damit beauftragt, alle feindlichen orcischen Spieler im Alteractal zu töten.$B$BTötet einen Orc und bringt seinen Zahn zurück zu Korporal Noreg Sturmlanze in Dun Baldar.",
    ["D"] = "Auf Befehl von Korporal Noreg Sturmlanze sind alle verfügbaren Soldaten dazu aufgefordert, jeden gegnerischen Orc auf dem Schlachtfeld umgehend zu töten.$B$BDie Soldaten sind weiterhin dazu aufgefordert, einen Hauer oder Zahn aus dem Maul des Feindes zu entfernen, um diese als Beweis für ihre Tat vorzeigen zu können.$B$BBitte tragt dafür Schutzhandschuhe.",
