  [7441] = {
    ["T"] = "Pusillin und der Älteste Azj\'Tordin",
    ["O"] = "Reist nach Düsterbruch und macht den Dämonen Pusillin ausfindig. Überzeugt ihn mit allen Mitteln davon, Euch Azj\'Tordin\'s Buch der Zauberformeln zu geben.$B$BKehrt mit dem Buch zu Az\'Tordin, beim Larisspavillon in Feralas, zurück.",
    ["D"] = "Ich habe nur einen Moment nicht aufgepasst, Fremder. In meiner Eile, dem Griff des gefallenen Prinzen zu entkommen, wurde ich ausgeraubt. Ein besonders widerlicher Dämon, namens Pusillin, stahl mir mein Buch der Zauberformeln und den Schlüssel zu den einstmals großen Hallen von Eldre\'Thalas.$B$BMein Interesse am Schlüssel ist verloschen als ich meine Unsterblichkeit gegen die Freiheit eingetauscht habe. Doch ich benötige unbedingt mein Buch der Zauberformeln.$B$BFindet den Dämon Pusillin und bringt mir mein Buch.$B$BSucht Pusillin im Wucherborkenviertel von Düsterbruch.",
  },
  [7461] = {
    ["T"] = "Der innere Wahnsinn",
    ["O"] = "Zerstört alle Wächter, die um die 5 Pylonen herumstehen, welche Immol\'thars Gefängnis mit Energie versorgen. Sobald die Pylone deaktiviert wurden, wird sich das Kraftfeld, das Immol\'thar umgibt, auflösen.$B$BBetretet Immol\'thars Gefängnis und vernichtet den verdorbenen Dämonen. Anschließend müsst Ihr Prinz Tortheldrin im Athenaeum entgegentreten.$B$BKehrt nach Abschluss der Aufgabe zur uralten Shen\'dralar im Hof zurück.",
    ["D"] = "Habt Ihr genug gehört? Seid Ihr $g ein Mann:eine Frau; der Tat?$B$BDieser Wahnsinn muss ein Ende haben. Das Zeitalter der Unsterblichen ist lange vergangen. Vernichtet die Wächter rings um die Pylonen herum, um das Kraftfeld zu senken. Habt Ihr das erst einmal geschafft, könnt Ihr Immol\'thar vernichten. Die Macht des Prinzen wird stark geschwächt sein, greift ihn an und beendet seine Existenz. Gewährt den Geistern dieser Stadt ihre letzte Ruhe, Abenteurer.",
  },
  [7462] = {
    ["T"] = "Der Schatz der Shen\'dralar",
    ["O"] = "Kehrt in das Athenaeum zurück und sucht den Schatz der Shen\'dralar. Nehmt Euch Eure Belohnung!",
    ["D"] = "In der Bücherei, dem Athenaeum, werdet Ihr unter dem Treppenaufgang eine uralte Truhe finden. Nehmt Euch aus der Truhe was Ihr wollt.",
  },
  [7463] = {
    ["T"] = "Arkane Erfrischung",
    ["O"] = "Reist nach Düsterbruch in das Wucherborkenviertel und besiegt den Wasserelementar Hydrobrut. Kehrt anschließend mit der Hydrobrutessenz zum Wissenshüter Lydros im Athenaeum zurück.",
    ["D"] = "$gEin:Eine:r; $R und dazu noch $gMagier:Magierin;! Wie wunderbar. Ich habe jemanden wie Euch schon seit... wie lange ist es nun schon her?... 500 Jahren nicht mehr gesehen!$B$BIch habe da etwas, das Euren Durst zu löschen vermag, doch zuerst müsst Ihr etwas für mich erledigen.$B$B<Lydros kichert.>$B$BEs gibt eine Anomalie in Eldre\'Thalas. Ein Wasserelementar hat sich im östlichen Flügel niedergelassen. Seine Anwesenheit muss unbedingt katalogisiert werden!$B$BReist nach Düsterbruch in das Wucherborkenviertel und sammelt etwas von der elementaren Essenz der Hydrobrut. Bringt Sie anschließend zu mir zurück und ich werde Euch einen äußerst nützlichen Zauberspruch beibringen.",
  },
  [7481] = {
    ["T"] = "Elfische Legenden",
    ["O"] = "Sucht in Düsterbruch nach Kariel Winthalus. Meldet Euch anschließend bei dem Weisen Korolusk in Camp Mojache.",
    ["D"] = "Vor vielen Jahren entkam ein einzelner Elfenmeister namens Kariel Winthalus der Vernichtung seines Heimatlandes. Bei sich trug er einige uralte Artefakte der Elfen.$B$BUnsere Suche nach dem verschwundenen Meister führte uns von den Ruinen Quel\'Thalas, durch die Brennende Steppe, über die großen Weiten der See, hinüber nach Feralas.$B$BWir nehmen an, dass der Elf in den Hallen von Eldre\'Thalas, die nun als Düsterbruch bekannt sind, Schutz suchte.$B$BIhr müsst ihn finden, Abenteurer. Ob er nun lebt oder schon lange tot ist, das Wissen, welches er bei sich trug, muss gerettet werden!",
  },
  [7482] = {
    ["T"] = "Elfische Legenden",
    ["O"] = "Sucht in Düsterbruch nach Kariel Winthalus. Meldet Euch anschließend bei der Gelehrten Runendorn in der Mondfederfeste.",
    ["D"] = "Vor vielen Jahren entkam ein einzelner Elfenmeister namens Kariel Winthalus der Vernichtung seines Heimatlandes. Bei sich trug er einige uralte Artefakte der Elfen.$B$BUnsere Suche nach dem verschwundenen Meister führte uns von den Ruinen von Quel\'Thalas durch die Brennende Steppe über die großen Weiten der See hinüber nach Feralas.$B$BWir nehmen an, dass der Elf in den Hallen von Eldre\'Thalas, die nun als Düsterbruch bekannt sind, Schutz suchte.$B$BIhr müsst ihn finden, Abenteurer. Ob er nun lebt oder schon lange tot ist, das Wissen, welches er bei sich trug, muss gerettet werden!",
  },
  [7483] = {
    ["T"] = "Buchband der Schnelligkeit",
    ["O"] = "Bringt ein Buchband der Schnelligkeit, 1 makellosen schwarzen Diamanten, 2 große glänzende Splitter und 2-mal das Blut von Helden, zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum der Schnelligkeit zu erhalten.",
    ["D"] = "Meister Winthalus verlor den Buchband der Schnelligkeit im Ostflügel. Mit dem Buchband kann ein Zauber der geringen Hast auf Eure Ausrüstung gewirkt werden. Ein einfacher Trick...$B$BWie auch immer... Falls Ihr Interesse habt, bringt mir den Buchband und einige grundlegende Reagenzien.$B$BHrm... Ich glaube man benötigt das Blut von Helden, einen makellosen schwarzen Diamanten und große glänzende Splitter. Ich mag mich natürlich Irren...$B$B<Lydros zuckt mit den Achseln.>",
  },
  [7484] = {
    ["T"] = "Buchband des Fokus",
    ["O"] = "Bringt ein Buchband des Fokus, 1 makellosen schwarzen Diamanten, 4 große glänzende Splitter und 2-mal Schattenhaut, zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum des Fokus zu erhalten.",
    ["D"] = "Dieser Buchband erweckte die Neugierde eines jungen Familiars, welcher einem der Hüter gehörte. Das arme Ding wurde nur ein paar Wochen alt$B$BDiejenigen unter Euch, die sich mit Magie beschäftigen, dürften dieser Gegenstand interessieren.$B$BDas Buchband des Fokus ging in diesem Flügel verloren. Ich kann nur annehmen, dass einer der Geister der Hochgeborenen es in Besitz genommen hat.$B$BBringt mir den Buchband, zusammen mit einen makellosen schwarzen Diamanten, einigen großen glänzenden Splittern und etwas Schattenhaut, und ich werde das Arkanum herbeizaubern.",
  },
  [7485] = {
    ["T"] = "Buchband des Schutzes",
    ["O"] = "Bringt ein Buchband des Schutzes, 1 makellosen schwarzen Diamanten, 2 große glänzende Splitter und 1 ausgefranste Monstrositätenstickerei zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum des Schutzes zu erhalten.",
    ["D"] = "Als er von dem Verlust dieses Buchbands hörte, weinte er wie ein kleines Kind. Wohlgemerkt, Ich selbst habe schon über derartige Verluste getrauert - und kein Verlust wiegt mehr, als der Verlust von Wissen und Geschichte- aber wegen diesem Haufen nutzloser Zauberformeln? Lächerlich! Mir scheint, der Prinz hat ihm wohl einen Gefallen getan, als er ihm das Fleisch von den Knochen riss.$B$BBringt mir den Buchband, zusammen mit einen makellosen schwarzen Diamanten, einigen großen glänzenden Splittern und eine ausgefranste Monstrositätenstickerei, und ich werde das Arkanum wiederherstellen.$B$BAch ja, sucht im Nordflügel.",
  },
  [7486] = {
    ["T"] = "Die Belohnung eines Helden",
    ["O"] = "Nehmt Eure Belohnung aus Hydraxis\' Kasten.",
    ["D"] = "$n, Euer Dienst für die Kriegsherrn hat alles übertroffen, was wir je von den Einwohnern dieser Welt erwartet haben.$B$BWir kennen zwar nicht die Vorstellung Eures Volkes, von Dankbarkeit oder wie Ihr gute Arbeit bei Euch belohnt, aber in diesem Fall...ist eine Belohnung mehr als angebracht.$B$BUnter der Brandung an der südlichen Basis der Insel, findet Ihr einen Kasten. In diesem werdet Ihr Eure... Belohnung finden.$B$BGeht, $n. Nehmt Euch Eure Belohnung.",
  },
  [7487] = {
    ["T"] = "Abstimmung mit dem Kern",
    ["O"] = "Begebt Euch zum Portal in den Schwarzfelstiefen, das in den Geschmolzenen Kern führt, und findet ein Kernfragment. Kehrt mit dem Fragment zu Lothos Felsspalter im Schwarzfels zurück.",
    ["D"] = "Risse und Abgründe bilden sich um uns herum und kollabieren wieder, $R. Keine zwei Schritte von mir entfernt, ist ein Riss, der hinab in die tiefsten Tiefen des Schwarzfels führt, bis in die Ruhestätte des Feuerfürsten.$B$BÜberrascht? Wie bedauerlich... Ihr sterblichen Völker versteht einfach nichts, was Ihr nicht sehen, anfassen oder fühlen könnt.$B$BIch versichere Euch, das Portal ist da und es ist passierbar.$B$BHabe ich Euer Interesse geweckt? Eine Anpassung ist simpel. Begebt Euch in die Schwarzfelstiefen und findet ein Kernfragment. Bringt es zu mir zurück und ich werde Eure Essenz mit dem Portal abstimmen.",
  },
  [7488] = {
    ["T"] = "Lethtendris\' Netz",
    ["O"] = "Bringt Lethtendris\' Netz zu Latronicus Mondspeer in der Mondfederfeste in Feralas.",
    ["D"] = "Die Magierin Lethtendris, eine bösartige Blutelfe, deren Brutalität nur durch ihre pervertiere Hingabe zur Magie in den Schatten gestellt wird, ist nach Düsterbruch geflüchtet. Sie hat eine Apparatur entwickelt, ein Netz, um die magischen Energien dieses Ortes einzufangen und wir fürchten, dass wenn Sie niemand aufhält, sie der Welt einen unwiderruflichen Schaden zufügen wird!$B$BStoppt Sie, $n. Findet Sie und nehmt das Netz an Euch. Am ehesten werdet Ihr sie nahe den Satyrn, im Wucherborkenviertel von Düsterbruch, finden. Bringt das Netz anschließend zu mir und ich werde versuchen, dessen Kräfte wieder der Wildnis zurückzugeben...",
  },
  [7489] = {
    ["T"] = "Lethtendris\' Netz",
    ["O"] = "Bringt Lethtendris\' Netz zu Talo Dornhuf im Camp Mojache in Feralas.",
    ["D"] = "Die Blutelfe Lethtendris hat ihre Grenzen überschritten. So versessen war sie darauf magische Macht zu erlangen, das sie ihre Brüder verraten und eine teuflische Apparatur gebaut hat, ein Netz welches in der Lage ist, die verdorbenen magischen Energien von Düsterbruch einzufangen. Selbst jetzt sammelt sie diese Energien und plant, sie gegen ihre Feinde einzusetzen. $B$BLethtendris muss aufgehalten werden. Sucht sie im Wucherborkenviertel von Düsterbruch, besiegt sie und bringt anschließend ihr Netz zu mir. So kann es vielleicht wieder in die Hände einiger Blutelfen übergeben werden, die dieses Artefakt mit klarem Verstand untersuchen können.",
  },
  [7490] = {
    ["T"] = "Sieg für die Horde",
    ["O"] = "Bringt Onyxias Kopf zu Garrosh in Orgrimmar.",
    ["D"] = "Ihr habt das Unmögliche vollbracht. Die Brutmutter des schwarzen Drachenschwarms liegt tot zu Euren Füßen. Nehmt ihren Kopf und präsentiert ihn Eurem Kriegshäuptling.",
  },
  [7491] = {
    ["T"] = "Für alle sichtbar",
    ["O"] = "Sucht Oberanführer Runthak im Tal der Stärke auf.",
    ["D"] = "Oberanführer Runthak erwartet Eure Ankunft im Tal der Stärke.$B$B<Der Kriegshäuptling salutiert euch.>$B$BLasst Euer Publikum nicht warten, großer Held.",
  },
  [7492] = {
    ["T"] = "Camp Mojache",
    ["O"] = "Sprecht mit Talo Dornhuf in Camp Mojache, in Feralas.",
    ["D"] = "Talo Dornhuf, ein Taurenältester in Feralas, hat uns um Hilfe gebeten. Er hat nur wenige Einzelheiten verraten, doch wie es scheint sucht er nach Agenten für eine Angelegenheit, die sowohl heikel als auch dringend ist.$B$BDient der Horde, $n. Begebt Euch ins Camp Mojache in Feralas und sprecht mit Talo.",
  },
  [7493] = {
