  [7701] = {
    ["T"] = "GESUCHT: Vorarbeiter Maltorius",
    ["O"] = "Tief in der Schlackengrube des Kessels in der Sengenden Schlucht werdet Ihr den verräterischen Dunkeleisenzwerg, Aufseher Maltorius, finden. $B$BTötet den Aufseher Maltorius und nehmt seinen Kopf an Euch. Bringt diesen zum Hauptmann des Ausgucks Lolo Langhieb in der Sengenden Schlucht für eine Belohnung.",
    ["D"] = "Auf Anordnung seiner Eminenz, Aufseher Schlickfaust, des Agenten der Thoriumbruderschaft und gleichzeitig neu angeheuerten Kommandanten der Schlackengrubentruppe der Dunkeleisenzwerge des Kessels, soll Aufseher Maltorius getötet und sein Kopf als Beweis für die Tat erbracht werden. $B$BTrefft Euch mit dem Hauptmann des Ausgucks, Lolo Langhieb, wegen der Belohnung für diese schwierige Aufgabe.",
  },
  [7703] = {
    ["T"] = "Die offene Rechnung der Gordok",
    ["O"] = "Findet die Stulpen der Gordokmacht und bringt sie zu Hauptmann Krombruch in Düsterbruch.$B$BKrombruch zufolge sagen die \"Alte Zeit Geschichten\", dass Tortheldrin - ein \"gruseliger\" Elf, der sich selbst als Prinz bezeichnet- sie einem der Gordokkönige gestohlen hat.",
    ["D"] = "Nun Ihr de\' König sein, vielleicht Ihr kriegen zurück wertvolles Artefakt der Gordok von gruseligen Elfen, der sich selbst nennen Prinz! König vor Euch hat versagt, so wie alle Könige seit Tortheldrin - de\' gruselige Elf - es gestohlen hat, von damaligen Gordokkönig!$B$BArtefakt heißen Stulpen der Gordokmacht. Alte Zeit Geschichten sagen, nur de\' wahre König de\' Gordok kann sie zurückholen. Alte Zeit Geschichten sagen auch, de\' gruselige Elf ist immer noch hier - Ihr finden es und es werden Eure mächtigen Stulpen, für immer! Yar, ich zeigen!",
  },
  [7721] = {
    ["T"] = "Treibstoff fürs Schrumpfen",
    ["O"] = "Sammelt 10 Wasserelementarkerne von den Meereselementaren und Meeresschauern entlang der Vergessenen Küste von Feralas und bringt sie zurück zu Zorbin von Schiller in der Nähe des dortigen Docks.",
    ["D"] = "Wenn ich gegen mein besseres Wissen und Gewissen handele und nun doch bleibe, um meinen Ingenieurladen an der Vergessenen Küste aufzumachen, muss ich mich dringend um diese nervigen Elementare kümmern. Verdammt sei der unkontrollierte Elementalismus, sage ich!$B$BGlücklicherweise stellen die Elementare eine ideale Quelle reiner Kraft für meinen Ultraschrumpfer dar – insofern, gebt ihnen Saures! Wenn Ihr dies allerdings tut, bringt mir doch bitte auch ein paar Elementarkerne.",
  },
  [7722] = {
    ["T"] = "Was zum Fluxus?!",
    ["O"] = "Irgendwo in der Schlackengrube des Kessels findet Ihr die geheimen Pläne des Feurigen Fluxus, die der Aufseher Maltorius stahl. Findet diese Pläne und bringt sie zurück zu Meisterschmied Burninate in der Sengenden Schlucht.",
    ["D"] = "Als dieser Verräter Maltorius unseren Klan verließ, verließ er ihn mit etwas viel wertvollerem als einem makellosen Arkankristall: Unserem Rezept für den feurigen Fluxus! Ich verwette mein letztes Hemd, dass das ein Teil der Absprache für seine Beförderung innerhalb der Dunkeleisenzwerge war.$B$BBringt unsere geheimen Pläne zurück, $n. Ich bin mir sicher, dass sie in der Nähe von Maltorius, tief in der Schlackengrube sind.",
  },
  [7723] = {
    ["T"] = "Verdammt seien diese fetten Finger",
    ["O"] = "Hansel Bleihand möchte, dass Ihr 20 Schwere Kriegsgolems tötet. $B$BDie Schweren Kriegsgolems wandern in der Gegend um den Kessel in der Sengenden Schlucht herum. Sobald Ihr genug getötet habt, kehrt zu Hansel zurück.",
    ["D"] = "Unglücklicherweise kann ich nicht mehr so austeilen, wie es früher einmal konnte, dank meines Problems hier, meine fetten Finger. Das Klima hier draußen und dazu der geringe Luftdruck lässt meine Finger einfach aufquellen. Die sehen schon aus wie gigantische Thelsamarer Blutwürste...$B$BDu wirst meine Aufträge ausführen müssen. $B$BAls erstes steht auf meiner Liste: Prügelt das letzte bisschen Leben aus den Schweren Kriegsgolems in der Schlucht. Leg los!",
  },
  [7724] = {
    ["T"] = "Feurige Bedrohung!",
    ["O"] = "Hansel Bleihand möchte, dass Ihr 20 große Lavaspinnen in der Sengenden Schlucht tötet. $B$BDie großen Lavaspinnen findet Ihr auf den Plateaus und in den Lavabecken der Sengenden Schlucht. Sobald Ihr genug getötet habt, kehrt zu Hansel zurück.",
    ["D"] = "Würd\' ich\'s nicht besser wissen, würd\' ich behaupten, dass jedes Mal, wenn der alte Hansel sich endlich \'mal ein wenig entspannen möchte, ihm die höheren Mächte einen weiteren Auftrag erteilen.$B$BDas macht mich echt nachdenklich, wisst Ihr? Ihr wisst doch was ich meine, oder?$B$BDie Lavaspinnen hier in der Gegend, haben in der letzten Zeit einen ganz schönen Krawall verursacht. Schlickfaust meint, dass sie Feuer spucken würden, und zwar aus ihren... Ähm, ja, jedenfalls beunruhigt so etwas die Kinder.$B$BSchätze wir werden sie erledigen müssen. Genau genommen wird das dein Job sein.",
  },
  [7725] = {
    ["T"] = "Und wieder unter den geschrumpften Riesen",
    ["O"] = "Benutzt Zorbins Ultraschrumpfer, um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt 10 Schrumpfüberreste, die auf den kleineren handlicheren Riesen liegen zu Zorbin von Schiller am Dock der Vergessenen Küste in Feralas.",
    ["D"] = "Ich weiß, dass Ihr schon Überreste für mich gesammelt habt... aber solltet Ihr auf der Suche nach einer zusätzlichen Aufgabe sein, könnte ich immer noch einige Überreste gebrauchen.$B$BIch kann zwar nicht genauso viel als Belohnung bieten wie zuvor, aber dafür wäre ich mehr als glücklich ein gutes Wort über Euch unter meinen Goblinfreunden verlauten zu lassen. Unter denen zumindest, die über Ohren verfügen. .$B$BGlaubt mir, $g Herr : Frau; wenn ich Euch sage, dass diese Goblins über wirklich ziemliche große Ohren verfügen.",
  },
  [7726] = {
    ["T"] = "Neuer Treibstoff fürs Schrumpfen",
    ["O"] = "Sammelt 6 Wasserelementarkerne von den Meereselementaren und Meeresschauern entlang der Vergessenen Küste von Feralas und bringt sie zurück zu Zorbin von Schiller in der Nähe des dortigen Docks.",
    ["D"] = "Mehr Arbeit, sagt Ihr? Hat meine heroische Aufgabe des Schleimsammelns von den Elementaren nicht Euren Durst nach Abenteuern gelöscht? Vielleicht wird etwas mehr davon den unerschöpflichen Brunnen, der Ihr seid, füllen...$B$BOk, Melodramatik beiseite, ich bräuchte noch ein paar Wasserelementarkerne. Ich versuche verzweifelt, einen Laden hier aufzumachen, also wird der Lohn leider nicht groß sein... aber ich verspreche, dass ich jedem in Hörweite erzählen werde, dass Ihr mir geholfen habt. Vertraut mir – Ich kenne eine Menge Leute im Kartell!",
  },
  [7727] = {
    ["T"] = "Incendosaurier? Was-auch-immer-Saurier?",
    ["O"] = "Hansel Bleihand möchte, dass Ihr 20 Incendosaurier tötet.$B$BDie Incendosaurier haben sich in den Lavabecken der Schlackengrube eingenistet. Ihr findet die Schlackengrube im Kessel der Sengenden Schlucht.$B$BSobald Ihr genug Incendosaurier getötet habt, kehrt zu Hansel Bleihand in der Sengenden Schlucht zurück.",
    ["D"] = "Meine Zehen, $G Kumpel: Mädel;. Meine verdammten Zehen sind angeschwollen. Wie soll ich irgendetwas jagen können, wenn meine Füße mich schon umbringen? Darum brauche ich Eure Hilfe.$B$BSkrange und Burninate haben mich beauftragt, diese Dinger zur Strecke zu bringen, damit sie weitere Expeditionen in die Lavabecken der Schlackengrube schicken können. Anscheinend stehen einige Incendosaurier oder so was in der Art diesem Vorhaben im Weg... Wollt Ihr hier Wurzeln schlagen oder habt Ihr die Incendosaurier schon erledigt?",
  },
  [7728] = {
    ["T"] = "GESTOHLEN: Schmiedewindform und ein Ausguckhandfernrohr",
    ["O"] = "Sucht die Schmiedewindform und das Ausguckhandfernrohr. Bringt diese Gegenstände zu Zuchtmeister Skrange in der Sengenden Schlucht.$B$BDie einzigen Informationen die Ihr habt sind: Die Schmiedewindform ist ein Schmiedewerkzeug welches von Schmieden verwendet wird und das Ausguckhandfernrohr ist ein unverzichtbares Werkzeug für Spähposten.",
    ["D"] = "Folgende Gegenstände wurden gestohlen:$B$BEine Schmiedewindform.$B$BEin Ausguckhandfernrohr.$B$BEs wurde eine Belohnung für das Zurückbringen der gestohlenen Gegenstände ausgesetzt.$B$BDie Thoriumbruderschaft ist sich sicher, dass diese Gegenstände von skrupellosen und neidischen Dunkeleisenzwergen aus der Sengenden Schlucht entwendet wurden.$B$BDas fragliche Fernrohr ist ein hervorragendes Werkzeug für Spähposten und die Schmiedeform ist schlicht unersetzlich. $B$BSolltet Ihr einen der vermissten Gegenstände finden, bringt sie zu Skrange.",
  },
  [7729] = {
    ["T"] = "STELLE ZU VERGEBEN: Erledige die Konkurrenz",
    ["O"] = "Tötet 15 Zuchtmeister der Dunkeleisenzwerge und 15 Sklavenhändler der Dunkeleisenzwerge. Kehrt zu Zuchtmeister Skrange zurück, sobald Ihr die Aufgabe erfüllt habt.",
    ["D"] = "Zuchtmeister Skrange sucht einige fähige Männer und Frauen, um seiner Konkurrenz in der Sengenden Schlucht ein wenig einzuheizen.$B$BInteressierte Bewerber sollten zur Sengenden Schlucht reisen und jeden Zuchtmeister und Sklavenhändler der Dunkeleisenzwerge erschlagen, der ihnen über den Weg läuft.$B$BMeldet Euch bei Zuchtmeister Skrange, wenn Eure Kleidung ausreichend mit Blut getränkt wurde.$B$BIhr werdet schon sehen, wann sie blutig genug ist, also spart Euch jegliche Rückfragen.",
  },
  [7730] = {
    ["T"] = "Zukk\'ash-Plage",
    ["O"] = "Bringt Hadoken Flinkschreiter im Camp Mojache in Feralas 20 Zukk\'ashknochenpanzer.",
    ["D"] = "Schenken wir unseren angesammelten Erkundungen Glauben, so gehört der Gegner südlich der Gnolle einer insektenartigen Rasse an. Offen gesagt spielt es keine Rolle, solange sie lebendig sind. Wenn sie die Gnolle gegen uns aufhetzen, dann sind sie eine mindestens genauso große Bedrohung - wenn nicht sogar eine größere - als die Gnolle selbst.$B$BDringt tief in das Gebiet vor, in dem die Insekten der Zukk\'ash hausen; auch bekannt als Gewundene Tiefe. Dezimiert ihre Anzahl, berichtet mir von Euren Entdeckungen und bringt ihre Knochenpanzer als Beweis Eurer Taten zurück.",
  },
  [7731] = {
    ["T"] = "Zornstachel",
    ["O"] = "Besiegt Zornstachel in der Gewundenen Tiefe und bringt seine Drüsen zu Hadoken Flinkschreiter im Camp Mojache in Feralas zurück.",
    ["D"] = "Einer unserer Späher berichtete, dass er während seiner Beobachtungen der Gewundenen Tiefe ein riesiges Insekt – viel größer als die anderen gesichteten Insekten – gesehen habe, das durch das verschlungene Gelände umherstreifte. Es ist nicht nur größer, es scheint auch eine Art Aura abzusondern, was die anderen Insekten nicht tun. Wir haben ihm einen Namen gegeben – Zornstachel.$B$BEine Untersuchung der Innereien dieser Bestie wäre von unschätzbarem Wert, solange wir nach dem besten Weg suchen, um mit dieser Bedrohung fertigzuwerden. Schneidet seine Sekretionsdrüsen heraus und bringt sie sofort zu mir zurück!",
  },
  [7732] = {
    ["T"] = "Bericht über die Zukk\'ash",
    ["O"] = "Bringt den Bericht über die Zukk\'ash aus Camp Mojache zu Zilzibin Tomtom. Er wohnt in der Gasse von Orgrimmar.",
    ["D"] = "Ihr habt mehr als richtig gehandelt, indem Ihr Euch der Bedrohung, die die Insekten der Zukk\'ash für das Camp Mojache darstellen, zugewandt habt. Die Gefahr, die von ihnen ausgeht, ist noch lange nicht vorüber. Deshalb würde ich Euch im Namen der Horde gerne um die Erledigung eines letzten Auftrags bitten.$B$BDies hier sind Aufzeichnungen, die alle Informationen, die wir über diese Zukk\'ash erhalten haben, detailliert beinhalten. Ich möchte, dass Ihr sie zu Zilzibin Tomtom in der Gasse in Orgrimmar bringt. Er ist ein Experte, wenn es um fremde Kreaturen wie diese geht; ich hoffe, er kann etwas mit den von uns gewonnenen Erkenntnissen anfangen.",
  },
  [7733] = {
    ["T"] = "Beste Qualität",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 10 Wutschrammyetibälge.",
    ["D"] = "Ob Ihr es glaubt oder nicht, ich habe eine noch viel bessere Quelle für Yetibälge entdeckt! Im nördlichen Teil von Feralas, tief in der Wutschrammfeste gibt es noch andere Yetis. Diese Wesen sind noch um einiges stärker, als jene mit denen Ihr schon zu tun hattet. Also, wenn Ihr eine größere Herausforderung sucht, sind diese Biester genau das Richtige für Euch.$B$BIch werde Euch großzügig entlohnen, wenn Ihr mir 10 Wutschrammyetibälge bringt!",
  },
  [7734] = {
    ["T"] = "Beste Qualität",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache in Feralas 10 Wutschrammyetibälge.",
    ["D"] = "Ob Ihr es glaubt oder nicht, ich habe eine noch viel bessere Quelle für Yetibälge entdeckt! Im nördlichen Teil von Feralas, tief in der Wutschrammfeste gibt es noch andere Yetis. Diese Wesen sind noch um einiges stärker, als jene mit denen Ihr schon zu tun hattet. Also, wenn Ihr eine größere Herausforderung sucht, sind diese Biester genau das Richtige für Euch.$B$BIch werde Euch großzügig entlohnen, wenn Ihr mir 10 Wutschrammyetibälge bringt!",
  },
  [7735] = {
    ["T"] = "Makelloser Yetibalg",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste den makellosen Yetibalg.",
    ["D"] = "Ihr habt einen makellosen Yetibalg gefunden, der besonders dick und widerstandsfähig ist - sogar robuster als die Yetibälge aus der Wutschrammfeste.$B$BIn Anbetracht der Tatsache, dass Pratt McGrubben in der Mondfederfeste mit Yetibälgen handelt, dürfte ihn dieser Fund besonders interessieren.",
  },
  [7736] = {
    ["T"] = "Nachschub an feurigem Fluxus: Königsblut",
    ["O"] = "Deliver 2 Incendosaur Scales, 4 Kingsblood and one Coal to Master Smith Burninate.",
    ["D"] = "Let\'s not waste more of my precious time with jibber jabber, $N. It\'s time to focus on replenishing our dwindling fiery flux supply.$BWhat I\'m gonna need from you is the following:$B$B*2 Incendosaur scales.$B*4 Kingsblood.$B*1 Coal.$B$BI\'ll take all that you can offer!$BAnd you\'ll do it fast if you wanna get in good with the Brotherhood.",
  },
  [7737] = {
    ["T"] = "Noch mehr Anerkennung erlangen",
    ["O"] = "",
    ["D"] = "",
  },
  [7738] = {
    ["T"] = "Perfekter Yetibalg",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache in Feralas den perfekten Yetibalg.",
    ["D"] = "Ihr habt einen Yetibalg gefunden, der beinahe perfekt scheint. Euch fällt schnell auf, dass dieser außergewöhnlich dick und widerstandsfähig ist - sogar robuster als die Yetibälge aus der Wutschrammfeste.$B$BIn Anbetracht der Tatsache, dass Jangdor Flinkschreiter im Camp Mojache mit Yetibälgen handelt, dürfte ihn dieser Fund besonders interessieren.",
  },
  [7761] = {
    ["T"] = "Schwarzfausts Befehl",
    ["O"] = "Das war ja vielleicht mal ein dummer Orc. Es sieht so aus, als müsstet Ihr dieses Brandzeichen finden, um an das Mal von Drakkisath zu gelangen. Damit sollte sich die Befehlskugel aktivieren lassen.$B$BDem Brief zufolge, wird das Brandzeichen von General Drakkisath bewacht. Vielleicht solltet Ihr diesem Hinweis nachgehen.",
    ["D"] = "Schwachkopf,$B$Bich habe Euch immer für unfähig gehalten, Fremde daran zu hindern, den Hort unseres Herrn zu betreten. Für den sehr wahrscheinlichen Fall Eures Todes hat diese Kugel einen eingebauten Sicherheitsmechanismus, der verhindert, dass sich Außenstehende direkt in den Pechschwingenhort teleportieren können.$B$BNur diejenigen, denen das Mal von Drakkisath in die Handfläche eingebrannt wurde, können die Kugel überhaupt benutzen. Glücklicherweise ist Drakkisath nicht einmal ansatzweise so inkompetent wie Ihr, Rüstmeister. Er selbst bewacht das Brandzeichen!$B$BKriegshäuptling Rend Schwarzfaust$B$BP.S. Verbrennt diesen Brief, Idiot!",
  },
  [7781] = {
    ["T"] = "Der Herrscher des Schwarzfels",
    ["O"] = "Bringt König Varian Wrynn in Sturmwind den Kopf von Nefarian.",
    ["D"] = "Ihr spürt wie längst vergessene Gefühle in Euch aufflammen. Der Schmerz des Sieges hallt durch Euer Innerstes. Euch zu Füßen liegt der vom Kampf gezeichnete, gigantische Kopf von Nefarian. Bringt ihn Eurem König Varian Wrynn, großer Held!",
  },
  [7782] = {
    ["T"] = "Der Herrscher des Schwarzfels",
    ["O"] = "Reist nach Sturmwind in das Tal der Helden und sprecht mit Feldmarschall Afrasiabi.",
    ["D"] = "Begebt Euch in das Tal der Helden und sprecht mit Feldmarschall Afrasiabi.",
  },
  [7783] = {
    ["T"] = "Der Herrscher des Schwarzfels",
    ["O"] = "Bringt Garrosh in Orgrimmar den Kopf von Nefarian.",
    ["D"] = "Ihr spürt wie längst vergessene Gefühle in Euch aufflammen. Der Schmerz des Sieges hallt durch Euer Innerstes. Euch zu Füßen liegt der vom Kampf gezeichnete, gigantische Kopf von Nefarian. Bringt ihn Eurem Kriegshäuptling, großer Held!",
  },
  [7784] = {
    ["T"] = "Der Herrscher des Schwarzfels",
    ["O"] = "Sprecht mit Oberanführer Runthak in Orgrimmar. Er wartet im Tal der Stärke auf Euch.",
    ["D"] = "Sucht Oberanführer Runthak im Tal der Stärke auf.",
  },
  [7785] = {
    ["T"] = "Untersuchung des Gefäßes",
    ["O"] = "Untersucht das Gefäß der Wiedergeburt und bringt es zu Hochlord Demitrian.",
    ["D"] = "Untersucht das Gefäß der Wiedergeburt, Sterblicher. Mit seiner Hilfe soll der Windsucher wieder auferstehen!",
  },
  [7786] = {
    ["T"] = "Donneraan der Windsucher",
    ["O"] = "Um Donneraan den Windsucher aus seiner Gefangenschaft zu befreien, müsst Ihr Hochlord Demitrian die rechten und linken Hälften der Fesseln des Windsuchers, 10 verzauberte Elementiumbarren und die Essenz des Feuerfürsten bringen.",
    ["D"] = "Ich habe Euch alles gesagt was ich weiß, $n. Es ist nun Eure Aufgabe Elementium zu finden. Ihr seid es, der die körperliche Form des Feuerfürsten zerstören muss.$B$BGewinnt dadurch den Segen von Donneraan.",
  },
  [7786] = {
    ["T"] = "Donneraan der Windsucher",
    ["O"] = "Um Donneraan den Windsucher aus seiner Gefangenschaft zu befreien, müsst Ihr Hochlord Demitrian die rechten und linken Hälften der Fesseln des Windsuchers, 10 verzauberte Elementiumbarren und die Essenz des Feuerfürsten bringen.",
    ["D"] = "Ich habe Euch alles gesagt was ich weiß, $n. Es ist nun Eure Aufgabe Elementium zu finden. Ihr seid es, der die körperliche Form des Feuerfürsten zerstören muss.$B$BGewinnt dadurch den Segen von Donneraan.",
  },
  [7787] = {
    ["T"] = "Donnerzorn erwache!",
    ["O"] = "Präsentiert Hochlord Demitrian die vom Wind berührte, schlummernde Klinge.",
    ["D"] = "Ihr habt den Windsucher bezwungen. Nehmt die schlummernde Klinge und die Materialen, die Ihr Demitrian für seinen Verrat geliefert habt, und zwingt den verlogenen Schuft, Donnerzorn zu erwecken.",
  },
  [7788] = {
    ["T"] = "Besiegt die Eindringlinge!",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.",
    ["D"] = "Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, $gjunger:junge:c; $C. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.$B$BBetretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.$B$BEin Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, $n und ich werde Euch belohnen.",
  },
  [7788] = {
    ["T"] = "Besiegt die Eindringlinge!",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.",
    ["D"] = "Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, $gjunger:junge:c; $C. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.$B$BBetretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.$B$BEin Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, $n und ich werde Euch belohnen.",
  },
  [7789] = {
    ["T"] = "Bezwingt die Eindringlinge der Silberschwingen",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor\'shan zurück.",
    ["D"] = "Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, $R. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!$B$BLasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, $n, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.",
  },
  [7789] = {
    ["T"] = "Bezwingt die Eindringlinge der Silberschwingen",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor\'shan zurück.",
    ["D"] = "Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, $R. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!$B$BLasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, $n, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.",
  },
