  [7838] = {
    ["T"] = "Großmeister der Arena",
    ["O"] = "Avast Lubber! Bring me 12 trinkets of the Arena Master.",
    ["D"] = "Avast Lubber!   Bring me 12 trinkets of the Arena Master and then ye shall be a Grandmaster of the Gurubashi Arena!   Arrrr!",
  },
  [7839] = {
    ["T"] = "Schläger der Blutfratzen",
    ["O"] = "Schmied Aschenbaum aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr seine verlorenen Werkzeuge ausfindig macht und dann zu ihm zurückkehrt.$B$BIhr erinnert Euch an Schmied Aschenbaums Worte, dass die Werkzeuge möglicherweise bei einem der Tempel der Blutfratzen zu finden seien, im Nordosten des Hinterlands. Ihr solltet auch den Lauerfels absuchen.",
    ["D"] = "Der Blutfratzenabschaum ist in unser Dorf eingedrungen und hat mein Werkzeug gestohlen! Ich habe zwar Ersatz, aber das Set, das sie mir geraubt haben, war das Geschenk eines gutherzigen Gnoms, der mir als junger Orc das Leben gerettet hat.$B$BIch möchte dieses Werkzeug wieder haben, $n - es bedeutet mir ungeheuer viel.$B$BMarkhor berichtete, dass die Werkzeuge nahe der Tempel von Agol\'watha und Shaol\'watha gesichtet wurden.$B$BDiese Tempel liegen im Nordosten des Hinterlands.$B$BFindet meine Werkzeuge!",
  },
  [7840] = {
    ["T"] = "Lards vergessene Brotzeit",
    ["O"] = "Lard aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Lards Brotzeit wieder findet. Kehrt nach Erfüllung dieser Aufgabe zu ihm zurück.$B$BLard erwähnte, dass er es auf der Insel im Norden vergessen habe. Achtet auf die Trolle der Blutfratzen.",
    ["D"] = "Lard Picknick gemacht hat, an geheimen Geheimplatz, als hässlicher, fieser Blutfratzentroll Lard mit großem Stock geschlagen hat. Lard zurück ins Dorf der Bruchhauer gerannt sein, aber Lard Brotzeit vergessen hat. Lard hungrig. Ihr vielleicht zurück gehen zu geheimen Geheimplatz und holen Brotzeit von Lard? Lard hat Angst. Gehen Norden! Insel dort im Norden seien geheimer Geheimplatz.",
  },
  [7841] = {
    ["T"] = "Eine Botschaft an die Wildhämmer",
    ["O"] = "Otho Moji\'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 5 Hochtalkundschafterinnen, 8 Hochtalspäher, 2 Hochtalschützen und 5 Hochtalwaldläuferinnen tötet. Kehrt nach vollendeten Taten zu ihm zurück.$B$BIhr findet die Hochtalelfen bei der Jagdhütte von Quel\'Danil im Nordwesten des Hinterlands.",
    ["D"] = "Die Wildhämmer fangen Geschäfte mit unseren Feinden an, Mann, schmieden neue Bündnisse. Diesmal sind es die Hochelfen, Mann.$B$B<Otho spuckt.>$B$BWir werden diesen Wildhammerzwergen wohl \'ne Botschaft überbringen müssen - \'ne Botschaft geschrieben mit dem Blut unserer Feinde. Tötet alle Hochtalelfen, alle die Euch über den Weg laufen, klar? Un\' lasst ihre Leichen als Warnung zurück.$B$BVerscherzt es Euch niemals mit den Trollen!",
  },
  [7842] = {
    ["T"] = "Eine weitere Botschaft an die Wildhämmer",
    ["O"] = "Otho Moji\'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihm 1 lange elegante Feder von Greifen besorgt, die das Hinterland bewohnen. Kehrt mit der Feder zu ihm zurück.$B$BEs ist bekannt, dass Greifen überall im Hinterland zu finden sind.",
    ["D"] = "Man sagt, dass die Wildhämmer den Greifen so nahe stehen wie die Trolle ihren Gefährten. Widerlich, aber wir können das zu unserem Vorteil nutzen, Mann. Wir senden ihnen eine weitere Botschaft.$B$BTötet alle Greifen, die Ihr im Hinterland seht und sammelt eine lange elegante Feder ein. Daraus machen wir dann die letzte Botschaft.",
  },
  [7843] = {
    ["T"] = "Die letzte Botschaft an die Wildhämmer",
    ["O"] = "Otho Moji\'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr zum Nistgipfel im Westen des Hinterlands reist und die letzte Botschaft an die Wildhämmer beim Brunnen im Stadtzentrum platziert. Kehrt nach Vollendung der Aufgabe zu ihm zurück.$B$BHütet Euch vor zornigen Wildhammerzwergen und niederträchtigen Mitgliedern der Allianz.",
    ["D"] = "<Otho überreicht Euch einen langen, blutverschmierten Speer, den mehrere Greifenfedern umhüllen.>$B$BNehmt den hier, Mann. Geht damit zum Nistgipfel, zum toten Stadtzentrum beim Brunnen. Platziert ihn dort! Rammt ihn richtig in den Boden und zeigt diesen Zwergen, dass die Trolle es ernst meinen! Kommt danach zu mir zurück... hoffentlich noch in einem Stück.",
  },
  [7844] = {
    ["T"] = "Kannibalistische Vettern",
    ["O"] = "Mystikerin Yayo\'jin aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 6 Skalpierer der Blutfratzen und 2 Sterndeuter der Blutfratzen tötet. Kehrt nach Vollendung dieser Aufgabe zu ihr zurück.$B$BYayo\'jin deutete an, dass man diese Trolle nahe der Tempel von Shaol\'watha und Agol\'watha finden könnte. Sie liegen im Nordosten des Hinterlands.",
    ["D"] = "Drei verschiedene Trollstämme bewohnen das Hinterland, $n. So viel zu den Gemeinsamkeiten der Stämme: Wir sind alle Trolle und wir leben im Hinterland.$B$BDie Blutfratzen sind eine verkommene Bande, die sich an anderen Trollen und Humanoiden nährt.$B$BWäre Zul\'jin hier, hätte er sie alle mit einem einzigen Faustschlag niedergeschmettert - ach, Zul\'jin ist noch immer nicht zurückgekehrt und wir sind uns selbst überlassen.$B$BEs liegt nun an Euch diese Gegend zu säubern!",
  },
  [7845] = {
    ["T"] = "Die Entführung des Ältesten Stumpfhauer",
    ["O"] = "Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihren Gefährten, den Ältesten Stumpfhauer, aus Jintha\'Alor befreit.$B$BMan sagte Euch, dass er hoch oben in Jintha\'Alor gefangen gehalten wird - der Hauptstadt der Blutfratzen im Süden des Hinterlands.",
    ["D"] = "Mein Gefährte wurde von den wilden Blutfratzen bei einem dreisten Überfall um Mitternacht verschleppt. Unsere Spione haben mir berichtet, dass er noch am Leben ist und hoch oben in Jintha\'Alor gefangen gehalten wird. Man sagt, dass die üble Priesterin Hexx ihn als Opfer für den gesichtslosen Blutgott Hakkar vorbereiten lässt.$B$BIhr müsst ihn retten, $n! Unser Dorf könnte einen so großen Verlust nicht verschmerzen.",
  },
  [7846] = {
    ["T"] = "Erlangt den Schlüssel!",
    ["O"] = "Der Älteste Stumpfhauer in Jintha\'Alor möchte, dass Ihr Hitah\'ya die Bewahrerin tötet, um in den Besitz des Fesselnschlüssels zu kommen. Kehrt zum Ältesten Stumpfhauer zurück, wenn Ihr den Schlüssel habt.",
    ["D"] = "Der Schlüssel zu meinen Fesseln ist im Besitz von Hitah\'ya der Bewahrerin. Sie hält sich in der nahegelegenen Höhle auf. Seid vorsichtig, $C, sie wird äußerst gut bewacht.$B$BTötet sie und bringt dann den Schlüssel zurück, um meine Fesseln zu öffnen. Erst dann werde ich frei und in der Lage sein, nach Hause zurückzukehren.",
  },
  [7847] = {
    ["T"] = "Rückkehr zu Ura Stumpfhauer",
    ["O"] = "Der Älteste Stumpfhauer in Jintha\'Alor möchte, dass Ihr Ura Stumpfhauer im Dorf der Bruchhauer die Neuigkeit von seiner Befreiung überbringt.",
    ["D"] = "Kehrt zu meiner Gefährtin Ura Stumpfhauer ins Dorf der Bruchhauer zurück. Richtet ihr aus, dass ich heimkehren werde! Sie wird Euch sicherlich belohnen.",
  },
  [7848] = {
    ["T"] = "Abstimmung mit dem Kern",
    ["O"] = "Begebt Euch zum Portal in den Schwarzfelstiefen, das in den Geschmolzenen Kern führt, und findet ein Kernfragment. Kehrt mit dem Fragment zu Lothos Felsspalter im Schwarzfels zurück.",
    ["D"] = "Risse und Abgründe bilden sich um uns herum und kollabieren wieder, $R. Keine zwei Schritte von mir entfernt, ist ein Riss, der hinab in die tiefsten Tiefen des Schwarzfels führt, bis in die Ruhestätte des Feuerfürsten.$B$BÜberrascht? Wie bedauerlich... Ihr sterblichen Völker versteht einfach nichts, was Ihr nicht sehen, anfassen oder fühlen könnt.$B$BIch versichere Euch, das Portal ist da und es ist passierbar.$B$BHabe ich Euer Interesse geweckt? Eine Anpassung ist simpel. Begebt Euch in die Schwarzfelstiefen und findet ein Kernfragment. Bringt es zu mir zurück und ich werde Eure Essenz mit dem Portal abstimmen.",
  },
  [7849] = {
    ["T"] = "Die getrennten Brüder",
    ["O"] = "Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr die Überreste seines Zwillingsbruders bergt. Findet den Schädel und die Knochen des Jägers Malkhor und bringt sie zu Jäger Markhor zurück.$B$BMarkhor erwähnte, dass die Überreste seines Bruders an die Schlamme und Wölfe der Blutfratzen verfüttert wurden. Er sagte weiterhin, dass es in Jintha\'Alor eine Grube mit Schlammen gibt sowie einen Wolfsbau. Beide liegen in der Nähe der Tempelspitze.",
    ["D"] = "Wie viele in unserem Dorf, habe auch ich einen Nahestehenden verloren. Ach, es war mein Zwillingsbruder Malkhor - er wurde beim selben Überfall wie Stumpfhauers Gefährte von den Blutfratzen verschleppt.$B$BDas Schicksal sollte ihn traurigerweise nicht vom Tod verschonen und er wurde schnell und brutal hingerichtet. Seine Überreste wurden an die Schlamme und Wölfe der Blutfratzen verfüttert.$B$BWir können zwar nicht mehr sein Leben retten, aber es ist noch nicht zu spät, um seine Seele zu befreien. Bergt seine Überreste, $n.",
  },
  [7850] = {
    ["T"] = "Dunkle Gefäße",
    ["O"] = "Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Euch 5 Gefäße mit besudeltem Blut aus Jintha\'Alor beschafft. Kehrt dann damit zu Ura Stumpfhauer zurück.",
    ["D"] = "Die Blutfratzen kämpfen mit übernatürlicher Wildheit. Dies liegt an der elenden Magie, die von der üblen Priesterin Hexx gewirkt wird.$B$BIhr werdet in ganz Jintha\'Alor dunkle Gefäße mit besudeltem Blut finden. Die Gefäße verstrahlen die elende Magie des gesichtslosen Blutgottes, verleihen den Blutfratzen Macht und treiben sie zusätzlich in den Wahnsinn.$B$BStehlt diese Gefäße und bringt sie zu mir, damit ich sie von der Besudelung reinigen und letztendlich die Gewalt des Blutgottes zerschlagen kann.",
  },
  [7861] = {
    ["T"] = "GESUCHT: Üble Priesterin Hexx und ihre Diener",
    ["O"] = "Ihr habt die Anweisung erhalten die üble Priesterin Hexx und 5 Aman\'zasiwachen der Blutfratzen zu töten. Habt Ihr Eure Aufgabe erfüllt, so sprecht mit Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland.$B$BDie üble Priesterin Hexx und die Aman\'zasiwachen halten sich hoch oben in Jintha\'Alor im Hinterland auf.",
    ["D"] = "Der üblen Priesterin Hexx und ihren Dienern soll im Namen von Ura Stumpfhauer Gerechtigkeit widerfahren!$B$BWer auch immer die üble Priesterin Hexx und alle ihre Aman\'zasiwachen vernichten möge, soll eine bedeutende Entlohnung erhalten.",
  },
  [7862] = {
    ["T"] = "Offener Posten: Wachbefehlshaber der Bruchhauer",
    ["O"] = "Ihr wurdet mit der Vernichtung von 5 Berserkern der Blutfratzen, 3 Schattenjägern der Blutfratzen, 3 Bluttrinkern der Blutfratzen und 2 Seelenfressern der Blutfratzen beauftragt.$B$BKehrt nach Beendigung dieser Aufgabe zu Ura Stumpfhauer ins Dorf der Bruchhauer im Hinterland zurück.",
    ["D"] = "Mit dem Verlust von Malkhor ist auch wieder der Posten des Wachbefehlshabers der Bruchhauer unbesetzt. Sollte es Euch interessieren Befehlshaber der Bruchhauerwachen zu werden, so erfüllt die folgenden Aufgaben und reicht Eure Bewerbung bei Ura Stumpfhauer ein:$B$BRottet die Berserker der Blutfratzen aus.$B$BRottet die Schattenjäger der Blutfratzen aus.$B$BRottet die Bluttrinker der Blutfratzen aus.$B$BRottet die Seelenfresser der Blutfratzen aus.",
  },
