  [7937] = {
    ["T"] = "Euer Schicksal erwartet Euch...",
    ["O"] = "Reist zum Holzfällerlager des Osttals im Wald von Elwynn und sucht nach Eurem Schicksal.",
    ["D"] = "\"Euer Schicksal erwartet Euch im Osttal.\"$B$BDas Horoskop, das Sayge Euch überreicht hat, fühlt sich beim Berühren leicht warm an. Haltet Ihr es etwas fester in der Hand, so seht Ihr das Holzfällerlager des Osttals im Wald von Elwynn vor Euch. Beim Fokussieren auf dieses Bild, seht Ihr einen Heuhaufen neben einem Stall.",
  },
  [7938] = {
    ["T"] = "Euer Schicksal erwartet Euch...",
    ["O"] = "Reist zu den Todesminen in Westfall und sucht nach Eurem Schicksal.",
    ["D"] = "\"Euer Schicksal erwartet Euch in den Todesminen.\"$B$BDas Horoskop, das Sayge Euch überreicht hat, fühlt sich beim Berühren leicht warm an. Haltet Ihr es etwas fester in der Hand, so seht Ihr die Todesminen in Westfalls Ruinen von Mondbruch. Beim Fokussieren auf dieses Bild, windet Ihr Euch durch verwinkelte Passagen bis Ihr ein schimmerndes Portal erreicht... eines, das in das Herz der Minen selbst führt. Beim Durchschreiten taucht eine seltsame Truhe aus dem Nichts auf.",
