  [8120] = {
    ["T"] = "Die Schlacht um das Arathibecken!",
    ["O"] = "Erobert die Mine, das Sägewerk, die Schmiede, die Farm und die Ställe und kehrt dann zu Todesmeister Dunkels in Hammerfall zurück.",
    ["D"] = "Wie Ihr sicherlich schon gehört habt, entsendet der Bund von Arathor seine Truppen ins Arathibecken. Versessen auf Nahrung und Vorräte, können diese Idioten der Allianz es kaum erwarten, die riesigen Ressourcen des Beckens zu kontrollieren. Wir müssen ihnen zeigen, dass Arathi niemals wieder das Zuhause der Menschen sein wird!$B$BGeht ins Arathibecken und greift die Mine, das Sägewerk, die Schmiede und die Ställe an. Reißt die feindlichen Banner herunter und besetzt das Gebiet für die Horde.$B$BGeht, $n. Kehrt zu mir zurück, sobald Ihr Eure Aufgabe erfüllt habt.",
  },
  [8121] = {
    ["T"] = "Erobert vier Stützpunkte",
    ["O"] = "Haltet vier Stützpunkte zur selben Zeit im Arathibecken und kehrt danach zu Todesmeister Dunkels nach Hammerfall zurück.",
    ["D"] = "$n, während die Schlachten über das Arathibecken wüten, haben sich die Neuigkeiten über Eure Taten wie ein Lauffeuer verbreitet. Wir schätzen Euch hier sehr und daher hoffe ich, dass Ihr die Mission erfüllen könnt, die ich Euch nun gebe.$B$BEs ist Zeit dem Bund von Arathor eine Nachricht zu senden. Wir müssen ihnen zeigen, dass ihre Stellungen im Arathibecken leicht angreifbar und schnell verloren sind. Das werden wir ihnen beweisen, indem wir gleichzeitig vier strategische Punkte im Arathibecken erobern und kontrollieren.$B$BSeid erfolgreich, $n und erntet mehr Respekt unter den Entweihten.",
  },
  [8122] = {
    ["T"] = "Erobert fünf Stützpunkte",
    ["O"] = "Haltet fünf Stützpunkte zur selben Zeit im Arathibecken und kehrt danach zu Todesmeister Dunkels nach Hammerfall zurück.",
    ["D"] = "Es ist schwer an Informationen zu gelangen, jetzt da wir so tief in diesem Konflikt stecken. Doch unsere Spione berichten, dass die Entschlossenheit des Bundes von Arathor schwindet. Wir sollten diesen Moment ausnutzen und eine große Offensive starten! Kehrt in das Arathibecken zurück und werft Euch unserem Feind entgegen. Verflucht und vernichtet sie, bis Ihr fünf strategische Punkte kontrolliert! Ihre Soldaten sollen blutend und schreiend in die Flucht geschlagen werden und ganz Azeroth mit zitternder Stimme von Eurer Grausamkeit berichten.$B$BGeht jetzt und erringt einen großen Sieg für die Entweihten.",
  },
  [8123] = {
    ["T"] = "Zerschlagt die Versorgungslinien Arathors",
    ["O"] = "Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.",
    ["D"] = "Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.$B$BIhr könnt uns helfen, $n. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpünkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.",
  },
  [8123] = {
    ["T"] = "Zerschlagt die Versorgungslinien Arathors",
    ["O"] = "Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.",
    ["D"] = "Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.$B$BIhr könnt uns helfen, $n. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpünkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.",
  },
  [8124] = {
    ["T"] = "Noch mehr Vorratskisten",
    ["O"] = "Win the battle for Arathi Basin, get an Arathi Resource Crate, and return it to Deathstalker Mortis at Hammerfall.",
    ["D"] = "The League of Arathor is sparing no expense in their move to retake Arathi. They, and all the races of the Alliance, now stream to this distant region, intent on expanding their hold from Refuge Pointe. We must stop them, and the best means to stop them is to take their supplies.$B$BYou can help us, $N. Enter Arathi Basin, known for its rich mines, fertile land and skilled craftsman. Capture and control every base you can, win the battle, and return to me with the resources you gain.",
  },
