  [8181] = {
    ["T"] = "Konfrontiert Yeh\'kinya",
    ["O"] = "Sprecht mit Yeh\'kinya.",
    ["D"] = "Meine eingehende Beschäftigung mit den Schrifttafeln von Mosh\'aru bestätigt den Verdacht auf schlechte Neuigkeiten, $n. Hakkars wahre Gestalt, der Gott selbst, kann mit genau dem Ei wiedergeboren werden, in dem Ihr ihn gefangen habt. Yeh\'kinya sagte Euch, dass es seiner Macht Einhalt gebiete, aber eigentlich dient es seiner Wiederherstellung! Der Troll hat Euch in die Irre geführt!$B$BGeht zu Yeh\'kinya und verlangt das Ei von ihm. Betet, dass wir Hakkars Rückkehr noch rechtzeitig verhindern können!",
  },
  [8182] = {
    ["T"] = "Die Hand von Rastakhan",
    ["O"] = "Sprecht mit Molthor im Schlingendorntal.",
    ["D"] = "$n, unsere schlimmsten Befürchtungen haben sich erfüllt. Laut der Legende herrschte der Seelenschinder in der alten Welt der Trolle mit uneingeschränkter Macht und ließ das Böse und Terror walten. Wenn er jetzt nicht aufgehalten werden kann, dann könnte sich seine neue Schreckensherrschaft in ganz Azeroth verbreiten!$B$BEs gibt eine Gesandtschaft von Zandalaritrollen, die uns beim Eindämmen dieser finsteren Flut behilflich sein könnte. Sie kampieren auf der Insel Yojamba, fern der Küste des Schlingendorntals, westlich der Ruinen von Zul\'Kunda. Sprecht mit ihrem Anführer Molthor und betet dafür, dass er weiß, wie man Hakkar bezwingen kann.",
  },
  [8183] = {
    ["T"] = "Das Herz von Hakkar",
    ["O"] = "Bringt das Herz von Hakkar zu Molthor auf die Insel Yojamba.",
    ["D"] = "Das Herz von Hakkar brennt noch immer im Inneren. Obwohl der Seelenschinder besiegt ist, bleibt das Wesen seiner Macht doch noch in dem verfluchten Organ enthalten.",
  },
  [8184] = {
