  [8231] = {
    ["T"] = "Wellenjagd",
    ["O"] = "Bringt 6 Wellenhauerschuppen zu Ogtinc in Azshara. Ogtinc wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath.",
    ["D"] = "Der Wellenhauer ist ein amphibisches Wildtier, das nur hier in Azshara vorkommt. Er ist gewandt und zäh.$b$bDie robusten, reflektierenden Schuppen eines Wellenhauers spiegeln die Stärke des Jägers wider.$b$bSpürt Wellenhauer entlang der Küste Azsharas auf, jagt sie und bringt mir ihre Schuppen.",
  },
  [8232] = {
    ["T"] = "Der grüne Drache",
    ["O"] = "Bringt Morphaz\' Zahn zu Ogtinc in Azshara. Ogtinc wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath.",
    ["D"] = "Ich entsende Euch nun auf eine gefährliche Jagd. Es gilt den grünen Drachen, Morphaz, zu bezwingen und mir seinen Zahn zu bringen. Morphaz haust tief im Tempel von Atal\'Hakkar, dessen Eingang in den Sümpfen des Elends liegt.$b$bWenn Ihr ihn besiegen möchtet, solltet Ihr nicht alleine gehen. Dieser Drache ist der Feind von vielen - sucht nach anderen, die ihn ebenfalls zu vernichten suchen.$b$bDer Zahn des Drachen steht für die Macht, den ein Jäger in der Gesellschaft anderer findet. Bringt ihn zu mir und Ihr werdet viel gelernt haben.",
  },
  [8233] = {
    ["T"] = "Ein simples Anliegen",
    ["O"] = "Sprecht mit Lord Jorach Rabenholdt im Rabenholdtanwesen.",
    ["D"] = "Lord Jorach Rabenholdt hat nach Euch rufen lassen. Die Angelegenheit ist geheim und ich kann auch nicht mehr dazu sagen.$b$bDas Rabenholdtanwesen liegt im Vorgebirge des Hügellands - haltet nach einem Pfad im Gebirge nordwestlich von Burg Durnholde Ausschau.",
  },
  [8234] = {
    ["T"] = "Die versiegelte azurblaue Tasche",
    ["O"] = "Erlangt die versiegelte azurblaue Tasche von dem Schamanen der Holzschlundfeste in Azshara zurück. Bringt sie anschließend zum Erzmagier Xylem, der ebenfalls in Azshara zu finden ist.",
    ["D"] = "Eins nach dem anderen. Ich habe eine kleine blaue Tasche verloren, deren Inhalt von privater Natur ist.$b$bWie dem auch sei, versucht erst gar nicht den Dieben nachzustellen, denn sie sind bereits tot. Wir wissen jedoch, dass diese Narren die Tasche nicht öffnen konnten. Sie verkauften sie also an einen Schamanen der Holzschlundfeste in Azshara als heiliges Relikt. Raffiniert.$b$bIch möchte, dass Ihr nach Azshara reist und die Tasche von dem Schamanen durch Geschick... oder mittels Kraft wiedererlangt. Ich glaube mit ersterer Methode dürftet Ihr mehr Glück haben. Habt Ihr sie, bringt sie zu Erzmagier Xylem in Azshara.",
  },
  [8235] = {
    ["T"] = "Verschlüsselte Fragmente",
    ["O"] = "Bringt Erzmagier Xylem in Azshara 10 verschlüsselte Fragmente.",
    ["D"] = "Lord Rabenholdt bittet uns beide um einen Gefallen. Er wünscht die Verzauberung von dieser Tasche zu entfernen.$B$BUnglücklicherweise hat man mir meine Abhandlungen über die Bannung von Zaubern vor Kurzem entwendet, nur um von Wilden zerstört zu werden. Die Überreste sind jedoch noch immer von Wert für mich.$B$BWenn Ihr mir genug verschlüsselte Fragmente bringen könnt, kann ich die Abhandlungen wiederherstellen. Am besten tötet Ihr dafür die Wildtiere Azsharas, da diese Kreaturen die Tendenz besitzen, alles aufzusammeln, was sie so finden können.",
  },
  [8236] = {
    ["T"] = "Der azurblaue Schlüssel",
    ["O"] = "Bringt den azurblauen Schlüssel zu Lord Jorach Rabenholdt.",
    ["D"] = "Es wird einige Zeit dauern, die Fragmente zu entschlüsseln und den Zauber zu bannen. Sobald ich mein Werk vollendet habe, werde ich die Tasche Lord Rabenholdt persönlich übergeben.$B$BAllerdings gibt es da noch das Problem mit dem Schlüssel, von dem gesagt wird, er befände sich im Besitz von Morphaz, einem grünen Drachen. Er haust tief im Tempel von Atal\'Hakkar, dessen Eingang in den Sümpfen des Elends liegt.$B$BVersammelt Eure getreusten Freunde und begebt Euch zu seinem Hort. Lord Rabenholdt erwartet Euch mit dem Schlüssel.",
  },
  [8238] = {
    ["T"] = "Münzen der Gurubashi, Blutfratzen und Bleichborken",
    ["O"] = "",
    ["D"] = "I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul\'Gurub, and each is imbued with subtle but powerful mojo.$B$BThere are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin\'wosho the Trader; he offers our heroes special items for them.",
  },
  [8239] = {
    ["T"] = "Münzen der Sandwüter, Schädelspalter und Blutskalpe",
    ["O"] = "",
    ["D"] = "I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul\'Gurub, and each is imbued with subtle but powerful mojo.$B$BThere are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin\'wosho the Trader; he offers our heroes special items for them.",
  },
  [8240] = {
    ["T"] = "Ein Schmuckstück für Zanza",
    ["O"] = "Zerstört jedes Schmuckstück der Hakkari, das Ihr in Zul\'Gurub findet, am Altar von Zanza auf der Insel Yojamba. Sprecht ganz in der Nähe mit Vinchaxa, sobald Ihr dies getan habt.",
    ["D"] = "Die Zerstörung der zahlreichen Schmuckstücke der Hakkari ist für uns von großer Bedeutung. Wir heißen sogar Außenseiter in unserem Stamm willkommen, um uns dabei in eigener Sache zu helfen!$B$BBringt, was auch immer für ein Schmuckstück ihr in Zul\'Gurub finden solltet, zum Altar von Zanza und zerstört es. Ich werde Euch nur für dieses eine Mal eine zusätzliche Ehrenmünze unseres Stammes geben; dies liegt hoch über dem, was Ihr normalerweise erhalten würdet.$B$BWerdet selbst Zeuge des Segens von Zanza!",
  },
  [8241] = {
    ["T"] = "Nachschub an feurigem Fluxus: Eisen",
    ["O"] = "",
    ["D"] = "Let\'s not waste more of my precious time with jibber jabber, $R. It\'s time to focus on replenishing our dwindling fiery flux supply.$B$BWhat I\'m gonna need from you is the following:$B$B*Incendosaur scales.$B$B*Iron bars.$B$B*Coal.$B$BI\'ll take all that you can offer!$B$BAnd you\'ll do it fast if you wanna get in good with the Brotherhood.",
  },
  [8242] = {
    ["T"] = "Nachschub an feurigem Fluxus: Schweres Leder",
    ["O"] = "",
    ["D"] = "Let\'s not waste more of my precious time with jibber jabber, $R. It\'s time to focus on replenishing our dwindling fiery flux supply.$B$BWhat I\'m gonna need from you is the following:$B$B*Incendosaur scales.$B$B*Heavy Leather.$B$B*Coal.$B$BI\'ll take all that you can offer!$B$BAnd you\'ll do it fast if you wanna get in good with the Brotherhood.",
  },
  [8243] = {
    ["T"] = "Zanzas wirksame Tränke",
    ["O"] = "I will allow you to choose one from the three I offer; in exchange, I require three Zandalar Honor Tokens. Note that only the effects of a single one may course through your spirit at any given time.",
    ["D"] = "With your increased status amongst the tribe comes access to some of our more potent potables. Behold... these brews are strong in the mojo - blessed by Zanza and fit for adventurers from all walks of life!",
  },
  [8243] = {
    ["T"] = "Zanzas wirksame Tränke",
    ["O"] = "I will allow you to choose one from the three I offer; in exchange, I require one Zandalar Honor Token. Note that only the effects of a single one may course through your spirit at any given time.",
    ["D"] = "With your increased status amongst the tribe comes access to some of our more potent potables. Behold... these brews are strong in the mojo - blessed by Zanza and fit for adventurers from all walks of life!",
  },
  [8246] = {
    ["T"] = "Das Siegel der Zandalari",
    ["O"] = "I ask for fifteen Zandalar Honor Tokens in exchange for your choice of one signet. If you have the tokens ready, then I\'m ready to make a deal!",
    ["D"] = "May - for someone as exalted among the Zandalar as you are, I have something very special for you. Direct from our home in the South Seas... the Signets of the Zandalar! These signets are used to enhance any shoulder item you may possess. Should you seek might, mojo, or serenity - I have what you need!",
  },
  [8249] = {
    ["T"] = "Plunderkisten benötigt",
    ["O"] = "",
    ["D"] = "",
  },
  [8250] = {
    ["T"] = "Geschick der Magier",
    ["O"] = "Sucht nach Sanath Lim-yo und verschafft Euch Zugang zu Erzmagier Xylem.",
    ["D"] = "Erzmagier Xylem hat seit kurzer Zeit begonnen sich umzuhören... nach jemanden mit Eurer Erfahrung.$b$bFalls Ihr ihn aufsuchen möchtet, dann könnt Ihr ihn in Azshara antreffen. Sein Turm steht hoch über den Klippen und der einzige Weg, der dort hinaufführt, ist ein Teleportationszauber, der von seinem arroganten Diener, Sanath Lim-yo, gewirkt wird.$b$bSanath kampiert unterhalb des Turms. Um ihn zu finden, müsst Ihr zu der Stelle reisen, an der sich Azsharas Hauptstraße beim Eingang zu den Ruinen von Eldarath gabelt. Geht von dort aus direkt nach Norden bis Ihr zu den Klippen kommt.",
  },
  [8251] = {
    ["T"] = "Magischer Staub",
    ["O"] = "Bringt Erzmagier Xylem in Azshara 10 glitzernde Staubproben.",
    ["D"] = "Die Blutelfen in Azshara haben eine Methode entwickelt, um magische Gegenstände, die sie erhalten, zu Staub zu pulverisieren. Dieser glitzernde Staub, der so produziert wird, gleicht nicht jenem, der sich durch eine übliche Entzauberung herstellen lässt.$B$BIch habe bereits meinen Lehrling entsendet, um durch Handel diesen Staub zu erlangen, doch er wurde ohne Umschweife grausam ermordet. Dafür müssen sie bezahlen.$B$BDie Zeiten der Konversation sind vorbei, $n. Sucht nach den Blutelfen entlang der Klippen von Azshara und wendet \"überzeugendere\" Mittel an, um an den glitzernden Staub zu kommen.",
  },
  [8252] = {
    ["T"] = "Die Koralle der Sirenen",
    ["O"] = "Sammelt 6 verzauberte Korallen und bringt sie zum Erzmagier Xylem.",
    ["D"] = "Seht Euch nur um. Die Ruinen und Strände sind buchstäblich von den Grollflossen überrannt worden. Unlängst haben sie sich soweit mit dem Land vertraut gemacht, dass sie die seltensten und wertvollsten Elemente entdeckt haben, die in Azshara heimisch sind.$B$BGenau genommen spreche ich von der verzauberten Koralle - sie werden aus mir unerfindlichen Gründen davon angezogen. In der Bucht gibt es nur noch sehr wenige Exemplare, die für meine Forschungen jedoch unverzichtbar sind. Ich brauche Euch, um mir verzauberte Korallen von den Sirenen der Grollflossen zu beschaffen.",
  },
  [8253] = {
    ["T"] = "Vernichtet Morphaz",
    ["O"] = "Beschafft den arkanen Splitter von Morphaz\' Leichnam und kehrt mit ihm zu Erzmagier Xylem zurück.",
    ["D"] = "Es gibt einen grünen Drachen, tief im Versunkenen Tempel von Atal\'Hakkar, der sich Morphaz nennt.$B$BVor Jahren überwältigte er mich aus einem kühnen Hinterhalt heraus und verschlang meinen Lehrling mit Haut und Haaren. Der Verlust eines Lehrlings ist mir zwar nicht unbekannt, allerdings trug dieser einen arkanen Splitter von unübertroffener Größe und Qualität.$B$BBitte versammelt Eure mächtigsten Verbündeten und dringt in die Tiefen des Tempels vor. Zerstört Morphaz und entreißt ihm den arkanen Splitter aus seinem Magen.",
  },
  [8254] = {
    ["T"] = "Cenarische Hilfe",
    ["O"] = "Sucht Ogtinc in Azshara auf.",
    ["D"] = "Es ist der Weg des Göttlichen, mit dem denen geholfen wird, die in Not sind. Das wie ist unbedeutend - allein das warum zählt.$b$bDer Zirkel des Cenarius ist in derartigen Nöten. Sprecht mit Ogtinc, um ihnen in ihrer Notlage auszuhelfen. Er wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath.",
  },
  [8255] = {
    ["T"] = "Renner für einen höheren Zweck",
    ["O"] = "Beschafft 4 gesunde Rennerdrüsen und bringt sie zu Ogtinc in Azshara. Ihr findet ihn auf den Klippen, nordöstlich der Ruinen von Eldarath.",
    ["D"] = "Die in Azshara lebenden Mooshufrenner sind prachtvolle Tiere, die ursprünglich aus dem Teufelswald stammen. Sie sind von Natur aus resistent gegen Krankheiten und Gifte und blieben so vor der Verderbnis in diesem Gebiet verschont. In ihrer ganzen Weisheit haben sie schlicht beschlossen, den Teufelswald zu verlassen.$B$BEs schmerzt mich von ganzem Herzen, dass ich Euch bitten muss, diese majestätischen Lebewesen für mich zu jagen. Sammelt vier Rennerdrüsen als erste Zutat für ein regenerierendes Mittel - ein Mittel, mit dem wir den Teufelswald heilen möchten.",
  },
  [8256] = {
    ["T"] = "Sekret des Untodes",
    ["O"] = "Beschafft ein Sekret des Untodes für Ogtinc in Azshara. Ihr findet ihn auf den Klippen, nordöstlich der Ruinen von Eldarath.",
    ["D"] = "Für dieses regenerierende Mittel braucht Ihr ein simples Rezept: ein Teil Gift, zwei Teile Heilmittel. Ihr habt bereits einen Teil des Heilmittels beschafft, nun müsst Ihr das Gift sammeln.$B$BMächtige Untote, wie die verweilenden Hochgeborenen, welche die Ruinen von Eldarath durchstreifen, sind dermaßen vom Bösen durchdrungen, dass es sich manchmal als eine Art dickflüssige, grünliche Flüssigkeit manifestiert. Unter den Lebenden ist diese Substanz als Sekret des Untodes bekannt.$B$BDie Wirksamkeit dieses widerwärtigen Elements ist derart mächtig, dass es genügen wird, wenn Ihr mir eine einfache Dosis des Sekrets bringt.",
  },
  [8257] = {
    ["T"] = "Morphaz\' Blut",
    ["O"] = "Tötet Morphaz im Versunkenen Tempel von Atal\'Hakkar und bringt Greta Mooshuf im Teufelswald sein Blut. Der Eingang zum Versunkenen Tempel liegt in den Sümpfen des Elends.",
    ["D"] = "Jetzt zum letzten Teil, dem zweiten Heilmittel.$B$BDer grüne Drache Morphaz ist dafür bekannt, gegen alle Formen von Krankheit und Gift immun zu sein - viele Druiden erlitten den Tod, um uns diese Nachricht zu übermitteln. Es gibt keine Zweifel, dass sein Blut die für uns nötige Wirkung hat.$B$BEin Kurier des Zirkels des Cenarius wird die von Euch bereits beschafften Zutaten übernehmen, während Ihr nach der letzten sucht. Versammelt Eure Verbündeten und sucht den Unterschlupf des Drachen im Versunkenen Tempel von Atal\'Hakkar auf. Bringt sein Blut anschließend zu Greta Mooshuf im Teufelswald.",
  },
  [8258] = {
    ["T"] = "Die Bedrohung durch Schattensichel",
    ["O"] = "Wendet das Orakel der Anrufung inmitten des Kellergewölbes des Großen Ossuariums in Scholomance an. Ihr werdet damit Geister herbeirufen, die es zu bekämpfen gilt. Nach Vernichtung dieser Geister wird der Todesritter Schattensichel auftauchen; besiegt ihn.$B$BBringt Schattensichels Kopf zu Sagorne Gratläufer im Tal der Weisheit in Orgrimmar.",
    ["D"] = "Der Todesritter Schattensichel herrscht über das Große Ossuarium in Scholomance. Er droht die spirituelle Ebene zu seinen eigenen Zwecken zu missbrauchen... Er muss gestoppt werden! Dabei kommt das Orakel der Anrufung, bei dessen Herstellung Ihr behilflich wart, zum Einsatz.$B$BSetzt das Orakel inmitten des Kellergewölbes des Großen Ossuariums ein, um Schattensichels Geistergefolge herbeizurufen. Besiegt die Geister und der Todesritter selbst wird erscheinen. $n, bringt mir seinen Kopf! Lediglich seine Niederlage wird den Schaden, den er der spirituellen Ebene zugefügt hat, wieder umkehren können!",
  },
