  [8275] = {
    ["T"] = "Die Rückeroberung von Silithus",
    ["O"] = "Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus.",
    ["D"] = "Ihr Helden der Allianz, horcht auf! Die Bedrohung durch die Silithiden blieb zu lange unbeachtet. Ohne einen wirklichen Gegner, der sie bekämpft, haben sie sich die ganze Zeit über auf eine großangelegte Invasion vorbereitet.$B$BDer Zirkel des Cenarius sammelt seine Streitkräfte in Silithus, um diesen verachtungswürdigen Kreaturen den Krieg zu erklären. Alle kampftauglichen Abenteurer werden hiermit aufgerufen, sich uns anzuschließen!$B$BWartet nicht, bis es zu spät ist! Helft uns bei der Verteidigung von Azeroth! Meldet Euch für genauere Informationen bei Windruferin Prachthorn auf Burg Cenarius.",
  },
  [8276] = {
    ["T"] = "Die Rückeroberung von Silithus",
    ["O"] = "Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus.",
    ["D"] = "Ihr Helden der Horde, horcht auf! Es gibt eine Bedrohung dort draußen, die wir zu lange ignoriert haben. Erderschütterungen in der Nähe von Silithidennestern, verursacht durch massive Wanderungen dieser Biester, haben unsere Aufmerksamkeit erregt. Es scheintn als bereiten sie ihre weitere Expansion vor.$B$BDer Zirkel des Cenarius sammelt seine Streitkräfte in Silithus, um diesen Silithiden den Krieg zu erklären, bevor es zu spät ist. Niemals wieder sollen diese Biester unsere Ländereien entweihen!$B$BAlle kampftauglichen Abenteurer sollten sich umgehend bei Windruferin Prachthorn auf Burg Cenarius melden. Mögen die Silithiden endgültig besiegt werden!",
  },
  [8277] = {
    ["T"] = "Tödliches Wüstengift",
    ["O"] = "Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr 8 Stachel von Steinpanzerskorpiden und 8 Giftzähne von Sandhuschern für sie sammelt. Kehrt zu ihr zurück, sobald Ihr diese Aufgabe erfüllt habt.",
    ["D"] = "Beetix Wankelhupf ist Euch zu Diensten! Mein Cousin Noggle hat es sich zum Lebensinhalt gemacht, seltene Kräuter für die Alchemisten der Burg Cenarius zu sammeln. Oder hatte es zumindest, egal. Er kam von seinem letzten Ausflug mit einer starken Vergiftung zurück.$B$BIch habe es geschafft die Wirkung des Gifts zu verlangsamen, aber um es vollständig neutralisieren zu können, benötige ich Giftproben von was auch immer ihn gestochen haben mag.$B$BNoggle kam aus dem Norden, daher verwette ich mein letztes Hemd darauf, dass es Steinpanzerskorpide oder Sandhuscher waren. Bringt mir ein paar Stachel und Giftzähne und ich werde versuchen ein Gegengift für ihn zu erstellen.",
  },
  [8278] = {
    ["T"] = "Noggles letzte Hoffnung",
    ["O"] = "Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr je 3 Stachel von Steinpanzerzangen und Steinpanzerschindern sowie 3 Felspirschergiftzähne für sie sammelt.",
    ["D"] = "Verflucht! Das Gegengift hat nicht gewirkt. Noggle muss von einer der stärkeren Kreaturen im fernen Süden gestochen worden sein.$B$BIch werde Euch brauchen, um dorthin zu gehen und mir noch mehr Proben zu besorgen, $n. Das Gute dabei - da ihr Gift stärker ist, werde ich nicht so viele brauchen. Der Haken ist allerdings, dass in der südlichen Gegend mehrere Arten giftiger Kreaturen herumfleuchen und Ihr daher verschiedene Proben besorgen müsst.",
  },
  [8279] = {
    ["T"] = "Das Zwielichtlexikon",
    ["O"] = "Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Zwielichtlexikons.",
    ["D"] = "Der Schattenhammer ist nicht gerade zimperlich mit denen, die seine Reihen verlassen wollen. Sie jagen mich bis heute, als sei ich ein wildes Tier. Unnötig zu erwähnen, dass ihre Feinde meine Freunde sind!$B$BUm diese Schrifttafel zu entschlüsseln, benötige ich das Kodebuch, welches ihre Schreiber benutzt haben, um geheime Nachrichten zu dekodieren: Das Zwielichtlexikon. Es ist dermaßen wertvoll, dass sie es in drei Kapitel aufgeteilt haben, welche an drei unterschiedlichen Orten aufbewahrt werden! Die Offiziere, denen die Teile des Lexikons anvertraut wurden, sind als Bewahrer des Schattenhammers bekannt. Sucht in den Lagern des Schattenhammers nach ihnen.",
  },
  [8280] = {
    ["T"] = "Sicherung der Versorgungslinien",
    ["O"] = "Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 15 Baggerschläger tötet.",
    ["D"] = "Burg Cenarius ist von außerordentlicher strategischer Bedeutung. Ich bin davon überzeugt, dass unser Sieg über die Silithiden feststeht, solange wir sie halten können. Natürlich unter der Bedingung, dass wir die Versorgung mit Nachschubgütern aufrechterhalten können.$B$BDie Sandwürmer in dieser Gegend stellen für unsere Nachschubkarawanen eine noch größere Gefahr dar als die Silithiden selbst. Erst heute Morgen verloren wir eine ganze Morgenkornlieferung an eine Gruppe Baggerschläger. Ich brauche Eure Hilfe, um die Bestände dieser Baggerschläger auszudünnen und um sicherzustellen, dass unsere Karawanen vor weiteren Überfällen sicher sind.",
  },
  [8281] = {
    ["T"] = "Verstärkte Sicherheit",
    ["O"] = "Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 20 Baggerbrecher tötet.",
    ["D"] = "Eure bisherigen Leistungen sind makellos, $n. Dennoch, sind unsere Versorgungslinien noch lange nicht sicher. In Abwesenheit der Baggerschläger, werden ihre südlichen Artgenossen nach Norden drängen, um ihren Platz einzunehmen.$B$BIhr werdet eine ganze Reihe Baggerbrecher töten müssen, um diese Biester von unseren Versorgungslinien fernzuhalten.",
  },
  [8282] = {
    ["T"] = "Noggles verlorener Ranzen",
    ["O"] = "Spürt Noggles Ranzen im südlichen Gebirge von Silithus auf und bringt ihn zu ihm, auf Burg Cenarius, zurück.",
    ["D"] = "Wo bin ich? Wo is\' meine Tasche? *Keuch* Ich muss sie verloren haben, als ich vor Totenstachel, dem Riesenskorpid, geflohen bin. Bei meiner überstürzten Flucht bin ich geradewegs in ein Nest von Steinpanzerschindern gerannt.$B$BIn dem Ranzen, den ich bei mir trug, befanden sich allerlei seltene Reagenzien. Beetix und ich werden ohne sie nicht die Tränke herstellen können, die von der Burg Cenarius benötigt werden. Ich bin noch zu schwach, um wieder aufzustehen, aber ich kann es für Euch lohnend machen, wenn Ihr mir meine Reagenzien zurückbringt.$B$BIch bin Totenstachel nahe dem südlichen Gebirgszug über den Weg gelaufen, hinter Bronzebarts Lager.",
  },
  [8283] = {
    ["T"] = "GESUCHT: Totenstachel, Terror der Wüste",
    ["O"] = "Bringt die Zange Totenstachels zu Vish Kozus, Hauptmann der Wache.",
    ["D"] = "Man sagt, dass sich der grausame Skorpid Totenstachel nahe des südlichen Gebirgszuges wegen erhöhter Silithidenaktivität zurückgezogen hat. Dies stellt eine große Bedrohung für Patrouillen und Expeditionen in der südlichen Gegend dar. Wer sich an dessen Vernichtung beteiligt, soll reichlich belohnt werden. Fragt bei Hauptmann Vish Kozus, ganz oben auf dem Wachturm der Burg Cenarius, wegen der Belohnung nach.",
  },
  [8284] = {
    ["T"] = "Das Geheimnis des Zwielichts",
    ["O"] = "Geologin Lerchenbann auf Burg Cenarius möchte, dass Ihr ihr 8 Zwielichtschrifttafelfragmente bringt.",
    ["D"] = "Einst existierte nordwestlich von hier ein florierender Außenposten des Schattenhammers. Wenn Ihr heutzutage in diese Gegend reist, werdet Ihr dort nur Schwärme von Elementaren antreffen. Verrottete Holzpfosten und alte Zelttuchfetzen sind das Einzige, was noch auf den Standort des Lagers hinweist.$B$BVor einiger Zeit fand ich dort die Fragmente einer Tontafel, allerdings nicht genug Teile, um sie zu irgendetwas Brauchbarem zusammenzusetzen. Bringt mir die fehlenden Fragmente der Schrifttafel. Vielleicht können wir so etwas Licht in die Sache bringen und herausfinden, was die Anhänger des Schattenhammers in Silithus gesucht haben.",
  },
  [8285] = {
    ["T"] = "Der Deserteur",
    ["O"] = "Bringt die wiederhergestellte Schrifttafel zu Ortell dem Einsiedler in Silithus.",
    ["D"] = "Es gibt Gerüchte über ein Mitglied des Schattenhammers, das aus seiner Sekte desertiert ist und nun als Einsiedler in den Bergen südöstlich vom Südwindposten lebt. Sein Name ist Ortell. Vielleicht könnt Ihr ihn davon überzeugen, Euch bei der Entschlüsselung der Schrifttafel behilflich zu sein.$B$BNehmt die wiederhergestellte Schrifttafel mit Euch und sucht Ortell auf.",
  },
  [8286] = {
    ["T"] = "Was uns morgen erwartet",
    ["O"] = "Reist zu den Höhlen der Zeit in Tanaris und findet Anachronos, Nachkomme Nozdormus.",
    ["D"] = "Der Tod umarmte mich bereits als er erschien und seine Macht auf mich anwandte. Er ließ seinen Atem nur einmal über meinen versehrten Körper streichen und wartete daraufhin geduldig bis die Zeit meine Wunden geheilt hatte.$B$B\"Wenn es sein muss, wirst du für immer existieren\", sagte er, und mit diesen Worten sollte ich auf ewige Zeiten als Wächter gebunden sein.$B$BWerft jetzt einen Blick auf die Wüste. Der zweite Krieg steht uns bevor. Ihr müsst nach der Ruhestätte des Bronzeschwarms suchen. Reist zu den Höhlen der Zeit und seht nach, ob der Meister zurückgekehrt ist. Ich muss mir dessen sicher sein, bevor ich weiter vorgehe.",
  },
  [8287] = {
    ["T"] = "Ein teuflischer Plan",
    ["O"] = "Bringt Kommandant Mar\'alith die entschlüsselte Abschrift der Schrifttafel.",
    ["D"] = "So, das sollte es gewesen sein… ziemlich wertvolle Informationen, muss ich sagen! Ich habe beobachtet, wie die Truppen des Zirkels des Cenarius in Silithus eingetroffen sind. Ich nehme an, sie planen den Schattenhammer aus Silithus zu vertreiben, nicht wahr? Diese Informationen sollten dabei hilfreich sein.$B$BBringt die Entschlüsselte Abschrift der Schrifttafel zu ihrem Anführer, Kommandant Mar\'alith. Schon bald wird der Schattenhammer vernichtet sein und ich bin endgültig vor ihrem Zugriff sicher.",
  },
  [8288] = {
    ["T"] = "Nur einer kann sich erheben",
    ["O"] = "Bringt Brutwächter Dreschbringers Kopf zu Baristolth der Sandstürme in die Burg Cenarius in Silithus.",
    ["D"] = "Wir dürfen keine Zeit mehr verschwenden! Es gilt einen Helden zu finden! Die Aufgabe, die ich Euch stellen werde, würde normalerweise eine Armee erfordern, aber nur einer aus diesem Haufen von Möchtegernhelden wird sich erheben und die Bürde und Verantwortung eines Verbündeten der Brut tragen.$B$BIm Hort des Herrschers des Schwarzfels befindet sich eine äußerst grausame Bestie. Der Brutwächter Dreschbringer bewacht die Hallen des Zwists und blockiert den Weg, der in Nefarians inneres Sanktum führt. Schlachtet dieses abartige Unwesen nieder und bringt mir seinen Kopf. Seid Ihr erfolgreich, erwartet Euch die nächste Aufgabe.",
  },
  [8289] = {
    ["T"] = "Verdienstabzeichen",
    ["O"] = "",
    ["D"] = "",
  },
  [8291] = {
    ["T"] = "Besiegt die Eindringlinge!",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.",
    ["D"] = "Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, $gjunger:junge:c; $C. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.$B$BBetretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.$B$BEin Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, $n und ich werde Euch belohnen.",
  },
  [8291] = {
    ["T"] = "Besiegt die Eindringlinge!",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.",
    ["D"] = "Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, $gjunger:junge:c; $C. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.$B$BBetretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.$B$BEin Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, $n und ich werde Euch belohnen.",
  },
