  [8365] = {
    ["T"] = "Piratenhüte Ahoy!",
    ["O"] = "Sammelt 20 Südmeerpiratenhüte und bringt sie zurück zur eingebildeten Modeschöpferin beim Dampfdruckpier in Tanaris.",
    ["D"] = "Ihr seid die Hilfe, die das Kartell entsandt hat? Ach, ich denke, Ihr werdet reichen. Hört mir genau zu, $C. Wie Ihr wisst, bin ich die Lieferantin für modische Kleidung, die in den höchsten Kreisen der Gesellschaft Azeroths gefragt ist. Könige und Königinnen, Kriegshäuptlinge und... naja wie auch immer die Horde ihre Königinnen bezeichnet, all jene tragen, was ich als modisch deklariere. Und ich verdiene mir an ihnen eine goldene Nase, was natürlich das Beste am Ganzen ist.$B$BAlso hierfür benötige ich Eure Hilfe. Lauft hinunter zur Mast- und Schotbucht und beschafft mir einen Stapel Piratenhüte, koste es was es wolle. Glaubt Ihr, Ihr schafft das, hmm?",
  },
  [8366] = {
    ["T"] = "Gaunerei und Erpressung im Südmeer",
    ["O"] = "Sicherheitschef Bilgenritzel beim Dampfdruckpier in Tanaris beauftragt Euch, 10 Piraten der Südmeerfreibeuter, 10 Seeräuber der Südmeerfreibeuter, 10 Dockarbeiter der Südmeerfreibeuter und 10 Schwadroneure der Südseefreibeuter zu töten.",
    ["D"] = "Argh! Sie verlangen das Unmögliche von mir!$B$BDas Dampfdruckkartell möchte, dass ich den Südmeerpiraten eine Lektion erteile. Ich kann doch aber nicht an zwei Orten gleichzeitig sein; Ich muss hierbleiben, um zumindest hier alles ruhig zu halten.$B$BSeit neuestem hält nicht mal mehr das Schutzgeld, dass das Kartell regelmäßig auszahlt, die Piraten auf. Daher möchte ich Euch zeitweilig abordnen, mal dort runter zu gehen und denen Saures zu geben.$B$BIhr findet die Piraten in der Mast- und Schotbucht, die hier gleich in der Nähe gen Süden ist. Besten Dank, $n, ich schulde Euch was!",
  },
  [8367] = {
    ["T"] = "Große Ehre",
    ["O"] = "Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.",
    ["D"] = "Der Horde in einer der größten Schlachten gegen die Allianz zu dienen, ist das Ausschlaggebende für große Ehre! Die Wenigen jedoch, die der Horde auf ALL unseren aktiven Schlachtfeldern gedient haben, sind unsere wahren, großen Kämpfer. Unsere Gegner sollen besiegt werden, aber nicht mittels vereinzelter Gefechte, sondern durch einen gut koordinierten Krieg.$B$BGeht und schmettert die Allianz an allen Fronten nieder! Für Ehre! Für die Horde!",
  },
  [8368] = {
    ["T"] = "Schlacht der Kriegshymnenschlucht",
    ["O"] = "Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["D"] = "Der Kampf gegen die Schildwachen der Silberschwingen in der Kriegshymnenschlucht ist von großer Bedeutung. Unter dem Vorwand einen Wald zu beschützen, der ihnen nicht gehört, versucht die Allianz der Horde eines unserer größten Holzvorkommen streitig zu machen.$B$BLasst dies nicht zu, $n! Kommt zu mir mit einem Beweis für ehrvolle Taten im Namen der Horde zurück!",
  },
  [8369] = {
    ["T"] = "Die Invasoren des Alteractals",
    ["O"] = "Bringt 3 Ehrenabzeichen des Alteractals zu einem der Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["D"] = "Hört mir genau zu, $n. Egal, was auch immer Ihr über die noch immer tobende Schlacht im Alteractal gehört habt, etwas davon ist wahr: Es war die Expeditionsarmee der Sturmlanzen, die diesen Konflikt durch die Besetzung des Territoriums der Frostwölfe entfachte. Der Tag, an dem wir es der Allianz erlauben, Gebiete der Horde zu erobern, ohne militärisch zurückzuschlagen, wird der Tag sein, an dem ich meine Axt an den Nagel hänge, um den Rest meines Lebens in Schande zu verbringen. Geht jetzt und schlagt die Invasoren zurück!",
  },
  [8370] = {
    ["T"] = "Eroberung des Arathibeckens",
    ["O"] = "Bringt 3 Ehrenabzeichen des Arathibeckens zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["D"] = "Unser Konflikt mit der Allianz hat sich zu einem richtigen Krieg entwickelt. Es ist nicht länger notwendig unter falschen Vorwänden in die Schlacht zu ziehen, keine Seite erwartet dies noch länger.$B$BIn der Schlacht um das Arathibecken geht es nur um eines, Ressourcen. Nicht mehr und nicht weniger. Entweder die Allianz erobert diese Ressourcen und benutzt sie gegen uns, oder wir kommen ihnen zuvor und verwenden die dortigen Ressourcenquellen für unsere Kriegspläne. $n, helft den Entweihten dabei, die von uns benötigten Ressourcenlieferungen aufrechtzuerhalten!",
  },
  [8371] = {
    ["T"] = "Gemeinsames Bestreben",
    ["O"] = "Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Brigadegeneral der Allianz in einer der Hauptstädte, Dalaran oder Shattrath.",
    ["D"] = "Wir kämpfen nicht mehr in vereinzelten Schlachten gegen die Horde, $GJunge:Mädel;.$B$BDer gesamte Sieg an einer Front bedeutet der Horde die Ressourcen abzusprechen, die sie ansonsten in einer anderen Schlacht gegen uns verwenden würden. Ebenso könnte das Aufgeben in was auch immer für einer Schlacht unserem Gegner andernorts in der Welt einen Vorteil verschaffen.$B$BDie Allianz benötigt mehr Kämpfer mit diesem globalen Verständnis... wirklich bewährte Veteranen! $n, kommt zu mir zurück, wenn Ihr Euch gegen die Horde auf all unseren aktiven Schlachtfeldern bewährt habt.",
  },
  [8372] = {
    ["T"] = "Kampf um die Kriegshymnenschlucht",
    ["O"] = "Bringt 3 Ehrenabzeichen der Kriegshymnenschlucht zu einem Brigadegeneral der Allianz, außerhalb der Schlachtfelder.",
    ["D"] = "Die Schildwachen der Silberschwingen sind auf Grund der Zerstörung, die die Orcs im Wald anrichten, im Krieg mit der Vorhut des Kriegshymnenklans. Allerdings gibt es noch andere Gründe, als die bloße Liebe zu der Natur, für die Verteidigung von ausgerechnet diesem Wald.$B$BDer Wald bildet eine strategische Barriere, die es einfach macht, das Eschental gegen jegliche Art von Großangriffen zu verteidigen. Ohne den Wald, würde Astranaar lediglich ein oder zwei Tage standhalten, bevor es vom Brachland einverleibt werden würde.$B$BSeid tapfer und schlagt Euch gegen die Vorhut des Kriegshymnenklans, $n! Für die Allianz!",
  },
  [8373] = {
    ["T"] = "Die Kraft der Pinie",
    ["O"] = "Verwendet einen Stinkbombenreiniger, um alle Stinkbomben der Verlassenen zu vernichten, die hier in Süderstade losgelassen wurden. Kehrt zu Unteroffizier Hartman in Süderstade zurück, wenn Ihr fertig seid.",
    ["D"] = "Was ich während der Schlotternächte am meisten hasse, sind die Stinkbomben, die die Verlassenen hier inmitten von Süderstade loslassen. Wer hätte gedacht, dass eine Gedenkfeier zur Freilassung mit einer so stinkigen Sache einhergehen würde?$B$BVerwendet diesen Stinkbombenreiniger während der Schlotternächte, um die scheußlichen Stinkbomben, die die Horde womöglich hier in der Stadt loslässt, zu vernichten. Er bekämpft, dank der Kraft von Pinien, schreckliche Gerüche.$B$BHelft mit Süderstade sauber zu halten und ich werde Euch dafür ein paar Schlotternachtssüßigkeiten geben, die Ihr sicherlich mögen werdet. Abgemacht?",
  },
  [8373] = {
    ["T"] = "Die Kraft der Pinie",
    ["O"] = "Verwendet einen Stinkbombenreiniger, um alle Stinkbomben der Verlassenen zu vernichten, die hier in Süderstade losgelassen wurden. Kehrt zu Unteroffizier Hartman in Süderstade zurück, wenn Ihr fertig seid.",
    ["D"] = "Was ich während der Schlotternächte am meisten hasse, sind die Stinkbomben, die die Verlassenen hier inmitten von Süderstade loslassen. Wer hätte gedacht, dass eine Gedenkfeier zur Freilassung mit einer so stinkigen Sache einhergehen würde?$B$BVerwendet diesen Stinkbombenreiniger während der Schlotternächte, um die scheußlichen Stinkbomben, die die Horde womöglich hier in der Stadt loslässt, zu vernichten. Er bekämpft, dank der Kraft von Pinien, schreckliche Gerüche.$B$BHelft mit Süderstade sauber zu halten und ich werde Euch dafür ein paar Schlotternachtssüßigkeiten geben, die Ihr sicherlich mögen werdet. Abgemacht?",
  },
  [8374] = {
    ["T"] = "Inbesitznahme des Arathibeckens",
    ["O"] = "Bringt 3 Ehrenabzeichen des Arathibeckens zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.",
    ["D"] = "Es gehört viel mehr dazu, einen Krieg zu gewinnen, als das bloße Überlisten des Gegners oder siegreiche Schlachten. Vielmehr erringt die Seite den Sieg, welche dauerhaft mehr Kriegsgüter als der Gegner produziert.$B$BDas Arathibecken, könnte sich als eine potentielle und äußerst ergiebige Ressourcenquelle für die Seite erweisen, die das Becken zuerst für sich beansprucht. Falls die Allianz den Sieg erringt, könnte man aus den Ressourcen hunderte neuer Waffen herstellen. Sollte allerdings die Horde gewinnen, würden diese Waffen zwar hergestellt, doch würde man sie anschließend auf uns richten! Jetzt ist es an der Zeit, die Allianz im Kampf um das Arathibecken zu unterstützen!",
  },
  [8375] = {
    ["T"] = "Vergesst das Alteractal nicht!",
    ["O"] = "Bringt 3 Ehrenabzeichen des Alteractals zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.",
    ["D"] = "Ihr liegt vollkommen richtig, $n. Die Horde verkündet die Wahrheit, mit ihrer Behauptung, das Alteractal gehöre zum Territorium der Frostwölfe. Die Sturmlanzen kamen, auf der Suche nach Erzvorkommen und Relikten, mit friedlichen Absichten in das Tal. Die Frostwölfe jedoch, reagierten auf die Anwesenheit der Sturmlanzen mit einer, in der Allianz, bis dato unbekannten Brutalität und Aggressivität.$B$BNiemals dürfen wir all die Zwerge vergessen, die während des folgenden feigen und unangekündigten Angriffs zu Tode kamen. Geht nun, $n, und tragt Euren Teil zur Schlacht im Alteractal bei. Für die Allianz!",
  },
