  [8572] = {
    ["T"] = "Schlachtrüstung des Veteranen",
    ["O"] = "Bringt 7 cenarische Gefechtsabzeichen, 4 cenarische Versorgungsabzeichen und 4 cenarische Strategieabzeichen zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf beim Zirkel des Cenarius.",
    ["D"] = "Die Abzeichen, die Ihr für Eure Erfüllung von Kampf-, Logistik- und taktischen Aufträgen für Burg Cenarius erhalten habt, dienen zu weit mehr als der Angeberei, $n.$B$BIhr solltet deren Wert besser zu schätzen wissen. Da Ihr gegenüber unserer Organisation Loyalität bewiesen habt, kann ich Euch bestimmte Ausrüstungsgegenstände anbieten, die wir lediglich für unsere engsten Verbündeten im Austausch gegen diese Abzeichen bereithalten.",
  },
  [8573] = {
    ["T"] = "Schlachtrüstung des Feldherrn",
    ["O"] = "Bringt 15 cenarische Gefechtsabzeichen, 20 cenarische Versorgungsabzeichen, 20 cenarische Strategieabzeichen und 1 Mal des Cenarius zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius.",
    ["D"] = "Die Abzeichen, die Ihr für Eure Erfüllung von Kampf-, Logistik- und taktischen Aufträgen für Burg Cenarius erhalten habt, dienen zu weit mehr als der Angeberei, $n.$B$BIhr solltet deren Wert besser zu schätzen wissen. Da Ihr gegenüber unserer Organisation Loyalität bewiesen habt, kann ich Euch bestimmte Ausrüstungsgegenstände anbieten, die wir lediglich für unsere engsten Verbündeten im Austausch gegen diese Abzeichen bereithalten.",
  },
  [8574] = {
    ["T"] = "Schlachtrüstung des Gefolgsmanns",
    ["O"] = "Bringt 15 cenarische Gefechtsabzeichen, 20 cenarische Versorgungsabzeichen, 17 cenarische Strategieabzeichen und 1 Mal von Remulos zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius.",
    ["D"] = "Die Abzeichen, die Ihr für Eure Erfüllung von Kampf-, Logistik- und taktischen Aufträgen für Windruferin Prachthorn erhalten habt, dienen zu weit mehr als der Angeberei, $n.$B$BIhr solltet deren Wert besser zu schätzen wissen. Da Ihr gegenüber unserer Organisation Loyalität bewiesen habt, kann ich Euch bestimmte Ausrüstungsgegenstände anbieten, die wir lediglich für unsere engsten Verbündeten im Austausch gegen diese Abzeichen bereithalten.",
  },
  [8575] = {
    ["T"] = "Azuregos\' magisches Buch",
    ["O"] = "Bringt Azuregos\' magisches Buch zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Ihr könnt kein einziges Wort in diesem Buch verstehen. Ihr wisst nicht einmal in welcher Sprache es geschrieben wurde!",
  },
  [8575] = {
    ["T"] = "Azuregos\' magisches Buch",
    ["O"] = "Bringt Azuregos\' magisches Buch zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Ihr könnt kein einziges Wort in diesem Buch verstehen. Ihr wisst nicht einmal in welcher Sprache es geschrieben wurde!",
  },
  [8576] = {
    ["T"] = "Übersetzung des Buchs",
    ["O"] = "",
    ["D"] = "",
  },
  [8577] = {
    ["T"] = "Stewvul, ehemals allerbester Freund",
    ["O"] = "Narain Pfauentraum möchte, dass Ihr seinen Ex-\'Besten Freund für immer\' (B.F.F.I.) Stewvul ausfindig macht. Nehmt ihm die gestohlene Wahrsagerbrille ab und bringt sie Narain zurück.",
    ["D"] = "Tja, um irgendetwas übersetzen zu können, brauche ich meine Wahrsagerbrille. Unglücklicherweise wurde sie vor einiger Zeit von meinem Ex-\'Besten Freund für immer\', Stewvul gestohlen. Er hat mir mein Herz fünffach gebrochen und mich dann hier verrotten lassen.$B$BMithilfe meiner Kristallkugel konnte ich seinen momentanen Aufenthaltsort einigermaßen genau bestimmen. Er ist entweder am Graumähnenwall im Silberwald oder in irgend so einer Portaldimension in der Scherbenwelt, die \'grad von der brennenden Legion überrannt wird. Schätze, es liegt jetzt an Euch, ihn ausfindig zu machen und mir meine Brille zurückzubringen, $n!",
  },
  [8578] = {
    ["T"] = "Wahrsagerbrille? Kein Problem!",
    ["O"] = "Findet Narains Wahrsagerbrille und bringt sie Narain Pfauentraum in Tanaris.",
    ["D"] = "<Ihr hört einen erleichterten Seufzer aus der Kiste.>$B$BIhr wollt nur seine Wahrsagerbrille? Großartig! Kein Problem, gebt mir kurz \'ne Minute, um… Hey! Wo zur verdammten…!$B$BEs gibt eine schlechte und eine verdammt schlechte Nachricht. Die Schlechte ist, dass ich die Brille verloren habe. Die viel schlimmere Nachricht ist, ich habe sie letzten Monat beim Herumstreifen im Geschmolzenen Kern irgendwo liegen lassen. Jep, der Geschmolzene Kern... Tja, so etwas passiert. Tschüss!",
  },
  [8579] = {
    ["T"] = "Sterbliche Helden",
    ["O"] = "",
    ["D"] = "",
  },
  [8580] = {
    ["T"] = "Die Horde braucht Feuerblüten!",
    ["O"] = "Bringt 20 Feuerblüten zu Fledermausreiter Pele\'keiki im Tal der Geister in Orgrimmar.",
    ["D"] = "Hey, $C! Ja, Ihr da! Es gibt da massenhaft Feuerblüten, die von der Horde für die Vorbereitungen des Krieges von Ahn\'Qiraj benötigt werden. Vielleicht werden sie ja in so nette Dinge verarbeitet, die dann in die Luft gehen? Vielleicht geben sie sie der Allianz aber auch als Treibstoff für ihre Dampfpanzer? Wer weiß? Nicht Pele\'keiki!$B$BPele\'keiki weiß nur eines... er weiß, dass Ihr ihm helfen und einen Haufen Feuerblüten für ihn sammeln werdet! Oder?",
  },
  [8581] = {
    ["T"] = "Die Horde braucht mehr Feuerblüten!",
    ["O"] = "Bring 20 Firebloom to Batrider Pele\'keiki at the Valley of Spirits in Orgrimmar.",
    ["D"] = "Pele\'keiki know you come to help. Bring many firebloom and make him very happy. Pele\'keiki might make big bombs with firebloom. Drop them on insects from his bat over Ahn\'Qiraj when war come. But Pele\'keiki need more firebloom, $C!$B$BYou gonna bring Pele\'keiki that firebloom, real quick now. Come back with at least as much as last time, and Pele\'keiki be pleased.",
  },
  [8582] = {
    ["T"] = "Die Horde braucht lila Lotus!",
    ["O"] = "Bringt 20 lila Lotus zu Apothekerin Jezel im Tal der Geister in Orgrimmar.",
    ["D"] = "Lila Lotus, $C, ist eine Zutat für nicht wenige Tränke, Elixiere und Öle, von denen uns einige im Krieg von Ahn\'Qiraj von Nutzen sein werden. Wir werden eine stattliche Menge dieser Tränke für die Kriegsanstrengungen herstellen müssen. Ich werde zusätzlich noch experimentieren, um weitere Verwendungsmöglichkeiten für lila Lotus zu finden. Allerdings werde ich dafür eine große Anzahl dieser Pflanzen benötigen.$B$BJetzt zu Eurer Aufgabe. Zieht durch die Felder und sammelt so viel lila Lotus ein, wie Ihr könnt. Wärt Ihr dazu in der Lage?",
  },
  [8583] = {
    ["T"] = "Die Horde braucht mehr lila Lotus!",
    ["O"] = "Bring 20 Purple Lotus to Apothecary Jezel at the Valley of Spirits in Orgrimmar.",
    ["D"] = "In fact it does appear that we are in need of even more purple lotus, $C. While my own studies have yet to determine a new useful application for the herb, there are tried and true methodologies yet to be employed.$B$BI need you to once again go out into the field and collect at least twenty purple lotus samples. Return them to me here.",
  },
  [8584] = {
    ["T"] = "Fragt mich nie nach meinen Angelegenheiten",
    ["O"] = "Narain Pfauentraum in Tanaris möchte, dass Ihr mit Dirge Schnetzelhack in Gadgetzan sprecht.",
    ["D"] = "Wenn es eine Sache gibt, die Ihr von mir wissen solltet, dann ist es das: Fragt mich NIEMALS nach meinen Angelegenheiten! Ich arbeite mit Dingen, die Ihr einfach nicht verstehen könnt. Zum Beispiel: Wisst Ihr was ein psychotronischer Muzzledorf ist? Natürlich nicht! Genau deshalb überlassen wir das Planen auch mir und das Beschaffen Euch.$B$BWie ich schon sagte, wenn es funktionieren soll, dann brauchen wir ein 500 Pfund-Hühnchen.$B$BGeht nach Gadgetzan und sprecht mit Dirge Schnetzelhack - er kann Euch bei der Suche nach dem passenden Exemplar auf die Sprünge helfen.",
  },
  [8585] = {
    ["T"] = "Die Insel des Schreckens!",
    ["O"] = "Dirge Schnetzelhack in Tanaris möchte, dass Ihr Lakmaerans Kadaver sowie 20 Chimaeroklenden beschafft.",
    ["D"] = "Der Legende nach haben die Chimaerok von der Insel des Schreckens in Feralas das saftigste und zarteste Fleisch weit und breit! Nun kommt es, dass diese Chimaerok ein Familienoberhaupt haben, das, nach entsprechender Zubereitung, wie ein 500 Pfund-Hühnchen aussehen könnte... oder auch nicht.$B$BGeht zu der Insel des Schreckens, tötet Lord Lakmaeran und bringt mir seinen frischen Kadaver. Das sollte den Gnom zufrieden stellen. Um mich zufrieden zu stellen, müsst Ihr ein paar Chimaeroklenden in Eure Finger bekommen.$B$BSollte Euch das gelingen, bekommt Ihr Euer 500 Pfund-Hühnchen und ein kleines Extra.",
  },
  [8586] = {
    ["T"] = "Dirges abgefahrene Chimaerokkoteletts",
    ["O"] = "Dirge Schnetzelhack in Gadgetzan möchte, dass Ihr ihm 20 Einheiten Goblinraketentreibstoff und 20 Einheiten Tiefsteinsalz bringt.",
    ["D"] = "Mein Rezept is fertig! Was ich jetzt von Euch brauche ist Raketentreibstoff und Tiefsteinsalz. Ich kümmere mich um die Gewürze und das Fleisch, das Ihr mir gebracht habt, sorgt für den Rest.$B$BSobald Ihr mir die benötigten Zutaten gebracht habt, könnt Ihr das fertige Hühnchen zu Narain bringen.",
  },
  [8587] = {
    ["T"] = "Rückkehr zu Narain",
    ["O"] = "Bringt das 500 Pfund-Hühnchen zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Ich bin ein Goblin, der sein Wort hält, Kind. Hier ist Euer 500 Pfund-Hühnchen.$B$BWas ein Gnom mit einem 500 Pfund-Hühnchen möchte, werde ich wohl nie verstehen...$B$B<Dirge grummelt etwas Unverständliches.>$B$BWahrscheinlich um darin zu schlafen... schmutzige Gnome...",
  },
  [8588] = {
    ["T"] = "Die Horde braucht schweres Leder!",
    ["O"] = "Bringt 10 Einheiten schweres Leder zu Kürschner Jamani im Tal der Geister in Orgrimmar.",
    ["D"] = "Da wir uns gerade auf den bevorstehenden Krieg vorbereiten, wäre es wahrscheinlich das Beste, wenn wir alle benötigten Güter zuerst beschaffen, meint Ihr nicht? Einer der benötigten Vorräte besteht aus schwerem Leder. Wir verwenden es für fast alles, von Verstärkungen bis hin zu Rüstungen und guten alten Planen.$B$BAllerdings brauchen wir erst einmal mehr davon. Das ist Eure Aufgabe, $C. Wetzt Euer Messer und bringt mir ein Bündel schweres Leder. Alles weitere besprechen wir dann.",
  },
  [8589] = {
    ["T"] = "Die Horde braucht mehr schweres Leder!",
    ["O"] = "Bring 10 Heavy Leather to Skinner Jamani at the Valley of Spirits in Orgrimmar.",
    ["D"] = "It\'s true, $C, I still need more heavy leather. The requests from the generals and their quartermasters seem endless. And that\'s nothing compared to what the zeppelin masters are asking for!$B$BI need to get my quota collected on the double. Baekun, bring me more stacks of heavy leather as soon as you can!",
  },
  [8590] = {
    ["T"] = "Die Horde braucht dickes Leder!",
    ["O"] = "Bringt 10 Einheiten dickes Leder zu Unteroffizier Umala im Tal der Geister in Orgrimmar.",
    ["D"] = "Ich bin froh, dass Ihr darüber mit mir sprechen möchtet, $C. Es sieht ganz so aus, als ob die Vorbereitungen der Horde für den Krieg in Ahn\'Qiraj in vollem Gange sind. Ihr und ich müssen aber noch unseren Teil leisten.$B$BIch wurde mit der Sammlung einer großen Menge dicken Leders beauftragt, das für die Herstellung von allerlei Kriegsmaterialien benötigt wird. Ich brauche Euch, um für mich loszuziehen und mir welches zu besorgen. Ihr würdet Euch Ehre und das Wohlwollen der Horde verdienen können. Würdet Ihr uns also helfen?",
  },
  [8591] = {
    ["T"] = "Die Horde braucht mehr dickes Leder!",
    ["O"] = "Bring 10 Thick Leather to Sergeant Umala at the Valley of Spirits in Orgrimmar.",
    ["D"] = "Druid, as you can see I still need to gather up more thick leather. Once again I ask your help with this task, and promise that if you complete it, you will be recognized for your efforts.$B$BTime is of the essence! Return to me with the thick leather so that we can finish our preparations and go to war, hero!",
  },
  [8592] = {
    ["T"] = "Tiara des Orakels",
    ["O"] = "Bringt Vek\'nilashs Reif, 2 Götzen der Weisen, 5 Silberskarabäen und 5 Knochenskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.",
    ["D"] = "Die Zwillingsimperatoren... ihr unaufhörliches Flüstern martert langsam meinen Verstand. Sie verhöhnen mich, wissend, dass ich nicht tiefer in ihr Reich vordringen kann, um meinen Bruder Arygos zu retten.$B$BIn meinen Visionen sehe ich ihre monströsen Fratzen, die mit mächtigen Kronen geschmückt sind. Ich werde meine Vergeltung noch bekommen... ich werde diese Symbole der Qirajimacht in solche verwandeln, die unserem Nutzen gerecht werden.$B$BBringt mir die benötigten Bestandteile, $n. Ich werde Euch dafür ein Kopfstück von unvergleichlicher Macht geben.",
  },
  [8593] = {
    ["T"] = "Beinkleider des Orakels",
    ["O"] = "Bringt Ouros intakte Haut, 2 Götzen des Lebens, 5 Goldskarabäen und 5 Tonskarabäen zu Kandrostrasz innerhalb von Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.",
    ["D"] = "Ihr möchtet Euch also uns gegenüber bewähren, Sterblicher? Nun gut, vielleicht können wir uns ja gegenseitig helfen.$B$BDer uralte Sandwurm, Ouro, ist ein Geschöpf von legendärer Macht. Man sagt, dass er von dem Alten Gott selbst als Gespött des Lebens erschaffen wurde.$B$BSeine Haut ist unnatürlich... dick, fast unbezwingbar und dennoch flexibel, was ihm ermöglicht, sich mit der Geschwindigkeit und Grazie eines kleineren Lebewesens zu bewegen.$B$BBringt mir eine Probe seiner Haut sowie andere geringfügigere Bestandteile und ich werde mächtige Gamaschen für Euch herstellen.",
  },
  [8594] = {
    ["T"] = "Mantel des Orakels",
    ["O"] = "Bringt die Befehlsbindungen der Qiraji, 2 Götzen der Wiedergeburt, 5 Silberskarabäen und 5 Elfenbeinskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus.",
    ["D"] = "Man sagt unter uns, dass die kunstvoll verzierten Schulterpanzerungen, die von den sterblichen Völkern getragen werden, zu Beginn eine Nachahmung von Drachenflügeln werden sollten.$B$BBringt mir die Bindungen, die von den höchsten Anführern der Qiraji getragen werden und ich werde sie in ein Paar Schulterstücke verarbeiten, die furchteinflößender als die Flügel Nefarians selbst sind!",
  },
  [8595] = {
    ["T"] = "Sterbliche Helden",
    ["O"] = "",
    ["D"] = "I see that you\'re no ordinary mortal, $n. Your chances against the Old God, however infinitely small, are greater than ours at this point. After all, while he expects dragons at his doorstep, he is not prepared for someone like you.$b$bJust as Anachronos and Staghelm defeated the Qiraji a thousand years ago through the alliance of mortal and dragon races, we too shall form a pact. Gain the trust of the Bronze Dragonflight and you\'ll gain our trust. Only then we shall make a true champion out of you.",
  },
  [8596] = {
    ["T"] = "Fußlappen des Orakels",
    ["O"] = "Bringt die Befehlsbindungen der Qiraji, 2 Götzen des Todes, 5 Bronzeskarabäen und 5 Goldskarabäen zu Kandrostrasz in Ahn\'Qiraj. Diese Aufgabe erfordert auch einen neutralen Ruf bei der Brut Nozdormus.",
    ["D"] = "Qirajimagie ist sehr stark und uralt. Die mächtigsten Qiraji handhaben Gegenstände, die so machtvoll sind, dass sie sogar einem Drachen Einhalt gebieten.$B$BBringt mir die Befehlsbindungen der Qiraji sowie andere geringfügigere Bestandteile und ich werde ein Paar mächtige Stiefel für Euch herstellen.$B$BIhr werdet sie im Kampf gegen den Feind als nützlich empfinden.",
  },
  [8597] = {
    ["T"] = "Drachisch für Dummies",
    ["O"] = "Findet Narain Pfauentraums Buch, das auf einer Insel der Südlichen Meere vergraben ist.",
    ["D"] = "Ich wusste, dass dieses Buch eines Tages nützlich sein würde! Glücklicherweise war ich clever und vorausschauend genug und habe es an einem sicheren Ort verstaut.$B$BKeine Angst, es befindet sich an einem Platz, auf den niemand kommen würde! Ich brauchte einen Gyrokopter, um das erste Mal dorthin zu gelangen. Bedauerlicherweise musste ich auf meinem Rückweg am Strand bruchlanden. Ich verdanke es diesem Unfall, dass ich diese gnomischen Superpsychokräfte erlangt habe!$B$BHaltet nach meinem verunglückten Gyrokopter irgendwo am Landendestrand Ausschau und schwimmt von da aus in Richtung Süden! Ihr werdet die Stelle erkennen, wenn Ihr sie seht!",
  },
  [8598] = {
    ["T"] = "lÖsEGeLd",
    ["O"] = "Bringt die Lösegeldforderung zu Narain Pfauentraum in Tanaris.",
    ["D"] = "<Der Text der Nachricht wurde aus unterschiedlichen, ausgeschnittenen Buchstaben zusammengesetzt und auf das Papier geklebt.>$B$BWir haben dein kostbares Buch, Gnom. Wenn du es jemals wiedersehen willst, dann machst du genau das, was wir dir sagen.$B$B<Dem Ganzen folgt eine ausführliche Liste von Anweisungen, die sich über 3 Seiten zieht.>$B$BBringt das Geld und kommt alleine!",
  },
  [8599] = {
    ["T"] = "Liebesbrief für Narain",
    ["O"] = "Bringt Meridiths Liebesbrief zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Es kommt nicht häufig vor, dass ich Besuch bekomme. Genaugenommen hatte ich das letzte Mal einen Besucher, als ich diesen liebreizenden, süßen Gnom nach dem Absturz mit seiner Flugmaschine gerettet habe.$B$B<Meridith starrt gedankenverloren ins Nichts.>$B$BKennt Ihr ihn? Narain? Narain Pfauentraum? Oh, es würde mir so viel bedeuten, wenn ihm jemand eine Nachricht von mir überbringen würde. Ich muss ihm mitteilen, was ich empfinde! Ich weiß, dass es Liebe ist! Ich weiß es einfach!$B$BKönntet Ihr Narain vielleicht eine Nachricht zustellen? Ich wäre Euch für immer zu Dank verpflichtet!",
  },
  [8599] = {
    ["T"] = "Liebesbrief für Narain",
    ["O"] = "Bringt Meridiths Liebesbrief zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Es kommt nicht häufig vor, dass ich Besuch bekomme. Genaugenommen hatte ich das letzte Mal einen Besucher, als ich diesen liebreizenden, süßen Gnom nach dem Absturz mit seiner Flugmaschine gerettet habe.$B$B<Meridith starrt gedankenverloren ins Nichts.>$B$BKennt Ihr ihn? Narain? Narain Pfauentraum? Oh, es würde mir so viel bedeuten, wenn ihm jemand eine Nachricht von mir überbringen würde. Ich muss ihm mitteilen, was ich empfinde! Ich weiß, dass es Liebe ist! Ich weiß es einfach!$B$BKönntet Ihr Narain vielleicht eine Nachricht zustellen? Ich wäre Euch für immer zu Dank verpflichtet!",
  },
  [8600] = {
    ["T"] = "Die Horde braucht unverwüstliches Leder!",
    ["O"] = "Bringt 10 Einheiten unverwüstliches Leder zu Doktor Serratus im Tal der Geister in Orgrimmar.",
    ["D"] = "Ich glaube, dass die richtige Handhabung eines Messers wichtiger ist als dessen Schneide. Beherrscht man sein Handwerk, kann man auch mit der stumpfsten Klinge schneiden, oder $C? Ihr und ich, wir haben solche Probleme aber nicht, da wir im Besitz der schärfsten Klingen sind.$B$BNaja, egal. Ich habe eine Aufgabe für Euch. Im Auftrag der Horde und im Namen der Kriegsvorbereitungen von Ahn\'Qiraj, möchte ich Euch bitten, mir eine gewisse Anzahl unverwüstliches Leder zu besorgen. Wäre das möglich?",
  },
  [8601] = {
    ["T"] = "Die Horde braucht mehr unverwüstliches Leder!",
    ["O"] = "Bring 10 Rugged Leather to Doctor Serratus at the Valley of Spirits in Orgrimmar.",
    ["D"] = "Preparations for the impending war at Ahn\'Qiraj are proceeding, $C. However, to ensure that we do not fall behind, or at least that I am not the one that causes us to fall behind, I want for you to bring back another stack of rugged leather. Your prompt attention to this matter will be greatly appreciated, $N.",
  },
  [8602] = {
    ["T"] = "Schulterstücke des Sturmrufers",
    ["O"] = "Bringt die Dominanzbindungen der Qiraji, 2 Götzen des Lebens, 5 Goldskarabäen und 5 Kristallskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus.",
    ["D"] = "Man sagt unter uns, dass die kunstvoll verzierten Schulterpanzerungen, die von den sterblichen Völkern getragen werden, zu Beginn eine Nachahmung von Drachenflügeln werden sollten.$B$BBringt mir die Bindungen, die von den höchsten Anführern der Qiraji getragen werden und ich werde sie in ein Paar Schulterstücke verarbeiten, die furchteinflößender als die Flügel Nefarians selbst sind!",
  },
  [8603] = {
    ["T"] = "Gewänder des Orakels",
    ["O"] = "Bringt die Hülle des Alten Gottes, 2 Götzen des Todes, 5 Steinskarabäen und 5 Kristallskarabäen zu Vethsera innerhalb von Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus.",
    ["D"] = "Der unaussprechliche Alte Gott erwacht langsam aus seinem Schlummer unterhalb Ahn\'Qirajs. Das schreckliche Schicksal, das Azeroth erwartet, könnte in Tagen oder auch Jahren Wirklichkeit werden, Zeit spielt für Wesen seiner Macht keine Rolle.$B$BStehen die Sterne günstig, wird er furchtbaren Zorn gegen alle Lebewesen walten lassen. Ihr müsst dem zuvorkommen und einen Weg finden, um seiner Existenz ein Ende zu bereiten.$B$BTut dies und bringt mir ein Stück seiner Hülle. Ich werde Euch eine Rüstung gewähren, die noch kein Sterblicher getragen hat... Rüstung für einen Helden, der einen Gott bezwungen hat!",
  },
  [8604] = {
    ["T"] = "Die Horde braucht Wollverbände!",
    ["O"] = "Bringt 20 Wollverbände zu Heiler Fernläufer im Tal der Geister in Orgrimmar.",
    ["D"] = "Seid gegrüßt, $C. Während der Vorbereitungen für den Krieg in Ahn\'Qiraj, bin ich hier, um die Bemühungen der Horde beim Zusammentragen von Wollverbänden zu koordinieren. Auf dem Schlachtfeld können sich unsere Soldaten nicht auf die Gegenwart eines Heilers verlassen, der sie am Leben hält. Glaubt mir, ich weiß das. Daher stellen wir all diese Sanitätskästen für sie zusammen.$B$BSolltet Ihr Zeit haben: Ich brauche sehr viele Wollverbände. Bringt sie hierher zu mir und ich werde sehen, dass Ihr dafür ordnungsgemäß entlohnt werdet.",
  },
  [8605] = {
    ["T"] = "Die Horde braucht mehr Wollverbände!",
    ["O"] = "Bring 20 Wool Bandages to Healer Longrunner at the Valley of Spirits in Orgrimmar.",
    ["D"] = "Good to see you again, $C. We are still working on preparations here for the war effort, and I can still use all of the help that you can give in gathering up wool bandaging. Any assistance at all will be greatly appreciated. I only hope that our efforts will be good enough. I\'ve lost too many noble souls on the battlefields of yesterday to not do everything in my power to see to it that doesn\'t happen again.",
  },
  [8606] = {
    ["T"] = "Lockvogel!",
    ["O"] = "Narain Pfauentraum in Tanaris möchte, dass Ihr nach Winterquell reist und die Tasche voller Gold, so wie es die Buchnapper angegeben haben, am Übergabepunkt platziert.",
    ["D"] = "Ich hab\'s! Wir werden Folgendes tun. Ich werde einen Sack mit Steinen füllen und Ihr werdet ihn überbringen. Ich weiß, dass man die Übergabe von mir verlangt und das werde ich auf gewisse Weise auch tun.$B$B<Narain sieht Euch von oben bis unten an.>$B$BJa, das dürfte passen. Nehmt diesen Turban, diese Robe und den Sack voller Steine, geht dann zu dem Übergabepunkt in Winterquell.$B$BAlles, was Ihr benötigen werdet, befindet sich in diesem Spezialset. Sobald sie auftauchen, um die Ware abzuholen, macht Ihr sie fertig! Das wird ihnen lehren, was es heißt, sich mit Narain anzulegen!",
  },
  [8607] = {
    ["T"] = "Die Horde braucht Magiestoffverbände!",
    ["O"] = "Bringt 20 Magiestoffverbände zu Lady Kahler im Tal der Geister in Orgrimmar.",
    ["D"] = "Ich nehme doch hoffentlich richtig an, dass Ihr mit mir sprecht, weil Ihr mich bei dem Sammelauftrag unterstützen wollt, der mir anvertraut wurde? Gut! Mir ist es egal wie, aber ich wünsche mir wirklich, dass Ihr einige Magiestoffverbände für mich zusammentragt. Ganz frisch hergestellte wären wunderbar, aber nicht unbedingt zwingend. Bringt sie hierher zu mir, $C, dann werde ich sehen, dass Ihr ordnungsgemäß dafür entlohnt werdet.",
  },
  [8608] = {
    ["T"] = "Die Horde braucht mehr Magiestoffverbände!",
    ["O"] = "Bring 20 Mageweave Bandages to Lady Callow at the Valley of Spirits in Orgrimmar.",
    ["D"] = "You returned? Interesting. As you can see I am still collecting for the Ahn\'Qiraj \'war effort\'. Which means that I need you to once again go out and collect a stack of mageweave bandages for me. I look forward to your return, $C.",
  },
  [8609] = {
    ["T"] = "Die Horde braucht Runenstoffverbände!",
    ["O"] = "Bringt 20 Runenstoffverbände zu Steingardist Lehmhuf im Tal der Geister in Orgrimmar.",
    ["D"] = "Für viele scheint es unausweichlich, dass die Streitkräfte von Horde und Allianz bei Ahn\'Qiraj aufeinander treffen werden. Interessanterweise handelt es sich dabei nicht um einen Krieg gegeneinander, sondern vielmehr gegen die Silithiden oder was auch immer sie kontrolliert. Beide Seiten leisten ihren Teil, um die nötigen Ressourcen für eine erfolgreiche Kriegsführung aufzubringen.$B$BIch sammle Runenstoffverbände, die an die Soldaten ausgeteilt werden. Ich bräuchte dabei allerdings Unterstützung. Könntet Ihr mir dabei helfen, $C?",
  },
  [8610] = {
    ["T"] = "Die Horde braucht mehr Runenstoffverbände!",
    ["O"] = "Bring 20 Runecloth Bandages to Stoneguard Clayhoof at the Valley of Spirits in Orgrimmar.",
    ["D"] = "Ever does the time grow nearer when we shall launch ourselves headlong into the conflagration at Ahn\'Qiraj, priest. But first we must gather our strength and prepare so that we can survive the onslaught of the insects and their masters.$B$BIf you are here again, then surely you wish to make another contribution of runecloth bandages to the war effort? Please do so as soon as you can and return to me once more.",
  },
  [8611] = {
    ["T"] = "Die Horde braucht magere Wolfsteaks!",
    ["O"] = "Bringt 20 magere Wolfsteaks zu Blutwache Rawtar im Tal der Geister in Orgrimmar.",
    ["D"] = "Ich stehe hier nicht nur herum, um etwas Farbe abzubekommen, $C. Es gilt Kriegsvorbereitungen zu treffen. Wenn wir diese Viecher in Ahn\'Qiraj niederschmettern möchten, dann müssen wir bereit sein, und dazu gehört auch Proviant!$B$BIch sammle und salze hier magere Wolfsteaks für den anstehenden Krieg ein. Da ich hier bleiben muss, um mich um alles zu kümmern, bräuchte ich Eure Hilfe. Zieht aus, um Wölfe zu töten, bereitet ihr Fleisch zu mageren Wolfsteaks zu und bringt diese dann zu mir. Alles klar?",
  },
  [8612] = {
    ["T"] = "Die Horde braucht mehr magere Wolfsteaks!",
    ["O"] = "Bring 20 Lean Wolf Steaks to Bloodguard Rawtar at the Valley of Spirits in Orgrimmar.",
    ["D"] = "As you can see, $C, we still need to pack away more food for the soldiers. I don\'t know if you\'ve ever been in a war before, but once you get past the initial shock of the battlefield, you can get pretty hungry. You did a good job last time, so I expect that you\'ll do as well, if not a little quicker, in getting me another stack of those lean wolf steaks.",
  },
  [8613] = {
    ["T"] = "Die Horde braucht Tüpfelgelbschwänze!",
    ["O"] = "Bringt 20 Tüpfelgelbschwänze zu Angler Lin\'do im Tal der Geister in Orgrimmar.",
    ["D"] = "Wär\' heute nicht ein guter Tag zum Angeln? Praktisch, denn es sieht aus, als würde ich hier noch \'ne Menge Fisch brauchen. Die Horde hat mich mit der Sammlung von einem Haufen Tüpfelgelbschwänzen beauftragt. Sie sagen, dass sie sie für ihre Vorbereitungen für den Krieg in Ahn\'Qiraj benötigen. Ich hab\' gesagt, dass ich mein Bestes geben werde. Ich brauche also etwas Unterstützung. Ihr werdet mir doch helfen, $C, oder?",
  },
  [8614] = {
    ["T"] = "Die Horde braucht mehr Tüpfelgelbschwänze!",
    ["O"] = "Bring 20 Spotted Yellowtail to Fisherman Lin\'do at the Valley of Spirits in Orgrimmar.",
    ["D"] = "So as you can see, we still be needin\' a lot of the fishies. The combatants in the upcomin\' Ahn\'Qiraj War gonna be needin\' a lot to eat, so I needin\' to get all the spotted yellowtail that I can. You catch \'em, cook \'em, and bringin\' \'em back to me here, and I be givin\' you thanks, and the thanks of the Horde.",
  },
  [8615] = {
    ["T"] = "Die Horde braucht gebackenen Lachs!",
    ["O"] = "Bringt 20 Stück gebackenen Lachs zu Häuptling Scharfklaue im Tal der Geister in Orgrimmar.",
    ["D"] = "Die Vorbereitungen für den Krieg von Ahn\'Qiraj sind ein immenses Unterfangen, $C. All die Materialien, die hier gesammelt werden, sind gleichermaßen wichtig - ob es Metall für Waffen oder Proviant zum Essen ist. Hat man seit Tagen nichts gegessen, so kann man, wenn es darauf ankommt, auch nicht mehr seine Axt schwingen.$B$BDafür sammeln wir hier so viele Nahrungsmittel wie wir können. Ich sammle zum Beispiel gebackenen Lachs, ein Gericht, das wegen seiner Nahrhaftigkeit und guten Lagerbedingungen gewählt wurde. Könnt Ihr mir bei dieser Aufgabe helfen, $C?",
  },
  [8616] = {
    ["T"] = "Die Horde braucht mehr gebackenen Lachs!",
    ["O"] = "Bring 20 Baked Salmon to Chief Sharpclaw at the Valley of Spirits in Orgrimmar.",
    ["D"] = "The last batch of baked salmon that you donated to the war effort looked very delicious, $C. I must admit that I was tempted to take a few for myself. As you can tell, we have not yet reached our quota here. I am hoping you have returned because you have another stack of baked salmon with you",
  },
  [8619] = {
    ["T"] = "Urahne Schwermut",
    ["O"] = "",
    ["D"] = "",
  },
  [8620] = {
    ["T"] = "Der einzige Weg",
    ["O"] = "Findet die 8 verlorenen Kapitel von Drachisch für Dummies und vereint sie mit dem magischen Bucheinband. Bringt das vollständige Buch Drachisch für Dummies: Band 2 zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Ich hätte es wissen müssen. Mein Erzfeind, Doktor Weavil, hat wieder seine dreckigen Finger im Spiel! Jetzt, nach Eurem Versagen in Winterquell, hat Doktor Weavil mein Buch zerstört! Wie wollt Ihr jetzt die Welt retten, Held?$B$BDie Nachricht die ich erhalten habe, gab an, dass die einzig bekannte Kopie von \"Drachisch für Dummies: Band 2\" in 8 Teile zerrissen und im Wind zerstreut wurde! Solltet Ihr diese fehlenden Kapitel irgendwie finden können, dann verwendet diesen magischen Bucheinband, um sie wieder zusammen zu flicken. Kehrt dann zu mir zurück.",
  },
  [8620] = {
    ["T"] = "Der einzige Weg",
    ["O"] = "Findet die 8 verlorenen Kapitel von Drachisch für Dummies und vereint sie mit dem magischen Bucheinband. Bringt das vollständige Buch Drachisch für Dummies: Band 2 zu Narain Pfauentraum in Tanaris.",
    ["D"] = "Ich hätte es wissen müssen. Mein Erzfeind, Doktor Weavil, hat wieder seine dreckigen Finger im Spiel! Jetzt, nach Eurem Versagen in Winterquell, hat Doktor Weavil mein Buch zerstört! Wie wollt Ihr jetzt die Welt retten, Held?$B$BDie Nachricht die ich erhalten habe, gab an, dass die einzig bekannte Kopie von \"Drachisch für Dummies: Band 2\" in 8 Teile zerrissen und im Wind zerstreut wurde! Solltet Ihr diese fehlenden Kapitel irgendwie finden können, dann verwendet diesen magischen Bucheinband, um sie wieder zusammen zu flicken. Kehrt dann zu mir zurück.",
  },
  [8621] = {
    ["T"] = "Fußschützer des Sturmrufers",
    ["O"] = "Bringt die Dominanzbindungen der Qiraji, 2 Götzen der Weisen, 5 Bronzeskarabäen und 5 Tonskarabäen zu Kandrostrasz in Ahn\'Qiraj. Diese Aufgabe erfordert auch einen neutralen Ruf bei der Brut Nozdormus.",
    ["D"] = "Qirajimagie ist sehr stark und uralt. Die mächtigsten Qiraji handhaben Gegenstände, die so machtvoll sind, dass sie sogar einem Drachen Einhalt gebieten.$B$BBringt mir die Dominanzbindungen der Qiraji sowie andere geringfügigere Bestandteile und ich werde ein Paar mächtige Stiefel für Euch herstellen.$B$BIhr werdet sie im Kampf gegen den Feind als nützlich empfinden.",
  },
  [8622] = {
    ["T"] = "Halsberge des Sturmrufers",
    ["O"] = "Bringt den Knochenpanzer des Alten Gottes, 2 Götzen der Weisen, 5 Silberskarabäen und 5 Knochenskarabäen zu Vethsera innerhalb von Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus.",
    ["D"] = "Der unaussprechliche Alte Gott erwacht langsam aus seinem Schlummer unterhalb Ahn\'Qirajs. Das schreckliche Schicksal, das Azeroth erwartet, könnte in Tagen oder auch Jahren Wirklichkeit werden, Zeit spielt für Wesen seiner Macht keine Rolle.$B$BStehen die Sterne günstig, wird er furchtbaren Zorn gegen alle Lebewesen walten lassen. Ihr müsst dem zuvorkommen und einen Weg finden, um seiner Existenz ein Ende zu bereiten.$B$BTut dies und bringt mir ein Stück seiner Hülle. Ich werde Euch eine Rüstung gewähren, die noch kein Sterblicher getragen hat... Rüstung für einen Helden, der einen Gott bezwungen hat!",
  },
  [8623] = {
    ["T"] = "Diadem des Sturmrufers",
    ["O"] = "Bringt Vek\'lors Diadem, 2 Götzen der Wiedergeburt, 5 Steinskarabäen und 5 Kristallskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.",
    ["D"] = "Die Zwillingsimperatoren... ihr unaufhörliches Flüstern martert langsam meinen Verstand. Sie verhöhnen mich, wissend, dass ich nicht tiefer in ihr Reich vordringen kann, um meinen Bruder Arygos zu retten.$B$BIn meinen Visionen sehe ich ihre monströsen Fratzen, die mit mächtigen Kronen geschmückt sind. Ich werde meine Vergeltung noch bekommen... ich werde diese Symbole der Qirajimacht in solche verwandeln, die unserem Nutzen gerecht werden.$B$BBringt mir die benötigten Bestandteile, $n. Ich werde Euch dafür ein Kopfstück von unvergleichlicher Macht geben.",
  },
  [8624] = {
    ["T"] = "Gamaschen des Sturmrufers",
    ["O"] = "Bringt die Haut des großen Sandwurms, 2 Götzen des Kampfes, 5 Bronzeskarabäen und 5 Elfenbeinskarabäen zu Kandrostrasz innerhalb von Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.",
    ["D"] = "Ihr möchtet Euch also uns gegenüber bewähren, Sterblicher? Nun gut, vielleicht können wir uns ja gegenseitig helfen.$B$BDer uralte Sandwurm, Ouro, ist ein Geschöpf von legendärer Macht. Man sagt, dass er von dem Alten Gott selbst als Gespött des Lebens erschaffen wurde.$B$BSeine Haut ist unnatürlich... dick, fast unbezwingbar und dennoch flexibel, was ihm ermöglicht, sich mit der Geschwindigkeit und Grazie eines kleineren Lebewesens zu bewegen.$B$BBringt mir eine Probe seiner Haut sowie andere geringfügigere Bestandteile und ich werde mächtige Gamaschen für Euch herstellen.",
  },
  [8625] = {
    ["T"] = "Schulterpolster des Mysteriums",
    ["O"] = "Bringt die Dominanzbindungen der Qiraji, 2 Götzen des Todes, 5 Steinskarabäen und 5 Bronzeskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen neutralen Ruf bei der Brut Nozdormus.",
    ["D"] = "Man sagt unter uns, dass die kunstvoll verzierten Schulterpanzerungen, die von den sterblichen Völkern getragen werden, zu Beginn eine Nachahmung von Drachenflügeln werden sollten.$B$BBringt mir die Bindungen, die von den höchsten Anführern der Qiraji getragen werden und ich werde sie in ein Paar Schulterstücke verarbeiten, die furchteinflößender als die Flügel Nefarians selbst sind!",
  },
