  [8728] = {
    ["T"] = "Die gute und die schlechte Nachricht",
    ["O"] = "Narain Pfauentraum in Tanaris möchte, dass Ihr ihm 20 Arkanitbarren, 10 Elementiumerz, 10 Azerothische Diamanten und 10 blaue Saphire bringt.",
    ["D"] = "Das hat ein wenig länger gedauert als erwartet. Nun lasst mich sehen, was dieses Buch zu berichten hat.$B$B<Leise vor sich hermurmelnd beginnt Narain das Buch zu lesen.>$B$BDie gute Nachricht ist, dass ich mir zu 99% sicher bin, eine Arkanitboje konstruieren zu können, die voll und ganz Euren Anforderungen entspricht. Kommen wir zur schlechten Nachricht: Ich brauche eine gehörige Menge Arkanit, Elementiumerz und Edelsteine.$B$BSobald Ihr mir diese Materialien gebracht habt, kann ich mit der Konstruktion der Boje beginnen.",
  },
  [8729] = {
    ["T"] = "Der Zorn von Neptulon",
    ["O"] = "Benutzt die Arkanitboje am wirbelnden Mahlstrom in der Bucht der Stürme in Azshara.",
    ["D"] = "Hatte ich die schlechte Neuigkeit schon zur Sprache gebracht? Sein Name ist \"Der weiße Riese\": Dreißig Meter Tod und Zerstörung pur. Den kleinen Leckerbissen hatte ich glatt vergessen.$B$BMeinen gnomischen Superpsychokräften nach zu urteilen, befindet sich der beste Ort, um diese Boje zu platzieren, an der Küste Azsharas in der Bucht der Stürme.$B$BHaltet nach einem wirbelnden Mahlstrom Ausschau. Vermutlich ist die ganze Gegend mit Wrackteilen übersät.$B$BMacht es gut und nehmt ein paar Freunde mit.$B$BFalls Ihr die ganze Sache wie durch ein Wunder überleben solltet, bringt den Szeptersplitter zu Anachronos.",
  },
  [8730] = {
    ["T"] = "Nefarius\' Verderbnis",
    ["O"] = "Tötet Nefarian und bringt den roten Szeptersplitter wieder in Euren Besitz. Bringt den roten Szeptersplitter zu Anachronos in den Höhlen der Zeit in Tanaris. Euch bleiben ein und halb Stunden, um diese Aufgabe zu erfüllen.",
    ["D"] = "Held, seid Ihr es? Es ist tausend Jahre her, seitdem man mir den Splitter anvertraut hat. Jetzt, in meiner finstersten Stunde soll jemand erscheinen, um mich davon zu entlasten... aber was wäre eine Tragödie ohne Grausamkeit?$B$B<Vaelastrasz hustet geschwächt.>$B$BNe... Nefarius besitzt nun den Szeptersplitter.$B$BDie Zeit ist entscheidend. Nefarius hat vor den Splitter zu zerstören. Ihr müsst Euch beeilen!",
  },
  [8731] = {
    ["T"] = "Felddienst",
    ["O"] = "Erstattet Krug Schädelspalt am Posten der Orgrimmarlegion beim Regalschwarmbau Bericht. Bereitet Eure nicht unterzeichneten Felddienstpapiere vor, lasst sie unterzeichnen und bringt sie dann zu Windrufer Kaldon auf Burg Cenarius.$B$BHinweis: das Heilen oder Wirken von begünstigenden Zaubern auf ein Mitglied der Orgrimmarlegion wird Euch für den PvP-Kampf markieren.",
    ["D"] = "Seid gegrüßt, $n. Bevor ich Euch mit umfassenderen Aufgaben in diesem Krieg beauftrage, müsst Ihr Euren Anteil am Felddienst leisten.$B$BIhr könnt die Orgrimmarlegion von Hauptmann Schädelspalt außerhalb des Regalschwarmbaus finden.$B$BErstattet ihm Bericht und kommt mit dem Beweis für Euren Felddienst zu mir zurück. Ich werde wichtigere Anweisungen für Euch bereithalten.$B$BOh, bitte haltet Euch von der Eisenschmiedebrigade fern. Es herrschen große Spannungen zwischen unseren Freiwilligen der Allianz und der Horde.",
  },
  [8732] = {
    ["T"] = "Felddienstpapiere",
    ["O"] = "",
    ["D"] = "",
  },
  [8733] = {
    ["T"] = "Eranikus, der Tyrann des Traums",
    ["O"] = "Reist nach Teldrassil und sucht Malfurions Agenten irgendwo außerhalb der Stadtmauern von Darnassus auf.",
    ["D"] = "Dies ist der Weg der Begleichung, Held. Würde ich heute getötet werden, dann würde ich für den gerechten Zweck sterben. Ihr müsst den grünen Szeptersplitter wiedererlangen. Ihr müsst Eranikus von der Besudelung, die die Alten Götter über ihn verhängt haben, reinigen.$B$BReist nach Darnassus. Ihr werdet einen meiner Agenten genau vor den Stadtmauern finden. Dieser Agent wird die Dinge für uns in Bewegung setzen und Tyrande über unsere Pläne informieren, ohne Hirschhaupt zu alarmieren.$B$B<Malfurion seufzt.>$B$BEin Bruch mit der in tausend Jahren geschaffenen Gunst...",
  },
  [8734] = {
    ["T"] = "Tyrande und Remulos",
    ["O"] = "Reist zur Mondlichtung und sprecht mit Bewahrer Remulos.",
    ["D"] = "Tyrande wird benachrichtigt, Sterblicher. Shan\'do Sturmgrimm hat mich für diesen Fall vorbereitet.$B$BIhr müsst jetzt zur Mondlichtung reisen und mit Remulos sprechen. Er wird weitere Anweisungen bezüglich der Herbeirufung von Eranikus haben.$B$BWir alle wünschen Euch viel Glück.",
  },
  [8735] = {
    ["T"] = "Die Verderbnis des Alptraums",
    ["O"] = "Reist zu den vier Portalen des smaragdgrünen Traums in Azeroth und sammelt dort jeweils ein Verderbnisfragment des Alptraums ein. Kehrt zu Bewahrer Remulos bei der Mondlichtung zurück, sobald Ihr diese Aufgabe erfüllt habt.",
    ["D"] = "Ihr müsst zu den vier Traumportalen von Azeroth reisen und Euch jeweils ein Verderbnisfragment des Alptraums beschaffen. Die Drachkin, die in dieser Gegend hausen, werden derartige Fragmente bei sich tragen. Kommt zu mir zurück, sobald Ihr diese Fragmente eingesammelt habt.",
  },
  [8736] = {
    ["T"] = "Der Alptraum offenbart sich",
    ["O"] = "Verteidigt Nachthafen gegen Eranikus. Bewahrer Remulos darf dabei nicht ums Leben kommen und Eranikus darf nicht getötet werden. Verteidigt Euch. Erwartet Tyrande.",
    ["D"] = "Wir müssen gegen Eranikus mit Bedacht vorgehen. Wir sollen ihn erlösen und nicht zerstören.$B$BTyrande ist bereits auf dem Weg, um uns bei dieser Aufgabe zu unterstützen. Wir müssen die Attacken des Drachen überstehen und Nachthafen vor der sicheren Zerstörung bewahren.$B$BSeid auf der Hut! Ihr und Eure Verbündeten seid unsere einzige Hoffnung.",
  },
  [8737] = {
    ["T"] = "Azurblauer Templer",
    ["O"] = "Beschwört und vernichtet einen azurblauen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz I, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die Schattenhammerkultisten und die von ihnen verehrten Elementarfürsten stellen eine dauerhafte Bedrohung für unsere Operationen in Silithus dar. Trotz der neuen Gegnermassen in Ahn\'Qiraj können wir es uns nicht erlauben, unsere Wachposten und Einsätze gegen den äußeren Feind zu verringern.$B$BFindet einen Weg, um einen azurblauen Templer auf unsere Existenzebene zu beschwören und zerstört ihn.",
  },
  [8738] = {
    ["T"] = "Späherbericht des Regalschwarms",
    ["O"] = "Setzt Euch mit dem cenarischen Späher Landion innerhalb des Regalschwarmbaus in Verbindung und bringt den Späherbericht des Regalschwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VII, um diese Aufgabe abschließen zu können.",
    ["D"] = "Wir haben unsere besten Späher bis weit in die Schwarmbauten der Silithiden ausgesendet, um Informationen zu sammeln. Späher Landion hat die Aktivitäten der Silithiden im Regalschwarm nun schon seit Tagen beobachtet. Findet ihn und besorgt Euch einen schriftlichen Bericht. Wir müssen so schnell wie möglich auf diese Informationen reagieren können, $n.",
  },
