  [8778] = {
    ["T"] = "Die Eisenschmiedebrigade braucht Sprengstoff!",
    ["O"] = "Bringt Arkanist Sprinkelstaub nahe des Zoraschwarmbaus in Silithus 6 Öle des Feuerbrandes, 5 Einheiten Goblinraketentreibstoff und 10 Einheiten dichtes Sprengpulver. Ihr benötigt auch Logistikeinsatz IV, um diese Aufgabe abschließen zu können.",
    ["D"] = "Uns liegt eine Anforderung der Eisenschmiedebrigade vor, die eine Menge… na sagen wir, recht explosive Komponenten umfasst. Beschafft diese Komponenten und bringt sie zu einem Kerl namens Sprinkelstaub, einem Mitglied der Eisenschmiedebrigade.",
  },
  [8779] = {
    ["T"] = "Magische Materialien",
    ["O"] = "Bringt 1 großen glänzenden Splitter, 1 großen strahlenden Splitter und 1 gewaltigen Smaragd zu Geologin Lerchenbann auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz V, um diese Aufgabe abschließen zu können.",
    ["D"] = "Geologin Lerchenbanns Studien haben sich für unsere Operationen in Silithus als sehr wertvoll erwiesen. Vor Kurzem hat sie eine Ladung magischer Materialien angefordert, die sie für ihre weiteren Forschungen dringend benötigt.$B$BBeschafft die notwendigen Materialien und liefert sie direkt an sie aus, damit Geologin Lerchenbann ihre Forschungen ungestört fortsetzen kann.",
  },
  [8780] = {
    ["T"] = "Rüstungssets für den Feldeinsatz",
    ["O"] = "Bringt 8 unverwüstliche Rüstungssets und 8 schwere Rüstungsset zu Janela Starkhammer am Posten der Eisenschmiedebrigade beim Zoraschwarmbau in Silithus. Ihr benötigt auch Logistikeinsatz VII, um diese Aufgabe abschließen zu können.",
    ["D"] = "Der Eisenschmiedebrigade gehen im Feld die Rüstungssets aus. Schnappt Euch eine Ladung und bringt sie zu Janela Starkhammer am Posten der Eisenschmiedebrigade beim Zoraschwarmbau.",
  },
  [8781] = {
    ["T"] = "Waffen für den Feldeinsatz",
    ["O"] = "Bringt 2 Mondstahlbreitschwerter zu Janela Starkhammer am Posten der Eisenschmiedebrigade beim Zoraschwarmbau. Ihr benötigt auch Logistikeinsatz VI, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die Eisenschmiedebrigade hat sich in den Kämpfen von Silithus als ein würdiger Verbündeter erwiesen. Unglücklicherweise hat sie auf dem Weg hierher die meisten ihrer Vorräte eingebüßt. Beschafft eine Ladung Waffen und bringt sie zu Janela Starkhammer nahe des Baus des Zoraschwarms.",
  },
  [8782] = {
    ["T"] = "Uniformnachschub",
    ["O"] = "Bringt 1 Einheit Mondstoff, 2 Runenstoffballen und 1 Einheit Eisenweberseide zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz VIII, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die letzte Ladung neuer Rekruten hat uns einen Engpass an Uniformen beschert. Beschafft uns eine Ladung Schneiderwaren, damit wir mehr Uniformen herstellen können. Bringt alles zur Windruferin Prachthorn.",
  },
  [8783] = {
    ["T"] = "Besondere Materialien",
    ["O"] = "Bringt Vargus auf Burg Cenarius in Silithus 2 verzauberte Thoriumbarren und 2 Einheiten verzaubertes Leder. Ihr benötigt auch Logistikeinsatz IX, um diese Aufgabe abschließen zu können.",
    ["D"] = "Vargus, der Schmied von Burg Cenarius, hat verzauberte Materialien zur Herstellung von stärkeren Waffen angefordert. Beschafft die benötigten Materialien und bringt sie direkt zu Vargus, um den hohen Ausrüstungsstand unserer Truppen auf Burg Cenarius aufrechtzuerhalten.",
  },
  [8784] = {
    ["T"] = "Geheimnisse der Qiraji",
    ["O"] = "Bringt das uralte Qirajiartefakt zu den Drachen, die sich nahe des Tempeleingangs versteckt halten.",
    ["D"] = "Ihr habt ein uraltes Artefakt der Qiraji entdeckt! Vielleicht interessiert sich einer der Drachen am Eingang des Tempels für dieses Relikt.",
  },
  [8785] = {
    ["T"] = "Die Orgrimmarlegion braucht Mojo!",
    ["O"] = "Bringt Schattenpriesterin Shai nahe des Regalschwarmbaus in Silithus 6 Einheiten mächtiges Mojo, 6 Fläschchen großes Mojo und 8 Öle des Feuerbrandes. Ihr benötigt auch Logistikeinsatz IV, um diese Aufgabe abschließen zu können.",
    ["D"] = "Uns liegt eine Anforderung der Orgrimmarlegion vor, die eine Menge recht ungewöhnliche Materialien umfasst. Aber in Anbetracht ihrer jüngsten Leistungen in Silithus, möchten wir dieser Anfrage so schnell wie möglich nachkommen. Beschafft die notwendigen Materialien und bringt sie einer Trollfrau namens Shai.",
  },
  [8786] = {
    ["T"] = "Waffen für den Feldeinsatz",
    ["O"] = "Bring 3 massive Eisenäxte zu Merok Langschreiter im Lager der Orgrimmarlegion außerhalb des Regalschwarmbaus. Ihr benötigt auch Logistikeinsatz VI, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die Orgrimmarlegion hatte bereits vor einiger Zeit zusätzliche Waffen angefordert, die wir bisher noch nicht liefern konnten. Beschafft die notwendigen Waffen und bringt sie zu Merok Langschreiter außerhalb des Regalschwarmbaus.",
  },
  [8787] = {
    ["T"] = "Rüstungssets für den Feldeinsatz",
    ["O"] = "Bringt 8 unverwüstliche Rüstungssets und 8 schwere Rüstungsset zu Merok Langschreiter nahe des Regalschwarmbaus. Ihr benötigt auch Logistikeinsatz VII, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die Orgrimmarlegion ist zu unserer Unterstützung in Silithus eingetroffen. Unglücklicherweise hat sie auf dem Weg hierher die meisten ihrer Rüsstungssets eingebüßt. Beschafft eine Ladung und bringt sie zu Merok Langschreiter im Lager der Orgrimmarlegion.",
  },
  [8788] = {
    ["T"] = "Ein leicht geschütteltes Geschenk",
    ["O"] = "",
    ["D"] = "",
  },
  [8789] = {
    ["T"] = "Imperiale Qirajiwaffen",
    ["O"] = "Arygos im Tempel von Ahn\'Qiraj wird Euch mit Elementium erfüllte Ausrüstung herstellen, wenn Ihr ihm imperiale Qirajiwaffen und 3 Elementiumblöcke bringt.",
    ["D"] = "Wir haben hier nicht ein Jahrtausend in völliger Untätigkeit verbracht, $n. Wir sahen mit eigenen Augen, wie sich diese Biester an ihren eigenen Artgenossen gütlich taten, nachdem diese ihre Nützlichkeit für den Schwarm verloren hatten. Wir lauschten, als sie ihre Pläne schmiedeten, diese Welt in Schutt und Asche zu legen. Und wir lernten, wie sich Verrat und Missgunst in ihren Reihen ausbreiten.$B$BSie haben Schwächen und wir wissen nun, wie wir diese zu unserem Vorteil nutzen können.$B$BFindet und bringt mir ihre Zeremonienwaffen. Zusammen, mit dem durch ihren Gott erschaffenen Element, werden wir daraus mächtige Instrumente der Zerstörung erschaffen.",
  },
  [8790] = {
    ["T"] = "Imperiale Qirajiinsignien",
    ["O"] = "Merithra des Traums im Tempel von Ahn\'Qiraj wird Euch mit Elementium erfüllte Ausrüstung herstellen. Bringt ihr dazu imperiale Qirajiinsignien und 3 Elementiumblöcke.",
    ["D"] = "Arygos hat die Theorie aufgestellt, dass das Element, welches während des Konflikts zwischen den Alten Göttern und ihren Anhängern entstanden ist, unter den richtigen Bedingungen für sie tödlich ist. Ich spreche vom Elementium, $G Sterblicher : Sterbliche;.$B$BFindet und bringt mir imperiale Qirajiinsignien sowie das giftige Element, dann werde ich Euch Waffen von größter Macht erschaffen!",
  },
  [8791] = {
    ["T"] = "Der Untergang von Ossirian",
    ["O"] = "Bringt den Kopf von Ossirian dem Narbenlosen zu Kommandant Mar\'alith auf Burg Cenarius in Silithus.",
    ["D"] = "Eine schreckliche Bedrohung hat ihr Ende gefunden. Der grausame Ossirian existiert nicht mehr.$B$BDie Bewohner Kalimdors werden aufatmen, wenn sie von diesem glorreichen Sieg erfahren. Nehmt Ossirians Kopf und bringt ihn zu Kommandant Mar\'alith auf Burg Cenarius.",
  },
  [8792] = {
    ["T"] = "Die Horde braucht Euch!",
    ["O"] = "Sprecht mit Kriegsherr Gorchuk im Tal der Geister in Orgrimmar.",
    ["D"] = "Hallo, schön, dass Ihr Euch entschieden habt, mir zuzuhören, was ich zu sagen habe. Die Horde braucht jede Hilfe, die sie kriegen kann, um sich auf den Krieg in Ahn\'Qiraj vorzubereiten. Die Horde braucht Euch! Während wir uns unterhalten, haben die offiziellen Sammler bereits damit begonnen, kriegstaugliche Materialien aller Art zu sammeln. Doch ohne Eure Mithilfe sind all diese Mühen vergebens, $n! Wir brauchen Eure Unterstützung!$B$BAm besten unterhaltet Ihr Euch mit dem verantwortlichen Offizier, Kriegsherr Gorchuk. Können wir auf Euch zählen, $C? Werdet Ihr uns bei den Kriegsvorbereitungen zur Seite stehen?",
  },
