  [8800] = {
    ["T"] = "Schlachtrüstung des Cenarius",
    ["O"] = "Sprecht mit Vargus auf Burg Cenarius in Silithus.",
    ["D"] = "Zwischen dem Erledigen der Euch im Krieg zugeteilten Aufträge, solltet Ihr ab und an mit Vargus dem Schmied sprechen. Zeigt ihm Eure erworbenen Abzeichen und er wird Euch im Gegenzug mit der Eurem Einsatz entsprechenden Ausrüstung versorgen.",
  },
  [8801] = {
    ["T"] = "C\'Thuns Vermächtnis",
    ["O"] = "Bringt Caelastrasz im Tempel von Ahn\'Qiraj das Auge von C\'Thun.",
    ["D"] = "Die Mauern von Ahn\'Qiraji erbeben. Eine finstere Macht, älter noch als die Welt, wurde vernichtet.$B$BAls Ihr auf die Überreste der gigantischen Scheußlichkeit hinab blickt, scheint Euer Herz fast zu gefrieren. Selbst im Tod, könnt Ihr das böse Erbe C\'Thuns um Euch herum spüren.$B$BIhr habt das Unmögliche vollbracht.$B$BEines seiner Augen, scheint Euch noch immer anzustarren. Ihr nehmt es auf und verstaut es in Eurem Gepäck.$B$BBringt den Drachen im nächsten Raum das Auge von C\'Thun.",
  },
  [8802] = {
    ["T"] = "Der Retter von Kalimdor",
    ["O"] = "Bringt Anachronos in den Höhlen der Zeit das Auge von C\'Thun.",
    ["D"] = "Begebt Euch zu den Höhlen der Zeit, $G Lord:Lady; $n. Anachronos erwartet Eure Rückkehr. Übergebt ihm das Auge von C\'Thun. Sicherlich wird er es der Sammlung seines Meisters hinzufügen wollen.",
  },
  [8803] = {
    ["T"] = "Ein Festtagsgeschenk",
    ["O"] = "",
    ["D"] = "",
  },
  [8804] = {
    ["T"] = "Wüstenüberlebenspaket",
    ["O"] = "Bringt 4 Kugeln des Wassers, 4 mächtige Gegengifte und 4 geräucherte Wüstenknödel zu Calandrath in der Taverne von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz I, um diese Aufgabe abschließen zu können.",
    ["D"] = "Seitdem ständig neue Rekruten auf Burg Cenarius eintreffen, stehen wir vor dem Problem, sie in der Wüste am Leben zu erhalten. Der ständige Wasserentzug und die zahlreichen giftigen Kreaturen in dieser Gegend sind eine ernstzunehmende Gefahr für die unerfahren Soldaten. Sammelt alles, was notwendig ist, um ein Wüstenüberlebenspaket zusammenzustellen und bringt die erforderlichen Vorräte zu Calandrath auf Burg Cenarius.",
  },
  [8805] = {
    ["T"] = "Stiefel für die Wache",
    ["O"] = "Bringt 3 Paar verschnörkelte Mithrilstiefel zu Vish Kozus, Hauptmann der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz II, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die berittene Einheit der Wache von Burg Cenarius hat kaum noch Vorräte an verschnörkelten Mithrilstiefeln. Beschafft Euch eine Ladung und bringt sie sofort zu Hauptmann Vish Kozus auf Burg Cenarius.",
  },
  [8806] = {
    ["T"] = "Schleifsteine für die Wache",
    ["O"] = "Bringt 10 verdichtete Schleifsteine, 10 robuste Schleifsteine und 10 schwere Schleifsteine zu Vish Kozus, Hauptmann der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz III, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die Wache von Burg Cenarius hat kaum noch Vorräte an Schleifsteinen. Beschafft Euch eine Ladung und bringt sie sofort zu Hauptmann Vish Kozus auf Burg Cenarius.",
  },
  [8807] = {
    ["T"] = "Magische Materialien",
    ["O"] = "Bringt 1 großen glänzenden Splitter, 1 großen strahlenden Splitter und 1 gewaltigen Smaragd zu Geologin Lerchenbann auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz V, um diese Aufgabe abschließen zu können.",
    ["D"] = "Geologin Lerchenbanns Studien haben sich für unsere Operationen in Silithus als sehr wertvoll erwiesen. Vor Kurzem hat sie eine Ladung magischer Materialien angefordert, die sie für ihre weiteren Forschungen dringend benötigt.$B$BBeschafft die notwendigen Materialien und liefert sie direkt an sie aus, damit Geologin Lerchenbann ihre Forschungen ungestört fortsetzen kann.",
  },
  [8808] = {
    ["T"] = "Uniformnachschub",
    ["O"] = "Bringt 1 Einheit Mondstoff, 2 Runenstoffballen und 1 Einheit Eisenweberseide zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz VIII, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die letzte Ladung neuer Rekruten hat uns einen Engpass an Uniformen beschert. Beschafft uns eine Ladung Schneiderwaren, damit wir mehr Uniformen herstellen können. Bringt alles zur Windruferin Prachthorn.",
  },
  [8809] = {
    ["T"] = "Besondere Materialien",
    ["O"] = "Bringt Vargus auf Burg Cenarius in Silithus 2 verzauberte Thoriumbarren und 2 Einheiten verzaubertes Leder. Ihr benötigt auch Logistikeinsatz IX, um diese Aufgabe abschließen zu können.",
    ["D"] = "Vargus, der Schmied von Burg Cenarius, hat verzauberte Materialien zur Herstellung von stärkeren Waffen angefordert. Beschafft die benötigten Materialien und bringt sie direkt zu Vargus, um den hohen Ausrüstungsstand unserer Truppen auf Burg Cenarius aufrechtzuerhalten.",
  },
  [8810] = {
    ["T"] = "Verbände für den Feldeinsatz",
    ["O"] = "Bringt 30 schwere Runenstoffverbände, 30 schwere Seidenverbände und 30 schwere Magiestoffverbände zu Windruferin Prachthorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz X, um diese Aufgabe abschließen zu können.",
    ["D"] = "Nach den neusten Verlusten, die wir hinnehmen mussten, gehen unsere Vorräte an Verbänden für das Schlachtfeld gefährlich zur Neige. Besorgt Euch eine Ladung an Vorräten für Erste Hilfe und bringt sie zu Windruferin Prachthorn auf Burg Cenarius.",
  },
