  [8827] = {
    ["T"] = "Winters Geschenke",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Eisenschmiede.",
    ["D"] = "Ich habe gehört, dass Altvater Winter mit großzügiger Unterstützung von Kokelwälder für jeden Geschenke unter den Weihnachtsbaum von Eisenschmiede gelegt hat. Wie gerne würde ich mich auf dem Weg machen, doch leider muss ich mich noch um den PX-238 Winterwundervolt kümmern.$B$BIhr solltet dort auf jeden Fall mal vorbeischauen. Ich bin mir sicher, dass Altvater Winter auch einige Geschenke für Euch mitgebracht hat.",
  },
  [8828] = {
    ["T"] = "Winters Geschenke",
    ["O"] = "Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.",
    ["D"] = "Ich habe gehört, dass Altvater Winter mit großzügiger Unterstützung von Kokelwälder für jeden Geschenke unter den Weihnachtsbaum von Orgrimmar gelegt hat. Wie gerne würde ich mich auf dem Weg machen, doch leider muss ich mich noch um den PX-238 Winterwundervolt kümmern.$B$BIhr solltet dort auf jeden Fall mal vorbeischauen. Ich bin mir sicher, dass Altvater Winter auch einige Geschenke für Euch mitgebracht hat.",
  },
  [8829] = {
    ["T"] = "Die ultimative Täuschung",
    ["O"] = "Bringt 1 Schattenhaut, 3 ausgefranste Monstrositätenstickereien und 1 Robe eines Schattenhammerkultisten zu Aurel Goldblatt auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz XI, um diese Aufgabe abschließen zu können.",
    ["D"] = "Die Anhänger des Schattenhammers operieren in zahllosen geheimen und gut getarnten Gruppen. Während der Kult unsere Welt durch zahllose Aktionen plagt, werden alle Operationen an einem uns immer noch unbekannten Ort geplant.$B$BWir sind zu der Erkenntnis gelangt, dass die Steine, mit denen die Kultisten des Schattenhammers ihre Anführer beschwören, in beide Richtungen funktionieren. Um einen der Steine ohne Gefahr benutzen zu können, werden wir versuchen, die Robe eines Abgesandten des Schattenhammers bis ins kleinste Detail nachzubilden.$B$BBeschafft die dafür benötigten Materialien und bringt sie zu Aurel Goldblatt, $n.",
  },
