  [8857] = {
    ["T"] = "Geheimnisse des Kolosses - Ashi",
    ["O"] = "Bringt das Fragment vom Koloss des Ashischwarms zu Glotz Widrikus in Beutebucht.",
    ["D"] = "Ich suche nach schlachterprobten Abenteurern, die für mich eine Fragmentprobe vom Koloss des Ashischwarms zu Glotz in Beutebucht bringen. Nein, nein, Ihr braucht Euch nicht einmal in die Nähe dieses Giganten zu wagen. Nehmt einfach eine von meinen Proben und beeilt Euch. Verrottet nutzt sie Glotz nicht viel!",
  },
  [8858] = {
    ["T"] = "Geheimnisse des Kolosses - Regal",
    ["O"] = "Bringt das Fragment vom Koloss des Regalschwarms zu Aufseher Schlickfaust in die Sengende Schlucht.",
    ["D"] = "Ich würde nichts lieber tun, als Schlickfaust dieses verdammte Fragment in sein fettes Maul zu stopfen. Stattdessen stecke ich hier fest und sammle Proben von dieser zu groß geratenen Kakerlake! In Anbetracht ihrer Größe, werde ich hier wohl noch eine Weile beschäftigt sein.$B$BNehmt diese Probe und bringt sie für mich zu Schlickfaust... und beeilt Euch, $C. Er zahlt auch sehr gut!$B$BAlso, ab zur Thoriumspitze in die Sengende Schlucht.",
  },
  [8859] = {
    ["T"] = "Geheimnisse des Kolosses - Zora",
    ["O"] = "Bringt das Fragment vom Koloss des Zoraschwarms zu Lord Maxwell Tyrosus an der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.",
    ["D"] = "Meine Leute hätte es beinahe erwischt, als sie diese Probe beschafft haben. Ihr müsst Euch unbedingt beeilen! Lord Maxwell Tyrosus wartet bereits voller Ungeduld auf eine frische Fragmentprobe. Er geht davon aus, dass uns die Geheimnisse dieses Kolosses im Kampf gegen die Geißel sehr von Nutzen sein werden!$B$BWieso seid Ihr noch hier!? Zur Kapelle des Hoffnungsvollen Lichts! Ja, ja, man wird Euch für diese Aufgabe großzügig entlohnen...",
  },
  [8860] = {
    ["T"] = "Neujahrsfeier!",
    ["O"] = "Bringt die Vorräte von Kokelwälder zu Gastwirtin Allison in Sturmwind.",
    ["D"] = "In Sturmwind wird das neue Jahr ganz groß gefeiert! Den ganzen Tag lang gibt es kostenlosen Hochprozentigen und nachts ein fantastisches Feuerwerk! Das wird sicher ein Riesenspaß... allerdings findet heute auch in Beutebucht eine Neujahrsfeier statt. Ich wette, dort ist sogar noch mehr los!$B$BEgal! Jedenfalls braucht Gastwirtin Allison in Sturmwind eine Ladung Kokelwälder Festtagsspeisen, um alle Partylöwen satt in das neue Jahr zu bringen. Würdet Ihr mir den Gefallen tun und die Vorräte für mich abliefern?$B$BDanke, $n! Ihr findet Gastwirtin Allison in der Güldenen Rose, drüben im Handelsdistrikt von Sturmwind.",
