  [8921] = {
    ["T"] = "Der Ektoplasmadestillierer",
    ["O"] = "Kehrt mit einem empfindlichen Arkanitwandler, 4 großen ewigen Essenzen, 10 Flaschen Steinschuppenöl, 25 Einheiten Vulkanasche und 40 Gold im Gepäck zu Mux Manamix in Gadgetzan zurück.",
    ["D"] = "Ah, Ihr seid an meinem letzten Projekt interessiert... Dem extradimensionalen Geisterdetektor! Dummerweise sind mir die Geldmittel und Hilfskräfte ausgegangen$B$BIhr sagt, dass Ihr mir aushelfen wollt? Sehr gut! Zunächst müssen wir einen Apparat bauen, der die Essenz des Todes absaugen kann! Einen Ektoplasmadestillierer zu konstruieren wird allerdings nicht ganz einfach sein, $n.$B$BIhr müsst mir einen Arkanitwandler, einige große ewige Essenzen und Steinschuppenöl bringen. Außerdem brauche ich noch Vulkanasche aus den Lavatümpeln in der Brennenden Steppe, um einen Filter zu konstruieren.",
  },
  [8922] = {
    ["T"] = "Ein übernatürlicher Apparat",
    ["O"] = "Bringt den versiegelten Blutbehälter zu Mux Manamix in Gadgetzan.",
    ["D"] = "Ich muss einen Weg finden, mit Anthion Harmon zu sprechen... Oder genauer gesagt mit seinem Geist!$B$BIch kenne da einen Ingenieur, der eine gewisse Faszination für das Übernatürliche hegt. Er behauptet eine Maschine konstruieren zu können, die es ihm erlaubt mit den Toten zu kommunizieren!$B$BDas Blut, dass Ihr gesammelt habt, war für ihn bestimmt. Reist nach Gadgetzan und bringt es ihm als Bezahlung für seine Dienste!",
  },
  [8923] = {
    ["T"] = "Ein übernatürlicher Apparat",
    ["O"] = "Bringt den versiegelten Giftbehälter zu Mux Manamix in Gadgetzan.",
    ["D"] = "Ihr müsst wirklich verdammt mutig sein. Respekt, dass Ihr immer noch an der Sache dran seid. Oder vielleicht haben die Rüstungsteile, die ich Euch gegeben habe, ja Euer Interesse geweckt. Wo die herkamen gibt\'s noch mehr, wenn Ihr daran interessiert seid, mir erneut auszuhelfen.$B$BDie Giftproben, die Ihr gesammelt habt, sind für einen Bekannten von mir bestimmt, Mux Manamix, ein Ingenieur mit einem gewissen Interesse für das Übernatürliche. Seine neueste Erfindung soll es angeblich ermöglichen, mit den Toten zu kommunizieren! Meine letzte Hoffnung ist es, Anthion zu kontaktieren und mehr über meine Situation herauszufinden.",
  },
  [8924] = {
    ["T"] = "Ektoplasmajagd",
    ["O"] = "Benutzt den Ektoplasmadestillierer in der Nähe von schemenhaften Untoten, um 12 Einheiten versengtes Ektoplasma in Silithus, 12 Einheiten gefrorenes Ektoplasma in Winterquell und 12 Einheiten stabiles Ektoplasma in den Östlichen Pestländern zu erhalten. Bringt das Ektoplasma dann mitsamt des Ektoplasmadestillierers zu Mux Manamix in Gadgetzan zurück.",
    ["D"] = "Ein hervorragendes Stück Ingenieurarbeit, wenn ich das mal selbst so sagen darf! Ich fürchte allerdings, dass Ihr weit umherreisen werden müsst, um die Art schemenhafter Untoten zu finden, die wir benötigen.$B$BIhr müsst verstehen, dass verschiedenen Klimabedingungen Einfluss auf die spektrale Struktur der Toten haben. Die gequälten Nachtelfen in Silithus, die Geister der Hochgeborenen in Winterquell und die Geister und Banshees in den Östlichen Pestländern sollten uns mit den verschiedenen Sorten von Ektoplasma versorgen, die wir benötigen. Stellt auf jeden Fall den Destillierer in der Nähe auf, bevor Ihr sie bekämpft.",
  },
  [8924] = {
    ["T"] = "Ektoplasmajagd",
    ["O"] = "Benutzt den Ektoplasmadestillierer in der Nähe von schemenhaften Untoten, um 12 Einheiten versengtes Ektoplasma in Silithus, 12 Einheiten gefrorenes Ektoplasma in Winterquell und 12 Einheiten stabiles Ektoplasma in den Östlichen Pestländern zu erhalten. Bringt das Ektoplasma dann mitsamt des Ektoplasmadestillierers zu Mux Manamix in Gadgetzan zurück.",
    ["D"] = "Ein hervorragendes Stück Ingenieurarbeit, wenn ich das mal selbst so sagen darf! Ich fürchte allerdings, dass Ihr weit umherreisen werden müsst, um die Art schemenhafter Untoten zu finden, die wir benötigen.$B$BIhr müsst verstehen, dass verschiedenen Klimabedingungen Einfluss auf die spektrale Struktur der Toten haben. Die gequälten Nachtelfen in Silithus, die Geister der Hochgeborenen in Winterquell und die Geister und Banshees in den Östlichen Pestländern sollten uns mit den verschiedenen Sorten von Ektoplasma versorgen, die wir benötigen. Stellt auf jeden Fall den Destillierer in der Nähe auf, bevor Ihr sie bekämpft.",
  },
  [8925] = {
    ["T"] = "Eine portable Energiequelle",
    ["O"] = "Findet Magmafürst Bokk in der Brennenden Steppe, nehmt seinen Magmakern an Euch und bringt ihn zu Mux Manamix in Gadgetzan.",
    ["D"] = "Jetzt brauchen wir nur noch eine stabile und portable Energiequelle, um das Ektoplasma aktiv zu halten. Ich habe Gerüchte über einen machtvollen Elementar namens Magmafürst Bokk gehört, dessen Herz ein mächtiger Magmakern ist - eine schier unerschöpfliche Hitzequelle!$B$BMan sagt, dass er sich irgendwo in der Brennenden Steppe südlich des Schwarzfels herumtreibt. Bringt mir den Magmakern und wir werden genug Saft haben, um den extradimensionalen Geisterdetektor zu betreiben!",
  },
  [8926] = {
    ["T"] = "Die angemessene Entlohnung",
    ["O"] = "Bringt Deliana in Eisenschmiede einen Gürtel des Wildherzens und ein Paar Handschuhe des Wildherzens.",
    ["D"] = "Ihr habt Euer Wort gehalten und den von Mux konstruierten Apparat beschafft. Ich denke, eine Belohnung wäre nun angebracht, um Euer Interesse daran, meine Haut zu retten, aufrechtzuerhalten. Bringt mir einen gewöhnlichen Gürtel und Handschuhe, von denen Ihr eine verbesserte Version wünscht. Ich kann Euch versichern, dass die Rüstung, die ich Euch geben werde, von weit höherer Qualität sein wird.",
  },
  [8927] = {
    ["T"] = "Die angemessene Entlohnung",
    ["O"] = "Bringt Mokvar in Orgrimmar einen Gürtel des Wildherzens und ein Paar Handschuhe des Wildherzens.",
    ["D"] = "Ihr habt große Verlässlichkeit bewiesen, $n. So sehr ich auch darauf vertraue, dass Ihr mir durch diese missliche Situation hindurchhelft, wäre es dennoch weiser, Euch zunächst Euren bisherigen Taten entsprechend zu entlohnen.$B$BIch werde zunächst Euren Gürtel und Eure Handschuhe austauschen. Wenn wir dies bis zum Ende durchstehen, werde ich Euch den Rest der Rüstung übergeben.",
  },
  [8928] = {
    ["T"] = "Ein verschlagener Händler",
    ["O"] = "Sucht den Wichtel in der Höhle in der Nähe der Flüsternden Schlucht im Süden von Winterquell. Kauft ihm einen teufelselementaren Brennstab ab und kehrt zu Mux Manamix in Gadgetzan zurück.",
    ["D"] = "Der Kern sollte uns mit genügend Energie zum Betreiben des Apparates versorgen, jetzt brauchen wir nur noch einen Brennstab, der diese Energie auch weiterleiten kann, ohne dabei zu schmelzen.$B$BIch habe in Winterquell mal einen Wichtel getroffen, der allerlei exotische Waren im Angebot hatte. Er hatte einen elementaren Brennstab, der einfach perfekt gewesen wäre. Ich könnte mich schwarz ärgern, dass ich den nicht gekauft habe.$B$BEr lebt in einer Höhle nahe dem Zugang zur Flüsternden Schlucht. Schleicht Euch hinein und seht zu, dass Ihr ihn findet.$B$BLasst Euch aber nicht von ihm übers Ohr hauen, zahlt ihm nicht mehr als 40 Goldstücke. Den Preis hat er mir damals angeboten!",
  },
  [8929] = {
    ["T"] = "Suche nach Anthion",
    ["O"] = "Benutzt den extradimensionalen Geisterdetektor am Eingang zu Stratholme und sprecht mit dem Geist von Anthion Harmon.",
    ["D"] = "Tja, wir haben wohl keine andere Wahl, als die Erfindung des Goblins auszuprobieren. Man sagt, dass Anthion von den untoten Horden Stratholmes niedergemetzelt wurde, als er versuchte, durch dessen Tore zu schreiten.$B$BVersucht den Apparat dort zu benutzen und herauszufinden, was auch immer Anthion wusste.",
