  [8945] = {
    ["T"] = "Die letzte Bitte eines toten Mannes",
    ["O"] = "Geht nach Stratholme und befreit Ysida Harmon aus den Fängen von Baron Totenschwur.",
    ["D"] = "Ich werde Euch mit den Informationen, nach denen Ihr verlangt, versorgen, $n. Aber jetzt gibt es Wichtigeres zu tun.$B$BMeine geliebte Frau wird von Totenschwurs Abschaum gefangen gehalten.$B$BIch war immer dagegen, dass sie sich dem närrischen Kreuzzug der Argentumdämmerung anschließt, aber jetzt ist es zu spät für Schuldgefühle. Ihr müsst das vollbringen, wozu ich nicht fähig war. Ihr müsst Ysida aus den Fängen des Barons befreien!",
  },
  [8946] = {
    ["T"] = "Lebensbeweis",
    ["O"] = "Bringt Anthion Harmon in den Östlichen Pestländern Ysidas Medaillon.",
    ["D"] = "Ein wahrhaft grausames Schicksal. Welche Ironie, dass mein Liebster bei dem Versuch mich zu retten ums Leben gekommen ist. Und dennoch behauptet Ihr, mit ihm sprechen zu können.$B$BBringt ihm dieses Medaillon als Beweis dafür, dass es mir gut geht. Er darf nicht länger in dieser Welt verweilen, mein Herz könnte seinen Schmerz nicht verkraften.$B$BLebet wohl, $n, ich stehe auf ewig in Eurer Schuld.",
  },
  [8947] = {
    ["T"] = "Anthions merkwürdige Anforderung",
    ["O"] = "Bringt Anthion Harmon in den Östlichen Pestländern 3 Dunkeleisenbarren, 20 verzaubertes Leder, 3 Mondstoff und 4 geschmeidige unverwüstliche Bälge.",
    ["D"] = "Wir waren uns der Wichtigkeit von Valthalaks Amulett nicht bewusst, also wurde es zusammen mit der restlichen Beute aufgeteilt. Das erste Stück bekam ein Zwerg namens Theldren. Ich habe versucht, es mir von ihm zurückzuholen und er hätte mich fast zu Tode geprügelt. Ihr werdet ihm sein Stück gewaltsam entreißen müssen.$B$BDas Letzte, was ich über ihn hörte, ist, dass er ein Gladiator für die Dunkeleisenzwerge geworden ist. Ihn zum offenen Kampf zu bewegen, dürfte sich jedoch schwierig gestalten.$B$BBringt mir die folgenden Materialien, Ihr werdet sie brauchen.",
  },
  [8948] = {
    ["T"] = "Anthions alter Freund",
    ["O"] = "Bringt Falrin Rankenweber in der Bibliothek von Düsterbruch das unvervollständigte Banner der Provokation.",
    ["D"] = "Theldren würde sich niemals freiwillig einem ehrenhaften Kampf stellen. Deshalb habe ich dieses Banner mit seinem Familienwappen hergestellt.$B$BMit der richtigen Verzauberung belegt, sollte es für ihn eine Provokation darstellen, die er nicht ignorieren kann.$B$BEin alter Kollege von mir namens Falrin weiß, was zu tun ist. Er versteht sich darauf... Leute zu verärgern. Ihr findet ihn in der Bibliothek von Düsterbruch.",
  },
  [8949] = {
    ["T"] = "Falrins Rachefeldzug",
    ["O"] = "Nehmt den Ogern in der Schwarzfelsspitze oder in Düsterbruch 25 Ogerkriegsperlen ab und bringt sie zu Falrin Rankenweber im Athenaeum, der Bibliothek von Düsterbruch.",
    ["D"] = "Ja, ich bin fürwahr mit der Verzauberung vertraut, die Ihr benötigt, $C. Aber alles hat seinen Preis, selbst wenn Euch ein alter Freund schickt.$B$BIch hasse und verabscheue meine barbarischen Ogernachbarn von ganzem Herzen. Und nicht nur die, ich hasse die Anhänger von Omokk genauso sehr, wie ich die Anhänger von König Gordok verabscheue.$B$BIch will, dass Ihr sie tötet, und zwar Unmengen von ihnen! Bringt mir die Perlen, die sie um den Hals tragen, als Beweis für Eure Taten. Solltet Ihr dies für mich tun, können wir uns weiter über den Gefallen unterhalten, den Ihr von mir verlangt.",
  },
  [8950] = {
    ["T"] = "Die Verzauberung des Aufhetzers",
    ["O"] = "Bringt Falrin Rankenweber in Düsterbruch 1 Essenz eines spöttischen Geisterwesens, 4 Dunkelrunen und 8 große glänzende Splitter.",
    ["D"] = "Es wäre mir ein Leichtes, das Banner mit der von Euch benötigten Verzauberung zu belegen, $n. Bedauerlicherweise verfüge ich allerdings nicht über die benötigten Materialien, da diese Art der Verzauberung nicht hoch im Kurs steht.$B$BIch werde die Essenz von einem der Eldrethgeisterwesen, die diese Hallen durchstreifen, einige Dunkelrunen aus Scholomance und ein paar große glänzende Splitter benötigen. Bringt mir diese Materialien und ich werde die Verzauberung für Euch vornehmen.",
  },
  [8951] = {
    ["T"] = "Anthions Abschiedsworte",
    ["O"] = "Kehrt mit einem Paar Stiefel des Wildherzens, einem Kilt des Wildherzens und einer Schiftung des Wildherzens zu Deliana in Eisenschmiede zurück.",
    ["D"] = "Valthalaks Seele wurde in diesem Amulett verwahrt. In unserer Gier haben wir es wie Narren in drei Teile aufgeteilt ohne um den Fluch zu wissen, der uns heimsuchen würde.$B$BDie einzige Möglichkeit, den Fluch aufzuheben, besteht darin das Amulett wieder zusammenzusetzen. Kehrt zu Deliana zurück und sagt ihr, dass Bodley unsere einzige Hoffnung ist, die einzelnen Teile wiederzufinden.$B$BIch selbst kann nun endlich in Frieden ruhen. Ich wünsche Euch viel Glück, Ihr werdet es brauchen.",
