  [8979] = {
    ["T"] = "Fenstads Visionen",
    ["O"] = "Bringt 1 Duftwasserflakon und 1 Parfümflakon zu Apothekerin Zinge im Apothekarium von Unterstadt.",
    ["D"] = "Ich denke, ich bin dem Grund für das merkwürdige Verhalten auf die Spur gekommen. Habt Ihr den Duft bemerkt, der in letzter Zeit den Gestank der Kanalisation überlagert? Das neue Duftwasser und Parfüm, das gerade so angesagt ist?$B$BUnsere Gedanken werden durch die Wirkung übelster Alchemie vernebelt.$B$BEs gibt nur einen Weg, um ganz sicher zu sein: Bringt meiner Freundin Apothekerin Zinge einen Flakon von dem Parfüm und dem Duftwasser. Die Königliche Apothekervereinigung wird die Wahrheit ans Licht bringen.$B$BIch werde Euch für Eure Ausgaben entschädigen, sobald Ihr zurückkommt.",
  },
  [8980] = {
    ["T"] = "Zinges Einschätzung",
    ["O"] = "Kehrt zu Fenstad Argyle außerhalb der Bank von Unterstadt zurück.",
    ["D"] = "Das ist in der Tat unerwartet. Es sieht ganz so aus, als ob Fenstad doch noch auf etwas gestoßen sein könnte. Ich habe Spuren von Substanzen gefunden, die in Gedanken beinflussenden Tränken Verwendung finden.$b$bEs ist nicht ungewöhnlich, dass man in Parfüm derartige Spuren auffindet, allerdings sind einige der Zutaten, die ich in den Düften finden konnte, doch recht... merkwürdig. Es handelt sich hierbei um Alchemiekünste, die nur den Erfahrensten vorbehalten sind, so wie die Mitglieder der Königlichen Apothekervereinigung.",
  },
  [8981] = {
    ["T"] = "Geschenkübergabe",
    ["O"] = "",
    ["D"] = "",
  },
  [8982] = {
    ["T"] = "Verfolgung der Spur",
    ["O"] = "Sprecht mit Gastwirt Norman in Unterstadt.",
    ["D"] = "Wir müssen unbedingt herausfinden, wer der Lieferant ist. Ich habe sie erst kürzlich dabei beobachtet, wie sie einen Parfümflakon in ihrem Bündel verstaute. Sie sagte mir, dass sie es von Norman, dem Gastwirt, erworben hat.",
  },
  [8983] = {
    ["T"] = "Verfolgung der Spur",
    ["O"] = "Sprecht mit Mara Rennick bei der Schneiderei im Magieviertel von Unterstadt.",
    ["D"] = "Ist etwas nicht in Ordnung mit meiner Ware? Nun, ich habe noch nie so viele Leute gesehen, die so sehr nach einer Sache verlangt haben.$b$bIch habe meine Bestände von einer Händlerin namens Mara Rennick. Sie ist neu in der Stadt.$b$bSie erwähnte, dass sie noch einige Waren beim Schneider im Magieviertel abzuliefern hätte.",
  },
  [8984] = {
    ["T"] = "Lüftung des Geheimnisses",
    ["O"] = "Findet Apotheker Staffron Lerent hinter dem Rabenholdtanwesen im Vorgebirge des Hügellands.",
    ["D"] = "Hört mal, die Taten eines abtrünnigen Apothekers sind nicht mein Problem. Sollte er tatsächlich unsere Sache untergraben, werde ich die Erste sein, die Euch die richtige Richtung weist.$B$BSollte er unschuldig sein, wird er nichts zu verbergen haben, oder?$B$BIch habe meine Ware beim Apotheker Staffron Lerent erworben. Er arbeitet im Vorgebirge des Hügellands, irgendwo hinter dem Rabenholdtanwesen. Ehrlich gesagt, kam er mir etwas komisch vor.",
  },
  [8985] = {
