  [8987] = {
    ["T"] = "Mehr Komponenten von großer Wichtigkeit",
    ["O"] = "Bringt Bodley im Schwarzfels ein gleißendes Schwert des Zeloten.",
    ["D"] = "Hört mir gut zu, $C. Jetzt, da wir wissen, wo das letzte Stück des Amuletts zu finden ist, brauchen wir noch ein paar weitere Dinge, die wir in das Räuchergefäß hineingeben müssen. Wir brauchen einen Fokus, der stark genug ist, um den Geist meines verstorbenen Kameraden herbeizurufen.$B$BDie Geschwister, Jarien und Sothos, glaubten fest an die Lehren des Scharlachroten Kreuzzugs. Sie strebten danach, Ritter dieser abscheulichen Organisation zu werden. Geht zu Tyrs Hand, in den Östlichen Pestländern, und beschafft mir ein gleißendes Schwert des Zeloten von den dortigen Prätorianern.",
  },
  [8988] = {
    ["T"] = "Mehr Komponenten von großer Wichtigkeit",
    ["O"] = "Bringt Bodley im Schwarzfels etwas Seelenasche der Verbannten.",
    ["D"] = "Hört mir gut zu, $C. Jetzt, da wir wissen, wo das letzte Stück des Amuletts zu finden ist, brauchen wir noch ein paar weitere Dinge, die wir in das Räuchergefäß hineingeben müssen. Wir brauchen einen Fokus, der stark genug ist, um den Geist meines verstorbenen Kameraden herbeizurufen.$B$BKormok, ein Ogernekromant, der unsererm Bund angehörte, liebte es die Seelen der verbannten Toten von der Fegefeuerinsel, nahe der Südküste der Vorgebirge des Hügellands, herbeizurufen. Geht dorthin und beschafft mir etwas Seelenasche der Verbannten von ihren Überresten.",
  },
  [8989] = {
    ["T"] = "Das rechte Stück von Lord Valthalaks Amulett",
    ["O"] = "Benutzt das Räuchergefäß der Beschwörung, um den Geist von Mor Grauhuf zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.",
    ["D"] = "Ein Freund von mir, Mor Grauhuf, war der Erste, der Lord Valthalaks Amulett und dem darin enthaltenen, korrumpierenden Seelenfragment zum Opfer fiel. Nachdem wir Valthalaks Zauberbuch und Amulett gestohlen hatten, flohen wir um unser Leben aus dem oberen Teil der Schwarzfelsspitze. Während unserer überstürzten Flucht fiel Mor in den unteren Teil der Schwarzfelsspitze hinab.$B$BWir erfuhren später, dass er den Fall überlebt hatte und von Kriegsmeister Voone gefangen genommen und gefoltert worden war. Später schlichen sich spektrale Assassine in die Schwarzfelsspitze ein und erschlugen Mor vor Voones Augen.",
  },
  [8990] = {
    ["T"] = "Das rechte Stück von Lord Valthalaks Amulett",
    ["O"] = "Benutzt das Räuchergefäß der Beschwörung, um den Geist von Isalien zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.",
    ["D"] = "Wir erfuhren später, während wir alle unser Bestes taten, um zu überleben, dass Isalien, eine Nachtelfenpriesterin, die unserer Gruppe angehörte, in die Heimatstadt der Urahnen der Nachtelfen zurückgekehrt war - jetzt als Düsterbruch bekannt. Demzufolge, was uns berichtet wurde, zog es sie sowohl aus persönlichen Gründen dorthin zurück, als auch weil sie die Geheimnisse ihres Stücks des Amuletts ergründen wollte.$B$BAllerdings gelang es ihr nicht, dieses Wissen zu erlangen. Wie uns zugetragen wurde, wurde sie am Schrein von Eldretharr von Alzzin dem Wildformer und seinen Schergen in einen Hinterhalt gelockt.",
  },
  [8991] = {
    ["T"] = "Das rechte Stück von Lord Valthalaks Amulett",
    ["O"] = "Benutzt das Räuchergefäß der Beschwörung, um die Geister von Jarien und Sothos zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.",
    ["D"] = "Die Geschwister, Jarien und Sothos, beschlossen, sich dem Scharlachroten Kreuzzug anzuschließen, nachdem sich unser Söldnerbund aufgelöst hatte. Das rechte Stück von Valthalaks Amulett nahmen sie mit sich. Ich war froh, sie los zu sein, aber jetzt brauchen wir dieses Stück zurück.$B$BUnseren Informationen zufolge, wurden sie vom Obersten Kreuzfahrer Dathrohan höchstpersönlich hingerichtet, da sie die Initiationsriten in seiner Kammer in der Scharlachroten Bastion nicht bestanden hatten. $B$BWie dem auch sei, wir... und damit meine ich Ihr, müssen dieses Stück des Amuletts zurückbekommen.",
  },
  [8992] = {
    ["T"] = "Das rechte Stück von Lord Valthalaks Amulett",
    ["O"] = "Benutzt das Räuchergefäß der Beschwörung, um den Geist von Kormok zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.",
    ["D"] = "Jetzt, wo wir alles beisammen haben, was wir benötigen, wird es Zeit, uns darüber zu unterhalten, wen Ihr als Nächstes beschwören werdet.$B$BKormok war für einen Oger ganz okay, bis er sich der Nekromantie zuwandte. Von da ab wurde es für jeden im Bund ganz schön gruselig. Es überraschte also niemanden, dass er mit seinem Stück des Amuletts in sein neues Zuhause verschwand, Scholomance.$B$BAber selbst dort, oder vielleicht gerade dort, war er nicht vor den spektralen Assassinen sicher. Sie erschlugen ihn vor den Augen seines Meisters, Ras Frostraunen.",
  },
  [8994] = {
    ["T"] = "Letzte Vorbereitungen",
    ["O"] = "Bringt Bodley im Schwarzfels 40 Schwarzfelsarmschienen und ein Fläschchen der obersten Macht.",
    ["D"] = "Wir sind nun beinah bereit, $C. Ich brauche nur noch einige weitere Komponenten, um das Räuchergefäß darauf abzustimmen, Lord Valthalak herbeizurufen.$B$BFür den Anfang würde ich Euch darum bitten, Euch in die Schwarzfelsspitze zu begeben und die dort heimischen Orcs zu töten. Ihre Armschienen enthalten Metallspuren, die ich benötigen werde. Da diese Armschienen nur Spuren des Metalls enthalten, werde ich eine Menge von ihnen benötigen. Um den Abstimmungsprozess abzuschließen, brauche ich weiterhin noch ein Fläschchen der obersten Macht.$B$BViel Glück, $n!",
  },
  [8995] = {
    ["T"] = "Mea Culpa, Lord Valthalak",
    ["O"] = "Benutzt das Räuchergefäß der Beschwörung, um Lord Valthalak zu beschwören. Macht ihn unschädlich und benutzt dann Lord Valthalaks Amulett bei seiner Leiche. Danach werdet Ihr dem Geist von Lord Valthalak sein Amulett zurückgeben müssen.",
    ["D"] = "Die nötigen Vorbereitungen sind nun getroffen, $n.$B$BZu seinen Lebzeiten bewohnte Lord Valthalak die Kammer, in der sich nun die Bestie aufhält. Geht dorthin und benutzt dann das Räuchergefäß der Beschwörung, um Lord Valthalak herbeizurufen. Es wird ihn wiederbeleben, also werdet Ihr ihn erneut töten müssen. Es wäre sinnvoll, zuerst die Bestie zu erledigen. Bringt außerdem genug Kameraden mit, um Euch durch den oberen Teil der Schwarzfelsspitze zu kämpfen.$B$BBenutzt, sobald Ihr ihn ausgeschaltet habt, das Amulett bei seiner Leiche. Nachdem seine Seele wieder vollständig ist, wird er es vermutlich zurückverlangen.$B$BViel Glück!",
  },
  [8996] = {
    ["T"] = "Rückkehr zu Bodley",
    ["O"] = "Bringt das Räuchergefäß der Beschwörung zurück zu Bodley im Schwarzfels.",
    ["D"] = "Ich werde Euch Eure Unverschämtheit dieses eine Mal vergeben, $C, da Ihr heute eine noble Tat vollbracht habt. Ihr habt mich wieder mit dem Rest meiner gestohlenen Seele vereint. Obgleich Eurer offensichtlich schwachen Natur, $R, habt Ihr den Mut bewiesen, über den jene, die es wagten mich zu bestehlen, nicht verfügten und vermutlich niemals werden.$B$BKehret schnell zu ihnen zurück, bevor ich es mir anders überlege und Euch dort niederstrecke, wo Ihr steht. Kehret zurück und richtet ihnen aus, dass sie vor mir sicher sind... Momentan zumindest.",
  },
