  [9120] = {
    ["T"] = "Der Niedergang Kel\'Thuzads",
    ["O"] = "Bringt Kel\'Thuzads Phylakterium zu der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.",
    ["D"] = "Das Phylakterium ist alles, was vom Herrscher Naxxramas\' übrig bleibt. Nach Eurem pflichtgemäßen Ermessen, müsstet Ihr das Phylakterium zerstören, um somit einem erneuten Aufbäumen des Lichs vorzubeugen. Glücklicherweise hört Ihr nur selten auf Eure innere Stimme der Vernunft.$B$BBei der Kapelle des Hoffnungsvollen Lichts wird Euch irgendjemand mit Sicherheit ein Vermögen für dieses Artefakt bezahlen. Wen kümmert es schon, ob Kel\'Thuzad eines Tages wieder von Neuem erstarkt?",
  },
  [9121] = {
    ["T"] = "Die Zitadelle des Schreckens - Naxxramas",
    ["O"] = "Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 5 Arkankristalle, 2 Nexuskristalle, 1 rechtschaffene Kugel und 60 Goldstücke. Euer Ruf bei der Argentumdämmerung muss außerdem wohlwollend sein.",
    ["D"] = "Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.$B$BWir haben einen Weg gefunden, wie wir uns mithilfe von arkaner Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Da es an Eurer Hingabe für die Ziele der Argentumdämmerung noch etwas mangelt, $n, wird der Preis hoch sein. Es steht Euch frei, abzulehnen und noch einmal für einen günstigeren Preis wiederzukehren, sobald Euer Ruf respektvoll oder ehrfürchtig ist.",
  },
  [9122] = {
    ["T"] = "Die Zitadelle des Schreckens - Naxxramas",
    ["O"] = "Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 2 Arkankristalle, 1 Nexuskristall und 30 Goldstücke. Euer Ruf bei der Argentumdämmerung muss außerdem respektvoll sein.",
    ["D"] = "Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.$B$BWir haben einen Weg gefunden, wie wir uns mithilfe von einer Art dauerhaften arkanen Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Diese Tarnung ist allerdings sehr kostspielig. Da Eure Hingabe für die Ziele der Argentumdämmerung bewundernswert ist, sind wir gewillt, einige dieser Kosten für Euch zu übernehmen.",
  },
  [9123] = {
    ["T"] = "Die Zitadelle des Schreckens - Naxxramas",
    ["O"] = "Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird Euch die arkane Tarnung kostenfrei gewähren. Euer Ruf bei der Argentumdämmerung muss ehrfürchtig sein.",
    ["D"] = "Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.$B$BWir haben einen Weg gefunden, wie wir uns mithilfe von einer Art dauerhaften arkanen Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Diese Tarnung ist allerdings sehr kostspielig. Da Eure Hingabe für die Ziele der Argentumdämmerung jedoch unerschütterlich ist, werden wir Euch sämtliche Kosten erlassen.",
  },
  [9124] = {
    ["T"] = "Was man für Gruftpirscherrüstung braucht...",
    ["O"] = "Jäger Leopold bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Teile einer Gruftbestie.",
    ["D"] = "Würdet Ihr Euch gerne eine dieser glänzenden Münzen verdienen?$B$B<Leopold hält ein Insigne des Kreuzzugs in die Höhe.>$B$BIch habe ein kleines Problem, bei dem Ihr mir vielleicht behilflich sein könntet, $n. Seht Ihr, ich fertige Gruftpirscherrüstung für diejenigen an, die dämlich genug sind, um sich nach Naxxramas zu wagen. Zwar versorgen sie mich mit den wichtigsten Bestandteilen für die Rüstung versorgen, mir gehen aber ständig die Teile von Gruftbestien aus. Bringt mir einen Schwung dieser Teile und ich werde Euch dafür mit einem Insigne bezahlen - einlösbar bei dem Rüstmeister.",
  },
