  [9247] = {
    ["T"] = "Der Ruf des Bewahrers",
    ["O"] = "Bringt den Brief vom Bewahrer der Schriften zu ihm, in die Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.",
    ["D"] = "$n,$B$BIn der Vergangenheit habt Ihr die Interessen der Argentumdämmerung nach bestem Gewissen und Können gewahrt. In einer Zeit der größten Not, ertönt wieder einmal der Ruf zu den Waffen.$B$B$n, solltet Ihr uns unterstützen wollen, meldet Euch bitte bei dem Bewahrer der Schriften an der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.",
  },
  [9248] = {
    ["T"] = "Eine bescheidene Darbringung",
    ["O"] = "Aurel Goldblatt auf Burg Cenarius in Silithus möchte, dass Ihr ihr 1 abyssisches Szepter bringt. Euer Ruf bei dem Zirkel des Cenarius muss außerdem wohlwollend sein.",
    ["D"] = "Ishnu-alah, $R. Bringt mir den Beweis für den Tod eines Lords des Abyssischen Rates und ich werde Euch mit einem Gegenstand entlohnen, der Euch bei Euren Kämpfen gegen die Qiraji von großem Nutzen sein wird.",
  },
  [9250] = {
    ["T"] = "Grundstab von Atiesh",
    ["O"] = "Findet jemanden, der Euch dabei hilft, die letzten beiden Teile des Stabs zu finden und das Böse daraus zu vertreiben.",
    ["D"] = "Mit dem Grundstab ist bereits der wichtigste Teil von Atiesh vervollständigt worden. Es fehlen lediglich noch Stabkopf und -fuß. Außerdem gibt es noch ein kleineres Problem bezüglich des Stabs und seiner Rolle hinsichtlich Sargeras\' Lenkung zum Bösen.$B$BFindet jemanden, der Euch beim Aufspüren der noch fehlenden Teile und beim Austreiben alles Verderbten aus dem Hohestab zur Seite steht.",
  },
  [9251] = {
    ["T"] = "Atiesh, der korrumpierte Hohestab",
    ["O"] = "Anachronos in den Höhlen der Zeit in Tanaris möchte den Stabkopf und -fuß von Atiesh.",
    ["D"] = "Ihr fragt Euch, warum Ihr noch immer am Leben seid? Schließlich haltet Ihr einen Gegenstand in Euren Händen, der vom Bösen erfüllt ist. Die Antwort ist nicht eindeutig, $n. Ich tue nur, was ich tun muss.$B$BKel\'Thuzad sitzt auf seinem Thron hoch oben in Naxxramas, konspiriert... Er sucht was Ihr besitzt und besitzt was Ihr sucht.$B$BWas den Stabfuß angeht, er wurde von dem Zwerg, Brann Bronzebart, gehalten, als er Ahn\'Qiraj betrat. Er hat Ahn\'Qiraj zwar verlassen, den Stabfuß von Atiesh jedoch zurückgelassen.$B$BSolltet Ihr beim Wiedererlangen der Stücke Erfolg haben, kehrt mit ihnen zu mir zurück.",
  },
  [9257] = {
    ["T"] = "Atiesh, Hohestab des Wächters",
    ["O"] = "Anachronos in den Höhlen der Zeit in Tanaris möchte, dass Ihr Atiesh, den Hohestab des Wächters, nach Stratholme bringt und dort auf dem geheiligten Boden platziert. Bezwingt das Wesen, das aus dem Stab getrieben wurde, und kehrt danach wieder zu ihm zurück.",
    ["D"] = "Die letzte Aufgabe ist zugleich die schwierigste von allen, $n. Ihr seid einem Alten Gott und dem General des Lichkönigs gegenübergetreten und habt beide besiegt. Nun müsst Ihr Euch jedoch der Hand Sargeras\' stellen.$B$BBringt den Stab nach Stratholme. Es ist der Ort, an dem Ihr geheiligten Boden finden werdet: Boden, auf dem die mächtigsten Ritter Lordaerons getötet wurden. Platziert den korrumpierten Stab auf dem heiligen Grund und bereitet Euch darauf vor, dass eine Welle immenser Macht daraus hervorbrechen wird... Vernichtet das dämonische Wesen, das den Stab kontrolliert und kehrt zu mir zurück.",
  },
  [9259] = {
    ["T"] = "Verrat am Blutsegel",
    ["O"] = "",
    ["D"] = "",
  },
  [9260] = {
    ["T"] = "Erforscht die Geißel von Sturmwind",
    ["O"] = "Beschafft Euch drei dunkle nekrotische Steine von den Dienern der Geißel außerhalb von Sturmwind und untersucht die leuchtenden Runenkreise in der Nähe ihres Lagers.",
    ["D"] = "Der Lichkönig hat seine untoten Diener hierher geschickt - sie stehen also sozusagen direkt vor unserer Haustüre. Wir können das nicht weiter untätig hinnehmen! Ihr steht uns doch sicherlich bei der Verteidigung von Sturmwind gegen diese verfluchten Eindringlinge zur Seite?$b$bEs gibt dort draußen ein paar merkwürdige Runenkreise, deren leuchtende Energie dieselbe ist wie diejenige, welche die Untoten und die Nekropole dort oben umgibt. Ich bin mir sicher, dass sie von Bedeutung sind. Findet heraus, was es damit auf sich hat. Lichtet die Reihen dieser entarteten Kreaturen, wenn Ihr schon einmal dabei seid, und bringt mir neben Eurem Bericht auch etwas als Beweis dafür, dass sie tot sind. Ihr werdet nicht leer ausgehen. Geht jetzt!",
  },
