  [9362] = {
    ["T"] = "Kriegsherr Krellian",
    ["O"] = "Beschafft die prismatische Muschel für Erzmagier Xylem. Der Erzmagier befindet sich in einem Turm auf den Klippen von Azshara.",
    ["D"] = "Die Zeit drängt, $n! Meine Orakelsprüche haben eine mächtige Quelle der Magie offenbart. Ich kann den Turm nicht verlassen und bitte Euch deshalb, diese bösartige Kreatur, die im Besitz dieses Artefakts ist, zu töten.$b$bDer erbärmliche Schwachkopf ist bekannt als Kriegsherr Krellian und versteckt sich hinter seinen Wachen im Tempel von Zin-Malor. Verbrennt ihn zu Asche und nehmt die prismatische Muschel, die er so begehrt, an Euch.",
  },
  [9364] = {
    ["T"] = "Gespaltene Magie",
    ["O"] = "Verwandelt die Grollflossen von Azshara und tötet die Kopien, die einige Sekunden später auftauchen. Wenn Ihr 50 verwandelte Kopien vernichtet habt, kehrt zu Erzmagier Xylem in Azshara zurück.",
    ["D"] = "Meine Untersuchungen haben den einzigartigen Zweck für die prismatische Muschel enthüllt.$b$bGreift die Grollflossen von Azshara mit Verwandlungszaubern an und tragt dabei diese Muschel bei Euch. Wenn Ihr einen Moment wartet, wird die chaotische Aura der Muschel dafür sorgen, dass sie sich für eine kurze Zeit in mehrere kleinere Versionen aufspalten. Tötet diese Kopien so schnell wie möglich, und deren gespaltene Essenzen werden in der Muschel gesammelt.$b$bKehrt zu mir zurück, sobald die Muschel gefüllt ist.",
  },
  [9365] = {
    ["T"] = "Der Lohn des Diebes",
    ["O"] = "Bringt die Flamme von Sturmwind zu (NAME).",
    ["D"] = "Die Flamme, die aus dem unmittelbaren Zentrum von Sturmwind entwendet wurde, durchflutet Euch mit Wärme. Diese durchdringende Macht ist Euch fremd, aber (NAME) könnte vielleicht mehr darüber wissen...",
  },
  [9368] = {
    ["T"] = "Das Fest des Feuers",
    ["O"] = "Wärmt Euch an den Freudenfeuern in Orgrimmar, Donnerfels und Unterstadt. Sprecht anschließend mit einem Geschichtenerzähler innerhalb der Hauptstädte.",
    ["D"] = "Die Zeit des Sonnenwendfests ist wieder angebrochen!$b$bJedes Jahr werden, wie die Tradition es verlangt, Flammenbewahrer auserkoren, die die Freudenfeuer in unseren Hauptstädten hüten. Auch wenn Feuer in ganz Azeroth brennen, so müssen unsere stets die heißesten und hellsten von allen sein, um dieser Zeit im Jahr ausreichend Ehrerbietung zu erweisen.$b$bIch hätte da übrigens eine Aufgabe für Euch. Mir ist noch nicht zu Ohren gekommen, ob die Feuer in allen Hauptstädten auch wirklich ordentlich brennen. Reist selbst dorthin und wärmt Euch im Schein der Flammen - überzeugt Euch, dass sie ausreichend heiß sind!",
  },
  [9386] = {
    ["T"] = "Licht im Dunkeln",
    ["O"] = "Findet und berührt die Freudenfeuer innerhalb der Schwarzfelsspitze, dem Düsterbruch, Scholomance und Stratholme. Sprecht dann mit dem Flammenbewahrer.",
    ["D"] = "Ihr seid ziemlich eifrig, oder? Solltet Ihr Euch noch einmal in die Tiefen wagen, so bin ich gerne bereit, Euch dafür zu belohnen.$b$bSucht die Freudenfeuer in Stratholme, Scholomance, dem Düsterbruch und den Hallen der Schwarzfelsspitze auf. Ich erwarte Eure Rückkehr, $n.",
  },
  [9415] = {
    ["T"] = "Meldung bei Marschall Himmelswand",
    ["O"] = "Sprecht mit Marschall Himmelswand beim Lager der Allianz in der Nähe des Zoraschwarmbaus.",
    ["D"] = "Gehört Ihr zu Marschall Himmelswands neuen Rekruten? Natürlich.$b$bAuf was wartet Ihr noch? Gute Soldaten verlieren ihr Leben in der Wüste. Freut es Euch nicht, bald dazuzugehören?$b$bLos jetzt, macht Euch auf den Weg... und trocknet dabei nicht aus.",
  },
  [9416] = {
    ["T"] = "Meldung bei General Kirika",
    ["O"] = "Sprecht mit General Kirika beim Lager der Horde in der Nähe des Regalschwarmbaus.",
    ["D"] = "Ihr müsst zu General Kirikas neuen Rekruten gehören. Es wurde Zeit, dass Ihr kommt. Der Truppenverschleiß in der Wüste ist hoch.$b$bDie Zwerge haben tagtäglich Verstärkung bekommen, wir können es uns nicht leisten in unserem Kampf um Ressourcen in Silithus an Boden zu verlieren.$b$bLasst Euch vom General weitere Anweisungen geben. Ich muss Euch Rekruten lediglich finden, nicht ausbilden.",
  },
  [9419] = {
    ["T"] = "Durchkämmen der Wüste",
    ["O"] = "Findet Silithyst, bringt es zum Silithystextraktor und sprecht anschließend mit Marschall Himmelswand beim Lager der Allianz in der Nähe des Zoraschwarmbaus.",
    ["D"] = "Wir hatten vor Kurzem herausgefunden, dass die Horde die Wüste von Silithus nach Ressourcen abgesucht hat, schenkten dem Ganzen aber keine besondere Beachtung. Wir dachten, es wäre die Sache nicht wert, eine größere Anzahl von Soldaten in dieser Bruthitze zu stationieren.$b$bWie dem auch sei, wir haben die Wichtigkeit dieser Ressourcen erkannt und versuchen nun, um jeden Preis aufzuholen.$b$bWenn Ihr uns unterstützen wollt, müsst Ihr in die Wüste gehen, dort nach Silithyst suchen und diesen anschließend hierher bringen.",
  },
  [9422] = {
    ["T"] = "Durchkämmen der Wüste",
    ["O"] = "Findet Silithyst, bringt es zum Silithystextraktor und sprecht anschließend mit General Kirika beim Lager der Horde in der Nähe des Regalschwarmbaus.",
    ["D"] = "Wir haben die Wüste von Silithus bereits seit einiger Zeit nach Kristallressourcen durchkämmt. Die einzigen Gefahren, die sich uns dabei in den Weg gestellt haben, waren die Kreaturen, die dort heimisch sind, sowie Sand, Staub und Hitze.$b$bDie Allianz scheint nun aber Wind von unserem Unterfangen bekommen zu haben. Ein Rennen hat begonnen. Ein Rennen um die natürlichen Ressourcen und um deren Verteidigung.$b$bWir brauchen jede helfende Hand, die wir bekommen können. Ich hoffe, Ihr werdet uns unterstützen.",
  },
  [9664] = {
    ["T"] = "Mehr Befestigungen",
    ["O"] = "Erobert den Turm der Kronenwache, den Ostwallturm, den Nordpassturm und den Pestwaldturm in den Östlichen Pestländern. Kehrt anschließend zum Abgesandten Weißbart bei der Kapelle des Hoffnungsvollen Lichts zurück.",
    ["D"] = "Willkommen in den Pestländern, $n. Wie Ihr wisst, behaupten sich die Verlassenen und ihre Verbündeten entgegen all unserer Anstrengungen in den von hier aus westlich gelegenen Ländern. Die Kommandanten der Allianz haben beschlossen, dass hier im Osten neue Befestigungen errichtet werden sollen, um die Schlinge um unseren Feind noch etwas enger zu schnüren.$b$bLange seit den Tagen Lordaerons verlassene und herrenlose Türme müssen für diesen Zweck erobert und wieder aufgebaut werden. Diese Länder hier sind gefährlich, wir brauchen Eure Hilfe.",
  },
  [9665] = {
    ["T"] = "Verstärkung der Verteidigung",
    ["O"] = "Erobert den Turm der Kronenwache, den Ostwallturm, den Nordpassturm und den Pestwaldturm in den Östlichen Pestländern. Kehrt anschließend zum Abgesandten Gormok bei der Kapelle des Hoffnungsvollen Lichts zurück.",
    ["D"] = "Agenten der Verlassenen sind hinter das Vorhaben der Allianz gekommen. Sie wollen neue Stützpunkte bei verlassenen Türmen hier in den Östlichen Pestländern einrichten.$b$bWir dürfen auf keinen Fall zulassen, dass sie sich an unserer östlichen Grenze dauerhaft festsetzen, wir müssen den Einfall der Allianz abwehren. Wir beabsichtigen sie aus jeglichen Türmen, die sie bereits besetzt haben, zu vertreiben. Dann gilt es diese Türme zu verteidigen und weitere Eroberungsversuche abzuwehren.",
  },
}
